INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(1, 1, 'iPhone 13 Pro Max 128GB từng được Nam sử dụng', '../assets/image/products/phone/iPhone-12-Pro-Max-128GB/nền.jpg', 100000000, 100, 5, 648, 1, 1, '<div class=\"content__row\"> <span class=\"content__row-title\">Màn hình:</span> <span class=\"content__row-content\">OLED, 6.7\", Super Retina XDR</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">Hệ điều hành:</span> <span class=\"content__row-content\">iOS 14</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">Camera sau:</span> <span class=\"content__row-content\">3 camera 12 MP</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">Camera trước:</span> <span class=\"content__row-content\">12 MP</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">Chip:</span> <span class=\"content__row-content\">12 MP</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">RAM:</span> <span class=\"content__row-content\">6 GB</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">Bộ nhớ trong:</span> <span class=\"content__row-content\">128 GB</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">SIM:</span> <span class=\"content__row-content\">1 Nano SIM & 1 eSIM, Hỗ trợ 5G</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">Pin:</span> <span class=\"content__row-content\">3687 mAh, Sạc nhanh</span> </div> ', '<b class=\"paragraph__title\">Đặc điểm nổi bật của OPPO A15</b>\r\n                <h1 class=\"paragraph__h1\">OPPO hãng điện thoại luôn được người Việt tin dùng và lựa chọn, mới đây hãng đã cho ra mắt mẫu OPPO A15 có thiết kế đẹp, cấu hình đủ dùng. Đây sẽ là mẫu điện thoại thông minh phù hợp cho mọi đối tượng người dùng đi cùng với mức giá vô cùng hợp lý.</h1>\r\n                <h2 class=\"paragraph__h2\">Màn hình lớn, thoải mái xem phim, đọc báo</h2>\r\n                <p class=\"paragraph__p\">OPPO A15 được trang bị công nghệ màn hình IPS LCD, cho độ phân giải HD+ (720 x 1600 Pixels), màn hình lớn 6.52 inch thoải mái nhìn khi sử dụng, đặc biệt là dành cho người lớn tuổi hay sử dụng điện thoại thông minh để đọc báo, xem tin tức.</p>\r\n                <img class=\"paragraph__img\" src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-1-1.jpg\" alt=\"\">\r\n                <p class=\"paragraph__p\">OPPO A15 được trang bị công nghệ màn hình IPS LCD, cho độ phân giải HD+ (720 x 1600 Pixels), màn hình lớn 6.52 inch thoải mái nhìn khi sử dụng, đặc biệt là dành cho người lớn tuổi hay sử dụng điện thoại thông minh để đọc báo, xem tin tức.</p>\r\n                <img class=\"paragraph__img\" src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-1-1.jpg\" alt=\"\">\r\n                <h2 class=\"paragraph__h2\">Màn hình lớn, thoải mái xem phim, đọc báo</h2>\r\n                <p class=\"paragraph__p\">OPPO A15 được trang bị công nghệ màn hình IPS LCD, cho độ phân giải HD+ (720 x 1600 Pixels), màn hình lớn 6.52 inch thoải mái nhìn khi sử dụng, đặc biệt là dành cho người lớn tuổi hay sử dụng điện thoại thông minh để đọc báo, xem tin tức.</p>\r\n                <img class=\"paragraph__img\" src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-3-1.jpg\" alt=\"\">\r\n                <p class=\"paragraph__p\">Hệ thống camera như thế này là quá đủ để cho bạn chụp mọi hình ảnh trong mọi khoảnh khắc với rõ nét cao. Ống kính macro 2 MP cho phép bạn chụp cận cảnh để khám phá những điều diệu kỳ của thế giới thu nhỏ. Ngoài ra, ống kính đo độ sâu 2 MP giúp mọi bức ảnh chân dung trở nên hoàn hảo làm nổi bật chủ thể hơn.</p>\r\n                <button class=\"paragraph__more\">Xem thêm !</button>', 1, 1),
(2, 1, 'Samsung Galaxy S22+ 5G 256GB', '../assets/image/products/phone/Samsung-Galaxy-S21+5G-256GB/Nền.jpg', 4500000, 100, 1, 129, 1, 2, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">Dynamic AMOLED 2X, 6.8\", Quad HD+ (2K+)</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">Android 11</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">Chính 108 MP & Phụ 12 MP, 10 MP, 10 MP</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">40 MP</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">Exynos 2100 8 nhân</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">12 GB</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">256 GB</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">2 Nano SIM hoặc 1 Nano SIM + 1 eSIM, Hỗ trợ 5G</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">5000 mAh, có sạc nhanh</span>\r\n</div> ', '<b class=\"paragraph__title\">Đặc điểm nổi bật của OPPO A15</b>\r\n                <h1 class=\"paragraph__h1\">OPPO hãng điện thoại luôn được người Việt tin dùng và lựa chọn, mới đây hãng đã cho ra mắt mẫu OPPO A15 có thiết kế đẹp, cấu hình đủ dùng. Đây sẽ là mẫu điện thoại thông minh phù hợp cho mọi đối tượng người dùng đi cùng với mức giá vô cùng hợp lý.</h1>\r\n                <h2 class=\"paragraph__h2\">Màn hình lớn, thoải mái xem phim, đọc báo</h2>\r\n                <p class=\"paragraph__p\">OPPO A15 được trang bị công nghệ màn hình IPS LCD, cho độ phân giải HD+ (720 x 1600 Pixels), màn hình lớn 6.52 inch thoải mái nhìn khi sử dụng, đặc biệt là dành cho người lớn tuổi hay sử dụng điện thoại thông minh để đọc báo, xem tin tức.</p>\r\n                <img class=\"paragraph__img\" src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-1-1.jpg\" alt=\"\">\r\n                <p class=\"paragraph__p\">OPPO A15 được trang bị công nghệ màn hình IPS LCD, cho độ phân giải HD+ (720 x 1600 Pixels), màn hình lớn 6.52 inch thoải mái nhìn khi sử dụng, đặc biệt là dành cho người lớn tuổi hay sử dụng điện thoại thông minh để đọc báo, xem tin tức.</p>\r\n                <img class=\"paragraph__img\" src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-1-1.jpg\" alt=\"\">\r\n                <h2 class=\"paragraph__h2\">Màn hình lớn, thoải mái xem phim, đọc báo</h2>\r\n                <p class=\"paragraph__p\">OPPO A15 được trang bị công nghệ màn hình IPS LCD, cho độ phân giải HD+ (720 x 1600 Pixels), màn hình lớn 6.52 inch thoải mái nhìn khi sử dụng, đặc biệt là dành cho người lớn tuổi hay sử dụng điện thoại thông minh để đọc báo, xem tin tức.</p>\r\n                <img class=\"paragraph__img\" src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-3-1.jpg\" alt=\"\">\r\n                <p class=\"paragraph__p\">Hệ thống camera như thế này là quá đủ để cho bạn chụp mọi hình ảnh trong mọi khoảnh khắc với rõ nét cao. Ống kính macro 2 MP cho phép bạn chụp cận cảnh để khám phá những điều diệu kỳ của thế giới thu nhỏ. Ngoài ra, ống kính đo độ sâu 2 MP giúp mọi bức ảnh chân dung trở nên hoàn hảo làm nổi bật chủ thể hơn.</p>\r\n                <button class=\"paragraph__more\">Xem thêm !</button>', 2, 1),
(3, 3, 'Air 4 Wifi 64GB', '../assets/image/products/tablet/Air-4-Wifi-64GB/1.jpg\r\n', 16190000, 0, 5, 98, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nLiquid Retina, 10.9\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\niPadOS 14\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nApple A14 Bionic\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n4 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n64 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n12 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n7 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nNghe gọi qua FaceTime\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\nKhông\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nFaceTime\r\n</span>\r\n</div>', '<b class=\"paragraph__title\">Đặc điểm nổi bật của iPad Air 4 Wifi 64GB</b>\r\n<h1 class=\"paragraph__h\"> Đây là thiết bị đầu tiên của hãng được trang bị chip A14 Bionic - chip di động mạnh nhất của Apple hiện nay</h1>\r\n<h2 class=\"paragraph__h2\">Chip mạnh hàng đầu cho trải nghiệm hoàn hảo</h2>\r\n<p class=\"paragraph__p\">Apple trang bị cho iPad Air 4 chip A14 Bionic 6 nhân được sản xuất bởi TSMC với tiến trình 5 nm hiện đại nhất hiện nay</p>\r\n<img class=\"paragraph__img\" src=\"../assets/image/products/tablet/Air-4-Wifi-64GB/description/1_2.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Tinh tế trong thiết kế cùng nhiều màu sắc đi kèm</h2>\r\n<p class=\"paragraph__p\">iPad Air 2020 được thiết kế nguyên khối đẳng cấp cùng nhiều màu sắc trẻ trung cho bạn có nhiều sự lựa chọn hơn như: bạc, xám đen, hồng, xanh lá và xanh dương.</p>\r\n<img class=\"paragraph__img\" src=\"../assets/image/products/tablet/Air-4-Wifi-64GB/description/1_3.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Màn hình 60 Hz không gian hiển thị tuyệt vời</h2>\r\n<p class=\"paragraph__p\"> iPad Air 2020 sử dụng công nghệ Liquid Retina và có độ phân giải 1640 x 2360 Pixels và có tần số quét đến 60 Hz giúp iPad Air hiển thị hình ảnh mượt mà hơn, các chuyển động hình ảnh trơn tru hơn, gần như loại bỏ hoàn toàn tình trạng giật lag</p>\r\n<img class=\"paragraph__img\" src=\"../assets/image/products/tablet/Air-4-Wifi-64GB/description/1_4.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Camera đơn đa chức năng</h2>\r\n<p class=\"paragraph__p\">Camera sau có độ phân giải 12 MP với khẩu độ f/1.8 cùng với đó là khả năng quay video 4K/60fps chống rung được nâng cấp, hỗ trợ Smart HDR, slow motion 240fps và không có đèn flash LED</p>\r\n<img class=\"paragraph__img\" src=\"../assets/image/products/tablet/Air-4-Wifi-64GB/description/1_5.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Cổng sạc Type-C thuận lợi</h2>\r\n<p class=\"paragraph__p\">iPad Air 2020 chuyển mình với cổng USB Type-C giống trên các thiết bị iPad Pro với tốc độ truyền tải 5 Gbps</p>\r\n<img class=\"paragraph__img\" src=\"../assets/image/products/tablet/Air-4-Wifi-64GB/description/1_6.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n\r\n\r\n\r\n\r\n', 1, 1),
(4, 3, 'Samsung Galaxy Tab S7', '../assets/image/products/tablet/Samsung-Galaxy-Tab-S7/2.jpg\r\n', 18990000, 0, 5, 48, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nLTPS IPS LCD, 11\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 865+ 8 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n6 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 13 MP & Phụ 5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM (chung thẻ nhớ)\r\n</span>\r\n</div>\r\n', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy Tab S7</b>\r\n<h1 class =\"paragraph__h1\">Chiếc máy tính bảng có: thiết kế tuyệt đẹp, màn hình 120 Hz siêu mượt, camera kép ấn tượng, bút S Pen</h1>\r\n<h2 class =\"paragraph__h2\">Thiết kế tràn viền cao cấp, thời thượng</h2>\r\n<p class =\"paragraph__p\">Galaxy Tab S7 vẫn giữ nguyên thiết kế kim loại nguyên khối, góc cạnh như người tiền nhiệm Samsung Galaxy Tab S6 trước đó, cho cảm giác sang trọng và cao cấp, cầm vào tay rất vừa vặn và thoải mái.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-S7/description/2_2.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Màn hình sắc nét, tốc độ làm mới 120 Hz</h2>\r\n<p class =\"paragraph__p\">Galaxy Tab S7 được trang bị màn hình LCD 11 inch độ phân giải WQXGA (2.560 x 1.600 pixels) có độ phủ gam màu 100% DCI-P3 và hỗ trợ HDR.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-S7/description/2_3.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Hỗ trợ các thiết bị thông minh</h2>\r\n<p class =\"paragraph__p\">Đầu tiên, Bút S Pen trên Tab S7 cũng đã được cải tiến rất nhiều, bạn có thể tạo các thao tác nhanh như quay lại, trang chủ, gần đây, chọn thông minh và quay màn hình, hỗ trợ tốt hơn cho nhiều tác vụ như trình chiếu.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-S7/description/2_4.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Camera kép với chất lượng </h2>\r\n<p class =\"paragraph__p\">Mặt sau của Galaxy Tab S7 được trang bị thiết lập camera kép bao gồm cảm biến chính 13 MP và ống kính siêu rộng 5 MP.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-S7/description/2_5.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Hiệu năng mạnh mẽ, dẫn đầu phân khúc máy tính bảng Android</h2>\r\n<p class =\"paragraph__p\">Không chỉ gây ấn tượng thiết kế và màn hình, Galaxy Tab S7 còn thể hiện mình là một thiết bị tốt toàn diện hơn nữa khi được trang bị vi xử lý Snapdragon 865+ mạnh mẽ, đây là chip mới nhất và tốt nhất của Qualcomm ở thời điểm hiện tại.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-S7/description/2_6.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Dung lượng pin lớn, hỗ trợ công nghệ sạc siêu nhanh</h2>\r\n<p class =\"paragraph__p\">Galaxy Tab S7 được trang bị viên pin dung lượng khổng lồ 8000 mAh, giúp bạn sử dụng cả ngày dài mà không cần lo lắng về việc hết pin khi đang dùng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-S7/description/2_7.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Thiết bị cung cấp thời lượng pin khoảng 7 giờ 30 phút khi duyệt web, 6 giờ 45 phút khi xem video và gần 10 giờ chơi game.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n\r\n\r\n\r\n\r\n', 2, 1),
(5, 3, 'Samsung Galaxy Tab A7 2020', '../assets/image/products/tablet/Samsung-Galaxy-Tab-A7-2020/3.jpg\r\n', 7390000, 0, 5, 10, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nTFT, 10.4\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 662 8 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n64 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy Tab A7</b>\r\n<h1 class =\"paragraph__h1\">Samsung Galaxy Tab A7 (2020) là một chiếc máy tính bảng có thiết kế đẹp, cấu hình khá, nhiều tính năng tiện ích</h1>\r\n<h2 class=\"paragraph__h2\">Màn hình lớn cho trải nghiệm bất tận</h2>\r\n<p class =\"paragraph__p\">Chiếc Galaxy Tab A7 mang khá nhiều nét kế thừa từ người đàn anh ở phân khúc cao cấp là Galaxy Tab S7 vừa ra mắt không lâu, sở hữu thiết kế nguyên khối cứng cáp liền mạch, được hoàn thiện từ nhôm đi kèm khung viền kim loại sang trọng, ấn tượng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-A7-2020/description/3_2.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Hiệu năng tốt, đa nhiệm mượt mà</h2>\r\n<p class =\"paragraph__p\">Bên trong Samsung Galaxy Tab A7 (2020) là bộ vi xử lý Snapdragon 662 gồm 4 lõi 2.0 GHz và 4 lỗi 1.8 Ghz được sản xuất theo tiến trình 11 nm mang đến hiệu năng ổn định, đảm bảo các tác vụ luôn được xử lý một cách mượt mà, hiếm khi xảy ra hiện tượng giật lag.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-A7-2020/description/3_3.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Camera cho hình ảnh sắc nét</h2>\r\n<p class =\"paragraph__p\">Samsung Galaxy Tab A7 (2020) sở hữu hệ thống camera chính độ phân giải 8 MP vừa đủ để đáp ứng trải nghiệm cơ bản của người dùng cho chất lượng hình ảnh ổn định, màu sắc hài hòa.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-A7-2020/description/3_4.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Thời lượng pin lớn, có hỗ trợ sạc nhanh</h2>\r\n<p class =\"paragraph__p\">Galaxy Tab A7 có viên pin lên đến 7040 mAh cho thời gian trải nghiệm tương đương 2 – 3 ngày khi thực thi các tác vụ cơ bản.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-A7-2020/description/3_5.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với mức pin lớn như vậy dĩ nhiên máy sẽ được hỗ trợ sạc nhanh qua cổng USB Type C, bạn sẽ không phải lo lắng mất kha khá thời gian để nạp đầy viên pin này. </p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n\r\n\r\n\r\n', 2, 1),
(6, 3, 'Samsung Galaxy Tab A8 8\" T295', '../assets/image/products/tablet/Samsung-Galaxy-Tab-A8-8-T295/4.jpg\r\n', 3690000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nTFT LCD, 8 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 429\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n32 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy Tab A8 8\" T295</b>\r\n<h1 class =\"paragraph__h1\">Samsung Galaxy Tab A8 8 inch T295 (2019) là một chiếc máy tính bảng gọn nhẹ</h1>\r\n<h2 class=\"paragraph__h2\">Màn hình lớn thoải mái sử dụng</h2>\r\n<p class =\"paragraph__p\">Ưu điểm của những chiếc máy tính bảng khi so với smartphone là kích thước màn hình lớn hơn đem lại không gian lớn hơn để sử dụng</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-A8-8-T295/description/4_2.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Màn hình của chiếc máy tính bảng Samsung được thiết kế theo tỷ lệ 16:10 – rất lý tưởng cho việc đọc sách, tạp chí, đọc báo hoặc lướt web.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-A8-8-T295/description/4_3.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Hỗ trợ kết nối 3G/4G</h2>\r\n<p class =\"paragraph__p\">Máy cũng hỗ trợ khe cắm thẻ nhớ mở rộng tối đa lên tới 512 GB cùng 1 khe cắm Nano Sim và kết nối 4G tốc độ cao giúp bạn truy cập internet với tốc độ nhanh chóng</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-A8-8-T295/description/4_4.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Camera có độ sắc nét cao</h2>\r\n<p class =\"paragraph__p\">Chụp ảnh mọi lúc mọi nơi với độ sắc nét hoàn hảo cùng Galaxy Tab A8, sở hữu camera trước 2 MP và camera sau 8 MP.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-A8-8-T295/description/4_5.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bạn sẽ thấy tất cả nút điều khiển video và máy ảnh trong giao diện máy ảnh giúp việc làm chủ camera của máy trở nên đơn giản hơn.\r\nNhờ tính năng chụp ảnh liên tục, bạn có thể chụp hàng loạt ảnh liên tiếp nhau, chỉ cần nhấn và giữ nút chụp ảnh trên máy tính bảng Galaxy Tab A8.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n', 2, 1),
(7, 3, 'Samsung Galaxy Tab S6 Lite', '../assets/image/products/tablet/Samsung-Galaxy-Tab-S6-Lite/5.jpg\r\n', 9090000, 0, 5, 3, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nPLS LCD, 10.4 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nExynos 9611 8 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n4 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n64 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G, Có nghe gọi\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy Tab S6 Lite</b>\r\n<h1 class =\"paragraph__h1\">Samsung tiếp tục ra mắt Galaxy Tab S6 Lite để chinh chiến ở phân khúc máy tính bảng giá rẻ hơn</h1>\r\n<h2 class=\"paragraph__h2\">Thiết kế thời thượng và cao cấp</h2>\r\n<p class =\"paragraph__p\">Máy tính bảng Galaxy Tab S6 Lite sở hữu thiết ấn tượng với độ dày chỉ 7mm và trọng lượng siêu nhẹ 467g, giúp người dùng dễ dàng bỏ vào túi xách hay mang theo bất kì đâu.\r\n</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-S6-Lite/description/5_2.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Bút S Pen mới tối ưu hiệu suất sử dụng</h2>\r\n<p class =\"paragraph__p\">Bút S Pen mới trên Tab S6 Lite được thiết kế lớn hơn với kích thước tương tự như cây bút thông thường cho cảm giác cầm tự nhiên và thoải mái. Tối ưu năng suất viết, vẽ trên máy.</p>\r\n<p class =\"paragraph__p\">Trải nghiệm viết tay tiếng Việt trên Tab S6 Lite cho thấy máy nhận diện chữ viết rất tốt và chính xác, tốc độ viết khoảng 30 từ/phút, tuy không nhanh như khi gõ phím nhưng là rất ấn tượng so với thao tác nhập liệu trên màn hình cảm ứng. Bút cũng ít gây mỏi khi sử dụng lâu.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-S6-Lite/description/5_3.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Trải nghiệm giải trí đỉnh cao</h2>\r\n<p class =\"paragraph__p\">\r\nTrải nghiệm hình ảnh sống động vượt trội trên màn hình rộng tới 10.4”. Phần viền siêu mảnh và tỷ lệ màn hình rộng 16:10 giúp cho không gian giải trí và làm việc cực thoải mái.\r\nTấm nền IPS LCD với độ phân giải 1200 x 2000 cho hình ảnh rõ nét và màu sắc chân thực.\r\n</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-S6-Lite/description/5_4.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bên cạnh phần hình ảnh chất lượng cao, phần âm thanh trên Galaxy Tab S6 Lite cũng được chăm chút với bộ loa kép đến từ AKG và công nghệ âm thanh Dolby Atmos sống động. Khi so sánh thực nghiệm với flagship Galaxy Note 10 thì chất âm trên tablet này thậm chí chắc và ấm hơn.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n', 2, 1),
(8, 3, 'Samsung Galaxy Tab with S Pen', '../assets/image/products/tablet/Samsung-Galaxy-Tab-with-S-Pen/6.jpg', 6990000, 0, 5, 2, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nTFT LCD, 8 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 9\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nExynos 7904\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n32 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy Tab with S Pen</b>\r\n<h1 class =\"paragraph__h1\">Đặc điểm nổi bật của Samsung Galaxy Tab with S Pen (P205)\r\nBộ sản phẩm chuẩn: Adapter, Sách hướng dẫn, Bút cảm ứng, Cây lấy sim, Hộp máy, Cáp Type-C\r\nSamsung Galaxy Tab A Plus 8.0 inch (2019) là một chiếc máy tính bảng có thiết kế đẹp</h1>\r\n<h2 class=\"paragraph__h2\">Thiết kế được làm tinh tế hơn</h2>\r\n<p class =\"paragraph__p\">Nguyên khối và siêu mỏng là hai đặc điểm nổi bật tạo nên nét tinh tế cho Galaxy Tab A.</p>\r\n<img class = \"paragraph__img\"src=\"h../assets/image/products/tablet/Samsung-Galaxy-Tab-with-S-Pen/description/6_2.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Máy chỉ dày 8.9 mm và có khối lượng nhẹ nhàng, chiếc máy tính bảng này rất thuận tiện khi bạn muốn nó đồng hành bên mình trong những chuyến đi xa.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-with-S-Pen/description/6_3.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Camera có độ sắc nét cao</h2>\r\n<p class =\"paragraph__p\">Chụp ảnh mọi lúc mọi nơi với độ sắc nét hoàn hảo cùng Galaxy Tab A, sở hữu camera trước 5 MP và camera sau 8 MP.\r\nBạn sẽ thấy tất cả nút điều khiển video và máy ảnh trong giao diện máy ảnh giúp việc làm chủ camera của máy trở nên đơn giản hơn.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-with-S-Pen/description/6_4.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Trải nghiệm độc đáo với bút S Pen</h2>\r\n<p class =\"paragraph__p\">Bạn có thể tận hưởng trải nghiệm viết, vẽ hoàn toàn như bút thật nhờ vào bút S Pen có trang bị trên máy tính bảng Galaxy Tab A.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-with-S-Pen/description/6_5.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Nhiều tiện ích và nội dung hấp dẫn</h2>\r\n<p class =\"paragraph__p\">Sở hữu Galaxy Tab A with S Pen 8 inch bạn sẽ được tận hưởng rất nhiều tiện ích đến từ Samsung.\r\nHiệu năng của máy được cung cấp bởi vi xử lý Exynos 7904 vừa đủ để đáp ứng tốt nhu cầu giải trí hằng ngày của mọi người dùng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-with-S-Pen/description/6_6.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Đi cùng với máy là màn hình kích thước lớn độ phân giải cao, giúp hiển được nhiều nội dung trải nghiệm chơi game sẽ cực kỳ thích.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-with-S-Pen/description/6_7.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Niềm vui cho bé yêu và cho cả gia đình</h2>\r\n<p class =\"paragraph__p\">Chế độ Kids Mode mang đến hàng loạt ứng dụng thân thiện với trẻ em, để bé vừa học vừa chơi và phát triển sở thích, tài năng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-with-S-Pen/description/6_8.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bạn không cần lo lắng trẻ chơi máy quá nhiều nữa, vì đã có tính năng cài đặt giới hạn thời gian dành riêng cho trẻ sử dụng.</p><button class =\"paragraph__more\">Xem thêm !</button>\r\n', 2, 1),
(9, 3, 'Samsung Galaxy Tab A 10.1 T515', '../assets/image/products/tablet/Samsung-Galaxy-Tab-A-10.1-T515/7.jpg\r\n', 6990000, 0, 5, 7, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nTFT LCD, 10.1 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 9\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nExynos 7904\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n32 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G, có nghe gọi\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM hoặc 1 eSIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy Tab A 10.1 T515</b>\r\n<h1 class =\"paragraph__h1\">Samsung Galaxy Tab A 10.1 T515 (2019) là chiếc máy tính bảng có màn hình lớn cùng cấu hình vừa phải đáp ứng tốt cho bạn trong hầu hết các nhu cầu giải trí hằng ngày.</h1>\r\n<h2 class=\"paragraph__h2\">Màn hình kích thước lớn, sử dụng thoải mái</h2>\r\n<p class =\"paragraph__p\">Với màn hình kích thước lớn lên tới 10.1 inch thì chiếc máy tính bảng Samsung giúp bạn có một không gian lớn hơn để có thể làm việc hay giải trí.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-A-10.1-T515/description/7_2.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Máy còn sở hữu riêng chế độ ngoài trời giúp bạn có thể theo dõi nội dung hiển thị một cách rõ ràng nhất mà không sợ hiện tượng lóa hay in bóng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-A-10.1-T515/description/7_3.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Giao diện mới với nhiều tính năng</h2>\r\n<p class =\"paragraph__p\">Samsung đã phát hành giao diện OneUI mới cho Galaxy Tab A 10.1 (2019), đây là phiên bản được điều chỉnh riêng cho những chiếc máy tính bảng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-A-10.1-T515/description/7_4.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Kết hợp với đó là con chip do chính Samsung sản xuất Exynos 7904 cùng 3 GB RAM và 32 GB bộ nhớ trong.</p>\r\n<h2 class=\"paragraph__h2\">Âm thanh chất lượng, giải trí cực đã</h2>\r\n<p class =\"paragraph__p\">Samsung Galaxy Tab A 10.1 (2019) sở hữu 2 loa ở mặt dưới với công suất khá lớn và đặc biệt là hỗ trợ công nghệ âm thanh vòm Dolby Atmos.\r\nNhững âm thanh khi bạn chơi game hay xem phim sẽ được tái tạo một cách chân thực nhất và chắc chắn sẽ không làm bạn thất vọng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Samsung-Galaxy-Tab-A-10.1-T515/description/7_5.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\"></h2>\r\n<p class =\"paragraph__p\"></p>\r\n<img class = \"paragraph__img\"src=\"\" alt=\"\">\r\n<p class =\"paragraph__p\">Ngoài ra thì Samsung vẫn giữ cho người dùng jack cắm tai nghe 3.5 mm để bạn có thể nghe nhạc trong những không gian đông người.\r\n</p>\r\n<img class = \"paragraph__img\"src=\"h../assets/image/products/tablet/Samsung-Galaxy-Tab-A-10.1-T515/description/7_6.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 2, 1),
(10, 3, 'iPad 8 Wifi Cellular 32GB', '../assets/image/products/tablet/iPad-8-Wifi-Cellular-32GB/8.jpg\r\n', 13190000, 0, 5, 23, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nRetina IPS LCD, 10.2 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\niPadOS 14\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nApple A12 Bionic 6 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n32 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n1.2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM hoặc 1 eSIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nFaceTime\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của iPad 8 Wifi 32GB</b>\r\n<h1 class =\"paragraph__h1\">iPad 8 Wifi 32 GB (2020) có giá trị tuyệt vời cho một iPad mới khi mang lại hiệu suất tốt hơn</h1>\r\n<h2 class=\"paragraph__h2\">Mỏng nhẹ và thân thiện môi trường</h2>\r\n<p class =\"paragraph__p\">Thay đổi chất liệu bằng nhôm tái chế 100% trên toàn bộ thân máy, tạo nên điểm nhấn đặc biệt nhất trên các mẫu Apple iPad nửa cuối 2020, bên cạnh việc duy trì một thiết kế mỏng nhẹ, iPad 8 mang lại cảm giác cầm nắm tốt, thoải mái trong quá trình sử dụng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-8-Wifi-Cellular-32GB/description/8_2.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\"></h2>\r\n<p class =\"paragraph__p\"></p>\r\n<img class = \"paragraph__img\"src=\"\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Hiệu năng mạnh mẽ, cân luôn AR</h2>\r\n<p class =\"paragraph__p\">Sử dụng chipset A12 Bionic với tốc độ xử lý 2.5 GHz, cùng với dung lượng bộ nhớ trong 32 GB có thể mở rộng lên đến 2 TB trên iCloud. Hiệu năng mạnh mẽ này đủ để cân luôn các tựa game AR, tối ưu hóa cho công việc, giúp việc học tập trở nên sinh động và dễ dàng hơn.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-8-Wifi-Cellular-32GB/description/8_3.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Thả ga 4G, tiện ích muôn nơi</h2>\r\n<p class =\"paragraph__p\">Được tích hợp 4G trên phiên bản iPad 8 Wifi Cellular, người dùng có thể thỏa sức trải nghiệm những tiện ích hàng đầu, kết nối Internet mọi lúc mọi nơi, phục vụ tối đa cho công việc và học tập.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-8-Wifi-Cellular-32GB/description/8_4.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Tối ưu hóa cho công việc và học tập</h2>\r\n<p class =\"paragraph__p\">Thế hệ iPad thứ 8 dần hòa nhập xu hướng tối ưu hóa trong môi trường làm việc và học tập, khi bổ sung cho mình khả năng tương tác với Apple Pencil và Smart Keyboard trên nền tảng iPadOS mới nhất từ Apple. Việc take note, edit video hay thiết kế cho mình những mẫu design ấn tượng nhất, từ nay trở nên vô cùng dễ dàng, hấp dẫn và sinh động</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-8-Wifi-Cellular-32GB/description/8_5.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 1, 1),
(11, 3, 'iPad Pro 12.9 inch Wifi Cellular 128GB', '../assets/image/products/tablet/iPad-Pro-12.9-inch-Wifi-Cellular-128GB/9.jpg', 31190000, 0, 5, 2, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nLiquid Retina, 12.9 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\niPadOS 14\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nApple A12Z Bionic\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n6 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 12 MP & Phụ 10 MP, TOF 3D LiDAR\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n7 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM hoặc 1 eSIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nFaceTime\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của iPad Pro 12.9 inch Wifi Cellular 128GB</b>\r\n<h1 class =\"paragraph__h1\">Với thiết kế không mấy khác biệt so với người anh em iPad Pro 2018 nhưng được Apple nâng cấp hệ thống camera</h1>\r\n<h2 class=\"paragraph__h2\">Thiết kế tràn viền không khuyết điểm</h2>\r\n<p class =\"paragraph__p\">iPad Pro 12.9 (2020) có thiết kế không khác với người tiền nhiệm thiết kế kim loại nguyên khối với độ hoàn thiện cực cao, 4 cạnh được vát thẳng vuông vức cho tổng thể mặt trước hài hòa và đẹp mắt hơn.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-12.9-inch-Wifi-Cellular-128GB/description/9_2.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Màn hình Retina kích thước lớn tần số 120Hz</h2>\r\n<p class =\"paragraph__p\">Apple còn trang bị cho iPad Pro 2020 màn hình Liquid Retina kích thước lớn 12.9 inch giúp hiển thị sắc nét, màu sắc sống động, độ tương phản cao.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-12.9-inch-Wifi-Cellular-128GB/description/9_3.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Apple cũng ưu tiên sử dụng màn hình có tần số 120 Hz sẽ mang lại cho iPad Pro khả năng hiển thị hình ảnh sắc nét, tốc độ xử lý hình ảnh nhanh chóng. </p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-12.9-inch-Wifi-Cellular-128GB/description/9_4.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Cụm camera hỗ trợ VR chất lượng cao</h2>\r\n<p class =\"paragraph__p\">Khác với những người anh em iPad Pro trước đây, ở iPad Pro 12.9 (2020) hệ thống camera đã được cải tiến vượt trội với hệ thống 2 camera sau kèm máy đo chiều sâu LiDAR tích hợp công nghệ thực tế ảo AR.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-12.9-inch-Wifi-Cellular-128GB/description/9_5.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Hiệu năng mạnh mẽ</h2>\r\n<p class =\"paragraph__p\">Apple cũng trang bị cho iPad Pro 12.9 (2020) hiệu năng mạnh mẽ, vượt trội khi sở hữu con chip A12Z Bionic 8 nhân mới được cải tiến, tối ưu hóa để mang đến hiệu suất cao hơn cho so với người tiền nhiệm.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-12.9-inch-Wifi-Cellular-128GB/description/9_6.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Thời lượng pin ấn tượng tối ưu trên hệ điều hành iPad OS</h2>\r\n<p class =\"paragraph__p\">Không chỉ nâng cấp hiệu năng mạnh mẽ mà Apple còn trang bị cho iPad Pro 12.9 (2020) viên pin Li-Po 36.71 Wh cho thời lượng lướt web liên tục lên đến 10 giờ, đem đến những trải nghiệm, làm việc, giải trí liên tục.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-12.9-inch-Wifi-Cellular-128GB/description/9_7.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Hỗ trợ nhiều phụ kiện đi kèm</h2>\r\n<p class =\"paragraph__p\">iPad Pro 12.9 (2020) còn hỗ trợ Apple Pencil để bạn có thể thực hiện mọi ghi chép, chỉnh sửa ảnh, vẽ,… một cách đơn giản và nhanh chóng, sẵn sàng ghi chép bất cứ lúc nào, vô cùng tiện lợi cũng như mang đến cảm giác ghi chép chân thực. </p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-12.9-inch-Wifi-Cellular-128GB/description/9_8.jpg\" alt=\"\">\r\np class =\"paragraph__p\">Bàn phím không dây Smart Keyboard với kích thước nhỏ gọn, tiện dụng cùng nhiều chức năng gõ phím tuyệt cũng là lựa chọn hoàn hảo giúp bảo vệ cho iPad của bạn một cách toàn diện. </p><button class =\"paragraph__more\">Xem thêm !</button>\r\n\r\n', 1, 1);
INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(12, 3, 'iPad Pro 11 inch Wifi Cellular 128GB ( 2020 )', '../assets/image/products/tablet/iPad-Pro-11-inch-Wifi-Cellular-128GB/10.jpg', 25190000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nLiquid Retina, 11 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\niPadOS 14\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nApple A12Z Bionic 6 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n6 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 12 MP & Phụ 10 MP, TOF 3D LiDAR\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n7 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM hoặc 1 eSIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nFaceTime\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của iPad Pro 11 inch Wifi 128GB \r\n</b>\r\n<h1 class =\"paragraph__h1\">iPad Pro 11 inch 2020 là mẫu iPad mới nhất vừa được Apple ra mắt vào 18/3 với thiết kế gần như không thay đổi so với thế hệ trước, chủ yếu là sự nâng cấp đến từ chip A12Z cho hiệu năng mạnh mẽ và cụm camera có cảm biến mới hỗ trợ tăng cường thực tế ảo.</h1>\r\n<h2 class=\"paragraph__h2\">Thiết kế “bình cũ rượu mới” sang trọng đậm chất “Táo”</h2>\r\n<p class =\"paragraph__p\">Có thể thấy rằng, mẫu iPad Pro 2020 11 inch hầu như không có sự khác biệt nhiều so với thế hệ iPad trước đó về mặt thiết kế.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-11-inch-Wifi-Cellular-128GB/description/10_2.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n<p class =\"paragraph__p\">Máy vẫn giữ thiết kế nguyên khối với các cạnh bên được vát thẳng cùng 4 góc được bo cong, Đây là thiết kế vuông vức quen thuộc mà Apple lần đầu sử dụng trên iPhone 5 trước đây.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-11-inch-Wifi-Cellular-128GB/description/10_3.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Màn hình Liquid Retina sắc nét, màu sắc cực chuẩn</h2>\r\n<p class =\"paragraph__p\">iPad Pro 2020 được trang bị màn hình công nghệ Liquid Retina tiên tiến do chính Apple phát triển và tích hợp trên các dòng sản phẩm mới của mình.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-11-inch-Wifi-Cellular-128GB/description/10_4.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Cụm camera xuất hiện cảm biến mới</h2>\r\n<p class =\"paragraph__p\">Camera chính là điểm nâng cấp mạnh mẽ và rõ ràng nhất trên chiếc iPad Pro mới này. Máy được trang bị cụm camera kép bao gồm camera góc rộng 12 MP và góc siêu rộng 10 MP. Bên cạnh đó là một cảm biến mới mang tên LiDAR lần đầu xuất hiện trên thiết bị Apple.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-11-inch-Wifi-Cellular-128GB/description/10_5.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Camera chính 12 MP cho chất lượng ảnh vô cùng sắc nét, độ chi tiết cao, cung cấp một góc chụp đủ rộng để bạn có thể thu được nhiều chi tiết hơn. Không những thế, Apple còn trang bị thêm 1 camera góc siêu rộng Ultra Wide 10 MP để bạn có thể chụp toàn cảnh ấn tượng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-11-inch-Wifi-Cellular-128GB/description/10_6.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Xử lý đồ họa chuyên nghiệp với chip A12Z</h2>\r\n<p class =\"paragraph__p\">Trang bị vi xử lý A12Z Bionic mới nhất, đã giúp cho iPad Pro 2020 trở thành máy tính bảng mạnh mẽ hơn hầu hết các mẫu laptop Window nào hiện nay.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-11-inch-Wifi-Cellular-128GB/description/10_7.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Hệ điều hành iPad OS tối ưu hiệu năng cực tốt</h2>\r\n<p class =\"paragraph__p\">Với iPad OS, giao diện được cải tiến thông minh, giao diện đa nhiệm nhiều cửa sổ tốt hơn giúp cho chiếc iPad giờ đây có thể đảm nhận nhiều công việc gần như tương đương 1 chiếc laptop mini.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-Pro-11-inch-Wifi-Cellular-128GB/description/10_8.jpg\" alt=\"\">\r\n\r\n', 1, 1),
(13, 3, 'iPad mini 7.9 inch Wifi Cellular 64GB (2019)\r\n', '../assets/image/products/tablet/iPad-mini-7.9-inch-Wifi-Cellular-64GB-(2019)/11.jpg', 14190000, 0, 5, 6, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nLED-backlit IPS LCD,7.9 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\niPadOS 14\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nApple A12 Bionic 6 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n64 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n7 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM hoặc 1 eSIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nFaceTime\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của iPad mini 7.9 inch Wifi Cellular 64GB</b>\r\n<h1 class =\"paragraph__h1\">iPad Mini 7.9 inch Wifi Cellular 64GB (2019) đánh dấu sự trở lại mạnh mẽ của Apple trong phân khúc máy tính bảng nhỏ gọn, có thể dễ dàng mang theo bên mình.</h1>\r\n<h2 class=\"paragraph__h2\">Hiệu năng mạnh mẽ hàng đầu</h2>\r\n<p class =\"paragraph__p\">iPad Mini 7.9 inch Wifi Cellular 64GB (2019) được Apple trang bị hiệu năng rất ấn tượng với con chip Apple A12 cùng RAM 3 GB và 64 GB bộ nhớ trong.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-mini-7.9-inch-Wifi-Cellular-64GB-(2019)/description/11_2.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n<p class =\"paragraph__p\">Mọi thao tác từ cơ bản nhất tới sử dụng các ứng dụng nặng nhất hiện nay thì chiếc máy tính bảng này đều đáp ứng một cách mượt mà.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-mini-7.9-inch-Wifi-Cellular-64GB-(2019)/description/11_3.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Thiết kế nhỏ gọn dễ dàng mang theo</h2>\r\n<p class =\"paragraph__p\">Một trong những lý do chính mà Apple hồi sinh dòng iPad Mini chính là bởi thiết kế nhỏ gọn của nó giúp người dùng có thể dễ dàng mang theo đi bất cứ đâu.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-mini-7.9-inch-Wifi-Cellular-64GB-(2019)/description/11_4.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Thiết kế nhỏ gọn dễ dàng mang theo</h2>\r\n<p class =\"paragraph__p\">Một trong những lý do chính mà Apple hồi sinh dòng iPad Mini chính là bởi thiết kế nhỏ gọn của nó giúp người dùng có thể dễ dàng mang theo đi bất cứ đâu.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-mini-7.9-inch-Wifi-Cellular-64GB-(2019)/description/11_5.jpg\" alt=\"\">\r\n< p class=\"paragraph__p> Bạn sẽ có cho mình một chiếc iPad với kích thước màn hình 7.9 inch không quá lớn và thậm chí bạn có thể cho chiếc iPad này vào túi quần của mình. Độ phân giải của máy là 1536 x 2048 pixels mang lại trải nghiệm làm việc giải trí xem phim tuyệt vời, đặc biệt là khi thư giản cùng với gia đình.\r\n</p>\r\n<h2 class=\"paragraph__h2\">Thời lượng pin thoải mái dùng 2 ngày</h2>\r\n<p class =\"paragraph__p\">Với iPad Mini 7.9 inch Wifi 2019 thì bạn sẽ có được 10 tiếng sử dụng liên tục với các tác vụ như xem video hay lướt web, một con số mà nhiều người hằng mơ ước.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/iPad-mini-7.9-inch-Wifi-Cellular-64GB-(2019)/description/11_6.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Nhờ việc tối ưu hóa hiệu quả nên việc để thiết bị qua đêm cũng sẽ không khiến bạn tốn quá nhiều dung lượng pin.</p>', 1, 1),
(14, 3, 'Huawei MatePad T10s', '../assets/image/products/tablet/Huawei-MatePad-T10s/12.jpg\r\n', 5290000, 0, 5, 6, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 10.1 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10 (Không có Google)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nKirin 710A\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n64 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM hoặc 1 eSIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Huawei MatePad T10s</b>\r\n<h1 class =\"paragraph__h1\">Với vi xử lý 8 nhân mở ra một thế giới giải trí mượt mà, sống động từng khoảnh khắc với màn hình cực lớn, hé lộ một chiếc máy tính bảng tốt trong tầm giá mà bất kỳ ai cũng đều yêu thích</h1>\r\n<h2 class=\"paragraph__h2\">Giải trí sống động với màn hình cực lớn</h2>\r\n<p class =\"paragraph__p\">MatePad T10s sở hữu màn hình 10.1 inch tràn đều cân xứng hoàn hảo ở cả 4 cạnh viền. Giúp cho thiết bị có một thiết kế gọn, nhẹ và mỏng hơn, từng giờ phút trải nghiệm là đều là sự thoải mái.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-T10s/description/12_2.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n<p class =\"paragraph__p\">Sử dụng tấm nền IPS LCD có độ phân giải 1200 x 1920 pixels cho bạn tận hưởng các nội dung giải trí lớn hơn, rõ ràng hơn với độ sắc nét tuyệt vời, nội dung sẽ linh hoạt thay đổi mượt mà theo chiều cầm ngang hay dọc.</p>\r\n<h2 class=\"paragraph__h2\">Loa kép Harman Kardon cho chất âm vòm ấn tượng</h2>\r\n<p class =\"paragraph__p\">Không chỉ có phần nhìn ấn tượng, mà phần nghe trên MatePad T10s cũng được đánh giá cao khi trang bị bộ đôi loa ngoài đối xứng, giả lập hiệu ứng âm thanh vòm 3D Histen 6.1 Sound nâng tầm trải nghiệm mỗi khi chơi game hay xem phim.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-T10s/description/12_3.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Chất âm của T10s đã được tinh chỉnh bởi Harman Kardon giúp âm thanh trở nên rõ ràng, hài hòa trên mọi âm vực, thể hiện đúng sắc thái đặc trưng của bài hát.</p>\r\n<h2 class=\"paragraph__h2\">Vi xử lý 8 nhân nhanh và siêu ổn định</h2>\r\n<p class =\"paragraph__p\">Trải nghiệm sự mượt mà đến từ vi xử lý 8 nhân Kirin 710A, mang đến hiệu năng vượt trội trong tầm giá, lướt chạm mượt mà, ứng dụng kích hoạt nhanh chóng, thỏa sức tận hưởng thế giới giải trí thú vị đến từ kho ứng dụng AppGallery của Huawei.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-T10s/description/12_4.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">EMUI 10.1 đa nhiệm tốt hơn, hỗ trợ không gian riêng cho bé.</h2>\r\n<p class =\"paragraph__p\">EMUI 10.1 hoạt động trên Android 10 mã nguồn mở với nhiều tính năng hấp dẫn. Tính năng HUAWEI App Multiplier tận dụng màn hình lớn cho phép bạn mở cùng lúc 2 cửa sổ ứng dụng hoạt động riêng biệt, làm 2 việc cùng lúc giúp cho công việc của bạn được xử lý nhanh chóng và hiệu quả hơn.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-T10s/description/12_5.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Pin 5100 mAh giải trí, làm việc liên tục nhiều giờ liền</h2>\r\n<p class =\"paragraph__p\">MatePad T10s được trang bị viên pin lớn 5100 mAh thoải mái cho bạn giải trí hay làm việc liên tục trong nhiều giờ liền. Ở bất kỳ không gian và thời gian nào, MatePad T10s sẽ luôn sẵn sàng giúp bạn hoàn thành công việc nhanh chóng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-T10s/description/12_6.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Không chỉ vậy, MatePad T10s còn có đầy đủ cả camera trước sau. Camera sau 5 MP, hỗ trợ quay phim Full HD đủ để cho bạn ghi lại những kỉ niệm đáng nhớ bên gia đình hay khoảnh khắc thú vị xung quanh bạn. Camera trước 2 MP sẽ hỗ trợ những cuộc gọi video call rõ nét hay tỏa sáng với những tấm ảnh selfie thần thái.</p>', 3, 1),
(15, 3, 'Huawei MatePad 64GB', '../assets/image/products/tablet/Huawei-MatePad-64GB/13.jpg\r\n', 6790000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 10.4 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nKirin 810\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n4 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n64 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nWiFi\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\nkhông\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nNghe\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Máy tính bảng Huawei MatePad 64GB</b>\r\n<h1 class =\"paragraph__h1\">Đặc điểm nổi bật của Huawei MatePad 64GB</h1>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-64GB/description/13_2.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Thiết kế cao cấp, bền bỉ</h2>\r\n<p class =\"paragraph__p\">Huawei MatePad sử dụng thiết kế nguyên khối với phần khung viền kim loại và mặt lưng được phủ mờ, giúp phần thân máy nhẹ nhàng, sang trọng cũng như hạn chế dấu vân tay.</p>\r\n<img class = \"paragraph__img\"src=\"h../assets/image/products/tablet/Huawei-MatePad-64GB/description/13_3.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n<p class =\"paragraph__p\">Sự phối hợp giữa kỹ thuật thủ công tinh xảo với bốn cạnh cong mềm mại và thiết kế trang nhã, bền bỉ vừa mang đến vẻ ngoài cao cấp vừa nâng cao trải nghiệm cầm nắm thoải mái, vừa vặn trong tay.</p>\r\n<h2 class=\"paragraph__h2\">Trải nghiệm chân thực, không giới hạn tầm nhìn</h2>\r\n<p class =\"paragraph__p\">Tỷ lệ màn hình của Huawei MatePad đạt đến độ lý tưởng với viền bezels cực mỏng. Màn hình có kích thước lớn 10.4 inch và thiết kế “đục lỗ” để chứa camera selfie khá nhỏ, đem đến trải nghiệm hình ảnh tối ưu với không gian cực rộng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-64GB/description/13_4.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Về chất lượng hiển thị, tấm nền IPS LCD của máy cho độ phân giải 2K (1200 x 2000 Pixels), khả năng hiển thị 16.7 triệu màu sắc và độ sáng lên đến 470 nits, từng chi tiết sáng tối đều đem lại sự sống động và rõ ràng đạt tiêu chuẩn điện ảnh.</p>\r\n<h2 class=\"paragraph__h2\">Bước nhảy vọt về hiệu suất</h2>\r\n<p class =\"paragraph__p\">Huawei MatePad được tích hợp vi xử lý tầm trung Kirin 810 gồm 8 lõi, sản xuất trên tiến trình 7 nm với xung nhịp cơ bản lên tới 2.27 GHz đem lại hiệu suất sử dụng tối ưu và toàn diện trong các nhiệm vụ liên quan đến AI</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-64GB/description/13_5.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Không chỉ hiệu suất CPU được nâng cao, GPU Mali-G52 được hỗ trợ bởi công nghệ GPU Turbo, cải thiện đáng kể về khả năng xử lý đồ họa khi xem video độ phân giải cao hay chơi những tựa game nặng, hiện tượng giảm fps hay giật lag ít xảy ra.</p>\r\n<h2 class=\"paragraph__h2\">Ghi lại mỗi khoảnh khắc đáng nhớ</h2>\r\n<p class =\"paragraph__p\">Huawei cũng trang bị cho Huawei MatePad camera selfie 8 MP hỗ trợ lấy nét tự động và camera đơn phía sau có độ phân giải 8 MP cùng đèn flash LED. Dù là chiếc tablet không chuyên về camera nhưng đủ để bạn tha hồ “sống ảo”.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-64GB/description/13_6.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Tận hưởng trọn vẹn mỗi ngày</h2>\r\n<p class =\"paragraph__p\">Sự kết hợp giữa công nghệ tiết kiệm năng lượng từ chipset và giao diện EMUI, cùng với dung lượng pin khủng lồ 7250 mAh giúp Huawei MatePad tiêu thụ pin ít đến kinh ngạc. Theo Huawei công bố, máy cho phép 12 giờ xem video trực tuyến và 7 giờ chơi game liên tục chỉ sau một lần sạc đầy.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-64GB/description/13_7.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\"></h2>\r\n<p class =\"paragraph__p\">Tính năng Huawei Share cho phép bạn kết nối chiếc tablet với một smartphone khác để thực hiện các thao tác trên màn hình lớn hơn, giúp cuộc sống và công việc của bạn trở nên dễ dàng hơn. Hay thậm chí là chế độ xem đa màn hình với khả năng đa tác vụ cùng một lúc, vừa đem đến hiệu quả, vừa thuận tiện, rút ngắn thời gian xử lý công việc.\r\nHuawei MatePad là một thiết bị học tập dành cho các em nhỏ bởi sự xây dựng đầy đủ tài nguyên học tập bên cạnh các biện pháp ngăn chặn thông báo, tin nhắn, giúp trẻ phát triển thói quen tốt là tập trung vào việc học\r\n</p>\r\n<img class = \"paragraph__img\"src=\"h../assets/image/products/tablet/Huawei-MatePad-64GB/description/13_8.jpg\" alt=\"\">', 3, 1),
(16, 3, 'Huawei Mediapad T5', '../assets/image/products/tablet/Huawei-Mediapad-T5/14.jpg', 4640000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 10.1 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 8\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nKirin 659\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n32 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Micro SIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Huawei Mediapad T5</b>\r\n<h1 class =\"paragraph__h1\">Huawei MediaPad T5 10.1 là một chiếc máy tính bảng có thiết kế hiện đại, sang trọng nổi bật với màn hình lớn, hiệu năng ổn định, hệ thống âm thanh chất lượng</h1>\r\n<h2 class=\"paragraph__h2\">Thiết kế hiện đại và kim loại cao cấp</h2>\r\n<p class =\"paragraph__p\">Huawei MediaPad T5 10.1 gây ấn tượng cuốn hút ngay từ cái nhìn đầu tiên khi sử dụng chất liệu kim loại tạo nên sự cứng cáp, đồng thời còn toát lên vẻ đẹp sang trọng, cao cấp. </p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-Mediapad-T5/description/14_2.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n<p class =\"paragraph__p\">Các cạnh viền và 4 góc được bo cong nhẹ rất tinh tế, các chi tiết khác như phím nguồn và tăng giảm âm lượng trên Huawei MediaPad T5 10.1 được hoàn thiện một cách cẩn thận mang tới sự yên tâm hơn cho người dùng trong quá trình sử dụng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-Mediapad-T5/description/14_3.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Màn hình giải trí sắc nét</h2>\r\n<p class =\"paragraph__p\">Huawei MediaPad T5 10.1 được trang bị công nghệ màn hình LCD kích thước lớn 10.1 inch tỷ lệ khung hình 16:10 rất phù hợp để bạn xem phim, duyệt web hay xem các chương trình TV.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-Mediapad-T5/description/14_4.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Màn hình này có độ phân giải Full HD (1200 x 1920 Pixels) sắc nét, hứa hẹn cung cấp cho người dùng một không gian rộng rãi để giải trí rõ nét với màu sắc trung thực sau những giờ học tập và làm việc mệt mỏi, căng thẳng trở nên thoải mái và thư giãn nhất.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-Mediapad-T5/description/14_5.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Hiệu năng đáp ứng tốt nhu cầu giải trí hằng ngày</h2>\r\n<p class =\"paragraph__p\">Huawei MediaPad T5 10.1 sẽ làm hài lòng bạn trong mọi trải nghiệm hằng ngày nhờ chip xử lý 8 nhân Kirin 659 với xung nhip tối đa lên tới 2.36 GHz vừa cung cấp hiệu năng tuyệt vời trong khi lại tiêu thụ ít năng lượng hơn.</p>\r\n<img class = \"paragraph__img\"src=\"h../assets/image/products/tablet/Huawei-Mediapad-T5/description/14_6.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Máy tính bảng MediaPad T5 10.1 sẽ đi kèm với 3 GB RAM và 32 GB dung lượng lưu trữ, bạn cũng có thể dễ dàng mở rộng thêm khả năng lưu trữ thông qua thẻ nhớ microSD hỗ trợ tối đa 256 GB.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-Mediapad-T5/description/14_7.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Camera đáp ứng khá tốt các nhu cầu cơ bản như chụp ảnh, video call</h2>\r\n<p class =\"paragraph__p\">MediaPad T5 10.1 được trang bị camera sau có độ phân giải 5 MP có khả năng lấy nét nhanh và cho ra hình ảnh có độ nét cao và chân thật. Camera trước với độ phân giải là 2 MP đáp ứng khá tốt nhu cầu gọi video.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-Mediapad-T5/description/14_8.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Dung lượng pin lớn giúp bạn thoải mái sử trong 24h</h2>\r\n<p class =\"paragraph__p\">Với dung lượng viên pin lớn lên đến 5100 mAh giúp cho thời gian sử dụng máy tính bảng của bạn vô cùng thoải mái khi có thể đáp ứng tốt nhu cầu làm việc và giải trí trong thời gian dài.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-Mediapad-T5/description/14_9.jpg\" alt=\"\">', 3, 1),
(17, 3, 'Huawei MatePad T8', '../assets/image/products/tablet/Huawei-MatePad-T8/15.jpg', 3090000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 8 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nMediaTek MT8768\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n32 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Huawei MatePad T8</b>\r\n<h1 class =\"paragraph__h1\">Huawei MatePad T8 là một trong những mẫu máy tính bảng giá rẻ của Huawei có thiết kế nguyên khối cùng một cấu hình cơ bản, đủ dùng cho các tác vụ hằng ngày của mọi người dùng.</h1>\r\n<h2 class=\"paragraph__h2\">Thiết kế nguyên khối liền mạch, màn hình viền mỏng</h2>\r\n<p class =\"paragraph__p\">Máy tính bảng Huawei MatePad T8 mang cho mình thiết kế chắc chắn, viền màn hình 2 bên mỏng tạo cho trải nghiệm xem rộng rãi và cảm giác cầm nắm tốt.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-T8/description/15_2.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n<h2 class=\"paragraph__h2\">Cấu hình khá tốt trong tầm giá</h2>\r\n<p class =\"paragraph__p\">Huawei MatePad T8 sở hữu vi xử lý MediaTek MT8768 8 nhân, RAM 2 GB giúp chiếc máy tính bảng này có thể đáp ứng tốt các nhu cầu cơ bản như lướt web, xem phim hay chơi một số các tựa game nhẹ.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-T8/description/15_3.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Máy có bộ nhớ trong 32 GB, tuy bộ nhớ không quá nhiều nhưng vẫn đủ để bạn có thể lưu trữ hay tải nhiều ứng dụng. Nếu bạn muốn có thêm không gian lưu trữ thì máy vẫn có hỗ trợ thẻ nhớ ngoài Micro SD dung lượng 512 GB, giúp bạn không phải lo lắng về khả năng lưu trữ của máy.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-T8/description/15_4.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\"></h2>\r\n<p class =\"paragraph__p\"></p>\r\n<img class = \"paragraph__img\"src=../assets/image/products/tablet/Huawei-MatePad-T8/description/15_5.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Hỗ trợ ứng dụng Petal Search, cho phép tìm kiếm thông tin, ứng dụng dễ dàng</h2>\r\n<p class =\"paragraph__p\">Với khả năng tổng hợp được nhiều nguồn ứng dụng và thông tin khác nhau, trên giao diện đơn giản, dễ sử dụng, Petal Search là công cụ cho phép người dùng vừa tìm kiếm được thông tin, vừa tìm kiếm được ứng dụng nhanh chóng, dễ dàng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-T8/description/15_6.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Công cụ hỗ trợ cài đặt và sử dụng được nhiều ứng dụng khác của Google và hoạt động khá ổn định như: Chrome, Google Dịch, Google Maps,... mang lại trải nghiệm tiện lợi hơn cho người dùng.</p>\r\n<h2 class=\"paragraph__h2\">Camera tốt, đáp ứng nhu cầu chụp, quay đơn giản</h2>\r\n<p class =\"paragraph__p\">Camera không phải là điểm nhấn trên chiếc máy khi camera trước chỉ 2 MP và camera sau 5 MP. Chất lượng không quá xuất sắc nhưng vẫn sẽ đáp ứng đủ nhu cầu cơ bản như chụp ảnh, quay video Full HD 30 fps.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Huawei-MatePad-T8/description/15_7.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với cấu hình và những tính năng trên, cùng với mức giá bán tốt thì Huawei MatePad T8 hoàn toàn đáp ứng tốt nhu cầu sử dụng cơ bản và đặc biệt phù hợp cho các bật phụ huynh muốn mua cho con em mình một thiết bị phù hợp để giải trí, học tập nhưng vẫn có thể kiểm soát một cách hiệu quả.</p>', 3, 1),
(18, 3, 'Lenovo Tab M10', '../assets/image/products/tablet/Lenovo-Tab-M10/16.jpg', 4290000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 10 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nMediaTek Helio P22T 8 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n2 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n32 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo Tab M10 - Gen 2</b>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M10/description/16_2.jpg\" alt=\"\">\r\n<h1 class =\"paragraph__h1\">Lenovo Tab M10 Gen 2 từ nhà Lenovo nổi bật trong phân khúc tầm trung khi sở hữu thiết kế gọn nhẹ, hiện đại cùng khả năng kết nối mạnh mẽ, viên pin ấn tượng và cấu hình ổn định trong tầm giá.</h1>\r\n<h2 class=\"paragraph__h2\">Thiết kế hiện đại, thân thiện với người dùng</h2>\r\n<p class =\"paragraph__p\">Lenovo Tab M10 Gen 2 được làm từ khung kim loại, nổi bật với những đường nét cứng cáp, vuông vức kết hợp với các góc được bo tròn tinh tế mang lại vẻ sang trọng cho thiết bị.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M10/description/16_3.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n<p class =\"paragraph__p\">Máy có kích thước 241.54 x 149.38 x 8.25 mm tương đối nhỏ gọn để có thể thoải mái cầm nắm bằng một tay. Đặc biệt, với trọng chỉ nặng 420 g nên người dùng hoàn toàn có thể yên tâm mang theo bên mình mỗi khi ra ngoài làm việc hoặc đi công tác.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M10/description/16_4.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Trải nghiệm thú vị hơn với màn hình lớn</h2>\r\n<p class =\"paragraph__p\">Lenovo Tab M10 Gen 2 sở hữu màn hình kích thước lớn lên tới 10 inch với phần viền bezel được làm mỏng tối ưu giúp người dùng có thể tận hưởng những khung hình ảnh bắt mắt hơn thông qua trải nghiệm giải trí hàng ngày.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M10/description/16_5.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Tấm nền IPS LCD cho độ phân giải 800 x 1280 pixel và độ sáng tối đa 400 nits mang đến hình ảnh khá chân thực, rực rỡ nhờ khả năng tái tạo màu sắc, độ chi tiết và tương phản cao ở các góc nhìn.</p>\r\n<h2 class=\"paragraph__h2\">Đáp ứng tốt nhu cầu sử dụng cơ bản</h2>\r\n<p class =\"paragraph__p\">Sức mạnh từ bộ vi xử lý MediaTek Helio P22T 8 nhân giúp máy hoạt động hiệu quả ở hầu hết các tác vụ trong cuộc sống hàng ngày.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M10/description/16_6.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Trang bị dung lượng RAM 2 GB cho phép hoạt động đa nhiệm ổn, ít xảy ra hiện tượng giật lag. Ngoài ra, bộ nhớ trong 32 GB và được hỗ trợ mở rộng dung lượng qua thẻ nhớ MicroSD tối đa 1 TB giúp người dùng lưu trữ thoải mái.</p>\r\n<h2 class=\"paragraph__h2\">Khả năng nhiếp ảnh ở mức đủ dùng</h2>\r\n<p class =\"paragraph__p\">Lenovo Tab M10 Gen 2 được nhà sản xuất trang bị cả camera trước và sau nhằm phục vụ cho nhu cầu chụp ảnh khác nhau. Camera trước 5 MP nằm ở vị trí trung tâm, chủ yếu phục vụ cho nhu cầu selfie nhanh chóng hay gọi video call trò chuyện cùng bạn bè, người thân.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M10/description/16_7.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Trong khi đó, camera sau với độ phân giải 8 MP vừa đủ nhu cầu sử dụng và máy cũng cung cấp đầy đủ các tính năng chụp ảnh không kém gì smartphone.</p>\r\n<h2 class=\"paragraph__h2\">Không lo gián đoạn vì cạn kiệt pin</h2>\r\n<p class =\"paragraph__p\">Để đảm bảo nhu cầu học tập, giải trí và làm việc liên tục cả ngày dài, nhà sản xuất ưu ái trang bị viên pin khủng 5000 mAh trên chiếc tablet của mình. Với cấu hình vừa phải và màn hình chỉ dừng ở mức HD thì thời lượng sử dụng của Lenovo Tab M10 Gen 2 khá ấn tượng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M10/description/16_8.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\"></p>', 4, 1),
(19, 3, 'Lenovo Tab M7\r\n\r\n', '../assets/image/products/tablet/Lenovo-Tab-M7/17.jpg', 2590000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 7 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 9\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nMediaTek MT8765 4 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n32 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo Tab M7</b>\r\n<h1 class =\"paragraph__h1\">Lenovo ra mắt dòng máy tính bảng mang tên Lenovo Tab M7 là sự lựa chọn rất đáng để cân nhắc cho người dùng phổ thông với thiết kế tinh tế, cứng cáp cùng một cấu hình mạnh mẽ và nhiều tính năng hữu ích</h1>\r\n<h2 class=\"paragraph__h2\">Thiết kế nguyên khối cứng cáp và sang trọng</h2>\r\n<p class =\"paragraph__p\">Lenovo Tab M7 sở hữu thiết kế nguyên khối cứng cáp và bền bỉ với khung kim loại và mặt lưng nhựa cao cấp có độ bền cao, tất cả các chi tiết đều được nhà sản xuất hoàn thiện một cách tinh xảo, chính xác bằng vật liệu thân thiện với môi trường. </p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M7/description/17_2.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n<p class =\"paragraph__p\">Phần viền màn hình và các góc cạnh đều được bo cong êm ái cho cảm giác cầm nắm cực kỳ thoải mái và dễ chịu nhưng không kém phần làm tôn nên vẻ đẹp của máy.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M7/description/17_3.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Màn hình giải trí sắc nét, bảo vệ mắt người dùng</h2>\r\n<p class =\"paragraph__p\">Lenovo Tab M7 được trang bị màn hình giải trí kích thước lớn 7.0 inch thích hợp cho việc đọc sách, lướt web hay xem Youtube.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M7/description/17_4.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bên cạnh đó, tấm nền IPS LCD cho chất lượng hình ảnh hiển thị sắc nét và chân thực người dùng sẽ có những phút giây giải trí thoải mái và tuyệt vời thông qua chiếc máy tính bảng này.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M7/description/17_5.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Hiệu năng ổn định, giải trí mượt mà</h2>\r\n<p class =\"paragraph__p\">Lenovo Tab M7 mang trong mình bộ vi xử lý 4 nhân MediaTek MT8765 giúp máy có thể đáp ứng đủ nhu cầu cơ bản từ lướt web, xem phim, thậm chí là chơi các tựa game nhẹ.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M7/description/17_6.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Dung lượng RAM 2 GB cho tốc độ xử lý các đa nhiệm tương đối ổn định và mượt mà. Ngoài ra, máy còn được trang bị modem 4G có tốc độ kết nối mạng với tốc độ cực kì nhanh.</p>\r\n<h2 class=\"paragraph__h2\">Camera đủ dùng và thời lượng pin ấn tượng</h2>\r\n<p class =\"paragraph__p\">Lenovo Tab M7 được trang bị bộ đôi camera trước và sau đều có độ phân giải 2 MP giúp người dùng có thể bắt kịp những khoảnh khắc trong cuộc sống một cách dễ dàng.\r\n</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M7/description/17_7.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Mặc dù camera của máy có độ phân giải không quá cao nhưng vẫn đảm bảo đủ dùng cho các nhu cầu cơ bản như gọi video call cho người thân và gia đình một cách dễ dàng.</p>', 4, 1),
(20, 3, 'Lenovo Tab M8', '../assets/image/products/tablet/Lenovo-Tab-M8/18.jpg', 3490000, 0, 5, 3, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 8 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 9\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nMediaTek Helio A22\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n32 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo Tab M8</b>\r\n<h1 class =\"paragraph__h1\">Lenovo Tab M8 là chiếc máy tính bảng giá rẻ, cấu hình ổn, thiết kế cao cấp trong tầm giá</h1>\r\n<h2 class=\"paragraph__h2\">Thiết kế đơn giản, tinh tế</h2>\r\n<p class =\"paragraph__p\">Lenovo Tab M8 có thiết kế cao cấp nhờ mặt lưng được làm bằng chất liệu nhựa nguyên khối, chắc chắn cho cảm giác cầm nắm vô cùng thích.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M8/description/18_2.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n<p class =\"paragraph__p\">Màn hình kích thước 8 inch nhỏ gọn, với tỉ lệ hiển thị đến 83% nhờ các cạnh viền được làm khá mỏng cho không gian hiển thị được nhiều thông tin mà nhưng vẫn cho trải nghiệm cầm nắm nhẹ nhàng, dễ dàng thao tác.\r\nMáy sở hữu màn hình IPS LCD với độ phân giải 1280 x 800 Pixels , cho chất lượng hiển thị tốt, mặc dù không quá xuất sắc nhưng đủ dùng cho các nhu cầu cơ bản.</p>\r\n<h2 class=\"paragraph__h2\">Hiệu năng ổn định cho các nhu cầu cơ bản hằng ngày</h2>\r\n<p class =\"paragraph__p\">Lenovo Tab M8 sử dụng vi xử lý MediaTek Helio A22 với 4 nhân tốc độ 2.0 Ghz, cùng với RAM 3 GB. Máy có thể đáp ứng đủ nhu cầu cơ bản, từ lướt web, xem phim, thậm chí chơi game.\r\nVới cấu hình này máy chỉ có thể chơi các tựa game nhẹ như: Candy Crush, Plants vs Zombies 2, Minecraft: Pocket Edition,... hay xử lý các tác vụ văn phòng nhẹ.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M8/description/18_3.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bên cạnh đó, máy được cài đặt sẵn hệ điều hành Android 9.0 (Pie) với giao diện đẹp mắt, các icon to, rõ kể cả người lớn tuổi cũng có thể dễ dàng thao tác và sử dụng. </p>\r\n<h2 class=\"paragraph__h2\">Camera đủ nhu cầu gọi thoại</h2>\r\n<p class =\"paragraph__p\">Lenovo Tab M8 được trang bị bộ đôi camera trước và sau với độ phân giải lần lượt là 5 MP và 2 MP. Mặc dù không quá xuất sắc nhưng vẫn đủ dùng cho các nhu cầu cơ bản như gọi video call cho người thân và gia đình một cách dễ dàng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Lenovo-Tab-M8/description/18_4.jpg\" alt=\"\">', 4, 1);
INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(21, 3, 'Masstel Tab 10 Ultra', '../assets/image/products/tablet/Masstel-Tab-10-Ultra/19.jpg\r\n', 3590000, 0, 5, 3, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 10.1 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10 \r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nSpreadtrum SC9863A\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n32 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM và 1 Micro SD\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Masstel Tab 10 Ultra</b>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Masstel-Tab-10-Ultra/description/19_2.jpg\r\n\" alt=\"\">\r\n<h1 class =\"paragraph__h1\">Masstel Tab 10 Ultra được cho là phiên bản được cải tiến từ các phiên bản tiền nhiệm trước</h1>\r\n<h2 class=\"paragraph__h2\">Thiết kế đơn giản nhưng không kém phần tinh tế</h2>\r\n<p class =\"paragraph__p\">Thiết kế vỏ nhựa kết hợp kim loại, các góc cạnh được bo tròn nhẹ, tạo cảm giác cứng cáp, bền bỉ và sang trọng. Máy có kích thước vừa vặn nên rất cầm nắm thoải mái, vừa tay, không quá cồng kềnh.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Masstel-Tab-10-Ultra/description/19_3.jpg\r\n\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n<h2 class=\"paragraph__h2\">Màn hình rộng thoải mái giải trí</h2>\r\n<p class =\"paragraph__p\">Màn hình 10.1 inch, có độ phân giải 800 x 1280 Pixels, sở hữu công nghệ màn hình IPS đem đến hình ảnh rõ ràng, sắc nét, góc nhìn rộng, cùng với công nghệ cảm ứng đa điểm, thao tác vuốt chạm trở nên dễ dàng, nhanh chóng. </p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Masstel-Tab-10-Ultra/description/19_4.jpg\r\n\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Cấu hình tốt trải nghiệm ổn định</h2>\r\n<p class =\"paragraph__p\">Máy chạy trên hệ điều hành Android 10 tiên tiến, với RAM 3 GB, giúp máy hoạt động nhanh, mượt mà. Sử dụng Spreadtrum SC9863A 8 nhân tốc độ 1.6 GHz, cho phép người dùng thỏa sức trải nghiệm các tác vụ xem phim, lướt web cơ bản và các tựa game nhẹ.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Masstel-Tab-10-Ultra/description/19_5.jpg\r\n\" alt=\"\">\r\n<p class =\"paragraph__p\">Ngoài ra, máy cũng hỗ trợ mạng 4G, thỏa thích sử dụng internet ở những nơi không có wifi, có thể sử dụng 2 SIM Nano cùng thẻ nhớ Micro SD hỗ trợ tối đa 128 GB (chung khe SIM 2).</p>\r\n<h2 class=\"paragraph__h2\">Camera đủ dùng lưu giữ khoảnh khắc</h2>\r\n<p class =\"paragraph__p\">Được trang bị camera trước và sau, có độ phân giải lần lượt là 2 MP và 8 MP. Người dùng có thể dễ dàng selfie và video call, bên cạnh đó, camera sau có khả năng lấy nét tự động, kèm đèn flash để quay video và chụp ảnh tốt hơn trong điều kiện thiếu sáng.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Masstel-Tab-10-Ultra/description/19_6.jpg\r\n\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Thời lượng pin tốt trong phân khúc</h2>\r\n<p class =\"paragraph__p\">Máy sử dụng pin Li-Po, nhẹ và an toàn, dung lượng 6000 mAh cho phép thời gian sử dụng cơ bản khoảng 1.5 ngày, thời gian đàm thoại đến 16 giờ.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Masstel-Tab-10-Ultra/description/19_7.jpg\r\n\" alt=\"\">', 5, 1),
(22, 3, 'Masstel Tab8 Pro', '../assets/image/products/tablet/Masstel-Tab8-Pro/20.jpg', 1940000, 0, 5, 2, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 8 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 9\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nMediaTek MT8321 4 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n2 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n16 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối mạng:</span>\r\n<span class=\"content__row-content\">\r\nHỗ trợ 3G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hỗ trợ SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Micro SIM\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đàm thoại:</span>\r\n<span class=\"content__row-content\">\r\nCó\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Masstel Tab8 Pro</b>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Masstel-Tab8-Pro/description/20_2.jpg\" alt=\"\">\r\n<h1 class =\"paragraph__h1\">Masstel Tab8 Pro là một chiếc máy tính bảng chạy Android</h1>\r\n<h2 class=\"paragraph__h2\">Màn hình lớn, dùng \"đã\" hơn</h2>\r\n<p class =\"paragraph__p\">Tất nhiên rồi, ai chọn máy tính bảng thì cũng đều thích chiếc màn hình của nó vì nó lớn hơn khá nhiều khi so với smartphone.\r\nChiếc máy tính bảng Masstel này sở hữu một màn hình có kích thước 8 inch cùng độ phân giải 1280 x 800 pixels, tuy không quá sắc nét nhưng vừa đủ cho bạn trải nghiệm hằng ngày.\r\nMáy sử dụng tấm nền IPS LCD cho màu sắc khá chính xác cùng độ sáng cao giúp bạn dễ dàng có thể sử dụng ngoài trời.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Masstel-Tab8-Pro/description/20_3.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n<h2 class=\"paragraph__h2\">Phù hợp với các tác vụ nhẹ nhàng</h2>\r\n<p class =\"paragraph__p\">Masstel Tab8 Pro được cung cấp sức mạnh bởi con chip MT8321 kết hợp với 2 GB RAM và 16 GB bộ nhớ trong.\r\nVới những tác vụ như lướt Facebook hay xem YouTube nhẹ nhàng thì chiếc máy tính bảng này phản hồi khá nhanh, ít xảy ra hiện tượng giật lag.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Masstel-Tab8-Pro/description/20_4.jpg\" alt=\"\">\r\n<h2 class=\"paragraph__h2\">Pin lớn xài lâu</h2>\r\n<p class =\"paragraph__p\">Masstel Tab8 Pro sở hữu viên pin có dung lượng 4000 mAh đảm bảo thời gian sử dụng bền bỉ.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Masstel-Tab8-Pro/description/20_5.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bạn hoàn toàn có thể sử dụng máy lên tới 2 ngày nếu nhu cầu của mình không quá cao.</p>\r\n<img class = \"paragraph__img\"src=\"../assets/image/products/tablet/Masstel-Tab8-Pro/description/20_6.jpg\" alt=\"\">', 5, 1),
(23, 1, 'iPhone 12 Pro Max 128GB', '../assets/image/products/phone/iPhone-12-Pro-Max-128GB/nền.jpg', 31690000, 0, 5, 6, 1, NULL, '<div class=\"content__row\"> <span class=\"content__row-title\">Màn hình:</span> <span class=\"content__row-content\">OLED, 6.7\", Super Retina XDR</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">Hệ điều hành:</span> <span class=\"content__row-content\">iOS 14</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">Camera sau:</span> <span class=\"content__row-content\">3 camera 12 MP</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">Camera trước:</span> <span class=\"content__row-content\">12 MP</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">Chip:</span> <span class=\"content__row-content\">12 MP</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">RAM:</span> <span class=\"content__row-content\">6 GB</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">Bộ nhớ trong:</span> <span class=\"content__row-content\">128 GB</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">SIM:</span> <span class=\"content__row-content\">1 Nano SIM & 1 eSIM, Hỗ trợ 5G</span> </div> <div class=\"content__row\"> <span class=\"content__row-title\">Pin:</span> <span class=\"content__row-content\">3687 mAh, Sạc nhanh</span> </div> ', '<b class =\"paragraph__title\">Đặc điểm nổi bật của iPhone 12 Pro Max 128GB</b>\r\n<h1 class = \"paragraph__h1\">Nếu không có gì thay đổi, iPhone 12 Pro Max 128 GB siêu phẩm smartphone đang được mong chờ nhất của Apple sẽ được ra mắt và khoảng nửa cuối năm nay (2020). Hứa hẹn đây sẽ là một sự thay đổi ngoạn mục của iPhone cả về thiết kế lẫn hiệu năng.</h1>\r\n<h2 class = \"paragraph__h2\">Thay đổi thiết kế mới sau 6 năm</h2>\r\n<p class = \"paragraph__p\">Theo chu kỳ cứ mỗi 3 năm thì iPhone lại thay đổi thiết kế và 2020 là năm đánh dấu cột mốc quan trong này, vì thế rất có thể đây là thời điểm các mẫu iPhone 12 sẽ có một sự thay đổi mạnh mẽ về thiết kế.</p>\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max sở hữu diện mạo mới mới với khung viền được vát thẳng và mạnh mẽ như trên iPad Pro 2020, chấm dứt hơn 6 năm với kiểu thiết kế bo cong trên iPhone 6 được ra mắt lần đầu tiên vào năm 2014.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-2-1.jpg\" alt=\"\">\r\n<p class = \"paragraph__p\">Nếu iPad Pro 2020 sử dụng chất liệu nhôm nguyên khối, thì iPhone 12 Pro Max sẽ là chất liệu mặt kính cùng khung viền thép, làm người dùng gợi nhớ nhiều hơn về bộ đôi iPhone 4 /4s huyền thoại với thiết kế đẹp nhất trong lịch sử thiết kế của Apple.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-4-1.jpg\" alt=\"\">\r\n<p class = \"paragraph__p\">Sự khác biệt với bộ đôi 4/4s nằm ở phần mặt kính của iPhone 12 Pro Max được làm cong nhẹ, giúp ôm khít khung viền hoàn hảo tạo sự liền mạch của màn hình khi cầm nắm sử dụng.</p>\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max sẽ có độ mỏng ấn tượng chỉ 7,4 mm tức còn mỏng hơn khoảng 10% so với iPhone 11 Pro Max hiện nay đang là 8,1 mm. Điều này càng làm tăng sự quyến rũ, đẳng cấp cho thiết bị từ mọi góc nhìn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-6-1.jpg\" alt=\"\">\r\n<h2 class = \"paragraph__h2\">Tai thỏ được thu gọn đáng kể</h2>\r\n<p class =\"paragraph__p\">Qua đến iPhone 12, thì phần khuyết được Apple thu gọn đi rất nhiều, điều này giúp tăng diện tích hiển thị đồng thời nâng cao trải nghiệm hình ảnh đáng kể.</p>\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max được trang bị màn hình OLED với công nghệ Super Retina XDR mang đến chất lượng hình ảnh sắc nét, màu sắc được tái tạo chuẩn xác, độ tương phản cao, màu đen sâu tiết kiệm pin hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-292420-102459.jpg\" alt=\"\">\r\n<p class = \"paragraph__p\">Sẽ tuyệt vời hơn nữa, nếu như iPhone 12 Pro Max cũng được trang bị màn hình có tần số quét 120 Hz tương tự như iPad Pro 2017. Gấp đôi tần số quét, giúp bạn tận hưởng độ mượt mà đến kinh ngạc trong khung hình, đặc biệt nhận biết rõ nhất là khi chơi game hay những bộ phim bom tấn ở chất lượng cao.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-1-1.jpg\" alt=\"\">\r\n<p class = \"paragraph__p\">Bên cạnh đó, một tin không vui cho iFan, iPhone 12 Pro Max rất có thể sẽ không đi kèm bất kỳ phụ kiện nào, kể cả cáp sạc đều là những phụ kiện cơ bản nhất. Điều này đồng nghĩa, người dùng cần chi thêm một số tiền kha khá để mua chính hãng.</p>\r\n<h2 class =\"paragraph__h2\">Cụm camera sau được nâng cấp mạnh mẽ</h2>\r\n<p class =\"paragraph__p\">Cụm camera iPhone 12 Pro Max tiếp tục sẽ là phần được mong chờ nhất cũng là phần quan trọng mà Apple tập trung nâng cấp qua các thế hệ iPhone.</p>\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max vẫn sử dụng hệ thống camera hình vuông ở phía sau đồng thời bổ sung thêm cảm biến LiDAR tạo thành 1 hệ thống 3 camera chất lượng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-3-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Cảm biến LiDAR đo độ sâu bằng tia laser tương tự như iPad Pro giúp nhận diện khoảng cách giữa vật thể chính xác, tăng cường cho các ứng dụng thực tế ảo đồng thời cũng hỗ trợ cho 3 camera hoạt động 1 cách tối ưu nhất.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-292720-102707.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Chất lượng hình ảnh iPhone 12 Pro Max cho ra luôn thuộc hàng top hiện nay với điểm mạnh là màu sắc vô cùng chân thực, độ chính xác cao, dải màu rộng. </p>\r\n<p class =\"paragraph__p\">Về tính năng, iPhone 12 Pro Max kế thừa mọi thứ hiện có trên iPhone 11, gồm chế độ Deep Fusion và Night Mode. Cũng có thể Apple sẽ bổ sung thêm tính năng xử lý hình ảnh mới.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-9-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Cuối cùng, camera trước trên iPhone 12 Pro Max với độ phân giải 12MP, hỗ trợ HDR, quay video Full HD, tăng chất lượng những bức ảnh selfie lên đáng kể.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-292820-102824.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Hiệu năng khủng từ “quái vật” A14 Bionic</h2>\r\n<p class =\"paragraph__p\">A14 Bionic sẽ là vi xử lý được trên bị trên iPhone 12 Pro Max với 6 nhân xung nhịp lên tới 3.1 GHz hứa hẹn một hiệu năng mạnh nhất từ trước đến nay trên các thiết bị Apple.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-9-2.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bên cạnh đó, thiết bị sẽ đi kèm RAM 6 GB giúp máy hoạt động mượt mà trơn tru nhiều ứng dụng lẫn widget mới đang có mặt trên iOS 14. Dù hơi muộn màng, nhưng cuối cùng A14 tích hợp modem mang 5G mang đến kết nối nhanh chóng nhất hiện nay.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-293120-103117.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với lượng trang bị chất lượng và mạnh mẽ này dễ dàng giúp điện thoại chơi game tốt trên mọi thể loại game từ nhẹ đến nặng ở mức cấu hình cao mà không gặp bất cứ trở ngại nào.</p>\r\n<p class =\"paragraph__p\">Có thể thấy, iPhone 12 Pro Max 128 GB là mẫu smartphone cao cấp đầy mạnh mẽ sở hữu cho mình những ưu điểm nổi trội về camera hay cả hiệu năng mạnh mẽ. Hứa hẹn đây sẽ là mẫu sản phẩm mang tới trải nghiệm tốt đến người dùng.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 1, 1),
(24, 1, 'Samsung Galaxy S21+ 5G 256GB', '../assets/image/products/phone/Samsung-Galaxy-S21+5G-256GB/Nền.jpg', 25990000, 0, 5, 4, 1, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">Dynamic AMOLED 2X, 6.8\", Quad HD+ (2K+)</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">Android 11</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">Chính 108 MP & Phụ 12 MP, 10 MP, 10 MP</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">40 MP</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">Exynos 2100 8 nhân</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">12 GB</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">256 GB</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">2 Nano SIM hoặc 1 Nano SIM + 1 eSIM, Hỗ trợ 5G</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">5000 mAh, có sạc nhanh</span>\r\n</div> ', '<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-9.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Công nghệ màn hình chuẩn cao nhất</h2>\r\n<p class =\"paragraph__p\">Samsung Galaxy S21 Ultra 5G có màn hình lớn với kích thước 6.8 inch cho bạn không gian giải trí thoải mái hơn. Hơn thế nữa, việc trang bị tấm nền Dynamic AMOLED 2X độ phân giải 2K+ đưa S21 Ultra 5G trở thành smartphone sở hữu màn hình sống động, sắc nét bậc nhất</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-3.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Màn hình nâng cấp tần số quét 120 Hz khiến mọi thao tác vuốt chạm, chuyển động hình ảnh cực kỳ mượt, bạn chỉ cần cử động nhẹ ngón tay và S21 Ultra 5G di chuyển mọi thứ theo ý bạn ngay lập tức, mang đến bạn cảm giác đắm chìm trong giải trí.</p>\r\n<p class =\"paragraph__p\">Dynamic AMOLED 2X có thể đạt độ sáng cao nhất lên đến 1500 nits, hỗ trợ HDR10+ độ tương phản cao giúp người dùng có thể sử dụng điện thoại dưới mọi điều kiện sáng</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-4.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Chiếc điện thoại Galaxy S21 Ultra 5G luôn được bảo vệ tốt khi trang bị kính cường lực ở cả 2 mặt, đặc biệt là mặt trước với kính cường lực Gorilla Glass Victus thế hệ mới rắn chắc, hạn chế tối đa các tác nhân gây va đập, trầy xước.</p>\r\n<h2 class =\"paragraph__h2\">Camera như một nhiếp ảnh gia chuyên nghiệp</h2>\r\n<p class =\"paragraph__p\">Hệ thống máy ảnh của S21 Ultra 5G sẽ luôn là điểm nhấn ấn tượng với những thông số độ phân giải cao vượt trội. Camera chính lên tới 108 MP, camera góc siêu rộng 12 MP và 2 camera tele 10 MP hỗ trợ thu phóng tầm xa tuyệt vời.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-5.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Độ phân giải cao cùng những tính năng chỉnh sửa ảnh như khả năng lấy nét, góc rộng, tự động cấp ánh sáng trong điều kiện thiếu sáng như buổi đêm, HDR và các tính năng zoom cự ly cực kỳ xa sẽ mang đến cho bạn những bức ảnh đẹp lung linh và từng chi tiết chất lượng cao thực sự.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-7.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Galaxy S21 Ultra 5G còn sở hữu tính năng quay phim mang chất lượng dẫn đầu thời đại quay video 8K siêu sắc nét. Hỗ trợ các chuẩn chống rung chuyên nghiệp cho bạn sự đắm chìm trong những thước phim chính tay mình sáng tạo.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-8.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">A.I camera trước độ phân giải lên đến 40 MP trang bị mọi tính năng giúp làm đẹp ảnh của bạn, S21 Ultra 5G tự tin sở hữu máy ảnh selfie xuất sắc không thua kém gì ống kính mặt sau. Ngoài ra, ống kính trước cũng sở hữu quay phim độ phân giải 4K. </p>\r\n<h2 class =\"paragraph__h2\">Cả ngày dài tràn đầy năng lượng </h2>\r\n<p class =\"paragraph__p\">Galaxy S21 Ultra 5G sẵn sàng đáp ứng mọi hoạt động làm việc, học tập và giải trí của người dùng nhiều giờ liền với sự hỗ trợ của viên pin dung lượng lớn 5000 mAh. Trang bị củ sạc nhanh siêu tốc công suất 25 W giúp máy nạp đầy trở lại năng lượng một cách nhanh chóng nhất có thể.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234308/samsung-galaxy-s21-ultra-256gb-800x533-10.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Lưu ý: Củ sạc sẽ không đi kèm trong hộp khi mua máy</p>\r\n<p class =\"paragraph__p\">PowerShare không dây đã có mặt trên S21 Ultra 5G. Tính năng này vô cùng tiện lợi khi cho smartphone của bạn khả năng chia sẻ pin của mình cho các thiết bị thông minh khác; đồng hồ hoặc điện thoại bạn bè,... trong trường hợp khẩn cấp.</p>\r\n<p class =\"paragraph__p\">Chiếc smartphone cao cấp Samsung Galaxy S21 Ultra 5G gần như hoàn hảo ở mọi khía cạnh khi mọi chức năng trong máy đều đạt hiệu năng tuyệt vời. Điện thoại khoác lên mình thiết kế độc đáo hứa hẹn khẳng định chất riêng và vẻ sang trọng, lịch lãm của chủ sở hữu nó.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 2, 1),
(25, 1, 'OPPO Reno5', '../assets/image/products/phone/OPPO-Reno5/Nền.jpg', 8390000, 0, 5, 9, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nAMOLED, 6.43\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 11\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 64 MP & Phụ 8 MP, 2 MP, 2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n44 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 720G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM, Hỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n4310 mAh, Sạc nhanh\r\n</span>\r\n</div>\r\n', '<b class =\"paragraph__title\">Đặc điểm nổi bật của OPPO Reno5</b>\r\n                    <h1 class = \"paragraph__h1\">OPPO vừa kịp ra mắt thế hệ OPPO Reno mới nhất của mình vào dịp cuối năm 2020. OPPO Reno5 là sự kết hợp đầy ấn tượng giữa hiệu năng và thiết kế, mang đến cho người dùng một chiếc điện thoại tích hợp nhiều công nghệ camera, sạc pin hàng đầu của OPPO trong một mức giá tầm trung.</h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế đẹp đậm chất OPPO Reno Series</h2>\r\n                    <p class = \"paragraph__p\">Có thể nói OPPO Reno5 là mẫu điện thoại phô diễn được đỉnh cao thiết kế và công nghệ chế tác của OPPO khi bề mặt lưng được phủ lớp Reno Glow với ngàn tinh thể phát sáng siêu nhỏ tạo nên hiệu ứng chuyển sắc vô cùng hút mắt.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-231120-031101.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Mặt trước OPPO Reno5 cũng ấn tượng không kém với màn hình tràn viền được làm cong nhẹ 2.5D. Hai bên cạnh viền siêu mỏng, thiết kế camera đục lỗ giúp không gian hiển thị được mở rộng tối đa.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-231320-031318.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Các góc cạnh của OPPO Reno5 cũng được bo cong mềm mại về 2 phía bên cạnh của máy giúp máy nhìn trông mỏng hơn và tạo sự dễ chịu mỗi khi cầm sử dụng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-133821-103834.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Bức phá hiệu năng với vi xử lý Snapdragon 720G mới</h2>\r\n                    <p class = \"paragraph__p\">Reno5 được OPPO ưu ái trang bị vi xử lý Snapdragon 720G, hậu tố “G” phía sau Snapdragon 720 ý muốn nói đây là dòng chip chuyên game đi kèm tính năng Elite Gaming của Qualcomm.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-181120-031138.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Không phải bàn về hiệu năng của Snapdragon 720G, khi đây là vi xử lý chuyên chơi game với những nâng cấp ấn tượng về xung nhịp lẫn khả năng đồ họa, hứa hẹn mang đến trải nghiệm game đỉnh cao.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-180520-030502.png\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bên cạnh đó, OPPO Reno5 còn chiếm ưu thế về lưu trữ và đa nhiệm khi trang bị RAM 8 GB tốc độ LPDDR4x cùng bộ nhớ trong 128 GB UFS 2.1, OPPO Reno5 vẫn dư sức cho bạn thoải mái cài đặt ứng dụng, lưu trữ ảnh, nhạc, video, đáp ứng đủ nhu cầu lưu trữ của bạn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-231620-031631.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Qua phần đánh giá hiệu năng Antutu, OPPO Reno5 đạt được 287.975 điểm, với số điểm này dễ dàng giúp máy chiến hầu hết các tựa game từ nhẹ đến nặng một cách mượt mà, ổn định.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-1-3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình lớn chuyển động 90 Hz mượt mà</h2>\r\n                    <p class = \"paragraph__p\">Màn hình chính là điểm cộng giúp cho Reno5 vượt trội trong tầm giá, khi tích hợp tần số quét 90 Hz và tốc độ lấy mẫu cảm ứng 180 Hz, mang đến độ mượt mà trong trải nghiệm vuốt chạm hàng ngày và cảm ứng siêu nhạy không độ trễ khi chơi game.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-231920-031951.jpg\" alt=\"\"> \r\n                    <h2 class = \"paragraph__h2\">Camera chụp siêu nét, selfie cực đỉnh</h2>\r\n                    <p class = \"paragraph__p\">OPPO Reno5 dùng chung hệ thống cảm biến cao cấp trên OPPO Reno5 Pro với cụm 4 camera sau gồm camera chính 64 MP, camera góc siêu rộng 8 MP, camera macro 2 MP và camera xóa phông 2 MP hứa hẹn đem đến chất lượng ảnh vô cùng ấn tượng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-182120-032144.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Cảm biến 64 MP cùng các camera phụ cung cấp nhiều tùy chọn chụp đa dạng từ chụp cận cảnh đến góc rộng, xóa phông dù ngày hay đêm, thỏa mãn đam mê nhiếp ảnh của bạn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-182920-032944.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Thách thức bóng đêm với cảm biến Sony cao cấp và thuật toán xử lý bằng AI cải tiến giúp thu sáng và tái tạo các chi tiết rõ nét và cho ra những tấm hình tỏa sáng trong đêm.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-180520-030550.png\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Giờ đây, Reno5 đã có thể quay phim HDR, bạn có thể quay phim khi ngược sáng mà các chi tiết vẫn được giữ lại đầy đủ cùng cân bằng trắng được thể hiện tự nhiên giữa các vùng chênh sáng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-183620-033655.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Cùng với đó là công nghệ siêu chống rung 3.0, chế độ quay đồng thời từ cả camera trước và sau, chỉnh sửa video dễ dàng với Soloop giúp bạn sáng tạo những nội dung vlog thú vị để chia sẻ với bạn bè.</p>\r\n                    <p class = \"paragraph__p\">Cực phẩm camera trước 44 MP của OPPO sẽ làm hài lòng các tín đồ chuyên chụp ảnh selfie bởi tính năng làm đẹp AI vô cùng tự nhiên, không cần đến ứng dụng chụp hình nào khác vì OPPO Reno5 đã sở hữu camera selfie tốt nhất hiện nay.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-180320-030314.png\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Sạc nhanh 50 W nhanh đến ấn tượng</h2>\r\n                    <p class = \"paragraph__p\">Trang bị viên pin dung lượng 4310 mAh, OPPO Reno5 cho bạn một ngày làm việc, trải nghiệm hay giải trí mà không cần lo nghĩ về pin.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-9.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Công nghệ sạc pin nhanh với công suất 50 W tiên phong về tốc độ sạc. OPPO Reno5 đáp ứng cuộc sống năng động của bạn chỉ với 5 phút sạc là bạn đã có thể trải nghiệm thêm nhiều giờ liền và chưa đầy 1 tiếng là pin được nạp đầy 100%, sẵn sàng cho cả ngày sử dụng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-232320-032303.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">ColorOS đầy màu sắc, đa tính năng, lướt mượt mà</h2>\r\n                    <p class = \"paragraph__p\">OPPO Reno5 được cài sẵn ColorOS 11.1 mới nhất cùng với phiên bản Android 11 vừa được ra mắt gần đây, mang đến tính năng thú vị như chế độ Dark Mode, trợ lý ảo Breeno, hay thư giãn cùng OPPO Relax 2.0, cho bạn tận hưởng những âm thanh thường ngày.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220438/oppo-reno5-184020-034039.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">ColorOS 11.1 còn là bản nâng cấp đáng giá về khả năng tối ưu hệ thống giúp tốc độ phản hồi tăng 32% và độ ổn định tốc độ khung hình tăng 17%.</p>\r\n                    <p class = \"paragraph__p\">Tóm lại, OPPO Reno5 là thế hệ tiếp nối đầy ấn tượng của Reno4 với nhiều nâng cấp mạnh mẽ về hiệu năng, thiết kế, phần mềm trong 1 sản phẩm duy nhất. Đặc biệt, hệ thống camera cải tiến chính là điểm hút khách, giúp cho OPPO Reno5 trở thành mẫu điện thoại chụp ảnh tốt nhất trong tầm giá.</p>\r\n                    <button class =\"paragraph__more\">Xem thêm !</button>', 6, 1),
(26, 1, 'Samsung Galaxy A52 5G', '../assets/image/products/phone/Samsung-Galaxy-A52-5G/Nền.jpg', 10590000, 0, 5, 10, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nSuper AMOLED, 6.5\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 11\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 64 MP & Phụ 12 MP, 5 MP, 5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n32 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 750G 5G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM, Hỗ trợ 5G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n4500 mAh, Sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy A52 5G</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/Slider/vi-vn-samsung-galaxy-a52-5g-tongquan.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Samsung Galaxy A52 5G ra mắt được trang bị con chip Snapdragon 750G có hỗ trợ 5G tốc độ cao, trải nghiệm đã mắt với màn hình Super AMOLED đi cùng với dung lượng pin lớn và thuộc phân khúc tầm trung rất dễ tiếp cận với mọi người dùng.</h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế hiện đại, mở đầu xu hướng </h2>\r\n                    <p class = \"paragraph__p\">Samsung Galaxy A52 5G sở hữu thiết kế nguyên khối với mặt lưng được phủ một lớp nhám, các chi tiết thừa được bỏ đi tạo nên tổng thể đơn giản, sang trọng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-01.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bên cạnh đó, Samsung Galaxy A52 5G còn sở hữu khả năng kháng nước và bụi chuẩn IP67 giúp máy có thể chống nước ở độ sâu 1 mét tối đa 30 phút, giúp bạn yên tâm trải nghiệm trong mọi hoàn cảnh và môi trường.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-11.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Hệ thống âm thanh vòm với hai loa một ở trên và một ở dưới cùng của điện thoại, cho bạn những trải nghiệm chơi game hay phút giây giải trí cùng phim ảnh lên tầm cao mới nhờ chất âm chuẩn điện ảnh.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-14.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Samsung đem tới cho người tiêu dùng các sắc màu thời thượng bao gồm: Tím Thanh Lịch, Đen Bản Lĩnh, Trắng Tối Giản và Xanh Năng Động để bạn thoả thích thể hiện cá tính riêng của mình.</p>\r\n                    <img class = \"paragraph__img\" src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-14.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình giải trí rực rỡ đa sắc màu</h2>\r\n                    <p class = \"paragraph__p\">Màn hình là một trong những nâng cấp đáng chú ý nhất trên Galaxy A52 5G khi được hỗ trợ tần số quét cực cao 120 Hz, mọi trải nghiệm vuốt chạm, cuộn trang của bạn sẽ càng ấn tượng hơn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-16.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Ấn tượng ban đầu với màn hình của Galaxy A52 5G là kiểu màn hình Infinity-O rộng 6.5 inch với độ sáng lên đến 800 nits, cho chất lượng hiển thị luôn sắc nét kể cả khi sử dụng dưới trời nắng gắt.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-28.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Ngoài ra, máy còn sở hữu công nghệ màn hình Super AMOLED mang đến chất lượng hiển thị sắc nét, hình ảnh tươi tắn cho bạn tận hưởng các chương trình giải trí hấp dẫn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-23.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Hơn thế nữa, Samsung còn tăng cường bảo mật trên Galaxy A52 5G bằng công nghệ cảm biến vân tay ngay dưới màn hình giúp bạn có thể sử dụng vân tay để mở khóa điện thoại hay đăng nhập vào các ứng dụng mà không cần mật khẩu.</p>\r\n                    <h2 class = \"paragraph__h2\">Hiệu năng bức phá cùng Snapdragon 750G</h2>\r\n                    <p class = \"paragraph__p\">Galaxy A52 5G được trang bị con chip Snapdragon 750G với quy trình 8 nm hiện đại mang đến năng lượng không giới hạn cho mọi trải nghiệm. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-003.jpg\" alt=\"\"> \r\n                    <p class = \"paragraph__p\">Kết hợp với 8 GB RAM cho bạn đa nhiệm được tốt hơn, tình trạng tải lại ứng dụng sẽ được hạn chế. Bộ nhớ trong 128 GB đi kèm đủ lớn để bạn lưu giữ những video, hình ảnh mà không lo bị đầy.</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-17.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Samsung còn trang bị trên Galaxy A52 5G chế độ Game Booster cho khả năng tự động tối ưu hóa smartphone với tựa game đang chạy, đồng thời kiểm soát nhiệt độ và theo dõi thời lượng pin để mang tới cho người dùng trải nghiệm gaming đỉnh cao nhất.</p>\r\n                    <h2 class = \"paragraph__h2\">Cụm 4 camera 64 MP hỗ trợ chống rung quang học OIS</h2>\r\n                    <p class = \"paragraph__p\">Cụm camera trên Samsung Galaxy A52 5G năm nay cũng rất đáng mong chờ khi sở hữu các thông số khủng bao gồm: Camera chính độ phân giải 64 MP f/1.8, camera góc rộng 12 MP f/2.2, cho góc nhìn 123 độ, camera độ sâu 5 MP và cuối cùng là camera macro 5 MP.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-21.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Camera chính với độ phân giải 64 MP hỗ trợ chống rung quang học OIS cao cấp đảm bảo chất lượng hình ảnh luôn luôn ổn định, lấy nét chính xác kể cả khi hoạt động ở điều kiện thiếu sáng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-13.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Camera góc rộng 12 MP với góc nhìn 123 độ tăng thêm góc chụp thu mọi cảnh vật thiên nhiên nhiên hùng vĩ, ảnh nhóm đông người thật dễ dàng.</p>\r\n                    <p class = \"paragraph__p\">Camera macro 5 MP giúp người dùng có thẻ chụp gần hơn mọi vật thể và ghi lại các chi tiết siêu nhỏ với các hiệu ứng bokeh độc đáo, ấn tượng, mang lại trải nghiệm chụp ảnh đầy nghệ thuật.</p>                    \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-22.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Camera độ sâu 5 MP cho phép bạn điều chỉnh độ sâu trường ảnh trong ảnh của mình. Bạn có thể dễ dàng tinh chỉnh độ mờ hậu cảnh phía sau chủ thể của mình để có những bức ảnh chân dung chất lượng cao thực sự nổi bật.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-002.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Galaxy A52 5G còn được trang bị camera trước với độ phân giải 32 MP tích hợp nhiều công nghệ thông minh tự động lấy nét, chế độ HDR, nhận diện khuôn mặt cùng nhiều hiệu ứng bokeh làm mờ phông nền hoàn hảo, để bạn luôn nổi bật và toả sáng trong mọi hoàn cảnh.</p>\r\n                    <h2 class = \"paragraph__h2\">Trải nghiệm thả ga cả ngày dài</h2>\r\n                    <p class = \"paragraph__p\">Giải trí bất tận cả ngày dài với dung lượng pin lớn 4500 mAh, tích hợp công nghệ AI sẽ phân tích và tùy chỉnh pin tùy theo điều kiện sử dụng của người dùng, nhờ đó kéo dài thời lượng sử dụng lên đến 2 ngày cho bạn có nhiều thời gian để làm việc hay giải trí mà không lo hết pin giữa chừng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-12.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Kết hợp cùng công nghệ sạc nhanh 25 W rất thích hợp với những ai có cường độ sử dụng cao mà không muốn đợi chờ thời gian sạc.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/235440/samsung-galaxy-a52-5g-20.jpg\" alt=\"\">                   \r\n                    <p class = \"paragraph__p\">Nhìn chung, Samsung Galaxy A52 5G hoàn toàn đáp ứng được mọi thao của người dùng từ cơ bản cho đến nâng cao đều mượt mà và nhanh nhạy. Galaxy A52 5G sẽ là một là làn gió mới trong phân khúc tầm trung hứa hẹn làm bùng nổ doanh số trong thời gian tới.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 2, 1);
INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(27, 1, 'Samsung Galaxy S21 5G', '../assets/image/products/phone/Samsung-Galaxy-S21-5G/Nền.jpg', 20990000, 0, 5, 11, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nDynamic AMOLED 2X, 6.2\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 11\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 12 MP & Phụ 64 MP, 12 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n10 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nExynos 2100\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM hoặc 1 Nano SIM + 1 eSIM, Hỗ trợ 5G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n4000 mAh, Sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy S21 5GB</b>\r\n<h1 class =\"paragraph__h1\">Đón năm mới rộn ràng cùng với series Galaxy S21 đến từ Samsung. Phiên bản tiêu chuẩn cho series lần này là Samsung Galaxy S21 5G, nổi bật với thiết kế tràn viền, cụm camera ấn tượng cho đến hiệu năng mạnh mẽ hàng đầu.</h1>\r\n<h2 class =\"paragraph__h2\">“Bộ cánh” mới nổi bật và sang trọng</h2>\r\n<p class =\"paragraph__p\">Nổi bật với cụm camera sau được thiết kế đầy mới lạ, phần khuôn hình chữ nhật chứa bộ 3 camera ôm trọn cạnh trái của chiếc smartphone, viền khuôn cong uyển chuyển, hạn chế tối đa độ nhô ra so với mặt lưng, mang lại cái nhìn tổng thể hài hòa, độc đáo.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_19.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Galaxy S21 có 3 màu sắc chính là: Tím, bạc và đen. Cho người dùng thoải mái lựa chọn để phù hợp với phong cách của bản thân nhất.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_20.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Samsung Galaxy S21 hỗ trợ đầy đủ tính năng bảo mật sinh trắc học, bảo mật tối đa điện thoại của bạn bằng công nghệ mở khóa vân tay siêu âm đặt dưới màn hình và bảo mật khuôn mặt. Kết hợp với tính năng chạm 2 lần sáng màn hình giờ đây bạn có thể bật, mở khóa điện thoại cực kì nhanh chóng, tiện lợi và an toàn</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_4.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Galaxy S21 là chiếc điện thoại kháng nước, bụi đạt chuẩn IP68, chuẩn cao nhất hiện tại. Máy có thể sống sót ở độ sâu 1.5m lên đến 30 phút, giúp hạn chế được khả năng hư hỏng thiết bị khi bị nước đổ vào, trời mưa hoặc không may làm rơi xuống nước.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_17.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Màn hình vô cực Infinity-O siêu mượt</h2>\r\n<p class =\"paragraph__p\">Galaxy S21 được trang bị màn hình Dynamic AMOLED 2X, hỗ trợ HDR10+ tiên tiến do Samsung phát triển, có thể đạt được độ sáng cao nhất lên đến 1300 nits, ngay cả khi dưới ánh sáng mặt trời Galaxy S21 vẫn cho hình ảnh sống động, rõ ràng đến không ngờ, sẽ nâng trải nghiệm thị giác của bạn lên một tầm cao mới</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_7.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Galaxy S21 sở hữu màn hình kích thước 6.2 inch độ phân giải Full HD+ có tần số quét 120 Hz, gấp đôi so với thế hệ trước. Không chỉ phản hồi cực nhạy, mà màn hình còn tối ưu tần số quét trong mỗi lần bạn thực hiện thao tác chạm, cho mọi thao tác chạm của bạn đều mịn màng, nhanh nhạy và có độ chính xác cao nhất.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_11.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bên cạnh đó, Galaxy S21 đã nhận được chứng nhận cho dải màu sắc trên di động, 100% phạm vi bao phủ DCI-P3 (chuẩn điện ảnh) cho hình ảnh không bị nhạt màu mà được hiển thị sống động, bất chấp mọi điều kiện ánh sáng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_13.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Hơn thế nữa, chiếc điện thoại này có khả năng giảm các tác động của ánh sáng xanh, giúp bảo vệ sức khỏe đôi mắt của bạn, hạn chế tình trạng nhức, mỏi mắt khi sử dụng thiết bị trong thời gian dài. Cho bạn thỏa sức tận hưởng những giây phút giải trí đỉnh cao, bất kể ngày đêm.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_9.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Galaxy S21 được bảo vệ bởi kính cường lực Corning Gorilla Glass Victus với khả năng chịu lực cực kỳ ấn tượng, hạn chế tối đa trầy xước và hư hỏng. Đây là loại kính cứng nhất trên điện thoại thông minh Samsung kể từ đầu năm 2021.</p>\r\n<h2 class =\"paragraph__h2\">Camera cho hình ảnh hoàn hảo đến từng chi tiết</h2>\r\n<p class =\"paragraph__p\">Thông số máy ảnh luôn là niềm tự hào của Samsung. Galaxy S21 chiếc điện thoại sở hữu 3 camera sau trong đó camera góc rộng, góc siêu rộng có cùng độ phân giải là 12 MP, camera tele có độ phân giải lên đến 64 MP. Ở mặt trước, camera selfie có độ phân giải 10 MP.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_14.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Cụm camera này hỗ trợ đầy đủ các tính năng mà một người dùng cần. Chế độ chụp chân dung, góc rộng, AI làm đẹp,... luôn sẵn sàng mang đến cho người dùng những bức ảnh xuất sắc mà không cần chỉnh sửa quá nhiều.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_16.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Chế độ chụp đêm (Night Mode) sẽ giúp các cuộc vui trong đêm của bạn được ghi lại một cách rõ ràng. Zoom kỹ thuật số lên đến 30x có thể giúp bạn tiếp cận được những vật thể ở xa với độ chi tiết cao nhất.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_18.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Samsung Galaxy S21 cho phép quay video chất lượng 8K siêu cao. Đây là độ phân giải mà ít có dòng smartphone nào sở hữu được. Nhờ sự hỗ trợ của các công nghệ chống rung siêu ổn định, cùng với một ít bộ lọc màu người dùng có thể sáng tạo những thước phim hàng ngày, khi đi du lịch hay vào dịp đặc biệt,... tất cả đều trông như những bộ phim chất lượng cao ngoài rạp.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_2.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Camera selfie AI 10 MP hỗ trợ chụp góc rộng, HDR, chế độ tự động làm đẹp kèm theo Stickers, người dùng thoải mái sáng tạo đa dạng kiểu mẫu ảnh mà mình thích. Ngoài ra, camera này cũng sở hữu quay video độ phân giải lên đến 4K. </p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_15.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Vi xử lý Exynos 2100 mới nhất của Samsung</h2>\r\n<p class =\"paragraph__p\">Cho đến tháng 01/2021 thì CPU Exynos 2100 8 nhân là con chip thứ 2 của nhà Samsung được sản xuất trên quy trình 5nm EUV, được cải tiến với tốc độ nhanh hơn, thông minh hơn và tiết kiệm điện hơn. Cho bạn thỏa sức làm việc và giải trí hiệu quả với khả năng xử lý mạnh mẽ vượt trội.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_3.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">CPU Exynos 2100 đi kèm với GPU Mali-G78 cải thiện hiệu suất đồ họa lên tới 40% so với thế hệ trước. Hiệu năng đo được trên ứng dụng AnTuTu cho số liệu là 460.537 điểm giúp điện thoại chơi game tốt mượt mà trên các dòng game phổ biến hiện nay như PUBG Mobile, Liên Quân Mobile,....</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/samsung-galaxy-s21-5g-test.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Chế độ Priority Mode mới từ Game Booster của hãng sẽ chặn các cuộc gọi, thông báo, AI sẽ tự động điều chỉnh các cài đặt để mang đến những trải nghiệm tốt nhất, đồng thời cải thiện hiệu suất của máy lên mức tối đa.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_6.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Đi kèm với CPU mạnh mẽ trên là 8 GB RAM cho khả năng đa nhiệm mượt mà, bộ nhớ trong 128 GB cho phép người dùng thoải mái lưu trữ dữ liệu cần thiết. Lưu ý rằng chiếc smartphone này sẽ không hỗ trợ thẻ nhớ ngoài.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Exynos 2100 là một CPU tương thích với mạng 5G, sẽ mang đến tốc độ truyền tải nhanh hơn gấp nhiều lần khi so sánh với mạng 4G, giúp bạn tải lên hoặc tải xuống mọi dữ liệu trong tích tắc</p>\r\n<h2 class =\"paragraph__h2\">Viên pin tích hợp nhiều công nghệ thông minh</h2>\r\n<p class =\"paragraph__p\">Samsung Galaxy S21 có viên pin dung lượng 4000 mAh, tuy dung lượng không quá cao nhưng khi kết hợp cùng công nghệ màn hình mới, CPU được nâng cấp. S21 có thể cùng bạn học tập, giải trí cả ngày mà không quá lo lắng về vấn đề cạn năng lượng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_10.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Công nghệ sạc nhanh 25 W trên Galaxy S21 cung cấp cho người dùng hàng giờ sử dụng chỉ trong vài phút sạc. Ngoài ra, công nghệ sạc nhanh không dây Qi 2.0 sẽ tiện lợi hơn nữa khi không cần đến cáp sạc, gọn gàng và thuận tiện. </p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_5.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">PowerShare đã có mặt trên Galaxy S21, công nghệ này có khả năng biến điện thoại thành một viên pin dự phòng khi có thể chia sẻ pin điện thoại của mình cho các thiết bị khác hỗ trợ sạc không dây như: Đồng hồ thông minh hoặc điện thoại,... một tính năng vô cùng lợi ích.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/220833/galaxy_s21_5g_8.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Chiếc điện thoại Samsung Galaxy S21 5G cùng toàn bộ S21 series mới hứa hẹn sẽ làm mưa làm gió ở phân khúc cao cấp, từ diện mạo sang trọng đến hiệu năng, từ công nghệ màn hình đến cụm camera. Đây là những yếu tố khẳng định đẳng cấp chính chiếc điện thoại và của chủ sở hữu.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 2, 1),
(28, 1, 'iPhone 12 Pro Max 256GB', '../assets/image/products/phone/iPhone-12-Pro-Max-512GB/Nền.jpg', 34490000, 0, 5, 7, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nOLED, 6.7\", Super Retina XDR\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\niOS 14\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n3 camera 12 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n12 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nApple A14 Bionic\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n6 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n256 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM & 1 eSIM, Hỗ trợ 5G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n3687 mAh, Sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của iPhone 12 Pro Max 256GB</b>\r\n<h1 class =\"paragraph__h1\">Chiếc điện thoại iPhone 12 Pro Max 256 GB là mẫu smartphone sở hữu nhiều tính năng nổi bật với hệ thống camera chất lượng, hiệu năng vượt trội hay hỗ trợ kết nối 5G hứa hẹn sẽ là mẫu sản phẩm mang lại cảm giác trải nghiệm tối ưu cho người dùng.</h1>\r\n<h2 class =\"paragraph__h2\">Hiệu năng cao vượt mọi thử thách</h2>\r\n<p class =\"paragraph__p\">Một sự cải tiến được coi là nguồn lõi và là trái tim hay nguồn sức mạnh để có thể vận hành một chiếc điện thoại siêu phẩm 2020 đó là chipset A14 Bionic được sản xuất trên tiến trình 5 nm cho phép tạo ra 11.8 tỷ bóng bán dẫn mang đến hiệu năng vô cùng mạnh mẽ.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-022620-042659.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">iOS 14 cũng được cài sẵn trên máy và mang nhiều điểm cải tiến giúp máy được tối ưu tốt hơn nâng cao hiệu năng.</p>\r\n<p class =\"paragraph__p\">Tính năng App Library cho phép gộp tự động các ứng dụng có sự tương đồng với nhau về chức năng như giải trí, mạng xã hội, game,... trông thật tiện lợi.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-093020-103002.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Lần đầu tiên, Apple cho phép người dùng mang “Widget” lên màn hình chính. Bạn có thể tùy biến kích cỡ, đặt widget ở bất kỳ đâu bạn thích. Và còn nhiều điểm được đổi mới mong chờ bạn khám phá.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-023020-043055.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Công nghệ 5G - tốc độ siêu nhanh</h2>\r\n<p class =\"paragraph__p\">Đây cũng lần đầu, máy được hỗ trợ 5G cho tốc độ mạng siêu nhanh, tốc độ tải xuống và phát trực tiếp nhanh hơn, giảm độ trễ và hiệu suất tổng thể được tốt hơn.</p>\r\n<p class =\"paragraph__p\">Giờ đây việc tải các tập phim Full HD chỉ tính bằng giây, độ trễ tương đối thấp giúp mọi trải nghiệm của bạn luôn được ổn định nhất.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-023220-043207.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Nhiều tựa game mobile cực nặng đòi hỏi cấu hình khủng như PUBG Mobile, Liên Quân hay mới nhất là Tốc Chiến được Apple giới thiệu tại sự kiện,... cùng với mạng 5G cho bạn chơi tốt ở các mức thiết lập với sự mượt mà, không giật lag.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-022920-042903.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bên cạnh đó, 5G trên iPhone không chỉ nhanh mà còn thông minh khi có tính năng “Smart Data” cho phép tự động chuyển đổi qua lại giữa 5G và LTE. Khi iPhone không cần tốc độ 5G thì nó sẽ tự động dùng LTE để tiết kiệm pin. Bạn cũng có thể sử dụng 5G bất cứ lúc nào có sẵn.</p>\r\n<h2 class =\"paragraph__h2\">Thiết kế đậm chất truyền thống nhưng hiện đại</h2>\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max làm người dùng nhớ lại những ký ức xưa cũ đi vào huyền thoại. Nét thiết kế với khung viền vuông vức, mạnh mẽ từng làm nên tên tuổi của iPhone 4, iPhone 5 giờ đây quay trở lại.</p>\r\n<p class =\"paragraph__p\">Khung viền iPhone 12 Pro Max được hoàn thiện bằng thép không gỉ, sáng bóng giúp tăng độ cứng cáp và sang trọng cho máy.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-4-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Mặt lưng trang bị cụm camera độc đáo được đặt lệch về bên trái, mặt kính sau cũng được phủ mờ, cải thiện độ bám khi cầm và hạn chế mồ hôi, dấu vân tay.</p>\r\n<p class =\"paragraph__p\">Mặt trước của iPhone 12 Pro Max phủ một lớp bảo vệ mới có tên là Ceramic Shield. Apple còn khẳng định lá chắn Ceramic Shield này sẽ giúp điện thoại bạn chống trầy xước và va đập tốt hơn 4 lần thông thường.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-023720-043759.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Và mặt trước vẫn còn tai thỏ khá lớn nơi chứa camera selfie và cũng như chứa cụm cảm biến Face ID nhận diện khuôn mặt. Tuy nhiên, các ứng dụng cũng đã được tối ưu sao cho không bị ảnh hưởng bởi tay thỏ quá nhiều.</p>\r\n<p class =\"paragraph__p\">Đây cũng là mẫu iPhone mỏng hơn đáng kể so với phiên bản tiền nhiệm, với độ mỏng chỉ vỏn vẹn 7.4 mm, iPhone 12 Pro Max đủ gọn nhẹ để bạn thao tác dùng một tay thoải mái hay nhét túi quần dễ dàng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-8-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Tăng thêm sự an toàn, iPhone 12 Pro Max cũng có khả năng kháng nước và bụi chuẩn IP68 chịu được độ sâu tối ta 6 mét trong vòng 30 phút cho bạn có thể yên tâm hơn trong những buổi đi chơi biển mà không hề lo bị vô nước.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-023620-043631.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Màn hình giải trí Super Retina XDR OLED chất lượng</h2>\r\n<p class =\"paragraph__p\">Không có nhiều thay đổi so với iPhone 11 Pro hay iPhone 11 Pro Max về màn hình. iPhone 12 Pro Max vẫn sử dụng công nghệ màn hình tiên tiến Super Retina XDR OLED với độ trung thực màu sắc, độ tương phản và độ sáng vượt trội.</p>\r\n<p class =\"paragraph__p\">Màn hình rộng 6.7 inch độ phân giải 1284 x 2778 Pixels và được nâng cấp công nghệ mới có tên Ceramic Shield. Nó không ảnh hưởng đến chất lượng trải nghiệm mà tăng thêm độ bền chống vỡ gấp 4 lần cho bạn sự yên tâm hơn trong khi sử dụng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-1-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Hỗ trợ True Tone và có độ sáng tối đa là 1.200 nits ở HDR từ đó giúp máy hiển thị tốt ngay cả ở ngoài trời dưới ánh nắng trực tiếp mà không không gặp bất kỳ vấn đề gì.</p>\r\n<h2 class =\"paragraph__h2\">Camera được nâng cấp nhiều tính năng</h2>\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max được trang bị 3 cụm camera có độ phân giải đều 12 MP gồm: camera góc rộng, camera góc siêu rộng và camera tele.</p>\r\n<img class =\"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-3-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Camera tele có tiêu cự 65 mm cao hơn iPhone 12 Pro trong khi chỉ có 52 mm. Vì thế mà bản Pro Max có phạm vi zoom quang đến 5x thay vì 4x như bản Pro. Zoom kỹ thuật số trên iPhone 12 Pro Max cũng đạt 12x đem những bức ảnh thu phóng đầy ấn tượng.></p>\r\n<img class =\"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-025520-045538.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max được trang bị cảm biến LiDAR hứa hẹn mang đến nhiều trải nghiệm AR trong tương lai. Công nghệ này cho phép chụp ảnh ở chế độ chân dung ngay cả trong điều kiện thiếu sáng khi bật chế độ ban đêm.</p>\r\n<p class =\"paragraph__p\">Ngoài ra, cảm biến LiDAR có thể hữu ích trong một số lĩnh vực công việc nhất định như thiết kế nội thất bằng cách LiDAR bắn ra những chùm sáng vô hình giúp nó xây dựng một bản đồ 3D nhận biết môi trường xung quanh.</p>\r\n<img class =\"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-024420-044422.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Nhiều công nghệ tối tân cũng được trang bị trên camera iPhone 12 Pro Max như: khả năng quay video HDR đạt chuẩn Dobly Vision, tính năng Deep Fussion, công nghệ Apple ProRAW hỗ trợ chụp ảnh RAW, tính năng Night Mode, Time-Lapse trên cả camera trước và sau cho khả năng chụp đêm xuất sắc.</p>\r\n<img class =\"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-025420-045429.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Áp dụng nhiều công nghệ sạc</h2>\r\n<p class =\"paragraph__p\">Nếu so với iPhone 11 Pro Max thì iPhone 12 Pro Max được Apple giảm dung lượng pin xuống chút ít. Tuy nhiên điều đó là không đáng kể, vì chipset A14 hoạt động luôn được tối ưu, nó tiêu thụ ít điện năng hơn cho cùng một nhu cầu trong khi nó có màn hình lớn hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-5-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max có khả năng phát video trực tuyến 12 giờ và 80 giờ nghe nhạc liên tục cho bạn khỏi lo không đáp ứng đủ trong một ngày sử dụng.</p>\r\n<p class =\"paragraph__p\">Máy có khả năng sạc pin nhanh thông qua củ sạc 20 W, giúp đầy 50% trong 30 phút giúp bạn khỏi phải đợi quá lâu.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-024020-044041.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Lưu ý: Củ sạc không kèm theo trong hộp.</p>\r\n<p class =\"paragraph__p\">MagSafe là công nghệ sạc không dây của Apple được mang trở lại trên iPhone 12 Pro Max năm nay. Với mặt lưng sẽ chứa một miếng nam châm và các linh kiện có từ tính được đặt ở vị trí thuận lợi giúp kết nối nhanh chóng và chính xác với các bộ sạc tích hợp MagSafe.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228743/iphone-12-pro-max-256gb-024020-044046.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Apple năm nay sẽ làm người dùng càng phải bỏ tiền ra để được sở hữu, khi mọi sự tối ưu, mọi sự tốt nhất đều đổ dồn về một siêu phẩm iPhone 12 Pro Max. Với một cấu hình hoàn toàn vượt trội, hệ thống camera đầy chuyên nghiệp cũng như đã mở ra một kỷ nguyên mới về 5G cho tốc độ siêu nhanh.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 1, 1),
(29, 1, 'Samsung Galaxy A72', '../assets/image/products/phone/Samsung-Galaxy-A72/Nền.jpg', 11490000, 0, 5, 3, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nSuper AMOLED, 6.7\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 11\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 64 MP & Phụ 12 MP, 8 MP, 5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n32 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 720G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n256 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM, Hỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n5000 mAh, Sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy A72</b>\r\n<h1 class =\"paragraph__h1\">Sau khi thành công ở phân khúc smartphone cao cấp với Galaxy S21 series, Samsung tiếp tục tấn công phân khúc tầm trung với sự ra mắt của Samsung Galaxy A72 thuộc Galaxy A series, sở hữu nhiều màu sắc trẻ trung, hệ thống camera nhiều tính năng cũng như nâng cấp hiệu năng vô cùng lớn mang đến những trải nghiệm hoàn toàn mới.</h1>\r\n<h2 class =\"paragraph__h2\">Thiết kế trẻ trung, thời thượng</h2>\r\n<p class =\"paragraph__p\">Galaxy A72 cuốn hút người dùng ngay ánh nhìn đầu tiên với đường cong mềm mại, bóng bẩy. Cụm 4 camera sau nằm gọn trong mô-đun hình chữ nhật quen thuộc cùng với đèn LED flash, màu sắc của phần mô-đun và mặt lưng được thiết kế cùng màu giúp cho tổng thể trở nên đồng bộ, hài hòa hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-800x533.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Galaxy A72 sở hữu nhiều màu sắc trẻ trung để người dùng có thể lựa chọn như: Tím, trắng, xanh dương và đen. Bộ màu lần này của Galaxy A72 nhấn mạnh nét thanh lịch, duyên dáng và không kém phần lịch thiệp, sang trọng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-800x533-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bên cạnh phần tạo hình mảnh mai cho cảm giác cầm nắm thoải mái thì Galaxy A72 lại có sức chống chịu vô cùng bền bỉ, máy có khả năng kháng bụi, nước chuẩn IP67, có thể kháng nước ở độ sâu 1 mét tối đa trong 30 phút. Nhờ vậy bạn có thể yên tâm sử dụng Galaxy A72 trong mọi hoàn cảnh, môi trường.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-800x533-2.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Giải trí đa sắc màu trên màn hình tuyệt đỉnh</h2>\r\n<p class =\"paragraph__p\">Galaxy A72 được trang bị tấm nền Super AMOLED cho màu sắc rực rỡ, có độ tương phản cao và khả năng mở rộng góc nhìn, kết hợp với kích thước 6.7 inch người dùng có thể trải nghiệm những hoạt động giải trí hay làm việc một cách thoải mái nhất.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-800x533-3.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Độ phân giải Full HD+ cho trải nghiệm hình ảnh của bạn thêm phần đẹp mắt. Độ sáng tối đa hỗ trợ đến 800 nits, tốc độ làm mới 90 Hz cho những chuyển động, thao tác trên màn hình trở nên mượt mà, uyển chuyển hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-800x533-4.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Màn hình của Galaxy A72 còn có khả năng tối ưu hoá năng lượng sử dụng giúp tiết kiệm pin. Công nghệ Eye Comfort Shield2 giảm thiểu ánh sáng xanh gây hại cho mắt. Bên cạnh đó, để tránh các tác nhân ảnh hưởng đến màn hình như va đập, trầy xước thì Galaxy A72 đã được trang bị kính cường lực hoàn toàn có thể hạn chế được vấn đề trên.</p>\r\n<h2 class =\"paragraph__h2\">Cụm 4 camera AI 64 MP siêu đỉnh</h2>\r\n<p class =\"paragraph__p\">Bắt trọn từng khoảnh khắc xung quanh thật dễ dàng với Galaxy A72, cụm camera đầy chất lượng với camera chính có độ phân giải lên đến 64 MP, camera góc rộng 12 MP 123 độ bắt trọn toàn cảnh, camera tele 8 MP cho khả năng zoom 30x và camera macro 5 MP siêu cận cảnh.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-800x533-5.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với thông số trên, A72 sẵn sàng mang đến cho bạn một hiệu năng chụp ảnh tốt nhất. Hỗ trợ các tính năng như: Chống rung quang học OIS, chụp đêm, AF, HDR,... hoàn toàn có thể đem đến những bức ảnh đầy chất lượng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-800x533-6.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Hệ thống chống rung trên Galaxy A72 giúp ổn định khung hình, giữ cho chuyển động luôn mượt mà và sắc nét trong cả điều kiện ánh sáng yếu.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-800x533-9.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Camera trước cũng có độ phân giải siêu cao lên đến 32 MP, hỗ trợ quay video Full HD chất lượng cao, xoá phông,... máy ảnh tốt sẽ cho bạn thêm tự tin, hứng khởi mỗi lúc tạo dáng selfie, call video với bạn bè, người thân.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-800x533-10.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Hiệu năng bức phá với con chip chuyên game</h2>\r\n<p class =\"paragraph__p\">Cung cấp sức mạnh cho Galaxy A72 là CPU Snapdragon 720G sản xuất trên quy trình 8 nm, mang đến hiệu năng xử lý vượt trội cùng khả năng tiết kiệm pin hiệu quả. GPU Adreno 618 cho mọi tác vụ đồ họa được hiển thị càng mượt mà hơn, những pha hành động trên các tựa game sẽ chân thực hơn bao giờ hết.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-800x533-11.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Đi kèm 8 GB RAM cho máy khả năng đa nhiệm tốt cùng lúc nhiều ứng dụng, 256 GB bộ nhớ trong đủ lớn để bạn lưu giữ nhiều dữ liệu, thông tin như: Video, ứng dụng, hình ảnh,..</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-800x533-13.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Viên pin thông minh, hỗ trợ sạc nhanh 25 W</h2>\r\n<p class =\"paragraph__p\">Sở hữu viên pin vượt mong đợi, sẵn sàng hoạt động cả ngày dài trong trạng thái tốt nhất với dung lượng 5000 mAh. Tính năng quản lý pin sử dụng AI để xác định thói quen sử dụng của người dùng và điều chỉnh năng lượng phù hợp, đáp ứng tốt nhu cầu dùng xuyên suốt như những lúc khi bạn cần chơi trận game hay làm việc hàng giờ.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226101/samsung-galaxy-a72-800x533-12.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Dù có viên pin lớn, người dùng không việc gì phải lo lắng về thời gian sạc vì giờ đây Galaxy A72 cũng đã được trang bị công nghệ sạc nhanh 25 W, luôn đảm bảo điện thoại của bạn sẵn sàng hoạt động trở lại.</p>\r\n<p class =\"paragraph__p\">Nhìn chung, Samsung Galaxy A72 là mẫu smartphone nổi bật với thiết kế thời thượng, màu sắc trẻ trung, cụm camera chuyên nghiệp, hiệu năng mạnh mẽ và màn hình sắc nét. Đây sẽ là sản phẩm hấp dẫn hứa hẹn đem lại những trải nghiệm tốt nhất đến người dùng.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 2, 1),
(30, 1, 'Xiaomi Mi 11 5G', '../assets/image/products/phone/Xiaomi-Mi-11-5G/Nền.jpg', 20990000, 0, 5, 27, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nAMOLED, 6.81\", Quad HD+ (2K+)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 11\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 108 MP & Phụ 13 MP, 5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n20 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 888\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n256 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM, Hỗ trợ 5G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n4600 mAh, Sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Xiaomi Mi 11 5G</b>\r\n<h1 class =\"paragraph__h1\">Xiaomi chốt năm 2020 cực đỉnh và mở đầu năm mới không thể nào ấn tượng hơn với siêu phẩm mang tên Xiaomi Mi 11. Trải nghiệm hiệu năng hàng đầu với vi xử lý Qualcomm Snapdragon 888, cùng loạt công nghệ đỉnh cao, khiến bất kỳ ai cũng sẽ choáng ngợp về smartphone này.</h1>\r\n<h2 class =\"paragraph__h2\">Thiết kế nổi bật với cụm camera độc nhất</h2>\r\n<p class =\"paragraph__p\">Có thể thấy, điểm sáng trong thiết kế của Mi 11 đến từ cụm 3 camera mặt sau được đặt trong mô-đun hình vuông, được phân tầng với 2 lớp kính tạo nên sự khác biệt và nổi bật ngay từ cái nhìn đầu tiên.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-115121-115115.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Mi 11 sở hữu thân máy thanh mảnh với độ mỏng ấn tượng 8.06 mm, được vuốt cong dần về cạnh 2 bên, nối liền hoàn hảo với phần khung kim loại siêu mỏng và màn hình ở mặt trước tạo nên một khối liền mạch, cho bạn trải nghiệm cầm nắm tốt nhất.</p>\r\n<h2 class =\"paragraph__h2\">Làm chủ mọi khung hình với bộ 3 camera 108 MP</h2>\r\n<p class =\"paragraph__p\">Với tổ hợp 3 cảm biến gồm có camera chính 108 MP, camera góc siêu rộng 13 MP và camera chụp cận macro 5 MP, cùng hệ thống phần cứng tối tân được trang bị bên trong. Tất cả đã làm nên đẳng cấp nhiếp ảnh di động chuyên nghiệp trên Mi 11.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-115121-115149.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Tích hợp bên trong camera chính 108 MP là cảm biến hình ảnh lớn 1/1.33 có hỗ trợ chống rung quang học OIS, với công nghệ pixel-binning cho phép gộp 4 điểm ảnh thành 1 điểm ảnh siêu sáng, Mi 11 cho phép người dùng chụp những bức ảnh trong điều kiện thiếu sáng mà vẫn có độ nét hoàn hảo.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-121921-031917.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Chụp phong cảnh, ảnh nhóm dễ dàng từ camera góc siêu rộng 123 độ 13 MP hay chụp ảnh chân dung ảo diệu với hiệu ứng xóa phông hút mắt.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-115121-115134.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Cuối cùng, camera macro sẽ giúp bạn chụp siêu cận ở khoảng cách gần, mang đến những tác phẩm macro ấn tượng, độc lạ.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-121121-031142.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Không chỉ chụp ảnh, Mi 11 còn sở hữu khả năng quay video nổi trội với chất lượng 8K, kèm theo đó là các thuật toán AI và giảm nhiễu RAW giúp tăng cường ánh sáng khi quay video trong điều kiện ánh sáng yếu.</p>\r\n<h2 class =\"paragraph__h2\">Hiệu năng hàng khủng với Snapdragon 888</h2>\r\n<p class =\"paragraph__p\">Được trang bị vi xử lý Qualcomm Snapdragon 888 cao cấp nhất, vừa được ra mắt gần đây với tiến trình 5 nm. Trang bị lõi Arm Cortex-X1 với xung nhịp khủng 2.84 GHz giúp tốc độ xử lý CPU tăng 25%, đồng thời tiết kiệm pin hơn so với Snapdragon 865.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-122221-032200.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Cùng sự nhảy vọt đáng kể về khả năng đồ họa với GPU Adreno 660 tăng 35%, Mi 11 hoàn toàn có thể chạy mượt bất kỳ tựa game mobile “hạng nặng” nào.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-114921-114958.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Mi 11 còn dẫn đầu về tốc độ xử lý AI khi trang bị bộ xử lý AI Engine thế hệ thứ 6 Hexagon 780 giúp cho máy có thể xử lý hình ảnh, video và các tác vụ AI, máy học diễn ra nhanh chóng hơn</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-114921-114947.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Kết hợp với 8 GB RAM LPDDR5 và 256 GB bộ nhớ trong chuẩn UFS 3.1 với tốc độ hàng đầu, cùng công nghệ tản nhiệt chất lỏng VC, giúp cho Mi 11 phát huy hết sức mạnh của vi xử lý Snapdragon 888 khi thực hiện các tác vụ nặng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-115021-115010.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Mi 11 được tích hợp MIUI 12.5 mới nhất hoạt động trên nền Android 11, được xem là bản cập nhật có nhiều cải tiến về bảo mật, tối ưu hiệu năng tốt hơn nhờ sử dụng bộ nhớ nền ít hơn 35% và giảm 25% mức tiêu thụ điện năng nhưng không làm giảm trải nghiệm và sự mượt mà trong mọi tác vụ.</p>\r\n<h2 class =\"paragraph__h2\">Sạc nhanh công suất cao 55 W</h2>\r\n<p class =\"paragraph__p\">Với thiết kế mỏng, nhưng Mi 11 vẫn sở hữu viên pin với dung lượng ấn tượng 4.600 mAh, đảm bảo thoải mái cho một ngày sử dụng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-115021-115040.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Trang bị công nghệ sạc pin nhanh Mi TurboCharge với công suất 55 W, giúp cho Mi 11 được sạc đầy chỉ trong 45 phút, thậm chí sạc không dây 50 W cũng nhanh không kém chỉ với 53 phút.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-115121-115104.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bên cạnh đó, tính năng sạc ngược không dây 10 W giúp bạn chia sẻ năng lượng cho những thiết bị khác vô cùng tiện lợi.</p>\r\n<h2 class =\"paragraph__h2\">Trải nghiệm đỉnh cao với màn hình 120 Hz</h2>\r\n<p class =\"paragraph__p\">Trang bị màn hình siêu tràn viền được uốn cong thác nước tuyệt đẹp với camera “đục lỗ”, sử dụng tấm nền AMOLED, độ phân giải 2K+, đảm bảo cho mọi chi tiết, đồ họa, hình ảnh đều hiển thị với chất lượng cao nhất.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-115221-115231.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Sử dụng vật liệu phát sáng E4 mới cung cấp độ sáng 1.500 nits, việc dùng ngoài trời nắng không còn là trở ngại với Mi 11</p>\r\n<p class =\"paragraph__p\">Mi 11 hỗ trợ tốc độ làm tươi 120 Hz và tốc độ lấy mẫu cảm ứng cực cao là 480 Hz mang đến sự mượt mà mỗi khi lướt cảm ứng và làm tăng trải nghiệm đặc biệt là khi bạn chơi game. </p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-114921-114930.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Màn hình của Mi 11 cũng được phủ lên mặt kính cường lực Corning Gorilla Glass Victus giúp bảo vệ màn hình khỏi các sự cố nứt vỡ trong khi sử dụng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/226264/xiaomi-mi-11-121121-031158.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Sở hữu vi xử lý Snapdragon 888, Mi 11 được dự đoán sẽ là một tân binh \"đáng gờm\" mở đầu cuộc đua hiệu năng đầy sôi động trong 2021. Tuy là một thiết bị cao cấp nhưng Mi 11 vẫn sẽ có mức giá cực kỳ hợp lý, cho phép tiếp cận đến nhiều đối tượng khách hàng hơn.\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 7, 1),
(31, 1, 'iPhone 11 256GB', '../assets/image/products/phone/iPhone-11-256GB/Nền.jpg', 21990000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 6.1\", Liquid Retina\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\niOS 14\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n2 camera 12 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n12 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nApple A13 Bionic\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n4 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n256 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM & 1 eSIM, Hỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n3110 mAh, Sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của iPhone 11 256GB</b>\r\n<h1 class =\"paragraph__h1\">iPhone 11 256GB là chiếc máy có mức giá \"dễ chịu\" trong bộ 3 iPhone vừa được Apple giới thiệu và nếu bạn muốn được trải nghiệm những nâng cấp về camera mới hay hiệu năng hàng đầu mà lại không muốn bỏ ra quá nhiều tiền thì đây thực sự là lựa chọn hàng đầu dành cho bạn.</h1>\r\n<h2 class =\"paragraph__h2\">Hiệu năng vẫn tương đương phiên bản Pro Max</h2>\r\n<p class =\"paragraph__p\">Mặc dù có mức giá rẻ hơn nhưng không vì vậy mà iPhone 11 bị cắt giảm đi về mặt cấu hình.</p>\r\n<img class = \"paragraph__img\"src=\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd9-1.jpg\"\" alt=\"\">\r\n<p class =\"paragraph__p\">Máy vẫn sẽ sở hữu cho mình con chip Apple A13 Bionic mạnh mẽ cùng 4 GB RAM tương đương với người anh em đắt tiền iPhone 11 Pro Max.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd4.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Phiên bản này sẽ có bộ nhớ lên tới 256 GB thoải mái cho bạn cài đặt game ứng dụng hay lưu trữ video độ phân giải cao.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd44.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Hiệu năng của những chiếc iPhone luôn được đánh giá rất cao và hiện tại để tìm được một đối thủ có thể đánh bại iPhone 11 256GB là điều không hề dễ dàng chút nào.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd6-2.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Trong sự kiện ra mắt sản phẩm, Apple đã cho biết Apple A13 là CPU nhanh nhất trên giới smartphone hiện nay và bạn sẽ cảm thấy rất hài lòng với nó.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd40.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với việc trang bị con chip mạnh đến vậy hoàn toàn dễ dàng giúp điện thoại chơi game tốt ở mức cấu hình max setting với mọi tựa game từ nhẹ nhàng như Temple Run hay thậm chí PUBG Mobile và nặng hơn thế thì chiếc iPhone 11 256GB đều đáp ứng một cách rất mượt mà và nhanh chóng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd34.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">iPhone 11 256GB chạy sẵn phiên bản iOS 13 mới được Apple tối ưu hiệu và giật lag sẽ là điều mà bạn khó có thể bắt gặp.</p>\r\n<h2 class =\"paragraph__h2\">Camera cải tiến lớn</h2>\r\n<p class =\"paragraph__p\">So với iPhone Xr năm ngoái thì iPhone 11 năm nay thực sự là một \"cuộc cách mạng của Appe\" về camera.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd4-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Chúng ta sẽ có thêm một ống kính góc siêu rộng mới cũng với độ phân giải 12 MP và thêm nhiều chế độ chụp ảnh mới cho bạn khám phá.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd9-2.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Đầu tiên là chế độ chụp ảnh góc siêu rộng, đây sẽ là lần đầu tiên 1 chiếc iPhone có thể chụp những toàn nhà cao tầng đầy đủ chi tiết một cách đơn giản mà không cần di chuyển ra xa.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd12-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với sự trợ giúp của camera thứ 2 thì iPhone 11 cũng đã có thể chụp xóa phông với vật thể, điều mà nhiều người cảm thấy thiếu sót trên chiếc iPhone Xr.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd13-2.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Tuy nhiên, chế độ chụp đêm mới chính là điểm nhấn trên camera của bộ 3 iPhone năm nay.</p>\r\n<p class =\"paragraph__p\">Khi chụp ảnh trong môi trường thiếu sáng iPhone 11 sẽ tự động kích hoạt chế độ Night Mode và bạn sẽ nhận thấy sự khác biệt ngay tức thì.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd10-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Ảnh chụp ra có độ sáng cao và ít nhiều hơn hẳn, thậm chí nhiều trường hợp ảnh còn sáng hơn so với mắt thường bạn có thể quan sát.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd11-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Camera selfie cũng được cải tiến và đã được nâng cấp lên độ phân giải 12 MP, tạo điều kiện cho các bức ảnh góc rộng hơn khi bạn xoay điện thoại sang ngang.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd8-2.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Ngoài ra thì bạn cũng có thể chụp ảnh selfie ở chế độ chuyển động chậm (mà Apple đang gọi với định nghĩa mới là Slofies).</p>\r\n<h2 class =\"paragraph__h2\">Màu sắc mới, thiết kế đẹp hơn</h2>\r\n<p class =\"paragraph__p\">Nếu bạn là người dùng ưa thích những màu sắc nổi bật, thể hiện được cá tính riêng của bản thân thì chiếc iPhone này là dành cho bạn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd41.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Chúng ta sẽ có màu sắc mới như màu tím mộng mơ hay màu xanh tươi mát rất thu hút người nhìn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd6-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Không chỉ có màu sắc mới mà chúng ta cũng sẽ có một mặt lưng bằng kính bền hơn, khả năng chống nước tốt hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd5-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">iPhone 11 năm nay cũng rất dễ để nhận ra khi nó được thiết kế cụm camera hình vuông to bản ở mặt sau, ít lồi hơn và \"không thể nhầm\" lẫn đi đâu được.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd3-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Máy vẫn sở hữu màn hình kích thước 6.1 inch và công nghệ màn hình True-Tone, giúp điều chỉnh độ sáng màn hình phù hợp.</p>\r\n<h2 class =\"paragraph__h2\">Thời lượng pin đã tốt nay còn tốt hơn</h2>\r\n<p class =\"paragraph__p\">iPhone Xr năm ngoái đã làm người dùng rất hài lòng về thời lượng pin thì tin vui là iPhone 11 thậm chí còn làm tốt hơn thế.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd45.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Theo công bố của Apple thì chiếc iPhone này cho thời gian sử dụng nhiều hơn 1 tiếng so với iPhone Xr trước đó.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd2-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Vì vậy mà bạn hoàn toàn có thể sử dụng chiếc máy này từ sáng sớm đến tối khuya mà không cần phải lo lắng về mức dung lượng pin còn lại.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd39.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Hơn thế nữa, với chiếc điện thoại pin trâu này nếu nhu cầu của bạn không quá cao thì chiếc máy này còn có thể cho thời gian sử dụng qua đến ngày thứ 2.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd39-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Tất nhiên chiếc máy này cũng sẽ hỗ trợ sạc nhanh và sạc không dây như iPhone 11 Pro hay bản Pro Max.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/210648/iphone-11-256gb-tgdd1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Tổng kết lại thì iPhone 11 năm nay thực sự là một chiếc máy rất đáng để sở hữu, và thậm chí nhiều người dùng còn cho rằng đây là chiếc iPhone đáng tiền nhất trong bộ 3 iPhone 2019 của Apple.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 1, 1);
INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(32, 1, 'iPhone 12 Pro Max 512GB', '../assets/image/products/phone/iPhone-12-Pro-Max-512GB/Nền.jpg', 41490000, 0, 5, 4, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nOLED, 6.7\", Super Retina XDR\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\niOS 14\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\n3 camera 12 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n12 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nApple A14 Bionic 6 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n6 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n	512 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n1 Nano SIM & 1 eSIM, Hỗ trợ 5G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n3687 mAh, có sạc nhanh\r\n</span>\r\n</div>', '\r\n<b class =\"paragraph__title\">Đặc điểm nổi bật của iPhone 12 Pro Max 512GB</b>\r\n<h1 class =\"paragraph__h1\">iPhone 12 Pro Max 512GB - đẳng cấp từ tên gọi đến từng chi tiết. Ngay từ khi chỉ là tin đồn thì chiếc smartphone này đã làm đứng ngồi không yên bao “fan cứng” nhà Apple, với những nâng cấp vô cùng nổi bật hứa hẹn sẽ mang đến những trải nghiệm tốt nhất về mọi mặt mà chưa một chiếc iPhone tiền nhiệm nào có được.</h1>\r\n<h2 class =\"paragraph__h2\">Thu hút từ cái nhìn đầu tiên</h2>\r\n<p class =\"paragraph__p\">Quay trở lại đầy hoài niệm với thiết kế phẳng, vuông vức tương tự iPhone 4 nhưng không hề cho cảm giác lỗi thời mà hoàn toàn sang trọng với thiết kế tinh tế và được cấu tạo từ những vật liệu cao cấp hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-144921-094924.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max được chế tác từ mặt kính cường lực Ceramic Shield có độ bền gấp 4 lần iPhone tiền nhiệm, phần khung cấu tạo từ thép không gỉ cực kỳ chắc chắn. Mang lại khả năng chống trầy, chống va đập tốt hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-122620-042638.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\"></h2>\r\n<p class =\"paragraph__p\">Sau những phàn nàn về phần viền màn hình của iPhone 11 series thì nay chiếc iPhone 12 Pro Max 512GB đã được cải thiện triệt để với kích thước màn hình 6.7 inch, tỉ lệ màn hình so với thân máy tương đương 87.4% tăng tối đa diện tích hiển thị.</p>\r\n<p class =\"paragraph__p\">Công nghệ màn hình Super Retina XDR mang đến trải nghiệm màn hình chân thực từng chi tiết. Nhờ tính năng True Tone tự động điều chỉnh cho độ chính xác màu sắc cao, ánh sáng hài hòa.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-145021-095032.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Độ sáng tối đa lên đến 1200 nits (HDR), hỗ trợ HDR 10, dải màu P3 chuẩn điện ảnh đảm bảo đây là công nghệ màn hình tốt nhất từ trước đến nay của nhà Apple, đáp ứng nhu cầu giải trí ngày càng thiết yếu của người dùng smartphone.</p>\r\n<h2 class =\"paragraph__h2\">Hệ thống camera được nâng cấp mạnh mẽ</h2>\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max được trang bị bộ 3 camera chuyên nghiệp với độ phân giải đều bằng 12 MP, trong đó gồm một cảm biến chính là camera góc rộng, một camera góc siêu rộng 120 độ và một camera chụp xóa phông. Ngoài ra, 12 Pro Max còn hỗ trợ một cảm biến LiDAR giúp việc lấy nét trong điều kiện thiếu sáng nhanh hơn gấp 6 lần.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-145321-095330.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max có cảm biến lớn hơn giúp thu sáng tốt hơn 27% và có công nghệ chống rung tiên tiến. Dựa vào các yếu tố trên, iPhone 12 Pro Max có thể chụp những bức ảnh chất lượng, đầy màu sắc, đủ chi tiết, dù trong môi trường thiếu sáng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-045920-025933.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Tất cả mang đến những thước ảnh tự nhiên hoàn hảo như cái nhìn trực tiếp từ đôi mắt của bạn.</p>\r\n<p class =\"paragraph__p\">Camera trước cũng có độ phân giải 12 MP hỗ trợ đầy đủ các tính năng như chế độ chụp chân dung với hiệu ứng bokeh, Memoji, Night Mode, Deep Fusion,... và còn nhiều tính năng khác.</p>\r\n<p class =\"paragraph__p\">Cảm biến LiDAR đo đạc và quét hiển thị sơ đồ 3D, là một ưu điểm lớn cho người dùng là “dân” thiết kế.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-040720-030754.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Chuẩn kết nối 5G - nhanh hơn, thông minh hơn</h2>\r\n<p class =\"paragraph__p\">Cuối cùng thì nhà Táo cũng đã đưa công nghệ 5G vào iPhone 12 series, bên cạnh tốc độ thực thì những ích lợi khác của 5G sẽ là độ trễ thấp và công suất lớn hơn.</p>\r\n<p class =\"paragraph__p\">Công nghệ này sẽ giúp người dùng sử dụng các trải nghiệm giải trí ổn định, tải phim chất lượng cao vô cùng nhanh chóng. Với 5G, chúng ta có thể tải một bộ phim chỉ trong vòng vài giây.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-041020-031059.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\"><Apple cho biết hãng đã phát triển được một số công nghệ nhằm giảm việc tiêu hao pin của iPhone khi sử dụng dữ liệu, bao gồm chế độ Dữ liệu Thông minh (Smart Data Mode), cho phép các ứng dụng chỉ sử dụng mạng 5G khi thực sự cần./p>\r\n<h2 class =\"paragraph__h2\">Apple A14 Bionic - Hiệu năng mạnh mẽ hàng đầu</h2>\r\n<p class =\"paragraph__p\">Là phiên bản cao cấp nhất của iPhone 12 series cấu hình của máy sẽ không làm bạn thất vọng đâu. iPhone 12 Pro Max sử dụng CPU Apple A14 Bionic, đây là thế hệ chip đầu tiên của Apple được sản xuất trên tiến trình 5 nm.</p>\r\n<p class =\"paragraph__p\">Theo Apple thì hãng đã tăng thêm 40% bóng bán dẫn giúp tăng tốc độ đồng thời tăng hiệu suất để có tuổi thọ pin tốt hơn thế hệ trước, Machine Learning được tăng lên gấp 10 lần, qua đó cải thiện hình ảnh trong từng cảnh phim hay đồ họa gaming.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-041820-031813.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Đây sẽ là một trong những nâng cấp mang lại ý nghĩa lớn cho Siri, các công cụ dịch. Vì Apple xử lý phần lớn các dữ liệu ngay trên máy chứ không sử dụng máy chủ, điều này giúp tăng tính riêng tư, tăng tốc độ xử lý do loại bỏ được độ trễ khi tương tác với máy chủ.</p>\r\n<p class =\"paragraph__p\">Với bộ nhớ trong 512 GB, giúp nhu cầu lưu trữ ảnh, video, ứng dụng… trên máy chưa bao giờ là vấn đề nan giải.</p>\r\n<h2 class =\"paragraph__h2\">Hỗ trợ công nghệ sạc không dây MagSafe</h2>\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max có dung lượng pin 3687 mAh, hỗ trợ sạc pin nhanh qua cáp công suất 20 W có thể sạc từ 0 - 50% trong khoảng 30 phút, khi bạn sạc bằng sạc không dây MagSafe thì máy hỗ trợ sạc đến 15 W, sạc với chuẩn Qi thì hỗ trợ đến 7.5 W.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-041920-031904.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Theo như Apple công bố thì phiên bản Pro Max có thể phát video offline khoảng 20 tiếng, phát nhạc khoảng 80 tiếng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228744/iphone-12-pro-max-512gb-142621-102605.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">iPhone 12 Pro Max 512GB một sản phẩm có những tính năng hiện đại nhất từ trước đến nay của nhà Apple. Có màn hình Super Retina XDR OLED siêu \"mát mắt\", hệ thống camera siêu khủng cho đến hiệu năng vô cùng mạnh mẽ từ A14 Bionic. Hứa hẹn sẽ mang lại trải nghiệm tốt nhất cho người sử dụng, nâng tầm đẳng cấp và sự thời thượng của chủ nhân sở hữu nó.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 1, 1),
(33, 1, 'Vivo V20 (2021)', '\r\n../assets/image/products/phone/Vivo-V20-(2021)/Nền.jpg', 7790000, 0, 5, 3, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nAMOLED, 6.44\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 11\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 64 MP & Phụ 8 MP, 2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n44 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 730 8 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM, Hỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n4000 mAh, Có sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Vivo V20 (2021)</b>\r\n<h1 class =\"paragraph__h1\">Vivo đã tung ra thị trường chiếc điện thoại Vivo V20 (2021), đây là phiên bản nâng cấp của Vivo V20 ra mắt cách đây không lâu. Chiếc smartphone này được nâng cấp lên bộ xử lý mạnh mẽ hơn mà vẫn giữ được thiết kế siêu mỏng và cụm camera chụp đẹp đáng kinh ngạc.</h1>\r\n<h2 class =\"paragraph__h2\">Sở hữu thiết kế mỏng đẹp mắt</h2>\r\n<p class =\"paragraph__p\">Vivo V20 (2021) nổi bật với thiết kế mỏng chỉ 7.38 mm, mặt lưng được hoàn thiện từ kính mờ cao cấp, giúp hạn chế bám dấu vân tay. Khung máy làm từ nhựa nhưng có chất lượng hoàn thiện cao, mang lại cảm giác cứng cáp, chắn chắn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/232614/vivo-v20-2021-203721-063746.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Ở mặt lưng chứa cụm 3 camera nằm thu gọn trong khuôn hình chữ nhật cùng với đèn LED kép, logo Vivo sang trọng được đặt ở phía dưới, cạnh viền cũng được gia công tỉ mỉ mang lại cảm giác thoải mái khi sử dụng trong thời gian dài.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/232614/vivo-v20-2021-008.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Một điểm cộng đến từ Vivo V20 (2021) là máy vẫn hỗ trợ đầy đủ các cổng kết nối như jack tai nghe 3.5 mm, khe cắm thẻ nhớ mở rộng lên đến 256 GB.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/232614/vivo-v20-2021-003.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Công nghệ màn hình AMOLED sắc nét</h2>\r\n<p class =\"paragraph__p\">Vivo V20 (2021) được trang bị tấm nền AMOLED cho chất lượng hình ảnh sống động, chân thực. Kết hợp với màn hình kích thước lên đến 6.44 inch sẽ mang đến cho người dùng những trải nghiệm hình ảnh bất tận từ giải trí cho đến công việc. </p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/232614/vivo-v20-2021-001.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Vivo V20 (2021) có thiết kế camera giọt nước phía trước giúp tăng diện tích hiển thị đáng kể, camera này cũng hỗ trợ tính năng mở khóa khuôn mặt. Mở khóa vân tay được đặt ngay phía dưới màn hình, giúp tăng cường độ bảo mật cho smartphone của bạn, ngoài ra còn giúp bật máy nhanh chóng, tiện lợi hơn</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/232614/vivo-v20-2021-006.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Mặt kính được bảo vệ cho màn hình này là kính cường lực Schott Xensation UP giúp tăng độ bền, hạn chế tối đa hậu quả của việc rơi vỡ, trầy xước.</p>\r\n<h2 class =\"paragraph__h2\"></h2>\r\n<p class =\"paragraph__p\"></p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/232614/vivo-v20-2021-007.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với độ phân giải này Vivo V20 (2021) dễ dàng cho ra hình ảnh có chi tiết rõ ràng, sống động, siêu nét ngay cả khi zoom mạnh. Hỗ trợ các tính năng như chụp bokeh, phim đen trắng, chế độ chụp đêm cung cấp đầy đủ ánh sáng cần thiết cho mọi bức ảnh buổi tối thêm lung linh,...</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/232614/vivo-v20-2021-203721-063753.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Máy hỗ trợ quay video độ phân giải lên đến 4K. Mang lại trải nghiệm màu sắc sống động và độ chi tiết cao. Cùng với bộ công cụ chỉnh sửa mạnh mẽ sẽ mang đến cho bạn những thước phim hoàn hảo nhất.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/232614/vivo-v20-2021-005.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Camera selfie của Vivo V20 (2021) sở hữu chế độ tự động lấy nét, bạn không còn phải khó khăn khi giữ nét cho những cảnh đẹp hay các chủ thể đang chuyển động nữa. Giờ đây, chỉ cần nhấn đúp và máy ảnh Ultra HD 44 MP sẽ làm thay bạn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/232614/vivo-v20-2021-004.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Mạnh mẽ hơn với bộ xử lý đổi mới</h2>\r\n<p class =\"paragraph__p\">Cung cấp sức mạnh cho máy là CPU Snapdragon 730 8 nhân có xung nhịp tối đa 2.2 GHz, là CPU thuộc nhóm cao cấp với nhiều tính năng tiên tiến. Đặc biệt là tính chuyên game của nó, sử dụng ít điện năng hơn, cải thiện hiệu suất đồ họa, người dùng yêu thích gaming sẽ có những trải nghiệm chơi game tuyệt vời trên Vivo V20 (2021).</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/232614/vivo-v20-2021-203821-063807.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Được trang bị RAM 8 GB giúp máy hoạt động đa nhiệm một cách mượt, bộ nhớ trong 128 GB cho bạn không gian lưu trữ lớn, bạn có thể thoải mái tải phim, ứng dụng mà không lo về vấn đề thiếu bộ nhớ.</p>\r\n<h2 class =\"paragraph__h2\">Công nghệ sạc pin nhanh 33 W </h2>\r\n<p class =\"paragraph__p\">Máy sở hữu viên pin Li-Po dung lượng 4000 mAh. Giờ đây, bạn có thể tích hợp cả làm việc, học tập và giải trí cả ngày dài mà máy vẫn có khả năng đáp ứng tốt.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/232614/vivo-v20-2021-002.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bên cạnh đó, sạc nhanh của Vivo V20 (2021) có tốc độ nạp pin siêu tốc lên đến 33 W, giúp tiết kiệm tối đa thời gian sạc, thoải mái trải nghiệm mà không còn lo lắng về hết pin hay sạc lâu nữa!</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/232614/vivo-v20-2021-203721-063703.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Nhìn chung Vivo V20 (2021) vẫn giữ nguyên những thông số đã vô cùng ấn tượng từ phiên bản tiền nhiệm là hệ thống camera siêu chất, màn hình nét và củ sạc công nghệ cao. Ngoài ra Vivo V20 (2021) được tiếp thêm sức mạnh với chip CPU cấp cao, tất cả đều trong mức giá tầm trung hấp dẫn. </p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 8, 1),
(34, 1, 'Vivo Y51 (2020)', '../assets/image/products/phone/Vivo-Y51-(2020)/Nền.jpg', 5990000, 0, 5, 26, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 6.58\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 11\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 48 MP & Phụ 8 MP, 2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n16 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 662 8 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM (SIM 2 chung khe thẻ nhớ), Hỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n5000 mAh, Có sạc nhanh\r\n</span>\r\n</div>\r\n', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Vivo Y51 (2020)</b>\r\n<h1 class =\"paragraph__h1\">Vivo đã mang chiếc điện thoại Vivo Y51 một lần nữa quay trở lại với người dùng trong một thiết kế hoàn toàn mới, nâng cấp từ công nghệ màn hình, cụm camera đến hệ điều hành với tên gọi Vivo Y51 (2020).</h1>\r\n<h2 class =\"paragraph__h2\">Màn hình rộng với màu sắc rực rỡ</h2>\r\n<p class =\"paragraph__p\">Y51 2020 sở hữu màn hình 6.58 inch độ phân giải 1080 x 2408 Pixels, có độ tương phản cao đem đến cho người dùng những trải nghiệm hình ảnh chân thực, màu sắc rực rỡ giúp việc sử dụng ứng dụng giải trí trên điện thoại trở nên cuốn hút hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228950/vivo-y51-2020-291420-111417.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Máy sử dụng màn hình IPS LCD cung cấp một giao diện màu sắc đa dạng, góc nhìn rộng, cho phép người dùng trải nghiệm được hết chất lượng hình ảnh mà không nhất thiết phải ngồi trực diện.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228950/vivo-y51-2020-293420-113419.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Camera luôn là vấn đề được chú trọng</h2>\r\n<p class =\"paragraph__p\">So với phiên bản trước thì cụm camera lần này đã được cải thiện một cách đáng kể với camera trước độ phân giải 16 MP, hỗ trợ tự động lấy nét, quay video Full HD, làm đẹp,...</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228950/vivo-y51-2020-235820-045811.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Công nghệ Super Night trên camera của Vivo Y51 2020 giúp giảm nhiễm, tăng chi tiết và cải thiện độ sáng cho bức hình khi chụp ở môi trường thiếu sáng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228950/vivo-y51-2020-235820-045819.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Ở mặt lưng Vivo Y51 (2020) sở hữu tổng cộng 3 camera, trong đó gồm: Camera chính 48 MP, camera góc siêu rộng 8 MP và camera macro 2 MP.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228950/vivo-y51-2020-291420-111426.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Máy còn hỗ trợ đầy đủ các tính năng như: Tự động lấy nét, HDR, xóa phông, panorama,... giúp bạn thoải mái ghi lại những khoảnh khắc hằng ngày.</p>\r\n<p class =\"paragraph__p\">Về quay video thì Y51 2020 hỗ trợ chống rung điện tử với thuật toán thông minh tự cân bằng và ổn định video khi quay mang lại những thước phim ít rung lắc, mượt mà hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228950/vivo-y51-2020-231720-061745.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Hiệu năng mạnh mẽ, tập trung vào khả năng gaming</h2>\r\n<p class =\"paragraph__p\">Cung cấp sức mạnh cho chiếc máy này là CPU Snapdragon 662 8 nhân với hiệu năng cao xung nhịp 2.0 GHz cho khả năng xử lý các tác vụ cơ bản cũng như chơi các tựa game phổ biến mượt mà ở cấu hình đồ họa phù hợp.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228950/vivo-y51-2020-235820-045856.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Công nghệ Multi-Turbo 4.0 độc quyền của Vivo tối ưu hóa hiệu suất hệ thống lõi để giảm độ trễ trong các trò chơi, đặc biệt khi nhiều ứng dụng đang chạy đồng thời.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228950/vivo-y51-2020-235820-045835.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Vivo Y51 2020 sở hữu 8 GB RAM, bộ nhớ trong 128 GB và hỗ trợ mở rộng bộ nhớ lên đến 1TB qua thẻ nhớ MicroSD mang lại khả năng lưu trữ lớn, giúp bạn có thể thoải mái lưu trữ hình ảnh, video, tài liệu,... mà không phải lo lắng về việc đầy bộ nhớ.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228950/vivo-y51-2020-291420-111458.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Vivo Y51 2020 có thể dễ dàng chơi tốt các tựa game phổ biến như: PUBG Mobile, Liên Quân Mobile, Tốc Chiến,... mượt mà, ổn định khi số điểm của Y51 2020 test được trên Antutu là 188.332 điểm.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228950/vivo-y51-2020-054921-084958.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Ngoài ra Snapdragon 662 còn trợ giúp khả năng chụp ảnh của camera, giúp tiết kiệm năng lượng và tăng tốc tốc độ xử lý ảnh,... nhằm mang lại trải nghiệm tối ưu nhất.</p>\r\n<h2 class =\"paragraph__h2\">Pin dung lượng 5000 mAh, cho thời gian sử dụng bền bỉ</h2>\r\n<p class =\"paragraph__p\">Với dung lượng pin 5000 mAh bạn có thể sử dụng thoải mái trong vòng một ngày mà không cần phải quá lo lắng về vấn đề sạc pin xứng đáng gọi là điện thoại pin trâu.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228950/vivo-y51-2020-291420-111442.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Nhằm tiết kiệm thời gian sạc cho viên pin lớn này thì hãng cũng đã trang bị cho Y51 2020 tính năng sạc pin nhanh qua cáp công suất 18 W chỉ khoảng 64 phút là có được 70% pin. Sử dụng công nghệ tiết kiệm năng lượng AI giúp thiết bị được tiết kiệm pin tốt hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228950/vivo-y51-2020-235920-045907.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Được nâng cấp từ ngoài vào trong từ CPU, thông số màn hình, máy ảnh, dung lượng pin,... cùng một số tính năng khác được cải tiến vượt trội hơn thế hệ Y51 trước đó nhưng Vivo Y51 2020 vẫn sở hữu mức giá vô cùng phù hợp với nhiều bạn dùng.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 8, 1),
(35, 1, 'Vivo V20 SE', '../assets/image/products/phone/Vivo-V20-SE/Nền.jpg', 6490000, 0, 5, 3, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nAMOLED, 6.44\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 48 MP & Phụ 8 MP, 2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n32 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 665 8 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM, Hỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n4100 mAh, Có sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Vivo V20 SE</b>\r\n\r\n<h1 class =\"paragraph__h1\">Mặc dù được định vị ở phân khúc điện thoại tầm trung, Vivo V20 SE vẫn sở hữu những nét riêng biệt, đủ để thu hút sự chú ý của những ai yêu công nghệ. Hãy cùng tìm hiểu xem chiếc smartphone này có gì khác biệt với các đối thủ khác.</h1>\r\n<h2 class =\"paragraph__h2\">Thu hút từ cái nhìn đầu tiên</h2>\r\n<p class =\"paragraph__p\">Mặt trước có thiết kế hình giọt nước sang trọng nhẹ khoảng 171 g, kết hợp cùng các đường cong 3D chất liệu polyme cao cấp, chiếc điện thoại dễ dàng nằm gọn trong lòng bàn tay người dùng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228141/vivo-v20-se-022020-032021.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Mặt sau gồm cụm 3 camera có công nghệ AI tiên tiến nằm gọn gàng, hơi gồ nhẹ lên, tạo thành hình chữ nhật đứng, nhìn rất nịnh mắt.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228141/vivo-v20-se-021920-031958.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Sử dụng chất liệu nhựa cao cấp, cùng màu sắc được lấy cảm hứng từ chiều sâu của vũ trụ, sự kiều diễm của thiên nhiên, phối hợp với yếu tố thời trang cao cấp tạo khả năng chuyển màu ở mặt sau của máy một cách tinh tế, hợp xu hướng.</p>\r\n<h2 class =\"paragraph__h2\">Màn hình rộng, hiển thị sắc nét</h2>\r\n<p class =\"paragraph__p\">Màn hình AMOLED rộng 6.44 inch, độ phân giải Full HD+ có thể hiện thị hơn 16 triệu sắc thái màu tổng hợp, cho phép các thước phim, trận game,.. hiển thị màu sắc sống động, chân thực. </p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228141/vivo-v20-se-021920-031923.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Việc sử dụng tấm nền AMOLED cho phép máy có khả năng mở khoá vân tay dưới màn hình một cách nhanh chóng. Bên cạnh đó, V20 SE vẫn có tính năng bảo mật khuôn mặt. Giờ đây khi kết hợp 2 tính năng trên thì thiết bị của bạn sẽ luôn được mở khoá một cách nhanh chóng, mọi dữ liệu của bạn sẽ được bảo mật một cách tốt hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228141/vivo-v20-se-022020-032032.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Dung lượng pin lớn cùng khả năng sạc nhanh</h2>\r\n<p class =\"paragraph__p\">Vivo V20 SE là chiếc điện thoại pin trâu khi sở hữu viên pin có dung lượng 4100 mAh, cho thời gian hoạt động ngày dài, người dùng thỏa thích cày phim, xem video, chơi game,… mà không lo hết pin.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228141/vivo-v20-se-022020-032010.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Hiệu năng ổn định, mượt mà</h2>\r\n<p class =\"paragraph__p\">Sử dụng bộ vi xử lý Snapdragon 665 giúp máy hoạt động mạnh mẽ, trơn tru hoàn toàn đáp ứng mọi tác vụ một cách dễ dàng cũng như sự đa nhiệm của máy luôn được ổn định khi trang bị dung lượng RAM 8 GB.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228141/vivo-v20-se-023020-033003.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Phần hiệu năng của máy được test qua ứng dụng antutu đạt được 185.673 điểm cho thấy máy có thể chiến tốt các dòng game hot hiện nay một cách mượt mà, ổn định.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228141/vivo-v20-se-045720-085723.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Ngoài ra, bộ tăng tốc phần mềm độc quyền Multi Turbo công cụ hổ trợ đắc lực của các game thủ giúp người chơi thỏa thích, ổn định không lo bị giật lag.</p>\r\n<p class =\"paragraph__p\">Bênh canh đó, bộ nhớ trong lớn đến 128 GB cho người dùng thoải mái lưu trữ dữ liệu. Và V20 SE cũng hỗ trợ thẻ nhớ ngoài tối đa 1 TB giờ đây người dùng không còn phải lo về vấn đề bộ nhớ.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228141/vivo-v20-se-022020-032016.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Khả năng selfie ấn tượng</h2>\r\n<p class =\"paragraph__p\">Camera trước có độ phân giải 32MP, đem đến chế độ Super Night Selfie nổi bật. Thuật toán HDR + Morpho độc quyền của hãng giúp làm sáng các điểm tối và giảm độ phơi sáng quá mức ở các vùng sáng để ảnh chân dung đạt được độ sáng, sắc nét và cân bằng. </p>\r\n<p class =\"paragraph__p\">Công cụ Selfie Softlight Band được thiết kế để mang lại ánh sáng tiêu chuẩn studio cho ảnh tự chụp ban đêm bằng tự động điều chỉnh theo ánh sáng xung quanh trong nhà và ngoài trời.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228141/vivo-v20-se-800x533.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Cụm camera sau có độ phân giải lần lượt là cảm biến chính 48 MP, camera góc siêu rộng 8 MP và camera xoá phông 2 MP.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228141/vivo-v20-se-022020-032005.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với thông số trên, cùng công nghệ AI tiên tiến cũng như kỹ thuật cân bằng sáng trên bức ảnh, máy sẽ tự động phân tích để điều chỉnh ánh sáng, giảm nhiễu, đem đến những bức ảnh chi tiết, sắc nét và sinh động mặc dù trong điều kiện thiếu sáng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228141/vivo-v20-se-800x533-2.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Khả năng quay video 4K ổn định</h2>\r\n<p class =\"paragraph__p\">Cho phép quay video 4K với chất lượng, độ nét cao. Nhờ vào EIS, camera sau sẽ tư động loại bỏ độ mờ khi bị rung, nhờ đó các cảnh quay trở nên mượt mà, ổn định, ngay cả khi người dùng đang chuyển động.</p>\r\n<p class =\"paragraph__p\">Ngoài ra, các hiệu ứng Face Beauty cho phép làm đẹp trực tiếp trong khi quay video, với hổ trợ độ phân giải lên đến 720p, người dùng không cần chỉnh sửa hậu kỳ phức tạp.</p>\r\n<p class =\"paragraph__p\">Tóm lại, Vivo V20 SE sẽ là sự lựa chọn phù hợp với những ai yêu cầu smartphone có mức giá tầm trung nhưng có cấu hình hợp lý với nhu cầu sử dụng. Hứa hẹn điện thoại thông minh V20 SE sẽ đem đến sự hài lòng cho người dùng.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 8, 1),
(36, 1, 'Vivo V20', '../assets/image/products/phone/Vivo-V20/Nền.jpg', 8490000, 0, 5, 44, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nAMOLED, 6.44\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 11\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 64 MP & Phụ 8 MP, 2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n44 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 720G 8 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM, Hỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n4000 mAh, có sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Vivo V20 SE</b>\r\n<h1 class =\"paragraph__h1\">Vivo V20 là mẫu smartphone đầy năng động cho giới trẻ khi có thiết kế siêu mỏng, phù hợp với xu hướng hiện đại với nhiều màu sắc thời thượng, cấu hình vượt trội cùng hệ thống camera siêu ấn tượng chắc chắn sẽ mang đến cho bạn những trải nghiệm chụp ảnh tuyệt vời.</h1>\r\n<h2 class =\"paragraph__h2\">Thiết kế thời trang, tràn viền</h2>\r\n<p class =\"paragraph__p\">Vivo V20 có thiết kế hiện đại theo xu hướng của những smartphone cao cấp hiện nay với thiết kế nguyên khối kết hợp cùng mặt lưng kính cao cấp tạo nên độ bóng bẩy, ấn tượng ngay từ cái nhìn đầu tiên.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v20-201620-101626.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Hơn thế nữa, các chi tiết máy được hoàn thiện tỉ mỉ đến từng chi tiết, cạnh viền được bo tròn nhẹ nhàng để tạo nên tổng thể mềm mại, vừa cho vẻ đẹp sang trọng, lại vừa cho cảm giác cầm nắm mượt mà và không bám dấu vân tay</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v20-201320-101356.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Mặt lưng của Vivo V20 cũng ấn tượng khi được khoác trên mình hiệu ứng chuyển màu gradient nhẹ nhàng, vô cùng đẹp mắt.</p>\r\n<h2 class =\"paragraph__h2\">Màn hình hiển thị sắc nét</h2>\r\n<p class =\"paragraph__p\">Vivo V20 được trang bị một màn hình AMOLED lớn 6.44 inch hỗ trợ HDR10, độ phân giải Full HD+ (1080 x 2400 pixels) sắc nét, kiểu giọt nước, qua đó cắt giảm đáng kể viền bezel đáng kể so với người tiền nhiệm, tăng tỷ lệ hiển thị ở mặt trước.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v20-201320-101351.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Công nghệ màn hình AMOLED cho hình ảnh hiển thị sắc nét, tinh xảo với màu sắc rực rỡ, độ sáng cao 1200 nits, cho màu đen sâu hơn tạo nên hình ảnh sống động hơn cũng như tiết kiệm pin tốt hơn.</p>\r\n<p class =\"paragraph__p\">Cảm biến vân tay giờ đây cũng đã được nhà sản xuất tích hợp bên dưới màn hình tăng tính bảo mật nhưng vẫn đảm bảo được thiết kế thẩm mỹ, ưa nhìn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v20-201420-101418.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Hơn nữa, nhờ được thiết kế theo phong cách tràn viền, màn hình trên Vivo V20 còn mang đến góc nhìn rộng cho bạn thỏa sức trải nghiệm xem ảnh, video, chơi game sẽ hấp dẫn hơn rất nhiều.</p>\r\n<h2 class =\"paragraph__h2\">Bộ 3 camera sau 64 MP</h2>\r\n<p class =\"paragraph__p\">Vivo V20 được trang bị bộ 3 camera sau ấn tượng, trong đó nổi bật nhất là camera chính độ phân giải “khủng” 64 MP khẩu độ lớn f/1.89, bên cạnh đó là camera góc siêu rộng 8 MP và camera đo chiều sâu 2 MP.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v20-201420-101401.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với 3 camera, Vivo V20 sẽ có đầy đủ tính năng chụp hình, từ chụp ngày, chụp đêm cho đến chụp góc siêu rộng 120 độ, chụp siêu cận, chụp chân dung xóa phông hay chụp ảnh mono đen trắng đầy nghệ thuật,... cho bạn thỏa sức sáng tạo trong nhiếp ảnh.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v20-120420-020426.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Nổi bật trong số đó chính là chế độ siêu chụp đêm siêu ấn tượng với hàng loạt cảm biến camera chất lượng cùng công nghệ AI tăng cường độ sáng và hiển thị dải màu hoàn hảo giúp bạn có thể chụp những cảnh đẹp lung linh trong đêm tối.</p>\r\n<p class =\"paragraph__p\">Giờ đây bạn có thể  tha hồ “sống ảo” trong những quán café hay đứng bên con sông đầy thơ mộng giữa trời đêm, Vivo V20 sẽ lưu giữ những khoảnh khắc đẹp nhất của bạn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v20-120520-020530.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Không những chụp ảnh đẹp, camera trên Vivo V20 còn có khả năng quay video chất lượng cao 4K siêu sắc nét. Hỗ trợ công nghệ chống rung kỹ thuật số EIS, dù bạn vận động như thế nào, đi bộ hay đang lướt ván trượt, V20 sẽ mang đến khung hình ổn định và giữ nguyên vẹn khoảnh khắc thú vị của bạn.</p>\r\n<h2 class =\"paragraph__h2\">Camera selfie 44 MP siêu sắc nét</h2>\r\n<p class =\"paragraph__p\">Camera trước của Vivo V20 có độ phân giải lên tới 44 MP, hoàn toàn vượt trội khi so sánh với bất cứ smartphone nào cùng phân khúc hiện nay, nhờ đó V20 có thể chụp ảnh selfie vô cùng sắc nét.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v20-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với thuật toán phân tách nền tiên tiến của Vivo, sẽ tự động phân biệt bối cảnh và chủ thể, giúp giữ nguyên nét đẹp của từng đối tượng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v205.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Hơn thế nữa, camera trước trên Vivo V20 không chỉ chụp ảnh selfie mà còn giúp bạn có thể quay những video làm đẹp, livestream trên mạng xã hội hay thậm chí là quay những đoạn vlog độ phân giải 4K đầy chất lượng. </p>\r\n<p class =\"paragraph__p\">Đi kèm với đó là công nghệ lấy nét theo vật thể chuyển động thông minh, bạn có thể chọn lấy nét liên tục bất kỳ chủ thể nào trong khung hình, ngay cả khi đối tượng ra khỏi rồi trở lại khung hình. Từ đây những thước phim của bạn luôn sinh động, tràn đầy sức sống.</p>\r\n<h2 class =\"paragraph__h2\">Hiệu năng mạnh mẽ, xử lý mượt mà mọi ứng dụng</h2>\r\n<p class =\"paragraph__p\">Vivo V20 mang trên mình bộ vi xử lý Qualcomm Snapdragon 720G mạnh mẽ, được sản xuất dựa trên tiến trình 8 nm mang lại cho máy hiệu năng ấn tượng cùng khả năng tiết kiệm điện năng khá là tốt so với các thế hệ tiền nhiệm</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v20-201420-101405.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với sức mạnh ấn tượng, smartphone này đáp ứng tốt mọi nhu cầu sử dụng của người dùng từ làm việc, giải trí, lướt web, xem phim, chơi tốt mọi tựa game,..</p>\r\n<p class =\"paragraph__p\">Khi đo trên Aututu kết quả đạt được 278.769 điểm con số khá cao cho bạn có thể mở bất cứ ứng dụng nào, thoải mái cài đặt và chạy nhiều ứng dụng cùng lúc, lưu trữ mọi thứ mà máy vẫn hết sức mượt mà, và giúp điện thoại chơi game đồ họa cao một cách ổn định, không bị lag giật. </p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v20-120420-020408.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Dung lượng RAM 8 GB và bộ nhớ trong 128 GB mở ra không gian lưu trữ rộng rãi cho người dùng để bạn có thể thoải mái lưu trữ các dữ liệu, hình ảnh, bài hát, cài đặt ứng dụng mà không lo đến việc tràn bộ nhớ.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v20-201420-101409.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Vivo V20 được cài đặt sẵn hệ điều hành Android 10 tùy biến trên giao diện Funtouch OS phiên bản 10.5 với nhiều cải tiến thông minh, nâng cao trải nghiệm sử dụng của người dùng.</p>\r\n<h2 class =\"paragraph__h2\">Thời lượng pin đủ dùng, tích hợp sạc nhanh siêu tốc</h2>\r\n<p class =\"paragraph__p\">Vivo V20 mang trong mình viên pin dung lượng 4000 mAh, vừa đủ để cho bạn sử dụng thoải mái trọn vẹn một ngày.  Máy còn được trang bị công nghệ sạc nhanh siêu tốc công suất lên tới 33W, nhanh chóng sạc được 50% chỉ sau khoảng 20 phút.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228453/vivo-v20-201420-101414.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Vivo V20 còn được trang bị công nghệ âm thanh Dolby Atmos mang đến âm thanh chân thực, sống động và âm thanh vòm chuẩn 3D cho bạn sẽ tha hồ chìm đắm trong những bài hát hay, những bộ phim, những trận game hấp dẫn, mang đến không gian âm thanh tuyệt vời.</p>\r\n<p class =\"paragraph__p\">Với thiết kế sang trọng, hiệu năng mạnh mẽ, camera ấn tượng cùng nhiều tính năng hiện đại Vivo V20 hứa hẹn sẽ là đối trọng nặng ký trong phân khúc điện thoại tầm trung, cận cao cấp. Sản phẩm hoàn toàn phù hợp với hầu hết người sử dụng, từ người dùng phổ thông đến những tín đồ yêu thích chụp hình hay chuyên game.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 8, 1),
(37, 1, 'OPPO Reno4 Pro', '../assets/image/products/phone/OPPO-Reno4-Pro/Nền.jpg', 11490000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nAMOLED, 6.5\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 48 MP & Phụ 8 MP, 2 MP, 2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n32 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 720G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n256 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM, Hỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n4000 mAh, Sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của OPPO Reno4 Pro</b>\r\n<h1 class =\"paragraph__h1\">Mới đây, OPPO đã chính thức trình làng chiếc smartphone mới mang tên OPPO Reno4 Pro. Máy được trang bị cấu hình vô cùng cao cấp với vi xử lý chip Snapdragon 720G, bộ 4 camera đến 48 MP ấn tượng, cùng công nghệ sạc siêu nhanh Super VOOC 65 W hướng tới nhóm khách hàng thích chụp ảnh, chơi game với hiệu năng cao nhưng được bán với mức giá vô cùng tốt.</h1>\r\n<h2 class =\"paragraph__h2\">Thiết kế tràn viền siêu ấn tượng</h2>\r\n<p class =\"paragraph__p\">OPPO Reno4 Pro được trang bị màn hình siêu tràn viền kích thước lớn 6.5 inch, tỉ lệ hiển thị lên tới 93.4%, mật độ điểm ảnh đạt ngưỡng 402 ppi, đạt 100% dải màu DCI-P3 điều này cho phép hình ảnh hiển thị chi tiết, sắc nét và sống động nhất.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/223497/oppo-reno4-pro9.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bên cạnh đó, màn hình này còn hỗ trợ công nghệ HDR10+, tần số quét 90 Hz vượt trội cùng tốc độ lấy mẫu cảm ứng 180 Hz, mang đến cho người dùng những giây phút thư giãn trải nghiệm các thước phim mãn nhãn hay chơi các tựa game mượt mà, không bị giật lag.</p>\r\n<p class =\"paragraph__p\">Hơn nữa, màn hình máy được phủ lớp kính bảo vệ cao cấp Gorilla Glass 5, bảo vệ máy một cách tối ưu hơn trong quá trình sử dụng trước những tính huống tác động va đập hay vô tình làm rơi máy. </p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/223497/oppo-reno4-pro7.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Một tính năng mà người dùng sẽ vô cùng thích trên OPPO Reno4 Pro chính là tính năng bảo mật vân tay quang học dưới màn hình hiện đại. Nhờ đó, bạn chỉ cần thao tác một chạm nhẹ nhàng để mở khóa màn hình, nhanh chóng và cực đơn giản. </p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/223497/oppo-reno4-pro11.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Ngoài ra bạn cũng có thể sử dụng thêm tính năng nhận diện bằng khuôn mặt cũng không kém phần hấp dẫn.</p>\r\n<h2 class =\"paragraph__h2\">Bộ 4 camera siêu nét, chống rung ấn tượng</h2>\r\n<p class =\"paragraph__p\">OPPO Reno4 Pro được trang bị bộ 4 camera sau gồm: cảm biến chính siêu nét 48 MP chống rung điện tử EIS, cùng các camera hỗ trợ 8 MP - 2 MP - 2 MP cho bức ảnh lung linh ở nhiều góc chụp như chân dung, góc rộng, zoom và cả khi chụp đêm cũng rất xịn và mịn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/223497/oppo-reno4-pro-303620-023643.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với OPPO Reno4 Pro, chắc chắn chinh phục bạn với những video 4K sắc nét đỉnh cao, được hỗ trợ bằng công nghệ siêu chống rung thế hệ thứ ba, đảm bảo bạn có những thước phim với chất lượng tuyệt hảo.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/223497/oppo-reno4-pro5.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Ngoài ra, OPPO Reno4 Pro còn được trang bị công nghệ AI lấy nét bằng laser cực nhanh và chính xác, tích hợp bộ chỉnh sửa video thông minh, bạn có thể cắt ghép, thêm hiệu ứng để những video tuyệt vời hơn bao giờ hết.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/223497/oppo-reno4-pro-selfie1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Mặt trước, máy được trang bị camera selfie đơn 32 MP được tích hợp hàng loạt các tính năng chụp ảnh thông minh nhất định sẽ không làm người dùng thất vọng trong các chuyến đi chơi tập thể, chụp ảnh nhóm đông người hay ảnh quang cảnh góc rộng,...</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/223497/oppo-reno4-pro-1-2.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Hiệu năng mạnh mẽ, chiến game cực đã</h2>\r\n<p class =\"paragraph__p\">OPPO Reno4 Pro được trang bị cấu hình phần cứng mạnh mẽ khi sở hữu sức mạnh chip Snapdragon 720G đến từ nhà sản xuất Qualcomm vừa cho phép thiết bị tối ưu hóa công suất hoạt động, vừa mang đến hiệu quả tiết kiệm điện năng tối đa.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/223497/oppo-reno4-pro8.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Đi kèm với đó là dung lượng bộ nhớ RAM khủng lên tới 8 GB cho bạn thoải mái sử dụng đa nhiệm, chuyển các ứng dụng một cách mượt mà không hề xảy ra hiện tượng giật lag.</p>\r\n<p class =\"paragraph__p\">Hơn thế nữa bộ nhớ trong lớn đảm bảo khả năng lưu trữ được nhiều dữ liệu, hình ảnh, video hay cài đặt các ứng dụng nặng. Với hiệu năng trên thì khi kiểm tra bằng ứng dụng Antutu thì máy đạt 272.278 điểm giúp điện thoại chơi game đồ hoạ cao cũng không thành vấn đề.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/223497/oppo-reno4-pro-303620-023648.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Cùng với đó, Qualcomm còn tối ưu Snapdragon 720G giúp nó xử lý thông tin đồ họa tốt hơn, nhất là khi được đồng bộ hóa cùng GPU Adreno 620 trên hệ điều hành Android thế hệ thứ 10, cụ thể hơn là giao diện ColorOS 7.2 để mang tới những trải nghiệm tuyệt vời nhất cho người dùng</p>\r\n<h2 class =\"paragraph__h2\">Công nghệ sạc siêu tốc 65 W</h2>\r\n<p class =\"paragraph__p\">Thêm một đặc trưng đáng chú ý và đắt giá, ghi điểm khác trên Reno4 Pro thu hút đặc biệt đối với các game thủ là: Thời lượng pin 4000 mAh tiêu chuẩn đủ đáp ứng trải nghiệm trọn vẹn suốt cả ngày dài chỉ sau một lần sạc.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/223497/oppo-reno4-pro6.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Công nghệ sạc pin nhanh siêu tốc 65 W cao nhất trên các thiết bị smartphone hiện đại (tính đến 07/2020), người dùng có thể nạp từ 0% lên 100% pin chỉ với 36 phút, từ 0% lên 60% chỉ trong vỏn vẹn 15 phút. </p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/223497/oppo-reno4-pro-sacnhanh.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Tính năng này thực sự quá tuyệt vời và thiết thực đối với những người dùng có nhu cầu sử dụng cao, mật độ liên hệ công việc dày đặc và nhất là những ai phải thường xuyên di chuyển bên ngoài. </p>\r\n<p class =\"paragraph__p\">Tổng kết, với ưu điểm cấu hình được trang bị gồm: chip Snapdragon 720G, camera siêu nét chống rung siêu ấn tượng, sạc nhanh 65W siêu tốc cùng màn hình tràn viền 90 Hz siêu nét,… có thể nói chiếc điện thoại OPPO Reno4 Pro quả thực là siêu phẩm công nghệ khiến người dùng phải lưu tâm.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 6, 1);
INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(38, 1, 'OPPO A94', '../assets/image/products/phone/OPPO-A15/Nền.jpg', 7690000, 0, 5, 37, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nAMOLED, 6.43\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 11\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 48 MP & Phụ 8 MP, 2 MP, 2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n32 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nMediaTek Helio P95\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM, Hỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n4310 mAh, Sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của OPPO A94</b>\r\n<h1 class =\"paragraph__h1\">Mẫu smartphone tầm trung mới của OPPO - OPPO A94 đã chính thức được giới thiệu đến người dùng với nhiều cải tiến nổi trội về thiết kế, pin và camera cũng như kế thừa những điểm mạnh của OPPO A93 vừa ra mắt cách đây không lâu.</h1>\r\n<h2 class =\"paragraph__h2\">Thiết kế cao cấp với mặt lưng bóng bẩy</h2>\r\n<p class =\"paragraph__p\">Nếu là một người theo dõi thường xuyên sản phẩm của OPPO, có thể bạn sẽ nhận thấy OPPO A94 sở hữu thiết kế giống Reno5 F đến 99% từ thiết kế cho đến thông số bên trong, máy cũng có mặt lưng bóng bẩy, chuyển sắc thu hút dưới nhiều góc nhìn và cụm 4 camera với thiết kế mới hiện đại hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-02.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">OPPO A94 mang đến trải nghiệm cầm nắm thoải mái với trong lượng mỏng và nhẹ, gọn gàng giúp cho mọi thao tác sử dụng trở nên hoàn hảo</p>\r\n<h2 class =\"paragraph__h2\">Màn hình AMOLED tràn cạnh, màu sắc rực rỡ</h2>\r\n<p class =\"paragraph__p\">Có thể nói màn hình OPPO A94 là màn hình tốt trong tầm giá khi trang bị màn hình AMOLED Full HD+ cao cấp với ưu điểm màu sắc rực rỡ, độ tương phản cao mang đến trải nghiệm hình ảnh sống động, giàu chi tiết.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-01.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với kích thước màn hình siêu lớn 6.43 inch, thiết kế camera đục lỗ thời thượng, các cạnh viền được kéo mỏng tối đa, mang đến không gian hiển thị rộng rãi cho việc chơi game hay xem phim trên A94 sẽ “đã” hơn rất nhiều.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-07.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">OPPO không quên tích hợp tính năng tự động điều chỉnh độ sáng và lọc giảm ảnh sáng xanh trên A94 để mang đến sự dễ chịu cho đôi mắt của bạn mỗi khi đọc tin tức, xem phim vào ban đêm.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-12-1.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Cấu hình giữ nguyên, tối ưu tốt khi chơi game</h2>\r\n<p class =\"paragraph__p\">OPPO A94 vẫn giữ nguyên cấu hình tương tự như A93, máy trang bị vi xử lý tầm trung mới MediaTek Helio P95 8 nhân với hiệu năng ổn định cho các tác vụ hàng ngày mượt mà cũng như cải thiện trải nghiệm chơi game tốt hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Về khả năng chơi game, MediaTek Helio P95 có phần nhỉnh hơn 1 chút so với Snapdragon 720G nhờ vào công nghệ Hyper Engine và GPU IMG PowerVR GM9446 giúp máy tối ưu khả năng xử lý đồ họa, duy trì số khung hình cao ổn định xuyên suốt trong quá trình chơi game.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-3.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Không chỉ vậy, hỗ trợ cho cấu hình hoạt động trơn tru hơn không thể nào bỏ qua RAM và bộ nhớ trong, OPPO A94 vẫn được trang bị RAM 8 GB, mức RAM khá cao hiện nay, đa nhiệm mượt mà, ứng dụng ít bị khởi động lại hay giảm hiệu năng đột ngột.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-6.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bộ nhớ trong 128 GB khá lớn cho nhu cầu lưu trữ từ cơ bản đến nâng cao, bạn có thể chụp ảnh, lưu trữ video, cài đặt nhiều game và ứng dụng mà không phải xóa bớt dữ liệu do thiếu bộ nhớ.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-06.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Bộ 4 camera sau hỗ trợ AI xử lý ảnh thông minh ấn tượng</h2>\r\n<p class =\"paragraph__p\">OPPO A94 trang bị 1 thiết lập 4 camera. Cung cấp 4 kiểu chụp phổ biến hiện nay gồm 1 camera chính 48 MP, camera góc rộng 8 MP, camera macro 2 MP và camera đơn sắc 2 MP cho bạn thỏa sức sáng tạo.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-03.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Camera chính 48 MP có tốc độ lấy nét nhanh trong điều kiện thiếu sáng, khắc họa cuộc sống xung quanh bạn một cách rõ nét, ảnh chụp có độ chi tiết tốt, dải màu rộng, tươi tắn, xử lý HDR tốt ở vùng sáng tối khá hài hoà.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-17.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">OPPO A94 được tích hợp công nghệ AI Scene Enhancement 2.0 tận dụng sức mạnh của AI để nhận diện đến 20 kiểu môi trường, từ đó sẽ tự động tinh chỉnh về màu sắc giúp cho bức ảnh trở nên lôi cuốn và đẹp hơn, mà không cần phải chỉnh qua một ứng dụng thứ 3 nào khác.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-08.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Các camera phụ còn lại sẽ giúp bạn sáng tạo nhiều kiểu chụp đa dạng không bị nhàm chán như chụp xóa phông, góc rộng hay chụp cận macro một cách dễ dàng.</p>\r\n<p class =\"paragraph__p\">Camera trước trên OPPO A94 lần này đã giảm chỉ 1 camera so với 2 camera trên A93 nhưng vẫn có đầy đủ các chế độ chụp để bạn chọn lựa như toàn cảnh, chân dung, chế độ làm đẹp,... Chất lượng ảnh chụp trên OPPO A94 thì không phải bàn cãi, ảnh sắc nét, màu da vẫn lên đẹp và tự nhiên.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-12.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Tương tự Reno5 F, A94 cũng được trang bị khả năng quay video AI Color Portrait độc đáo với khả năng phân tích môi trường bằng AI, camera A94 sẽ chuyển toàn bộ hậu cảnh phía sau thành trắng đen và giữ lại màu sắc của chủ thể chính để tạo ra đoạn video thật ấn tượng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-15.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">A94 hỗ trợ quay phim đồng thời 2 camera trước và sau, tạo nên những đoạn video độc đáo, không bỏ lỡ bất kỳ điều gì diễn ra xung quanh bạn.</p>\r\n<h2 class =\"paragraph__h2\">Pin khủng hơn, sạc siêu tốc đến 30 W</h2>\r\n<p class =\"paragraph__p\">Về thời lượng pin, nhờ được trang bị viên pin 4310 mAh, mức dung lượng khá ổn với mặt bằng chung hiện nay, giúp cho OPPO A94 có thể đồng hành cùng bạn trong một ngày dài với mức sử dụng vừa đủ, không chơi game quá nhiều.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-11.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bù lại, với sạc nhanh công suất 30 W công nghệ VOOC độc quyền, OPPO A94 có thời gian sạc pin nhanh hơn, đảm bảo bạn rời khỏi nhà với chiếc điện thoại đầy pin sẵn sàng cho một ngày làm việc.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-05.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">OPPO A94 cũng bổ sung chế độ siêu tiết kiệm pin, giúp bạn tận dụng những % pin ít ỏi còn lại một cách hiệu quả hơn. Theo OPPO, với 5% trong chế độ siêu tiết kiệm pin cho thời lượng nghe gọi lên đến 1 giờ.</p>\r\n<p class =\"paragraph__p\">OPPO A94 khi ra mắt sẽ cài đặt Android 11 mới nhất và chạy Color OS 11.1 mới nhất với những tính năng hữu ích như chế độ Dark Mode, bộ hình nền độc quyền OPPO,...</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/234316/oppo-a94-10.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Có thể thấy, OPPO A94 là phiên bản nâng cấp nhẹ của OPPO A93 với thay đổi về thiết kế, bổ sung thêm 1 số tính năng để trở nên hoàn thiện hơn, OPPO A94 là mẫu điện thoại mà giới trẻ năng động, yêu thích sáng tạo sẽ chọn lựa.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 6, 1),
(39, 1, 'OPPO A15', '../assets/image/products/phone/OPPO-A15/Nền.jpg', 3190000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 6.52\", HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 13 MP & Phụ 2 MP, 2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n8 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nMediaTek Helio P95\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n3 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n32 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM, Hỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n4230 mAh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của OPPO A15</b>\r\n<h1 class =\"paragraph__h1\">OPPO hãng điện thoại luôn được người Việt tin dùng và lựa chọn, mới đây hãng đã cho ra mắt mẫu OPPO A15 có thiết kế đẹp, cấu hình đủ dùng. Đây sẽ là mẫu điện thoại thông minh phù hợp cho mọi đối tượng người dùng đi cùng với mức giá vô cùng hợp lý.</h1>\r\n<h2 class =\"paragraph__h2\">Màn hình lớn, thoải mái xem phim, đọc báo</h2>\r\n<p class =\"paragraph__p\">OPPO A15 được trang bị công nghệ màn hình IPS LCD, cho độ phân giải HD+ (720 x 1600 Pixels), màn hình lớn 6.52 inch thoải mái nhìn khi sử dụng, đặc biệt là dành cho người lớn tuổi hay sử dụng điện thoại thông minh để đọc báo, xem tin tức.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-1-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Với thiết kế mặt lưng 3D OPPO A15 vừa trông đẹp mắt vừa tạo cảm giác cầm nắm tốt. Máy có 2 màu sắc chính, tuy ít sự lựa chọn nhưng với vẻ đẹp thanh lịch và sang trọng chắc sẽ không làm bạn phải thất vọng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-2-1.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Cụm 3 camera độc đáo ở mặt sau</h2>\r\n<p class =\"paragraph__p\">Sở hữu 3 camera ở phía sau cho độ phân giải vừa đủ, nằm trong một cụm hình vuông ở góc trên cùng bên trái với thông số lần lượt là 13 MP cho camera chính, camera macro 2 MP và camera xóa phông 2 MP.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-3-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Hệ thống camera như thế này là quá đủ để cho bạn chụp mọi hình ảnh trong mọi khoảnh khắc với rõ nét cao.</p>\r\n<p class =\"paragraph__p\">Ống kính macro 2 MP cho phép bạn chụp cận cảnh để khám phá những điều diệu kỳ của thế giới thu nhỏ. Ngoài ra, ống kính đo độ sâu 2 MP giúp mọi bức ảnh chân dung trở nên hoàn hảo làm nổi bật chủ thể hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-11.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Camera trước có độ phân giải 8 MP cùng khả năng làm đẹp AI do OPPO phát triển, OPPO A15 giúp bạn cho ra những bức ảnh selfie vô cùng đẹp mắt, màu da trên khuôn mặt sẽ được hoàn thiện một cách tự nhiên.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-12.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Ngoài ra, vẫn có tính năng bảo mật vân tay và bảo mật khuôn mặt, OPPO A15 có thể mở khóa nhanh chỉ bằng một cái chạm. Nhận dạng khuôn mặt tiên tiến của OPPO xác định 128 đặc điểm trên khuôn mặt, giúp bạn mở khóa thiết bị ngay lập tức.</p>\r\n<img class = \"paragraph__img\"src=\"Ngoài ra, vẫn có tính năng bảo mật vân tay và bảo mật khuôn mặt, OPPO A15 có thể mở khóa nhanh chỉ bằng một cái chạm. Nhận dạng khuôn mặt tiên tiến của OPPO xác định 128 đặc điểm trên khuôn mặt, giúp bạn mở khóa thiết bị ngay lập tức.\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Nhẹ nhàng thao tác với Helio P35</h2>\r\n<p class =\"paragraph__p\">Chip xử lý (CPU) MediaTek Helio P35 8 nhân cùng với RAM 3 GB cho phép đa nhiệm tốt, ít bị load lại ứng dụng hơn cũng như giúp mọi thao tác thường xuyên như lướt Facebook, đọc báo, xem YouTube,... đều trở nên mượt mà ổn định.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-195420-125411.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Khi test qua phần mềm Antutu máy cũng cho ra số điểm tương đối ổn khoảng 109.183 điểm đủ để phục vụ các nhu cầu hằng ngày hay chơi một số tựa game đồ họa với mức thiết lập cơ bản.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-053121-083146.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Điện thoại OPPO này có bộ nhớ trong 32 GB, nếu bạn cần thêm khoảng trống thì máy vẫn hỗ trợ mở rộng qua thẻ nhớ ngoài tối đa 256 GB cho bạn thoải mái lưu trữ dữ liệu.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-2.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Liên tục sử dụng với dung lượng pin lớn</h2>\r\n<p class =\"paragraph__p\">OPPO A15 sở hữu viên pin lớn với dung lượng 4230 mAh cho phép sử dụng suốt cả ngày dài. Xem phim HD trực tuyến tối đa 16 giờ hay chơi game khoảng 6 giờ mà không cần sạc xứng đáng là chiếc điện thoại pin trâu.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-4-1.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Kèm theo máy là củ sạc 10 W cũng là quá đủ đi khi đây chỉ là máy thuộc phân khúc giá rẻ.</p>\r\n<h2 class =\"paragraph__h2\">Các tiện ích hay ho trên ColorOS 7.2</h2>\r\n<p class =\"paragraph__p\">Chế độ tối của OPPO được cải thiện giúp máy trở nên nổi bật và dễ nhìn hơn. Thanh bên thông minh cho phép truy cập nhanh các chức năng như chụp màn hình hay các ứng dụng hiện đang được sử dụng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229885/oppo-a15-193620-113625.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Nếu bạn là một người cần chiếc điện thoại vừa đủ thông minh, tiện cho việc liên lạc và giải trí đơn giản thì đây hoàn toàn đáp ứng tốt được cho bạn. Ngoài ra, chiếc điện thoại còn rất phù hợp khi sử dụng làm quà tặng cho bố mẹ, cô bác,... để vừa liên lạc với người thân, vừa có thể đọc báo, xem tin tức trên chiếc điện thoại màn hình rộng 6.52 inch này.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 6, 1),
(40, 1, 'Xiaomi Mi 10T Pro 5G', '\r\n../assets/image/products/phone/Xiaomi-Mi-10T-Pro-5G/Nền.jpg', 12490000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 6.67\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 108 MP & Phụ 13 MP, 5 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n20 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 865 8 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n256 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM, Hỗ trợ 5G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n5000 mAh, có sạc nhanh\r\n</span>\r\n</div>', '\r\n<b class =\"paragraph__title\">Đặc điểm nổi bật của Xiaomi Mi 10T Pro 5G</b>\r\n<h1 class =\"paragraph__h1\">Mi 10T Pro 5G mẫu smartphone cao cấp của Xiaomi trong năm 2020 cuối cùng cũng được trình làng với loạt những thông số gây “choáng ngợp”: màn hình tần số quét 144 Hz, vi xử lý Snapdragon 865 và cụm camera khủng 108 MP kèm theo đó là một mức giá dễ chịu cho người dùng.</h1>\r\n<h2 class =\"paragraph__h2\">Mượt mà trong từng khung hình với tần số quét 144 Hz</h2>\r\n<p class =\"paragraph__p\">Tần số quét 144 Hz là một trong những điểm ấn tượng của Mi 10T Pro, cung cấp lên đến 144 khung hình/giây mang đến sự mượt mà tối đa, nâng cao mọi trải nghiệm cho dù bạn đang chơi game, xem phim hay chỉ đơn giản là cuộn trang web để xem tin tức.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-111720-101756.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Tính năng AdaptiveSync sáng tạo tự động điều chỉnh tốc độ làm mới từ 30 Hz đến 144 Hz để thích ứng với các đặc điểm của từng ứng dụng, do đó tìm thấy sự cân bằng hoàn hảo giữa trải nghiệm người dùng và tối ưu hóa mức tiêu thụ điện năng của thiết bị.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-112420-102432.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Xiaomi còn tích hợp MEMC - Công nghệ đặc biệt giúp cải thiện các nội dung, video có tần số quét thấp bằng cách bổ sung thêm những khung hình ảo giúp tái hiện lại những phân cảnh hành động trở nên rõ ràng và mượt mà hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-111820-101820.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Mi 10T Pro trang bị màn hình viền siêu mỏng Full HD+ với camera selfie “đục lỗ” trong màn hình cho kích thước mở rộng lên đến 6.67 inch, hỗ trợ không gian màu DCI-P3 với độ bao phủ rộng hơn so với sRBG hiển thị màu sắc vô cùng chuẩn xác mang đến những thước phim, khung hình có độ trung thực cao.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-142420-032426.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Sử dụng Mi 10T Pro vô cùng thoải mái dễ chịu khi màn hình của máy có khả năng giảm bớt ánh sáng xanh gây mỏi mắt, màn hình đạt chứng nhận TÜV Rheinland có lượng ánh sáng xanh dương thấp bảo vệ đôi mắt của bạn.</p>\r\n<h2 class =\"paragraph__h2\">Chơi game cực “mướt” với Snapdragon 865</h2>\r\n<p class =\"paragraph__p\">Sức mạnh hiệu năng của Mi 10T Pro là “vô địch” trong tầm giá khi trang bị vi xử lý tối tân Qualcomm Snapdragon 865 gồm 8 nhân xung nhịp tối đa 2.84 GHz, tiến trình 7 nm tiên tiến mang đến hiệu năng ấn tượng mà ít tiêu tốn điện năng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-111820-101827.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Qualcomm Snapdragon 865 cũng là con chip có tích hợp kết nối mạng 5G tốc độ cao, đang dần thay thế 4G trong thời gian sắp tới cùng Wifi 6 đáp ứng mọi nhu cầu tra cứu thông tin, chơi game trực tuyến hay xem phim chất lượng cao mọi lúc mọi nơi mọi không lo kết nối mạng yếu, chậm gây gián đoạn trải nghiệm của bạn.</p>\r\n<p class =\"paragraph__p\">Bên cạnh đó, RAM và bộ nhớ trong của Mi 10T Pro cũng thuộc dạng khủng khi trang bị RAM 8 GB và bộ nhớ tốc độ UFS 3.1 nhanh nhất hiện nay, cho tốc độ mở ứng dụng vô cùng nhanh chóng, đa nhiệm mượt mà với độ trễ gần như bằng không.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-111920-101906.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Mi 10T Pro còn là mẫu điện thoại chơi game đáng tiền với nhiều tính năng giúp tăng trải nghiệm cho anh em game thủ như mô-tơ rung phản hồi xúc giác, loa kép âm thanh stereo, đặc biệt là công nghệ tản nhiệt LiquidCool giúp duy trì nhiệt độ lý tưởng trong suốt ván game giữ cho hiệu năng của thiết bị ổn định, bảo vệ tuổi thọ pin và các linh kiện bên trong.</p>\r\n<h2 class =\"paragraph__h2\">Camera 108 MP sắc nét, thoả thích thu phóng</h2>\r\n<p class =\"paragraph__p\">Về khả năng chụp ảnh, Mi 10T Pro trang bị cụm 3 camera sau trong đó ấn tượng nhất là camera chính độ phân giải khủng 108 MP sử dụng cảm biến cao cấp của Samsung cho ra những tấm hình với độ sắc nét kinh ngạc, cho phép bạn tùy ý thu phóng bất kỳ vị trí nào trong bức ảnh.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-142520-032538.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Không những vậy, với độ phân giải cao nên chỉ với zoom kỹ thuật số 30x là đã đủ để bạn khám phá những chi tiết ở rất xa nhưng bầu trời đêm đầy sao, nóc của tòa nhà cao tầng.</p>\r\n<p class =\"paragraph__p\">Ngoài camera chính, Xiaomi không quên trang bị camera góc siêu rộng 13 MP với góc chụp 123 độ, bao quát toàn bộ mọi hình ảnh ngay trước mặt bạn và ghi chúng lại trong 1 khung hình duy nhất.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-111720-101742.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Những ai thích khám phá chắc sẽ không bỏ qua camera macro 5 MP, cho bạn không ngừng sự sáng tạo với những tác phẩm macro ấn tượng độc đáo hay chỉ đơn giản là tìm hiểu cuộc sống của thế giới thu nhỏ đầy bí ẩn.</p>\r\n<p class =\"paragraph__p\">Mi 10T Pro còn hỗ trợ đa dạng nhiều tính năng chụp ảnh để bạn có thể sáng tạo theo cách riêng của mình như chế độ chụp siêu nhanh Timed Burst cho phép người dùng chụp nhiều ảnh theo thiết lập thời gian và chuyển đổi thành những đoạn video ấn tượng, cung cấp nhiều bộ lọc màu nghệ thuật, trong chế độ chuyên nghiệp bạn có thể chỉnh thời gian phơi sáng để tạo ra những bức ảnh chụp đường phố rực rỡ trong đêm.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-111720-101728.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Chế độ chụp đêm 2.0 cải tiến không chỉ đơn thuần là đẩy sáng cho bức hình, mà còn khử nhiễu, tái hiện cân bằng các vùng sáng, vùng tối thông minh để cho ra những tấm ảnh chụp đêm đẹp rõ chi tiết thể hiện cuộc sống về đêm sôi động đầy màu sắc.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-142720-042740.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Ống kinh của Mi 10T Pro còn trang bị khả năng quay phim 8K và cho phép bạn có thể chụp hình khi quay phim với độ phân giải 20 MP, để bạn không bỏ lỡ những khoảnh khắc thú vị trong khi đang quay phim.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-111920-101916.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Máy còn trang bị tính năng camera kép độc đáo, quay phim đồng thời bằng cả camera trước và sau, cho bạn tạo ra những đoạn video blog, TikTok vui vẻ cùng bạn bè.</p>\r\n<p class =\"paragraph__p\">Camera selfie 20 MP được hỗ trợ đầy đủ tính năng từ làm xóa phông, nhận diện khuôn mặt cho đến làm đẹp thông minh giúp bạn tự tin tỏa sáng ngay với bạn bè và người thân.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-5g-1.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Sạc nhanh 33 W tốc độ ấn tượng</h2>\r\n<p class =\"paragraph__p\">Mi 10T Pro chiếc điện thoại pin trâu được trang bị viên pin có dung lượng 5000 mAh, đáp ứng cho một ngày làm việc bận rộn. Bên cạnh đó, công nghệ sạc pin nhanh 33 W với 2 luồng sạc song song, rút ngắn thời gian sạc chỉ còn phân nửa. Theo Xiaomi, công nghệ sạc nhanh Mi Fast Charge độc quyền của hãng chỉ mất 1 giờ để sạc đầy pin Mi 10T Pro.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-142720-032708.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Thiết kế ấn tượng, mặt lưng kính hút mắt</h2>\r\n<p class =\"paragraph__p\">Thiết kế của Mi 10T Pro vô cùng đẹp mắt sự kết hợp hài hòa giữa chất liệu khung kim loại và kính bóng bẩy, chuyển sắc tinh tế dưới ánh sáng tạo nên vẻ sang trọng cho người sử dụng.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-111720-101734.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Điểm nhấn ở mặt lưng nằm là nằm ở cụm camera. Xiaomi lựa chọn mô đun chữ nhật đứng quen thuộc, nhưng cách sắp xếp độc đáo đã giúp cho máy trở nên khác biệt hơn, với hơn 50% diện tích phía trên dành cho camera chính 108 MP và phần còn lại là các camera phụ, cảm biến Light Sensor cùng đèn flash vô cùng cân đối và hài hoài.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-143020-033056.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Toàn bộ máy được phủ lớp kháng khuẩn công nghệ ion bạc có khả năng loại bỏ hơn 99% vi khuẩn, một tính năng vô cùng cần thiết trong giai đoạn phức tạp như hiện nay.</p>\r\n<p class =\"paragraph__p\">Mi 10T Pro chỉ trang bị tấm nền IPS LCD, vì thế nút nguồn ở cạnh bên chính là vị trí không thể nào tốt hơn để đặt cảm biến vân tay, tối ưu cho thao tác mở khóa máy nhanh chóng tiện lợi hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/228136/xiaomi-mi-10t-pro-142920-032918.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Tổng kết, Xiaomi 10T Pro có thể được xem là mẫu smartphone cao cấp nhất của Xiaomi trong nằm 2020. Trong phân khúc cận cao cấp đây là một lựa chọn đáng đầu tư, người dùng vẫn sẽ có cấu hình mạnh mẽ, mạng 5G, camera 108 MP cùng hàng loạt tính năng hấp dẫn đây là điều không phải smartphone nào cũng đáp ứng được.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 7, 1),
(41, 1, 'Xiaomi Redmi Note 10 Pro (8GB/128GB)', '../assets/image/products/phone/Xiaomi-Redmi-Note-10-Pro-(8GB128GB)/Nền.jpg', 7490000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nAMOLED, 6.67\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 11\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 108 MP & Phụ 8 MP, 5 MP, 2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n16 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 732G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM, Hỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n5020 mAh, Sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Xiaomi Redmi Note 10 Pro (8GB/128GB)</b>\r\n<h1 class =\"paragraph__h1\">Kế thừa và nâng cấp từ thế hệ trước, Xiaomi đã cho ra mắt Xiaomi Redmi Note 10 Pro (8GB/128GB) sở hữu một thiết kế cao cấp, sang trọng bên cạnh cấu hình vô cùng mạnh mẽ, hứa hẹn mang đến sự cạnh tranh lớn trong phân khúc smartphone tầm trung</h1>\r\n<h2 class =\"paragraph__h2\">Cấu tạo bền bỉ, thiết kế cao cấp</h2>\r\n<p class =\"paragraph__p\">Xiaomi Redmi Note 10 Pro có thiết kế nguyên khối mang lại cảm giác rất đầm tay và chắc chắn, bên cạnh việc trang bị mặt kính ở hai cả hai mặt, tăng khả năng chống chịu va đập và các trầy xước không mong muốn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229228/xiaomi-redmi-note-10-pro-8gb-002.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Thiết kế sang trọng với khung viền được gia công bằng vật liệu kim loại cao cấp, máy sử dụng bản màu thanh lịch với những màu sắc trẻ trung và năng động tùy chọn theo sở thích.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229228/xiaomi-redmi-note-10-pro11.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Redmi Note 10 Pro còn được trang bị tính năng mở khóa vân tay và mở khóa khuôn mặt với tốc độ nhận diện cao, chính xác giúp người dùng mở điện thoại nhanh chóng và tăng cường độ bảo mật.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229228/xiaomi-redmi-note-10-pro-8gb-009.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Màn hình rộng mở, trải nghiệm đầy hứng khởi</h2>\r\n<p class =\"paragraph__p\">Sử dụng công nghệ màn hình AMOLED độ phân giải Full HD+ (1080 x 2400 Pixels) cho độ sắc nét cao vừa sở hữu kích thước lớn 6.67 inch, thoải mái xem phim, lướt web hay giải trí hàng ngày.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229228/xiaomi-redmi-note-10-pro-8gb-001.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Bên cạnh đó, màn hình của Redmi Note 10 Pro sở hữu tần số quét lên đến 120 Hz mà ít điện thoại nào trong cùng phân khúc giá có được, giúp tốc độ chuyển động hình ảnh trở nên mượt mà và sắc nét hơn bao giờ hết</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229228/xiaomi-redmi-note-10-pro-8gb-007.jpg\" alt=\"\">\r\n<h1 class =\"paragraph__h1\"Độ phân giải máy ảnh cao đáng kinh ngạc></h1>\r\n<p class =\"paragraph__p\">Cụm camera siêu khủng với độ phân giải camera chính lên đến 108 MP, camera góc rộng 118 độ 8 MP, camera macro 5 MP và camera đo độ sâu 2 MP, tạo nên một trong những điểm nhấn đặc biệt nhất trên Xiaomi Redmi Note 10 Pro.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229228/xiaomi-redmi-note-10-pro-8gb-003.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Thiết bị còn sử dụng được các chuẩn quay phim từ HD cho đến video chất lượng 4K. Giờ đây, bạn có thể thả ga thu trọn từng khoảnh khắc đặc biệt trong những khung hình sắc nét và chất lượng tuyệt vời.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229228/xiaomi-redmi-note-10-pro-008.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Camera trước có độ phân giải 16 MP, máy hỗ trợ các tính năng làm đẹp, tự động lấy nét giúp mang lại khả năng selfie bắt mắt, chụp đẹp trong vòng “một nốt nhạc” mà không cần phải tốn nhiều thời gian chỉnh sửa ảnh.</p>\r\n<h1 class =\"paragraph__h1\">Hiệu năng mạnh mẽ từ Snapdragon 732G 8 nhân</h1>\r\n<p class =\"paragraph__p\">Redmi Note 10 Pro hoạt động mạnh mẽ với con chip Snapdragon 732G 8 nhân của Qualcomm, chipset cho hiệu năng khủng, cung cấp khả năng xử lý tuyệt vời, qua đó khẳng định tham vọng vươn mình trở thành một trong những smartphone có cấu hình gaming hấp dẫn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229228/xiaomi-redmi-note-10-pro5.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Snapdragon 732G được nâng cấp để tập trung mạnh mẽ vào xử lý các tác vụ và cấu hình cao của những tựa game online chất lượng và đồ họa sắc nét như: Liên Quân Mobile, PUBG Mobile, Tốc Chiến,...</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229228/xiaomi-redmi-note-10-pro-8gb-006.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Thiết bị còn được đi kèm 8 GB RAM giúp người dùng có thể bật đa nhiệm mượt mà, cùng với 128 GB bộ nhớ trong và hỗ trợ thẻ nhớ ngoài MicroSD đem lại không gian lưu trữ lớn.</p>\r\n<h1 class =\"paragraph__h1\">Pin trâu kết hợp với sạc pin nhanh</h1>\r\n<p class =\"paragraph__p\">Máy trang bị viên pin Li-Ion dung lượng cao 5020 mAh, viên pin cung cấp khả năng hoạt động xuyên suốt nhiều giờ liền với hiệu suất cao. Những trận game kéo dài hay một lượng bài tập, công việc lớn làm tiêu hao đáng kể pin sẽ không còn là vấn đề với Redmi Note 10 Pro nữa. </p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229228/xiaomi-redmi-note-10-pro2.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Ngoài ra, thiết bị còn được trang bị củ sạc nhanh siêu tốc 33 W, giúp tiết kiệm tối đa thời gian sạc. Xóa tan mọi âu lo về việc mất nhiều thời gian để nạp đầy năng lượng cho smartphone của bạn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/229228/xiaomi-redmi-note-10-pro-8gb-005.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Xiaomi Redmi Note 10 Pro sở hữu những tính năng vô cùng hấp dẫn của một chiếc điện thoại cao cấp trong một mức giá tầm trung như: Pin tốt sạc pin nhanh, vi xử lý tốc độ, màn hình và camera cho chất lượng hình ảnh xuất sắc,... hứa hẹn sẽ bùng nổ trở thành một trong những smartphone tầm trung đáng chú ý nhất nửa đầu năm 2021.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 7, 1),
(42, 1, 'Xiaomi POCO X3 NFC', '../assets/image/products/phone/Xiaomi-POCO-X3-NFC/Nền.jpg', 5990000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\nIPS LCD, 6.67\", Full HD+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 10\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera sau:</span>\r\n<span class=\"content__row-content\">\r\nChính 64 MP & Phụ 13 MP, 2 MP, 2 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Camera trước:</span>\r\n<span class=\"content__row-content\">\r\n20 MP\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chip:</span>\r\n<span class=\"content__row-content\">\r\nSnapdragon 732G 8 nhân\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n6 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Bộ nhớ trong:</span>\r\n<span class=\"content__row-content\">\r\n128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">SIM:</span>\r\n<span class=\"content__row-content\">\r\n2 Nano SIM (SIM 1 chung khe thẻ nhớ), Hỗ trợ 4G\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Pin:</span>\r\n<span class=\"content__row-content\">\r\n5160 mAh, có sạc nhanh\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Xiaomi POCO X3 NFC</b>\r\n\r\n<h1 class =\"paragraph__h1\">Tiếp nối sự thành công về thế mạnh là điện thoại tầm trung với thiết kế hiện đại sang trọng, Xiaomi đã cho ra mắt Xiaomi POCO X3 NFC mang theo nhiều tính năng hấp dẫn trên 1 chiếc điện thoại thông minh, cụm camera chất lượng, hiệu năng mạnh mẽ, dung lượng pin khủng cùng khả năng sạc nhanh vượt trội 33W.</h1>\r\n<h2 class =\"paragraph__h2\">Hệ thống camera đột phá, ấn tượng</h2>\r\n<p class =\"paragraph__p\">Cụm 4 camera của POCO X3 được đặt phía trên ở giữa mặt lưng với cảm biến chính 64 MP hỗ trợ khả năng chụp đêm (Night Mode) ấn tượng giúp lấy nét nhanh, giảm nhiễu và thu sáng tốt hơn cho bạn những tấm ảnh trong màn đêm rõ nét và chi tiết.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/227900/xiaomi-poco-x3-220820-100848.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Ba camera còn lại bao gồm camera góc siêu rộng 13 MP, camera xóa phông 2 MP và cuối cùng là camera macro 2 MP hỗ trợ chụp cận cảnh.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/227900/xiaomi-poco-x3-131720-081737.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Thiết bị cũng hỗ trợ các tính năng chụp ảnh thông minh, tối ưu màu sắc, quay phim 4K giúp người dùng có những tấm ảnh hay video chất lượng cao nhất trong mỗi khung hình.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/227900/xiaomi-poco-x3-203020-063050.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Chế độ chụp chân dung trên POCO X3 có những nét cải tiến rõ rệt khi có thể xóa phông sắc nét dù là khoảng cách gần hay xa. Đồng thời trí tuệ nhân tạo AI sẽ can thiệp để khuôn mặt của bạn rõ nét dù chụp ở những môi trường phức tạp.</p>\r\n<p class =\"paragraph__p\">Ở mặt trước là camera selfie 20 MP được tích hợp các thuật toán thông minh, làm đẹp giúp bạn tự tin tỏa sáng với bạn bè.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/227900/xiaomi-poco-x3-220820-100857.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Màn hình giải trí siêu mượt 120 Hz</h2>\r\n<p class =\"paragraph__p\">Xiaomi POCO X3 được trang bị màn hình tràn viền IPS LCD 6.67 inch với độ phân giải Full HD+ cho chất lượng hiển thị sắc nét, màu sắc chân thực. Bên cạnh đó, màn hình hỗ trợ tốc độ làm mới 120 Hz giúp cho mọi hoạt động hay chơi game mượt mà và trơn tru hơn.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/227900/xiaomi-poco-x3-203120-063128.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Máy được thiết kế nguyên khối và bọc kính cường lực Corning Gorilla Glass 5 ở mặt trước và lớp nhựa giả kính cao cấp mặt lưng góp phần tăng độ cứng cáp lẫn nét sang trọng cho máy.</p>\r\n<p class =\"paragraph__p\">Giờ đây bảo mật vân tay được trang bị bên cạnh viền của máy giúp bạn dễ dàng mở khóa một cách nhanh chóng. Ngoài ra bạn có thể sử dụng tính năng bảo mật khuôn mặt trong lúc không tay bạn không thuận tiện cho việc mở khóa.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/227900/xiaomi-poco-x3-203120-063110.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Chiến game mượt mà với Snapdragon 732G</h2>\r\n<p class =\"paragraph__p\">Dung lượng RAM 6 GB đem lại khả năng đa nhiệm tốt, bộ nhớ trong 128 GB và thẻ nhớ ngoài MicroSD hỗ trợ tối đa 256 GB cho người dùng không gian lưu trữ lớn với các ứng dụng nặng hay video.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/227900/xiaomi-poco-x3-203120-063100.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Điểm số mà POCO X3 đo được trên Antutu là 282.004 điểm, kèm theo đó là bộ vi xử lý Snapdragon 732G và chip đồ họa GPU Adreno 618 giúp điện thoại chơi game đồ hoạ cao hot nhất hiện nay một cách mượt mà.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/227900/xiaomi-poco-x3-131720-081751.jpg\" alt=\"\">\r\n<h2 class =\"paragraph__h2\">Viên pin khủng 5160 mAh, thoải mái sử dụng</h2>\r\n<p class =\"paragraph__p\">Xiaomi POCO X3 là chiếc điện thoại pin trâu khi sở hữu viên pin khủng 5160 mAh đáp ứng tốt nhu cầu sử dụng các tác vụ thông thường cả ngày dài.</p>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/42/227900/xiaomi-poco-x3-220920-100919.jpg\" alt=\"\">\r\n<p class =\"paragraph__p\">Để sạc cho viên pin khủng thì Xiaomi đã tích hợp cho máy công nghệ sạc pin nhanh 33W qua cổng Type-C giúp bạn nhanh chóng trở lại những trận đấu game gay cấn.</p>\r\n<p class =\"paragraph__p\">Với những tính năng hấp dẫn được trang bị trên máy, Xiaomi POCO X3 NFC là sản phẩm tầm trung rất phù hợp đa số người dùng ưa thích một thiết kế sáng trọng, cụm camera chất lượng và hiệu năng mạnh mẽ.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 7, 1);
INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(43, 4, 'Samsung Galaxy Watch 3 41mm viền thép bạc dây da', '../assets/image/products/watch/Samsung-Galaxy-Watch-3-41mm-viền-thép-bạc-dây-da/Nền.jpg', 5490000, 0, 5, 13, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nSUPER AMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.2 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 40 tiếng\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nOS TIZEN\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 5.0 trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nKính cường lực Gorilla Glass Dx+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n41 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v5.0, Wifi, GPS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy Watch 3 41mm viền thép bạc dây da</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/230634/Slider/vi-vn-samsung-galaxy-watch-3-41mm-bac-8.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế đơn giản, màu sắc sang trọng </h2>\r\n                    <p class = \"paragraph__p\">Samsung Galaxy Watch 3 41mm viền thép bạc dây da sở hữu 2 nút bấm và vòng bezel xoay điều khiển vật lý độc đáo. Màn hình 1.2 inch cùng độ phân giải 360 x 360 pixels giúp hình ảnh hiển thị được chân thật, rõ nét. Viền đồng hồ được làm bằng thép cứng cáp cùng dây đeo bằng da tạo cảm giác chắc chắn và dễ chịu cho người dùng khi đeo.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/7077/230634/samsung-galaxy-watch-3-41mm-bac1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Mặt kính chống trầy, màn hình SUPER AMOLED </h2>\r\n                    <p class = \"paragraph__p\">Màn hình sử dụng công nghệ SUPER AMOLED giúp hình ảnh và thông tin trên thiết bị đồng hồ được hiển thị rõ ràng và sắc nét. Ngoài ra còn có tính năng màn hình luôn hiển thị, dễ dàng quan sát giờ và thông báo. Đồng hồ được trang bị mặt kính cường lực Gorilla Glass Dx+ giúp đồng hồ được bảo vệ khỏi những vết trầy xước, va đập thường gặp.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/230634/samsung-galaxy-watch-3-41mm-bac4.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kết nối bluetooth v5.0 (Android 5.0 trở lên, iOS 9 trở lên) </h2>\r\n                    <p class = \"paragraph__p\">Kết nối với các thiết bị qua cổng bluetooth, khi kết nối, người dùng có thể kiểm tra tin nhắn, nhận cuộc gọi, xem thông báo ứng dụng ( Messenger, Zalo, Instagram,…), theo dõi sức khoẻ, luyện tập thể thao,… Đồng hồ có khả năng tương thích với hệ điều hành android 5.0 trở lên và iOS 9 trở lên</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/230634/samsung-galaxy-watch-3-41mm-bac2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thoả sức thay đổi phong cách </h2>\r\n                    <p class = \"paragraph__p\">Với bộ sưu tập hơn 50.000 giao diện tùy chọn người dùng có thể tùy ý thay đổi giao diện, màu sắc của mặt đồng hồ. Thay đổi mặt đồng hồ bằng cách kết nối với ứng dụng Galaxy Wearable trên điện thoại hoặc có thể thay đổi trong mục cài đặt trên đồng hồ.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/230634/samsung-galaxy-watch-3-41mm-bac11.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thời lượng pin lên đến 40 giờ</h2>\r\n                    <p class = \"paragraph__p\">Sở hữu viên pin 247 mAh cho thời gian sử dụng 40 giờ trong một lần sạc, đi kèm với sản phẩm là đế sạc không dây, Samsung Galaxy Watch 3 cần 2 giờ để có thể sạc đầy 100% pin.\r\n                    Lưu ý: Thời gian sử dụng pin có thể thay đổi, phụ thuộc vào cách dùng của người đeo.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/230634/samsung-galaxy-watch-3-41mm-bac5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi sức khoẻ thường xuyên</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ có hỗ trợ tính năng theo dõi nhịp tim, theo dõi giấc ngủ, khả năng đo Oxy trong máu (SpO2), huyết áp và theo dõi chu kỳ kinh nguyệt cho phái nữ. Trang bị 8 đèn cảm biến cùng thiết kế dây đeo ôm trọn cổ tay người dùng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/230634/samsung-galaxy-watch-3-41mm-bac3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Rèn luyện sức khoẻ mỗi ngày</h2>\r\n                    <p class = \"paragraph__p\">Việc định vị vị trí sẽ trở nên dễ dàng hơn khi người dùng chơi các môn thể thao di chuyển như chạy bộ hay đạp xe bằng hệ thống định vị hàng đầu GPS. Đồng hồ Samsung Galaxy Watch 3 tích hợp thêm một số tính năng tập luyện thể dục cùng với hơn 120 bài tập video trong Samsung Health.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/230634/samsung-galaxy-watch-3-41mm-bac8.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Tính năng phát hiện té ngã</h2>\r\n                    <p class = \"paragraph__p\">Trong trường hợp người dùng vô ý bị ngã, đồng hồ sẽ mặc định người dùng trong tình trạng nguy hiểm và sẽ đổ chuông trong vòng 1 phút, nếu không nhận được sự phản hồi nào từ người dùng, đồng hồ sẽ tự động liên lạc các số khẩn cấp đã được cài đặt trước đó.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/230634/samsung-galaxy-watch-3-41mm-bac7.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thưởng thức âm nhạc mọi lúc</h2>\r\n                    <p class = \"paragraph__p\">Người dùng không những có thể kết nối điều khiển phát nhạc trên điện thoại, mà còn có thể kết nối với tai nghe bluetooth tạo nên những trải nghiệm thú vị dành cho người dùng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/230634/samsung-galaxy-watch-3-41mm-bac9.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nhiều tính năng hữu ích</h2>\r\n                    <p class = \"paragraph__p\">Các tính năng khác như: Báo thức, rung thông báo, đếm bước chân, dự báo thời tiết,…</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/230634/samsung-galaxy-watch-3-41mm-bac10.jpg\" alt=\"\">  \r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Samsung Galaxy Watch 3 41mm với thiết kế sang trọng, màn hình SUPER AMOLED 1.2 inch cho hình ảnh sắc nét, vòng bezel vô cùng tiện lợi, dây đeo bằng da dễ chịu cùng các tính năng ưu việt như theo dõi nhịp tim, giấc ngủ, chu kỳ kinh nguyệt, nồng độ Oxy trong máu, luyện tập thể thao, phát hiện té ngã. Samsung Galaxy Watch 3 chắc chắn sẽ là mẫu đồng hồ phù hợp với những người dùng luôn quan tâm đến việc chăm sóc sức khoẻ và rèn luyện thân thể mỗi ngày.</p><button class =\"paragraph__more\">Xem thêm !</button>', 2, 1),
(44, 4, 'Huawei Watch GT2 46mm dây silicone', '../assets/image/products/watch/Huawei-Watch-GT2-46mm-dây-silicone/Nền.jpg', 3990000, 0, 5, 2, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nAMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.39 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 30 giờ khi sử dụng GPS, Khoảng 14 ngày\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nLiteOS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid 4.4 trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nKính cường lực \r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n46 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v5.1, Wifi, GPS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Huawei Watch GT2 46mm dây silicone</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212192/Slider/watch-gt2-46mm-day-sillicone-141320-081324.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Đồng hồ thông minh Huawei Watch GT2 với nhiều sự cải tiến về mặt công nghệ đặc biệt thời lượng pin vượt trội lên đến 2 tuần, các tính năng về giải trí, chăm sóc sức khỏe sẽ là người bạn đồng hành hữu ích cho bạn.</h2>\r\n                    <h2 class = \"paragraph__h2\">Dung lượng pin sử dụng liên tục 14 ngày</h2>\r\n                    <p class = \"paragraph__p\">So với các sản phẩm đồng hồ thông minh khác thì dung lượng pin của Huawei Watch GT2 vượt trội hơn rất nhiều.\r\n                    Thời lượng pin lên đến 14 ngày với mục đích sử dụng thông thường như xem giờ, theo dõi vận động thì khi bật GPS định vị liên tục đồng hồ của bạn sẽ sử dụng được trong 30 giờ, còn với việc đàm thoại qua bluetooth, gọi điện liên tục trong 10 giờ.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212192/watch-gt2-46mm-day-sillicone-5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Trả lời cuộc gọi trực tiếp trên đồng hồ</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh trang bị loa, micro... nghe nhận cuộc gọi trực tiếp không gần điện thoại, bạn có thể vừa chạy bộ vừa nghe điện thoại.Nếu kết hợp với điên thoại Huawei thì khả năng kết nối của đồng hồ với điện thoại có thể lên đến 150m.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212192/watch-gt2-46mm-day-sillicone7.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nghe nhạc bằng loa ngoài</h2>\r\n                    <p class = \"paragraph__p\">Bạn có thể dễ dàng điều khiển âm nhạc như trên một chiếc điện thoại thông thường như tua bài, chọn bài.Hơn nữa có thể chép nhạc vào đồng hồ và nghe, số lượng bài hát có thể lưu được khoảng 500 bài hát.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212192/watch-gt2-46mm-day-sillicone6.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế thanh lịch</h2>\r\n                    <p class = \"paragraph__p\">Chất liệu thép cùng với dây đeo silicone mềm mại tạo cảm giác thoải mái.Màn hình sắc nét, tấm nền AMOLED 1.39 inch độ sáng cao cho hình ảnh rõ ràng, sắc nét và đẹp mắt hơn.</p>  \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212192/watch-gt2-46mm-day-sillicone-2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">15 chế độ tập luyện thông minh</h2> \r\n                    <p class = \"paragraph__p\">Huawei hỗ trợ 15 bài tập thông dụng nhất để giúp người dùng rèn luyện sức khỏe. Các tính năng như tính lượng calories tiêu thụ, tính quãng đường chạy,... hỗ trợ người dùng luyện tập có hiệu quả hơn.Những bộ môn thể thao kết hợp như chạy bộ, bơi lội, đạp xe.</p>  \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212192/watch-gt2-46mm-day-sillicone8-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">13 bài tập chạy được lập trình sẵn</h2> \r\n                    <p class = \"paragraph__p\">Với tính năng thể thao thông minh hỗ trợ người tập những bài tập được lập trình sẵn: chạy bộ, chạy giảm cân,.. với cường độ cao, thấp khác nhau.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212192/watch-gt2-46mm-day-sillicone812.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Giúp cải thiện khả năng bơi lội</h2> \r\n                    <p class = \"paragraph__p\">Với tính năng nhận diện tư thế bơi và ghi lại kết quả bơi, đồng hồ thông minh Huawei Watch GT2 hứa hẹn sẽ là huấn luyện viên bơi lội của bạn.</p2>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212192/watch-gt2-46mm-day-sillicone3-2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Đo nhịp tim của bạn liên tục</h2> \r\n                    <p class = \"paragraph__p\">Đo nhịp tim của bạn liên tục nhờ vào áp dụng công nghệ tia sáng nhận diện hiện đại kết hợp với thuật toán đo nhịp tim AI thông minh, đồng hồ của bạn có thể theo dõi nhịp tim ngay cả khi bạn ngủ, chơi thể thao... và thống kê số liệu để gửi thông báo đến bạn nếu nhịp tim bất thường.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212192/watch-gt2-46mm-day-sillicone5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Một số tính năng quan trọng khác</h2>\r\n                    <p class = \"paragraph__p\">Định vị 3 chiều: Ứng dụng thuật toán 3D có thể giúp bạn đo độ cao, xác định phương hướng qua la bàn, đo khí áp. Bên cạch đó chiếc đồng hồ này còn tích hợp các tính năng dự báo thời tiết, báo thức, nhắc lịch, đèn pin...</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212192/watch-gt2-46mm-day-sillicone-15.jpg\" alt=\"\"><button class =\"paragraph__more\">Xem thêm !</button>  ', 3, 1),
(45, 4, 'Samsung Galaxy Watch 3 LTE 41mm viền thép dây da', '../assets/image/products/watch/Samsung-Galaxy-Watch-3-LTE-41mm-viền-thép-dây-da/Nền.jpg', 6490000, 0, 5, 2, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nSUPER AMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.2 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 40 tiếng\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nOS TIZEN\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nKính cường lực \r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n4 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v5., Wifi, GPS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy Watch 3 LTE 41mm viền thép dây da</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226477/Slider/samsung-galaxy-watch-3-lte-41mm-200820-0940591.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Sự thoải mái của bạn là cảm hứng thiết kế</h2>\r\n                    <p class = \"paragraph__p\">Samsung Galaxy Watch 3 LTE 41mm mang trong mình thiết kế cổ điển pha lẫn chút hiện đại nhưng không kém phần cao cấp, lịch lãm. Sở hữu màn hình hiển thị 1.2 inch, rực rỡ và sắc nét với công nghệ màn hình Super AMOLED cao cấp mang lại trải nghiệm tốt nhất. Dây đồng hồ bằng chất liệu da cao cấp, vừa sang trọng vừa thoải mái khi đeo trên tay. Khung viền xoay bezel bằng thép không gỉ chắc chắn nhưng vẫn giữ được độ mỏng tối ưu, gọn gàng cho mặt đồng hồ.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226477/samsung-galaxy-watch-3-lte-41mm-122920-102936.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">40 mặt đồng hộ đa dạng, đặc sắc</h2>\r\n                    <p class = \"paragraph__p\">Với bộ sưu tập đa dạng gồm 40 mẫu mặt đồng hồ khác nhau, bạn sẽ dễ dàng tìm được phong cách yêu thích và thể hiện dấu ấn cá nhân.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226477/samsung-galaxy-watch-3-lte-41mm-124520-084508.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hỗ trợ eSIM giúp nghe, nhận cuộc gọi mà không cần mang theo điện thoại</h2>\r\n                    <p class = \"paragraph__p\">Khác với các loại đồng hồ thông minh khác chỉ hiển thị tin nhắn đến, đồng hồ thông minh Samsung sẽ kết hợp hiển thị lịch sử trò chuyện giúp cuộc trao đổi của bạn được liền mạch. Tính năng hồi đáp thông minh của sản phẩm sẽ phân tích đoạn tin nhắn và hình ảnh bạn nhận được và đề xuất một số câu trả lời theo các chủ đề có sẵn, tiết kiệm được rất nhiều thời gian. eSIM được tích hợp cho phép bạn nhận và trả lời cuộc gọi mà không cần mang theo điện thoại.\r\n                    Xem thêm: Cách kết nối eSIM trên smartwatch</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226477/samsung-galaxy-watch-3-lte-41mm-200620-090611.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Pin dùng được trong 40 giờ</h2>\r\n                    <p class = \"paragraph__p\">Galaxy Watch 3 có dung lượng pin là 247 mAh với đế sạc nam châm không dây, sạc đầy trong 2 giờ và có thời gian sử dụng lên đến 40 giờ.\r\n                    Lưu ý: Chế độ Always On Display và GPS sẽ khiến đồng hồ mau hết pin hơn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226477/samsung-galaxy-watch-3-lte-41mm-200520-090543.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi các chỉ số sức khỏe</h2>\r\n                    <p class = \"paragraph__p\">Galaxy Watch 3 ghi lại độ sâu giấc ngủ và tổng thời gian ngủ để tính điểm và đề xuất giải pháp cải thiện chất lượng nghỉ ngơi của bạn. Sản phẩm được trang bị đèn LED đỏ và tia hồng ngoại để ước lượng chỉ số SpO2 hoặc VO2 max.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226477/samsung-galaxy-watch-3-lte-41mm-200620-090606.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Tập luyện thể thao tại nhà với 120 bài tập từ chuyên gia</h2>\r\n                    <p class = \"paragraph__p\">Việc luyện tập trở nên đơn giản và khoa học hơn nhờ 40 chế độ luyện tập và 120 bài tập có sẵn dưới dạng video do chuyên gia tư vấn được trang bị thông qua ứng dụng Samsung Health. Hệ thống sẽ theo dõi hoạt động và đưa ra hướng dẫn từng bước. Số đo nhịp tim, calorie, số bước chân, quãng đường đi được sẽ luôn được cập nhật tức thời.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226477/samsung-galaxy-watch-3-lte-41mm-123020-103008.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Độ bền đạt chuẩn quân đội, hữu ích cho những chuyến đi</h2>\r\n                    <p class = \"paragraph__p\">Bên cạnh độ bền ấn tượng, khả năng chống chịu với nhiệt độ và thời tiết khắc nghiệt cùng chức năng định vị GPS chuẩn xác cũng khiến Watch 3 trở thành một người bạn thân thiết đắc lực cho những người yêu thích đi phượt, leo núi... Ngoài những tiện ích quản lý hoạt động trên, đồng hồ sẽ tự phát hiện những cú té ngã gây nguy hiểm khiến bạn không thể di chuyển và phản hồi trong vòng 1 phút, hệ thống sẽ ngay lập tức gửi tín hiệu cầu cứu đến những thông tin liên hệ khẩn cấp do bạn cung cấp.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226477/samsung-galaxy-watch-3-lte-41mm-200520-090553.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Khả năng kết nối</h2>\r\n                    <p class = \"paragraph__p\">Galaxy Watch 3 tương thích với điện thoại Android phiên bản 5.0 trở lên, iPhone sử dụng hệ điều hành iOS 9 trở lên. Đồng hồ có thể dễ dàng kết hợp và đồng bộ hóa với Galaxy Note 20 hay Galaxy Buds Live thông qua Samsung Cloud và Bluetooth 5.0.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226477/samsung-galaxy-watch-3-lte-41mm-200520-090549.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Những tính năng khác</h2>\r\n                    <p class = \"paragraph__p\">Sở hữu màn hình Super AMOLED nên Samsung Galaxy Watch 3 LTE sẽ có năng Always on Display. Ngoài ra chiếc smartwatch này còn có các tính năng khác như đồng hồ bấm giờ, dự báo thời tiết, tìm điện thoại.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226477/samsung-galaxy-watch-3-lte-41mm-123520-083521.jpg\" alt=\"\"><button class =\"paragraph__more\">Xem thêm !</button>', 2, 1),
(46, 4, 'Mi Watch', '../assets/image/products/watch/Mi-Watch/Nền.jpg', 3090000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nAMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.39 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 16 ngày\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nOS TIZEN\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nKính cường lực \r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n46 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v5., Wifi, GPS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Đồng hồ thông minh Mi Watch</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/232899/Slider/vi-vn-mi-watch-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế thể thao chuyên nghiệp</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Mi Watch này mang phong cách trẻ trung, cá tính và đậm chất thể thao. Đồng hồ được trang bị công nghệ màn hình AMOLED với kích thước 1.39 inch cùng độ phân giải 454 x 454 pixels và độ sáng lên đến 450 nits giúp người dùng có thể quan sát thông tin rõ nét, chất lượng. Bên cạnh đó, đồng hồ còn được trang bị mặt kính cường lực Gorilla Glass 3 hạn chế trầy xước và tăng độ bền cho thiết bị.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/232899/mi-watch-2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Trọng lượng siêu nhẹ</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh có trọng lượng 32 gram siêu nhẹ với khung viền nhựa tạo cảm giác thoải mái cho người dùng khi đeo đồng hồ trong suốt ngày dài. Đi kèm là dây đeo silicone mềm mại, mỏng nhẹ và ôm cổ tay, dây đeo này có thể tháo rời giúp người dùng có thể vệ sinh đồng hồ một cách tiện lợi và dễ dàng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/232899/mi-watch-3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">100 giao diện mặt đồng hồ phong cách</h2>\r\n                    <p class = \"paragraph__p\">Với 100 mẫu thiết kế mặt đồng hồ được cung cấp sẵn, bạn có thể thoải mái lựa chọn và thay đổi giao diện cho chiếc đồng hồ. Bạn cũng có thể tự tạo mẫu mặt đồng hồ của riêng mình bằng những tấm hình mà bạn yêu thích.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/232899/mi-watch-4.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thời lượng pin 16 ngày</h2>\r\n                    <p class = \"paragraph__p\">Thời lượng sử dụng pin lên đến 16 ngày sau mỗi lần sạc đầy và khoảng 22 ngày khi để đồng hồ ở chế độ tiết kiệm pin. Tuy nhiên, khi sử dụng đồng hồ ở chế độ luyện tập thể thao, thời lượng sử dụng pin là hơn 50 giờ.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/232899/mi-watch-5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Cung cấp chế độ tập luyện chuyên nghiệp</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ cung cấp cho người dùng 17 chế độ luyện tập thể thao chuyên nghiệp như chạy bộ, bơi lội, đạp xe, leo núi, yoga,... Ngoài ra còn có 100 bài tập hỗ trợ, giúp quá trình luyện tập nâng cao sức khỏe của bạn chuyên nghiệp và hiệu quả hơn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/232899/mi-watch-6.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi sức khỏe người dùng</h2>\r\n                    <p class = \"paragraph__p\">Người dùng có thể cập nhật tình hình sức khỏe của bản thân chính xác và thường xuyên thông qua những tính năng như đo nhịp tim, đo nồng độ oxy trong máu (SpO2), theo dõi giấc ngủ, tính lượng calories,...</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/232899/mi-watch-7.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kết nối và hiển thị thông báo</h2>\r\n                    <p class = \"paragraph__p\">Tính năng màn hình luôn hiển thị hỗ trợ người dùng tối đa việc quan sát và theo dõi thông báo cuộc gọi, tin nhắn và từ các ứng dụng khác như Zalo, Messenger (Facebook), Line,... Bạn có thể kết nối đồng hồ với các thiết bị khác thông qua Bluetooth v5.0, GPS.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/232899/mi-watch-8.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Tiện ích khác</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ cung cấp nhiều tính năng vô cùng tiện lợi như đếm bước chân, điều khiển chơi nhạc, tìm điện thoại,... hỗ trợ tối đa mang lại sự hài lòng cho người dùng khi sử dụng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/232899/mi-watch-15.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Mi Watch là sản phẩm đồng hồ đến từ hãng đồng hồ Xiaomi với vẻ ngoài năng động, thiết kế thể thao chuyên nghiệp với công nghệ màn hình AMOLED và kính cường lực Gorilla Glass 3 bền chắc. Thời lượng pin dài cho người dùng thoải mái sử dụng và những tính năng hỗ trợ quá trình tập luyện một cách tối đa. Đây sẽ là một thiết bị cần thiết và phù hợp với những người yêu thích sự thời trang và quan tâm tới việc rèn luyện nâng cao sức khỏe.</p><button class =\"paragraph__more\">Xem thêm !</button>', 9, 1),
(47, 4, 'BeU B2 Hồng', '../assets/image/products/watch/BeU-B2-Hồng/Nền.jpg', 690000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nips\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.54inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 7 ngày\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nOS TIZEN\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nMặt kính nhựa\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n53 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Đồng hồ thông minh BeU B2 Hồng</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/231681/Slider/beu-b2-hong-061220-0123438.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế trẻ trung, hiện đại</h2>\r\n                    <p class = \"paragraph__p\">- Đồng hồ thông minh BeU B2 Hồng sở hữu khung viền chịu va đập tốt, mặt kính cứng cáp, trang bị dây đeo bằng silicone co giãn, chống thấm mồ hôi, ôm tay tốt đem lại cảm giác êm nhẹ thoải mái.\r\n                    - Mặt đồng hồ 1.54 inch với độ phân giải 240x240 pixels, sử dụng công nghệ IPS cho màu sắc sống động.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/231681/beu-b2-hong-cont-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình IPS hiển thị sống động, góc nhìn rộng</h2>\r\n                    <p class = \"paragraph__p\">Công nghệ IPS tự hào với khả năng tái tạo màu sắc chính xác và chân thực kể cả ở góc nhìn hẹp, sở hữu độ sáng và độ tương phản cao. Điểm trừ duy nhất là tiêu tốn nhiều năng lượng hơn các loại màn hình khác.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/231681/beu-b2-hong-cont-2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Tính năng thông minh hỗ trợ việc luyện tập thể thao hiệu quả, khoa học</h2>\r\n                    <p class = \"paragraph__p\">SmartWatch được trang bị nhiều tính năng giúp ích cho hoạt động thể thao như: theo dõi nhịp tim, chế độ tập luyện, đếm số bước chân, tính lượng calories tiêu thụ,...  hỗ trợ người dùng quản lý sức khỏe của bản thân tốt hơn, từ đó đặt ra những mục tiêu hợp lý và lộ trình tập luyện thể thao khoa học.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/231681/beu-b2-hong-cont-6.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Cải thiện giấc ngủ giúp tinh thần thoải mái, tràn đầy sức khỏe</h2>\r\n                    <p class = \"paragraph__p\">- Điều chỉnh giấc ngủ cũng là một phương pháp khoa học để cải thiện sức khỏe, tính năng theo dõi giấc ngủ sẽ phân tích và cung cấp cho bạn những thông tin hữu ích giúp bạn dễ dàng quản lý và xây dựng cho mình một thời gian ngủ khoa học, hợp lý với bản thân.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/231681/beu-b2-hong-cont-5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Dung lượng pin lớn, sạc đầy nhanh chóng</h2>\r\n                    <p class = \"paragraph__p\">Beu SmartWatch có thời lượng pin kéo dài khoảng 7 ngày khi hoạt động ở chế độ thông thường và chỉ tốn 2 giờ để sạc đầy, đem đến cho bạn những trải nghiệm trọn vẹn nhất.Beu SmartWatch có thời lượng pin kéo dài khoảng 7 ngày khi hoạt động ở chế độ thông thường và chỉ tốn 2 giờ để sạc đầy, đem đến cho bạn những trải nghiệm trọn vẹn nhất.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/231681/beu-b2-hong-cont-4.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Quản lý thông tin dễ dàng hơn chỉ với một chiếc đồng hồ thông minh nhỏ gọn</h2>\r\n                    <p class = \"paragraph__p\">Kết nối với điện thoại cho phép những thông báo cuộc gọi, tin nhắn được gửi về và hiển thị rõ ràng trên đồng hồ, đây là tính năng hữu ích giúp bạn nắm bắt thông tin một cách thuận tiện hơn đồng thời tránh bỏ lỡ những thông báo quan trọng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/231681/beu-b2-hong-cont-7.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh BeU có thiết kế hiện đại, sang trọng, sở hữu màn hình công nghệ IPS cho khả năng hiển thị sống động, chân thực. Phụ kiện thời trang đẳng cấp với nhiều tính năng tiện lợi như: đo nhịp tim, theo dõi giấc ngủ, kết nối với điện thoại (trả lời nhanh tin nhắn, điều khiển nhạc...), các chế độ tập luyện đa dạng dành cho các bạn yêu thích thể thao. Bên cạnh đó, BeU SmartWatch sở hữu mức giá hợp lý, đem sản phẩm đến gần hơn với thị trường người tiêu dùng Việt Nam.</p><button class =\"paragraph__more\">Xem thêm !</button>', 10, 1),
(48, 4, 'Oppo Watch 41mm dây silicone hồng', '../assets/image/products/watch/Oppo-Watch-41mm-dây-ilicone-hồng/Nền.jpg', 5900000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nAMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.6 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 24 ngày\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nGoogle Wear OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nMặt kính nhựa\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n41 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', ')<b class =\"paragraph__title\">Đặc điểm nổi bật của Oppo Watch 41mm dây silicone hồng</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225649/Slider/oppo-watch-41mm-day-silicone-170820-1040330.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Mang thiết kế đơn giản, sang trọng</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Oppo Watch màu hồng phiên bản 41mm trang bị màn hình AMOLED 1.6 inch cùng độ phân giải là 320 x 360 pixels, mật độ điểm ảnh 326ppi cho chất lượng hiển thị cực kì sắc nét. Dây đeo làm từ silicon với thiết kế mang lại cảm giác vô cùng mềm mại, không bị đau khi đeo lâu. \r\n                    Thiết kế của Oppo Watch khá giống với Apple watch, tuy nhiên vẫn có một số điểm khác biệt:\r\n                    + Không có núm xoay Digital Crown, thay vào đó là hai phím cứng vật lý ở cạnh phải.\r\n                    + Ở cạnh trái là dải loa dùng để phát nhạc và thực hiện nghe gọi.\r\n                    + Ở mặt lưng trang bị các đèn cảm biến khá giống với Apple Watch, hãng còn in thêm logo ở mặt sau.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225649/oppo-watch-41mm-day-silicone.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kết nối với điện thoại nhanh chóng (Bluetooth v4.2)</h2>\r\n                    <p class = \"paragraph__p\">Oppo Watch kết nối với điện thoại thông qua sóng Bluetooth 4.2, kết nối cho phép người dùng có thể xem tin nhắn, nghe nhận cuộc gọi (chỉ nghe gọi được trên điện thoại Android), kiểm tra thông báo ứng dụng (Facebook, Messenger, Zalo, Instagram …), xem video, tải ứng dụng từ Google Play, luyện tập thể thao,… Và còn nhiều tính năng khác. Oppo Watch tương thích với điện thoại chạy hệ điều hành Android và iOS.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225649/oppo-watch-41mm-day-silicone22.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thoải mái tuỳ chỉnh giao diện đồng hồ</h2>\r\n                    <p class = \"paragraph__p\">Oppo Watch tích hợp nhiều mặt đồng hồ theo từng phong cách, người dùng có thể tuỳ chỉnh giao diện trên đồng hồ, ngoài ra còn sử dụng trí tuệ nhân tạo AI để điều chỉnh mặt đồng hồ và màu sắc, style thích hợp với bộ đồ outfit của người dùng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225649/oppo-watch-41mm-day-silicone-writee3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thời lượng pin khoảng 1 ngày. Hỗ trợ sạc nhanh</h2>\r\n                    <p class = \"paragraph__p\">Trang bị viên pin dung lượng 300mAh dùng được 1 ngày trong một lần sạc, Oppo Watch có hỗ trợ sạc nhanh VOOC, mất 15 phút từ 0% lên 30% và mất 75 phút để sạc đầy pin.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225649/oppo-watch-41mm-day-silicone-writee11.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi nhịp tim</h2>\r\n                    <p class = \"paragraph__p\">Trang bị các đèn cảm biến ở mặt sau cùng thiết kế ôm trọn cổ tay giúp tính năng đo nhịp tim người dùng được liên tục và chính xác. Khi phát hiện hoạt động của tim có dấu hiệu bất thường, đồng hồ sẽ gửi những số liệu và cảnh báo sức khoẻ đến người dùng kịp thời.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225649/oppo-watch-41mm-day-silicone-writee5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi giấc ngủ và nhịp tim</h2>\r\n                    <p class = \"paragraph__p\">Oppo watch trang bị nhiều tính năng tiện ích để người dùng có thể dễ dàng theo dõi sức khoẻ của mình một cách chi tiết, chẳng hạn như tính năng theo dõi giấc ngủ mỗi ngày và đo nhịp tim.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225649/oppo-watch-41mm-day-silicone-170220-110240.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi vận động, rèn luyện thể thao</h2>\r\n                    <p class = \"paragraph__p\">Với tính năng “Huấn luyện viên”, Oppo Watch sẽ hướng dẫn người dùng từng động tác để luyện tập các bài tập thể dục được tích hợp sẵn. Người dùng chỉ việc mở ứng dụng và làm theo “Huấn luyện viên” hướng dẫn. Trong quá trình luyện tập, Oppo Watch sẽ theo dõi các chỉ số như nhịp tim, lượng calories tiêu thụ và số bước chân để đo mức độ hiệu quả mỗi buổi tập.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225649/oppo-watch-41mm-day-silicone-den-writeefix111.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chuẩn chống nước 3ATM</h2>\r\n                    <p class = \"paragraph__p\">Với khả năng chống nước ở độ sâu 30m, người đeo có thể an tâm dùng để tắm, bơi lội, tránh những cơn mưa bất chợt. Cần lưu ý khi đeo đồng hồ để bơi, nên để ở chế độ Lock và dùng không quá 30 phút.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225649/oppo-watch-41mm-day-silicone-191620-021659.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thoả sức đam mê âm nhạc</h2>\r\n                    <p class = \"paragraph__p\">Oppo watch có thể kết nối với tai nghe không dây, thoả sức trải nghiệm âm nhạc khi luyện tập thể thao hay thư giãn mỗi ngày.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225649/oppo-watch-41mm-day-silicone-writeefix3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Rất nhiều tính năng tiện ích</h2>\r\n                    <p class = \"paragraph__p\">Gồm: Màn hình luôn hiển thị, dự báo thời tiết, xem phim, chơi game, la bàn, tìm điện thoại,….</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225649/oppo-watch-41mm-day-silicone-writee9.jpg\" alt=\"\">  \r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Oppo Watch 41mm với thiết kế màn hình AMOLED 1.6inch bo tròn 4 góc, dây đeo silicon mềm mại cổ tay cùng các tính năng chăm sóc sức khoẻ như theo dõi nhịp tim, giấc ngủ, rèn luyện thể thao. Oppo Watch hứa hẹn sẽ là mẫu đồng hồ thông minh mang đến nhiều trải nghiệm thú vị cho người dùng trong việc theo dõi sức khoẻ và giải trí hằng ngày.</p><button class =\"paragraph__more\">Xem thêm !</button>', 6, 1);
INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(49, 4, 'Huawei Watch GT2 Pro 46mm dây da', '../assets/image/products/watch/Huawei-Watch-GT2-Pro-46mm-dây-da/Nền.jpg', 8290000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nAMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.6 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 14 ngay\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nGoogle Wear OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nMặt kính nhựa\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n41 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Huawei Watch GT2 Pro 46mm dây da</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229649/Slider/watch-gt2-pro-46mm-day-da-081020-0739530.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kiểu dáng sang trọng, dây đeo da cao cấp</h2>\r\n                    <p class = \"paragraph__p\">Huawei Watch GT2 Pro 46mm dây da là phiên bản nâng cấp của Huawei Watch GT2, trang bị thêm tính năng sạc không dây, cùng với đó là cấu tạo vật liệu cao cấp, khung làm bằng titan, mặt lưng gốm và mặt kính sapphire. Trang bị màn hình AMOLED kích thước 1.39 inch cùng độ phân giải 454 x 454 pixels giúp hình ảnh và thông tin hiển thị rõ ràng và sắc nét. Thiết kế dây đeo làm từ da tạo cảm giác chắc chắn và mềm mại cho cổ tay khi đeo cả ngày.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229649/watch-gt2-pro-46mm-day-da-fix-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kết nối nhanh chóng qua sóng bluetooth</h2>\r\n                    <p class = \"paragraph__p\">Huawei Watch GT2 Pro tương thích với hầu hết điện thoại chạy hệ điều hành Android và iOS trên thị trường hiện nay. Kết nối ổn định cho phép người dùng dễ dàng nghe gọi, kiểm tra tin nhắn, nhận các thông báo ứng dụng (Messenger, Face, Zalo,...), không bỏ lỡ những thông báo quan trọng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229649/watch-gt2-pro-46mm-day-da-fix-2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hiển thị sắc nét, mặt kính sapphire chống trầy</h2>\r\n                    <p class = \"paragraph__p\">Sử dụng tấm nền AMOLED giúp hiển thị hình ảnh và những thông tin đầy đủ, rõ ràng và sắc nét, bên cạnh đó còn tiết kiệm một lượng pin đáng kể cho đồng hồ. Mặt kính Sapphire giúp hạn chế tối đa vết trầy xước và va đập mà người dùng thường gặp phải khi làm việc và luyện tập thể thao. Ngoài ra, với tính năng màn hình luôn hiển thị (Always-on Display), người dùng chỉ cần giơ tay hoặc nhìn lướt qua là có thể xem được thông tin giờ giấc và các thông báo ứng dụng, không cần phải dùng thao tác chạm hay nhấn nút.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229649/watch-gt2-pro-46mm-day-da-thietke-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Dễ dàng tùy chỉnh mặt đồng hồ</h2>\r\n                    <p class = \"paragraph__p\">Để thay đổi mặt trực tiếp trên đồng hồ, bạn chỉ cần chạm và giữ màn hình chính của Huawei Watch GT2 Pro trong khoảng 3 giây, sau đó vuốt sang trái hoặc phải để chọn mặt đồng hồ yêu thích.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229649/watch-gt2-pro-46mm-day-da-fix-4.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Pin dùng được 14 ngày, hỗ trợ sạc không dây</h2>\r\n                    <p class = \"paragraph__p\">Trang bị pin dung lượng 455 mAh cho thời gian sử dụng lên đến 14 ngày, dùng được 30 giờ khi bật chế độ GPS liên tục và mất 2 giờ để sạc đầy pin. Ngoài ra còn hỗ trợ tính năng sạc không dây (5 phút sạc nhanh có thể dùng được 10 giờ) và có thể sạc trực tiếp từ điện thoại Huawei tương thích với thiết bị.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229649/watch-gt2-pro-46mm-day-da-fix-11.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi sức khỏe đều đặn, liên tục</h2>\r\n                    <p class = \"paragraph__p\">Tính năng theo dõi nhịp tim sử dụng cảm biến nhịp tim TruSeen 4.0+, theo dõi nhịp tim cùng các chỉ số SpO2 (nồng độ oxy trong máu) và VO2max liên tục, thống kê các dữ liệu quan trọng về hoạt động của tim đồng thời đưa ra cảnh báo khi phát hiện tim có dấu hiệu bất thường. Tính năng theo dõi giấc ngủ sẽ phân tích kỹ các giai đoạn ngủ, nhịp thở khi ngủ, chấm điểm giấc ngủ và đưa ra các đề xuất để cải thiện giấc ngủ, giúp người dùng ngủ ngon hơn.Ghi chú: VO2max chỉ có thể được đo khi chạy ngoài trời.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229649/watch-gt2-pro-46mm-day-da-fix-6.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nhắc nhở sức khỏe người dùng</h2>\r\n                    <p class = \"paragraph__p\">Huawei Watch GT2 Pro sẽ theo dõi hoạt động của người dùng, nhắc nhở khi phát hiện ngồi quá lâu, cảnh báo mức căng thẳng và đưa ra các bài tập hít thở, giúp người dùng giải tỏa căng thẳng, lấy lại phong độ để làm việc hiệu quả hơn. Ngoài ra còn hỗ trợ cho nữ giới với tính năng theo dõi chu kỳ kinh nguyệt, nhắc nhở thời gian tới kì dựa theo dữ liệu người đeo đã nhập trên ứng dụng Huawei Health.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229649/watch-gt2-pro-46mm-day-da-fix-7.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chế độ tập luyện chuyên nghiệp, hiệu quả</h2>\r\n                    <p class = \"paragraph__p\">Hỗ trợ hơn 100 chế độ tập luyện thể thao, gồm 17 chế độ chuyên nghiệp và 85 chế độ luyện tập khác. Các tính năng mới gồm chế độ trượt tuyết giúp phân tích toàn diện khi luyện tập (nhịp tim, tốc độ hiện tại, độ dốc tối đa, đường đi và khoảng cách đi được) và chế độ đánh golf giúp ghi lại các tư thế đánh golf, tốc độ và tỉ lệ vung gậy.Ngoài ra còn có tính năng Route Back (quay lại điểm bắt đầu), ghi lại lộ trình di chuyển, cho phép người dùng quay trở lại điểm xuất phát và hướng dẫn tốc độ chạy phù hợp dựa theo định vị GPS và GLONASS.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229649/watch-gt2-pro-46mm-day-da-fix-8.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Các tiện ích khác </h2>\r\n                    <p class = \"paragraph__p\">Bao gồm: Tìm điện thoại, điều khiển chụp ảnh, la bàn, đồng hồ bấm giờ, dự báo thời tiết, đếm bước chân,...</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229649/watch-gt2-pro-46mm-day-da-fix-10.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Huawei Watch GT2 Pro 46mm dây da trang bị màn hình tròn AMOLED 1.39 inch cho hình ảnh hiển thị rõ nét cùng các tính năng sức khỏe như đo nhịp tim, các chỉ số SpO2 và VO2max, theo dõi giấc ngủ, chu kỳ kinh nguyệt, nhắc nhở người dùng, đặc biệt là hỗ trợ hơn 100 chế độ thể thao chuyên nghiệp. Huawei Watch GT2 Pro không những mang nhiều tính năng thông minh mà còn là mẫu đồng hồ tuyệt vời để chăm sóc và rèn luyện sức khỏe người dùng mỗi ngày.</p><button class =\"paragraph__more\">Xem thêm !</button>', 3, 1),
(50, 4, 'Apple Watch S5 44mm viền nhôm dây cao su đen', '../assets/image/products/watch/Apple-Watch-S5-44mm-viền-nhôm-dây-cao su-đen/Nền.jpg', 11490000, 0, 5, 2, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.78 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 1.5 ngay\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nưatchOS 6.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 13 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nMặt kính nhựa\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n44 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Apple Watch S5 44mm viền nhôm dây cao su đen</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/Slider/apple-watch-s5-44mm-vien-nhom-day-cao-su-011020-0952570.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình OLED Retina không bao giờ tắt</h2>\r\n                    <p class = \"paragraph__p\">Tính năng màn hình luôn hiển thị (Always-on) giúp bạn xem giờ và thông báo tiện lợi bất cứ lúc nào. Khi không quan sát, đồng hồ sẽ tự giảm độ sáng xuống để tiết kiệm pin.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-11.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Xác định phương hướng bằng la bàn</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Apple Watch S5 có tính năng định vị GPS giúp định vị chính xác quãng đường đi khi tập, từ đấy cung cấp thông tin về lộ trình trong quá trình luyện tập cho bạn.Điểm cải tiến ở Apple Watch S5 so với các thế hệ trước đó là la bàn từ tính để xác định phương hướng. Tính năng này sẽ hữu ích đối với những người dùng thích đi chạy bộ trong rừng, giúp định vị phương hướng ở nơi không thể truy cập mạng hay GPS không thể định vị được. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Tính năng gọi cứu hộ khẩn cấp có sẵn ở nhiều khu vực hơn</h2>\r\n                    <p class = \"paragraph__p\">Tính năng gọi cứu hộ khẩn cấp có hỗ trợ ở Việt Nam, và đặc biệt hữu ích với ai thường xuyên đi du lịch. Apple Watch sẽ định vị bạn đang ở đâu và gọi đến cứu hộ của địa phương đó khi bật SOS. Apple có ghi chú rõ các địa phương không được hỗ trợ, tham khảo Tại đây.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su4.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hỗ trợ Apple Music</h2>\r\n                    <p class = \"paragraph__p\">Apple Watch thế hệ mới hỗ trợ kho nhạc số Apple Music với hơn 50 triệu bài hát có sẵn. Bạn có thể nghe nhạc trực tiếp trên đồng hồ bằng loa ngoài trong trường hợp không có loa, điện thoại kế bên.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Phát hiện té ngã</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ có tính năng phát hiện khi bạn bị ngã và bất động 15 giây, nó sẽ phát tín hiệu SOS đến số được cài sẵn. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su9.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Đo nhịp tim liên tục</h2>\r\n                    <p class = \"paragraph__p\">Cảm biến điện tử đo nhịp tim một cách chính xác, bạn có thể liếc nhanh để kiểm tra tình trạng nhịp tim của mình.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su-7.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế sang trọng</h2>\r\n                    <p class = \"paragraph__p\">Apple Watch S5 44mm viền nhôm dây cao su có thiết kế hầu như không có gì thay đổi so với Series S4, vẫn là mặt đồng hồ sapphire tinh xảo, viền nhôm được vát gọt tinh tế và dây cao su dễ dàng thay đổi. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su-6.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nhắc nhở sức khỏe</h2>\r\n                    <p class = \"paragraph__p\">Bên cạnh tính năng đo nhịp tim, đồng hồ còn giúp ích cho sức khỏe của bạn với các tính năng như: Nhắc khi ngồi quá lâu, nhắc nhở tập luyện, kiểm soát căng thẳng mệt mỏi khi làm việc quá lâu.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su8.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Các chế độ luyện tập</h2>\r\n                    <p class = \"paragraph__p\">Apple Watch S5 tích hợp nhiều bộ môn tập luyện như chạy bộ, boxing, đạp xe,... Đồng hồ còn đo lường chính xác được lượng calories tiêu thụ đối với các môn thể thao, giúp bạn điều chỉnh chế độ tập luyện phù hợp. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212770/apple-watch-s5-44mm-vien-nhom-day-cao-su13-saochep.jpg\" alt=\"\"><button class =\"paragraph__more\">Xem thêm !</button>', 1, 1),
(51, 4, 'Kidcare 08S', '../assets/image/products/watch/Kidcare-08S/Nền.jpg', 1390000, 0, 5, 4, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nMặt kính nhựa\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n33 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 12 tiếng (sử dụng liên tục), Khoảng 1 ngày\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Độ dài dây:</span>\r\n<span class=\"content__row-content\">\r\n24 cm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu dây:</span>\r\n<span class=\"content__row-content\">\r\nSilicone\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Độ rộng dây:</span>\r\n<span class=\"content__row-content\">\r\n1.9 cm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nLBS, Wifi Location, Hỗ trợ Nano Sim, GPS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Tiện ích:</span>\r\n<span class=\"content__row-content\">\r\n	Định vị trẻ em, Có nghe gọi, Lộ trình đường đi, Cuộc gọi khẩn cấp SOS, Cài đặt vùng an toàn, Chặn số lạ gọi đến, Chụp ảnh, Báo thức\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Đồng hồ định vị trẻ em Kidcare 08S</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225569/Slider/kidcare-08s-040720-0116520.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màu sắc trẻ trung, năng động</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh trẻ em Kidcare 08S có thiết kế màn hình 1.3 Inch, độ phân giải 240 x 240 Pixels cùng với dây đeo làm từ silicone mang đến cảm giác êm ái khi đeo. Tính năng nổi trội ở mẫu đồng hồ này là khả năng định vị chuẩn xác, cảnh báo vùng nguy hiểm và gọi điện khẩn cấp, giúp phụ huynh của bé có thể yên tâm theo dõi bé từ xa.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225569/kidcare-08s-1-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Pin dùng khoảng 1 ngày, 12 tiếng khi sử dụng liên tục</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ Kidcare 08S trang bị pin dung lượng 450 mAh dùng được trong 1 ngày và có thể sạc đầy lại trong 2 giờ. Kết nối qua mạng 2G/3G và Wifi, tương thích với hệ điều hành iOS 10 trở lên, Android 4.3 trở lên.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225569/kidcare-08s-2-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Khả năng định vị chính xác và cảnh báo SOS</h2>\r\n                    <p class = \"paragraph__p\">Nhờ vào tính năng định vị GPS, phụ huynh của bé có thể an tâm quan sát bé di chuyển, ngoài ra phụ huynh có thể tuỳ chỉnh những khu vực hoặc địa điểm cảm thấy không an toàn để đồng hồ Kidcare 08S có thể cảnh báo kịp thời khi bé lại gần những khu vực đó.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225569/kidcare-08s-3-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Tạo bản đồ lộ trình đường đi cho bé</h2>\r\n                    <p class = \"paragraph__p\">Khi kết nối đồng hồ với điện thoại, phụ huynh bé có thể tạo lộ trình đường đi cho bé, ví dụ như nhờ bé đi đến cửa hàng mua sắm hay đến trường học, không sợ bị lạc mà còn tập cho bé tính tự lập. Ngoài ra còn có thể xem lại lịch sử quá trình di chuyển của bé trong ngày.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225569/kidcare-08s-4-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Lưu giữ những bức hình đáng nhớ</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Kidcare 08S trang bị camera trước có độ phân giải 0.08Mp và bộ nhớ dung lượng 32mb giúp bé có thể chụp ảnh selfie cùng với bạn bè và lưu giữ những khoảnh khắc vui vẻ bên gia đình.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225569/kidcare-08s-5-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Loại bỏ số lạ, bảo vệ an toàn cho bé </h2>\r\n                    <p class = \"paragraph__p\">Tính năng này chỉ cho phép những số điện thoại nằm trong danh bạ đã lưu mới có thể liên lạc với bé, còn những số chưa lưu sẽ vào danh sách \"kẻ lạ\" và chiếc đồng hồ này sẽ chặn để tránh làm ảnh hưởng đến quá trình học tập, đời sống hằng ngày của bé.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225569/kidcare-08s-6.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nhiều tính năng hữu ích cho bé</h2>\r\n                    <p class = \"paragraph__p\">Nhiều tính năng hữu ích cho bé</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225569/kidcare-08s-7.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh trẻ em Kidcare 08S với thiết kế trẻ trung, năng động, màn hình 1.3 inch hiển thị đầy đủ thông tin, dây đeo silicone tạo cảm giác thoải mái khi đeo cùng với khả năng định vị chuẩn xác, cảnh báo vùng nguy hiểm, các bậc phụ huynh có thể yên tâm quan sát hành trình và bảo vệ an toàn cho con khỏi những mối nguy hiểm hằng ngày.</p><button class =\"paragraph__more\">Xem thêm !</button>', 11, 1),
(52, 4, 'Huami Amazfit GTS vàng', '../assets/image/products/watch/Huawei-Amazfit-GTS-vàng/Nền.jpg', 2541000, 0, 5, 5, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nAMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.6 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 14 ngay\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nGoogle Wear OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nMặt kính nhựa\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n41 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Đồng hồ thông minh Huami Amazfit GTS vàng</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/223747/Slider/huami-amazfit-gts-vang-011020-1017370.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế thể thao và cá tính</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Huami Amazfit GTS phiên bản màu vàng với thiết kế nhiều điểm giống với Apple Watch, màn hình 1.65 inch cùng độ phân giải là 348 x 442 pixels giúp hiển thị thông tin, hình ảnh rõ ràng và sắc nét. Đi kèm theo là dây đeo silicone 20mm mang lại cảm giác vô cùng mềm mại và dễ chịu khi đeo.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/223747/huami-amazfit-gts-vang-1-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Mặt kính cường lực và hiển thị rõ nét</h2>\r\n                    <p class = \"paragraph__p\">Mặt kính kính cường lực Gorilla Glass 3 giúp chống trầy và tăng độ bền cho thiết bị. Màn hình sử dụng tấm nền AMOLED Super Retina có mật độ điểm ảnh 341 ppi cực kì sắc nét và gam màu 100% NTSC (National Teltevision System Committee) cho màu sắc cực kì trung thực.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/223747/huami-amazfit-gts-vang-3-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hơn 100 giao diện mặt đồng hồ phong cách</h2>\r\n                    <p class = \"paragraph__p\">Huami cung cấp hơn 100 giao diện mặt đồng hồ, người dùng có thể thoải mái lựa cho giao diện phù hợp với phong cách của mình. Để có thể thay đổi mặt đồng hồ, người dùng cần cài ứng dụng Amazfit Watch Face và kết nối điện thoại với đồng hồ để tuỳ chỉnh.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/223747/huami-amazfit-gts-vang-5-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Always-on Display - Màn hình luôn bật</h2>\r\n                    <p class = \"paragraph__p\">Amazfit GTS trang bị tính năng màn hình Always-on Display, màn hình luôn luôn hiển thị thời gian và những thông báo trên màn hình nhưng vẫn đảm bảo tiết kiệm pin cho thiết bị. Người dùng chỉ cần liếc ngang qua là có thể xem được, không cần phải dùng thao tác chạm màn hình hay mở khoá lên để xem.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/223747/huami-amazfit-gts-vang-6-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kết nối nhanh chóng</h2>\r\n                    <p class = \"paragraph__p\">Khi kết nối với điện thoại thông minh, Amazfit GTS giúp người dùng nhanh chóng kiểm tra tin nhắn, các cuộc gọi, theo dõi sức khoẻ, luyện tập thể thao, xem thông báo ứng dụng Zalo, Messenger, Line, Viber,...</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/223747/huami-amazfit-gts-vang-2-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thời lượng pin lên đến 7 ngày sử dụng</h2>\r\n                    <p class = \"paragraph__p\">Dù chỉ trang bị pin dung lượng 220mAh, nhưng Amazfit GTS có thể sử dụng được đến 7 ngày sử dụng trong một lần sạc, thoải mái sử dụng cả tuần mà không lo hết pin.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/223747/huami-amazfit-gts-vang-011820-101857.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nhiều tính năng chăm sóc người dùng</h2>\r\n                    <p class = \"paragraph__p\">Trang bị cảm biến BioTracker™ PPG giúp theo dõi nhịp tim liên tục và theo dõi giấc ngủ đều đặn. Amazfit GTS sẽ đưa ra các số liệu chính xác và cảnh báo sức khoẻ người dùng khi phát hiện nhịp tim có dấu hiệu bất thường. Ngoài ra còn có tính năng đánh giá sức khoẻ - Huami-PAI™ Health, tính năng này sẽ theo dõi mọi hoạt động người dùng trong ngày, nhắc nhở người dùng giành thời gian thư giãn và rèn luyện thể thao.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/223747/huami-amazfit-gts-vang-7-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">12 chế độ luyện tập </h2>\r\n                    <p class = \"paragraph__p\">Amazfit GTS hỗ trợ đến 12 môn thể thao phổ biến hiện nay, từ những môn trong nhà như chạy bộ, đi xe đạp, nâng tạ,… đến các môn ngoài trời như bơi lội, đi bộ, leo núi,… Được tích hợp sẵn định vị GPS+GLONASS, giúp mẫu đồng hồ này theo dõi chính xác các hoạt động luyện tập thể thao của người dùng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/223747/huami-amazfit-gts-vang-8-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Điều khiển phát nhạc tiện lợi</h2>\r\n                    <p class = \"paragraph__p\">Amazfit GTS khi kết nối với điện thoại thông minh, tai nghe không dây hoặc loa bluetooth, người dùng có thể thoải mái điều khiển phát nhạc theo danh sách yêu thích, chuyển bài và tăng chỉnh âm lượng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/223747/huami-amazfit-gts-vang-9-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nhiều tính năng tiện lợi khác</h2>\r\n                    <p class = \"paragraph__p\">Bao gồm các tính năng: Đồng hồ bấm giờ, đếm số bước, báo thức, rung thông báo, xem lịch, dự báo thời tiết, ...</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/223747/huami-amazfit-gts-vang-063720-103717.jpg\" alt=\"\">  \r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Huami Amazfit GTS phiên bản màu vàng có màn hình AMOLED 1.65 inch cho hình ảnh sắc nét cùng với dây đeo silicone tạo cảm giác êm ái khi đeo và nhiều tính năng sức khoẻ như đo nhịp tim, theo dõi giấc ngủ, nhắc nhở vận động, đánh giá sức khoẻ, chế độ luyện tập. Huami Amazfit GTS sẽ là một thiết bị tuyệt vời trong việc chăm sóc và rèn luyện sức khoẻ của bạn mỗi ngày.</p>          \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 14, 1),
(53, 4, 'Suunto 7 Dây silicone', '../assets/image/products/watch/Suunto-7-Dây-silicone/Nền.jpg', 14590000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nAMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.9 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 2 ngay\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nGoogle Wear OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nMặt kính nhựa\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n41 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Đồng hồ thông minh Suunto 7 Dây silicone</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226297/Slider/suunto-7-day-silicon-den-270720-014701-618.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình AMOLED, dây đeo silicone</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Suunto 7 Dây silicone phiên bản màu đen với mặt kính cường lực hình tròn, màn hình AMOLED 1.97 inch với độ phân giải 454 x 454 pixels cùng độ sáng lên tới 1000 nits, mức độ sáng này sẽ thay đổi dựa trên điều kiện ánh sáng của môi trường xung quanh, nhờ vậy có thể hiển thị tốt dưới ánh sáng mặt trời. Dây đeo làm từ silicone tạo cảm giác mềm mại và dễ chịu khi đeo.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226297/suunto-7-day-silicon-den-274220-084229.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kết nối với điện thoại nhanh chóng</h2>\r\n                    <p class = \"paragraph__p\">Kết nối với điện thoại qua cổng bluetooth, Suunto 7 tương thích với điện thoại chạy hệ điều hành Android 5.0 trở lên hoặc iOS 10 trở lên. Sau khi kết nối, người dùng có thể xem tin nhắn, cuộc gọi, nhận thông báo từ các ứng dụng (Facebook, Messenger, Instagram…), sử dụng cửa hàng CH play, rèn luyện thể thao,…</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226297/suunto-7-day-silicon-den-274320-084306.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hệ điều hành WearOS mạnh mẽ</h2>\r\n                    <p class = \"paragraph__p\">Suunto 7 là chiếc đồng hồ GPS đầu tiên của hãng chạy WearOS, với hệ điều hành này, người dùng có thể sử dụng các tính năng sẵn có như: Google Assistant, Google Pay, Google Play, Google Fit, tiện lợi trong việc hỗ trợ người dùng sử dụng, giúp người dùng thanh toán nhanh, theo dõi sức khoẻ và hoạt động hằng ngày.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226297/suunto-7-day-silicon-den-274320-084301.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thỏa sức thay đổi phong cách</h2>\r\n                    <p class = \"paragraph__p\">Suunto 7 đi kèm theo 4 giao diện mặt đồng hồ được cài sẵn (Heatmap, Original, Marine và Rose). Ngoài ra người dùng có thể tải thêm giao diện trên cửa hàng Google Play. Chạm và giữ giữa màn hình để xem danh sách các mặt đồng hồ, vuốt sang trái hoặc phải để chọn, sau đó nhấn chọn mặt đồng hồ bạn muốn sử dụng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226297/suunto-7-day-silicon-den-274220-084237.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thời lượng pin 2 ngày, 1.5 giờ để sạc đầy</h2>\r\n                    <p class = \"paragraph__p\">Trang bị viên pin với dung lượng 450mAh cho phép người đeo dùng được khoảng 2 ngày, dùng được 12 giờ khi bật chế độ GPS, dùng được đến 40 ngày khi bật chế độ tiết kiệm pin (chỉ dùng để xem giờ). Tiết kiệm pin hơn ở chế độ màn hình luôn hiển thị, chế độ này chỉ sử dụng một lượng nhỏ năng lượng để có thể hiển thị các thông tin cho người dùng khi tập luyện thể thao, người dùng không cần thao tác chạm để mở màn hình.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226297/suunto-7-day-silicon-den-274220-084242.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình luôn hiển thị, thao tác nhanh chóng</h2>\r\n                    <p class = \"paragraph__p\">Một điểm hơi thiếu sót ở Suunto 7 là tính năng màn hình luôn bật, người đeo cần phải lật cổ tay hoặc sử dụng nút bấm để đánh thức chiếc đồng hồ này, hoàn toàn khác với các sản phẩm trang bị tấm nền AMOLED từ năm 2019 đến nay.- Nếu muốn truy cập nhanh vào các cài đặt cho đồng hồ, chỉ cần vuốt mặt đồng hồ từ trên xuống.- Nhấn nút phía trên bên trái để mở trình khởi chạy ứng dụng, cho phép bạn bắt đầu các ứng dụng bạn đã tải xuống hoặc được cài đặt sẵn.- Khi vuốt mặt đồng hồ từ dưới lên, bạn sẽ xem được các thông báo ứng dụng trên điện thoại của mình.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226297/suunto-7-day-silicon-den-274220-084233.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi nhịp tim trên cổ tay</h2>\r\n                    <p class = \"paragraph__p\">Trang bị cảm biến nhịp tim quang học ở mặt sau đồng hồ cùng thiết kế dây đeo ôm trọn cổ tay, đảm bảo quá trình đo nhịp tim được liên tục và đạt độ chính xác cao. Tính năng theo dõi giấc ngủ sẽ không có sẵn trên Suunto 7, người dùng phải tải xuống ứng dụng của “bên thứ 3” mới nhận được dữ liệu về giấc ngủ.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226297/suunto-7-day-silicon-den-274220-084247.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">70 môn thể thao chuyên dụng </h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Suunto 7 hỗ trợ đến 70 môn thể thao, một con số hết sức ấn tượng. Tuy là chạy hệ điều hành WearOS nhưng Suunto đã khéo léo gom tất cả những tính năng thể thao chuyên dụng vào một ứng dụng có tên là Suunto Smartphone, ngoài ra còn có thể xem lịch sử tập luyện và hoạt động hàng ngày trên ứng dụng này.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226297/suunto-7-day-silicon-den-274220-084256.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chăm sóc sức khỏe toàn diện</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Suunto 7 hỗ trợ chăm sóc sức khỏe của người dùng một cách toàn diện như theo dõi giấc ngủ, tính lượng calories tiêu thụ, đếm số bước chân.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226297/suunto-7-day-silicon-den-274220-084252.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nhiều tính năng khác</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Suunto 7 với màn hình AMOLED 1.97 inch, mặt kính cường lực, dây đeo silicon mềm mại cho cổ tay cùng với các tính năng như đo nhịp tim, hỗ trợ đo đạc 70 môn thể thao chuyên dụng, theo dõi sức khoẻ, thanh toán không dây nhanh chóng. Suunto 7 hứa hẹn sẽ là mẫu đồng hồ thể thao không thể thiếu đối với các tín đồ công nghệ thích chăm sóc sức khỏe và rèn luyện thân thể mỗi ngày.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/226297/suunto-7-day-silicon-den-155520-085510.jpg\" alt=\"\">  \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 12, 1),
(54, 4, 'Garmin Lily dây silicone trắng', '../assets/image/products/watch/Garmin-Lily-dây-silicone-trắng/Nền.jpg', 4990000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nLCD\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 5 ngay\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nGoogle Wear OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nMặt kính nhựa\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n41 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Đồng hồ thông minh Garmin Lily dây silicone trắng</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235503/Slider/vi-vn-garmin-lily-day-silicone-trang-11.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế thời trang, nữ tính thanh lịch</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Garmin Lily có thiết kế nhỏ gọn với đường kính mặt chỉ 35 mm và trọng lượng nhẹ 24g. Chất liệu khung viền được làm từ polyme bền chắc chịu sự va chạm tốt. Dây deo silicone mềm dẻo, đeo tay êm ái và ôm gọn cổ tay có độ dài dây 10 cm, độ rộng dây thanh mảnh 1,4 cm. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235503/garmin-lily-day-silicone-trang1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Mặt hình LCD hiển thị sắc nét</h2>\r\n                    <p class = \"paragraph__p\">Sử dụng công nghệ màn hình LCD có kích thước 1 inch và độ phân giải 240 x 201 pixels cho hiển thị sắc nét giúp dễ dàng quan sát thông tin trên màn hình. Mặt kính cường lực Gorilla Glass 3 trong suốt có khả năng chống trầy và chịu sức va chạm cao.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235503/garmin-lily-day-silicone-trang2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kết nối bluetooth với điện thoại thông minh</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ có thể kết nối với điện thoại thông minh đang sử dụng hệ điều hành iOS và Android qua cổng bluetooth. Khi kết nối người dùng dễ dàng đọc trả lời tin nhắn, từ chối cuộc gọi, nhận thông báo từ các ứng dụng (Facebook, Zalo, Messenger, Viber,...) ngay trên chiếc đồng hồ.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235503/garmin-lily-day-silicone-trang4.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Dung lượng Pin 5 ngày sử dụng - Thỏa sức trải nghiệm</h2>\r\n                    <p class = \"paragraph__p\">Thoải mái tập luyện với những bài tập cần nhiều thời gian như: Chạy bộ, đi bộ, đạp xe, leo núi,... hoặc những chuyến du lịch dài ngày thì đồng hồ thông minh Garmin này sẽ là lựa chọn tuyệt vời cho bạn. Bởi dung lượng pin kéo dài đến 5 ngày sử dụng và chế độ sạc nhanh chỉ mất 1.5 tiếng để sạc đầy</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235503/garmin-lily-day-silicone-trang3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Tận hưởng nhiều bài tập thể thao cùng chuyên gia</h2>\r\n                    <p class = \"paragraph__p\">Khi sử dụng đồng hồ thể thao chuyên nghiệp Garmin, bạn có thể nhận được nhiều bài tập thể thao chuyên nghiệp: chạy bộ, đi bộ, đạp xe, bơi lội,... cùng chuyên gia huấn luyện viên Garmin, cùng với gợi ý cho chế ăn uống dinh dưỡng hợp lý để mang đến cho mang sức khỏe tốt nhất. Để nhận các gợi ý bài tập cùng với chế độ dinh dưỡng, bạn cần tải ứng dụng Garmin Connect. Ứng dụng thích hợp trên cả 2 hệ điều hành iOS và Android: Tải trên Appstore | Tải trên Google Play.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235503/garmin-lily-day-silicone-trang9.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Đo lượng pin cơ thể hàng ngày cùng chế độ độc quyền Body Batery</h2>\r\n                    <p class = \"paragraph__p\">Ở các thiết bị đồng hồ thông minh Garmin, người dùng sẽ được trải nghiệm tính năng độc quyền đo năng lượng cơ thể Body Batery của Garmin. Tính năng này sẽ cho thấy năng lượng tiêu thụ và năng lượng nạp vào cơ thể hằng ngày của bạn bằng cách thông qua các chỉ số: đo nhịp tim, số bước đi, quãng đường di chuyển, thời gian nghỉ ngơi,...</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235503/garmin-lily-day-silicone-trang8.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi sức khỏe toàn diện</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Garmin Lily được thiết kế mặt sau có cảm biến, khi tiếp xúc với da cổ tay sẽ đo các chỉ số báo sức khỏe cơ thể: Đo nhịp tim, nồng độ oxy trong máu (SpO2), lượng oxy tiêu thụ tối đa (VO2 max),... Các chỉ số này sẽ rất quan trọng cho việc cho người dùng biết ngưỡng luyện tập thể thao của mình để cải thiện thêm sau những buổi tập.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235503/garmin-lily-day-silicone-trang6.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chia sẻ vị trí khi gặp sự cố</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ đo nhịp tim khi phát hiện sự cố bất thường như: nhịp tim tăng cao đột ngột, giảm đột ngột và tự động định vị GPS sau đó sẽ gửi vị trí đến số điện thoại người thân của bạn mà bạn đã cài đặt trước đó.Chức năng này thật sự hữu ích khi bạn cần sự trợ giúp của người thân khi gặp sự cố trong những trường hợp: tập thể dục một mình, đi du lịch xa nhà,... </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235503/garmin-lily-day-silicone-trang12.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chăm sóc sức khỏe chuyên sâu</h2>\r\n                    <p class = \"paragraph__p\">Giúp những bạn nữ theo dõi chu kỳ kinh nguyệt, sử dụng ứng dụng Garmin Connect và nhập các dữ liệu cần thiết của bạn, đồng hồ thông minh sẽ phân tích và ghi lại các triệu chứng thể chất, chế độ dĩnh dưỡng để thông báo và gợi ý sức khỏe cho bạn trong mỗi giai đoạn của chu kỳ.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235503/garmin-lily-day-silicone-trang5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thỏa sức trải nhiệm thêm nhiều tiện ích khác</h2>\r\n                    <p class = \"paragraph__p\">Không chỉ dừng lại ở các tiện ích chăm sóc sức khỏe, đồng hồ Garmin Lily còn được tích hợp thêm nhiều tích năng hữu dụng khác để cho người dùng có thêm nhiều trải nghiệm thú vị khác ngay trên chiếc đồng hồ của mình: Tìm điện thoại, đồng hồ báo thức, dự báo thời tiết, đồng hồ bấm giờ, điều khiển nhạc, la bàn,...</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235503/garmin-lily-day-silicone-trang10.jpg\" alt=\"\">  \r\n                    <p class = \"paragraph__p\">Bên cạnh thiết kế nhỏ gọn, thời trang nữ tính thanh lịch pha trộn một chút phong cách năng động thể thao, đồng hồ thông mình Garmin Lily sở hữu nhiều tính năng nổi bật theo dõi và chăm sóc sức khỏe toàn diện. Đây sẽ là sản phẩm đồng hồ phù hợp cho các bạn nữ có lối sống khoa học và lành mạnh.</p>    \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 13, 1);
INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(55, 4, 'Garmin Lily dây silicone tím', '../assets/image/products/watch/Garmin-Lily-dây-silicone-tím/Nền.jpg', 4990000, 0, 5, 2, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nLCD\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 5 ngay\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nGoogle Wear OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nKính cường lực Gorilla Glass 3\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n41 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Đồng hồ thông minh Garmin Lily dây silicone tím</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235402/Slider/garmin-lily-day-silicone-220221-0444537.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế nữ tính, sang trọng với phong cách cổ điển</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ Garmin Lily có khung viền polyme bền chắc, chịu va đập tốt cùng dây silicone êm nhẹ, thoải mái khi đeo. Sở hữu thiết kế nhỏ nhắn với đường kính mặt 35 mm cùng trọng lượng khoảng 24g, chiếc đồng hồ này rất thích hợp với các bạn nữ, đặc biệt khi đeo chiếc đồng hồ này khi tập luyện thể thao.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235402/garmin-lily-day-silicone-thietke.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Dễ dàng quan sát thông tin với màn hình LCD 1 inch</h2>\r\n                    <p class = \"paragraph__p\">Màn hình LCD 1 inch cùng độ phân giải 240 x 201 pixels mang lại khả năng hiển thị sắc nét, rõ ràng. Mẫu đồng hồ này còn được trang bị kính cường lực Gorilla Glass 3 trong suốt, chịu va đập tốt, hạn chế nứt vỡ trước những va chạm thông thường.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235402/garmin-lily-day-silicone7.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kết nối Bluetooth với hệ điều hành iOS và Android </h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ có thể kết nối tai nghe không dây cũng như điện thoại chạy hệ điều hành Android và iOS qua cổng Bluetooth, người dùng thoải mái kiểm tra tin nhắn, từ chối cuộc gọi, xem thông báo ứng dụng (Facebook, Messenger, Zalo, Viber,...) ngay trên chiếc đồng hồ của mình.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235402/garmin-lily-day-silicone2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thời lượng pin khoảng 5 ngày cho bạn thỏa sức trải nghiệm</h2>\r\n                    <p class = \"paragraph__p\">Người dùng thoải mái đeo mẫu đồng hồ Garmin Lily này khi vui chơi, làm việc và học tập với thời lượng pin lên đến 5 ngày. Chỉ mất khoảng 1.5 giờ để sạc đầy bằng đế sạc từ gọn nhẹ, cho bạn thoải mái mái trải nghiệm với chiếc đồng hồ của mình.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235402/pin-fix.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Tính năng theo dõi năng lượng cơ thể - Body Battery</h2>\r\n                    <p class = \"paragraph__p\">Garmin đem đến tính năng đo lượng pin cơ thể (Body Battery), đồng hồ sẽ phân tích sự biến thiên nhịp tim, mức độ căng thẳng,... từ đó tính toán được năng lượng nạp vào và tiêu hao trong ngày giúp bạn có thể tìm ra thời gian tốt nhất cho hoạt động và nghỉ ngơi.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235402/garmin-lily-day-silicone1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chức năng theo dõi chu kì kinh nguyệt dành cho nữ giới</h2>\r\n                    <p class = \"paragraph__p\">Sử dụng ứng dụng Garmin Connect để theo dõi chu kỳ kinh nguyệt của bạn. Người dùng chỉ cần nhập dữ liệu chi tiết về chu kỳ của mình, chiếc đồng hồ sẽ ghi lại các triệu chứng thể chất và cảm xúc và tìm hiểu về luyện tập và dinh dưỡng trong mỗi giai đoạn của chu kỳ. Từ đó đưa ra các dự đoán về chu kỳ và khả năng sinh sản cũng như lời nhắc về chu kỳ của bản thân.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235402/kinhnguyet-fix.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Garmin Coach hỗ trợ luyện tập bài bản, chuyên nghiệp hơn</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ sẽ hỗ trợ bạn rèn luyện thể lực tốt hơn với nhiều chế độ luyện tập như: yoga, gym và pilates,... Người dùng có thể truy cập Garmin Connect để tải thêm bài tập mới hoặc chỉnh sửa bài tập cho phù hợp với bản thân, nâng cao sức khỏe của mình.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235402/thethao-fix.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chăm sóc sức khỏe người dùng tốt hơn</h2>\r\n                    <p class = \"paragraph__p\">Chức năng đo nhịp tim trên cổ tay giúp bạn đánh giá được tình trạng sức khỏe và cường độ vận động, từ đó đưa ra những cảnh báo khi thấy có dấu hiệu bất thường. Điểm đặc biệt của chiếc đồng hồ này chính là chức năng đo nồng độ oxy trong máu (SpO2) và lượng oxy tiêu thụ tối đa (VO2 max) cho bạn đánh giá được ngưỡng tập luyện của bản thân, cũng như có những chế độ tập luyện, nghỉ ngơi thích hợp để tim hoạt động tốt nhất.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235402/garmin-lily-day-silicone11.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chia sẻ vị trí của bạn khi gặp sự cố</h2>\r\n                    <p class = \"paragraph__p\">Khi điện thoại và đồng hồ được ghép nối, nếu bạn gặp sự cố khi đang hoạt động thể thao ngoài trời, đồng hồ thông minh này sẽ gửi vị trí của bạn tới số điện thoại liên lạc đã được cài đặt trước đó. Chức năng này hỗ trợ theo dõi và bảo vệ bạn trước những chuyến đi phượt hay lúc tập luyện thể thao một mình.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235402/tenga-fix.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Trải nghiệm nhiều hơn với các tiện ích khác</h2>\r\n                    <p class = \"paragraph__p\">Ngoài những chức năng trên, đồng hồ còn được tích hợp thêm nhiều tiện ích như: La bàn, dự báo thời tiết, tìm điện thoại, tính lượng calories tiêu thụ, báo thức, đồng hồ bấm giờ, điều khiển nhạc trên đồng hồ, đếm số bước chân,... hỗ trợ người dùng thuận tiện hơn trong các hoạt động thường ngày, cũng như trải nghiệm thú vị với chiếc đồng hồ của mình.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235402/garmin-lily-day-silicone8.jpg\" alt=\"\">  \r\n                    <p class = \"paragraph__p\">Bên cạnh thiết kế nhỏ gọn đầy nhẹ nhàng, các tiện ích chăm sóc sức khỏe và luyện tập thể thao, chiếc đồng hồ thông minh Garmin Lily dây silicone này còn sở hữu tính năng theo dõi chu kì kinh nguyệt và đo lượng pin cơ thể, giúp người dùng, đặc biệt là phái nữ có một chế độ sống khoa học, lành mạnh hơn. Ngoài ra, mẫu đồng hồ này cũng là món phụ kiện thời trang đáng tin cậy, giúp bạn thể hiện phong cách của bản thân.</p>   \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 13, 1),
(56, 4, 'Apple Watch S6 40mm viền nhôm dây cao su (Product RED)', '../assets/image/products/watch/Apple-Watch-S6-40mm-viền-nhôm-dây-cao-su-(Product RED)/Nền.jpg', 11790000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.57 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 15 ngay\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nGoogle Wear OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nIon-X strengthened glass\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n4 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Apple Watch S6 40mm viền nhôm dây cao su (Product RED)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/Slider/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-221020-1220110.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Giữ vững nét sang trọng và hiện đại qua từng thế hệ</h2>\r\n                    <p class = \"paragraph__p\">Chiếc Apple Watch S6 40mm viền nhôm dây cao su (RED) này vẫn giữ trọn vẹn nét tinh tế và sắc sảo trong thiết kế từ trước đến nay, mặt đồng hồ sapphire ưu việt, viền nhôm được vát gọt công phu và dây đeo cao su co dãn, êm tay. Số tiền mà Apple thu được từ việc bán sản phẩm này sẽ quyên góp và gửi đến Quỹ toàn cầu phòng chống HIV/AIDS.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/229054-fix.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Công nghệ mới giúp bạn quản lý và theo dõi sức khỏe của bản thân dễ dàng, khoa học hơn</h2>\r\n                    <p class = \"paragraph__p\">Sáng giá nhất trong đợt update lần này chính là tính năng đo nồng độ oxy trong máu SpO2 và điện tâm đồ ECG. Tuy nhiên chúng ta sẽ phải chờ thêm một thời gian để có thể trải nghiệm vì tính năng này vẫn chưa được hỗ trợ tại Việt Nam.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-162720-122757.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chip xử lý S6 nhanh hơn tới 20% so với S5</h2>\r\n                    <p class = \"paragraph__p\">Một ưu điểm phải kể đến của dòng Apple Watch S6 này đó là chip xử lý S6 cải thiện hiệu năng thêm 20% so với ở Apple Watch S5 giúp nâng cao sự mượt mà và độ chính xác, đem đến những trải nghiệm tốt hơn cho người dùng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122348.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hệ điều hành WatchOS 7 mang đến nhiều tính năng mới hỗ trợ người dùng tối ưu</h2>\r\n                    <p class = \"paragraph__p\">WatchOS 7 trên đồng hồ thông mình Apple S6 được điều chỉnh để tối ưu hóa việc theo dõi chế độ luyện tập, ra mắt dịch vụ fitness hỗ trợ tập luyện hiệu quả qua các chương trình như: Yoga, đạp xe, nhảy, chạy, bơi lội,... Đồng thời trang bị thêm nhiều tính năng hữu ích nổi bật như Family Setup, nhận biết rửa tay, chia sẻ màn hình qua iMessages, Siri dịch nhanh,...</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122354.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình OLED luôn luôn hiển thị, độ sắc nét đạt chuẩn Retina</h2>\r\n                    <p class = \"paragraph__p\">- Màn hình của đồng hồ sử dụng tấm nền OLED cao cấp mang đến độ sắc nét chân thực và giúp tiết kiệm pin.- Tính năng màn hình luôn hiển thị (Always-on) ở Apple Watch series 6 giống với dòng đồng hồ thông minh Apple S5 nhưng ưu điểm là màn hình ở chế độ chờ sáng hơn 2.5 lần nhưng không gây tốn pin hơn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252220-122258.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nhận thông báo và nghe gọi trên đồng hồ một cách thuận tiện</h2>\r\n                    <p class = \"paragraph__p\">Kết nối đồng hồ với điện thoại qua cổng bluetooth hay wifi cho phép người dùng dễ dàng thực hiện nghe gọi, xem tin nhắn, kiểm tra thông báo ứng dụng (Messenger, Line, Viber,…), thông báo theo dõi sức khỏe, rèn luyện thể thao,… Hỗ trợ bạn dễ dàng quản lý hiệu quả công việc và sinh hoạt thường ngày.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252220-122252.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nhiều giao diện với phong cách đa dạng, phù hợp với mọi người, mọi lứa tuổi</h2>\r\n                    <p class = \"paragraph__p\">Thoải mái lựa chọn giao diện phù hợp với phong cách riêng của bản thân với bộ sưu tập giao diện đa dạng, hoặc bạn cũng có thể tìm kiếm các giao diện yêu thích trên App Store.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122305.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Pin dung lượng lớn, sạc đầy nhanh chóng</h2>\r\n                    <p class = \"paragraph__p\">Pin của Apple Watch Series 6 dùng được trong khoảng 1.5 ngày và chỉ cần 2 giờ đồng hồ để sạc đầy pin giúp bạn sử dụng đồng hồ một cách thoải mái, trọn vẹn nhất.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122312.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Quản lý sức khỏe của bản thân tốt hơn</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ luôn theo dõi nhịp tim của bạn mọi lúc mọi nơi và thông báo cho bạn khi nhịp tim cao, thấp hoặc không đều bất thường.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122319.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Còn rất nhiều các tiện ích thú vị khác chờ bạn khám phá</h2>\r\n                    <p class = \"paragraph__p\">Apple Watch series 6 sở hữu nhiều tiện ích khác như tìm điện thoại, hướng dẫn lộ trình, đếm số bước chân, dự báo thời tiết, định vị, nghe nhạc trên đồng hồ, tính lượng calories tiêu thụ, ... mang đến cho bạn một cuộc sống thoải mái, hiện đại.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/229054/apple-watch-s6-40mm-vien-nhom-day-cao-su-red-252320-122342.jpg\" alt=\"\">  \r\n                    <p class = \"paragraph__p\">Dòng Apple Watch đáng mong chờ trong năm 2020 với thiết kế sang trọng và hiện đại, dây đeo cao su êm ái, là phụ kiện thời trang thể hiện đẳng cấp cũng như người trợ thủ lý tưởng trong thời đại khoa học công nghệ 4.0. Hơn thế, Apple Watch Series 6 còn mang đến những nâng cấp ấn tượng như: dòng chip S6 cải thiện 20% hiệu năng, hệ điều hành WatchOS 7 được cập nhật thêm nhiều tính năng hữu ích (Family Setup, nhận biết rửa tay, chia sẻ màn hình qua iMessages, Siri dịch nhanh,...).</p>   \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 1, 1),
(57, 4, 'Huami Amazfit T-Rex Đen', '../assets/image/products/watch/Huami-Amazfit-T-Rex-Đen/Nền.jpg', 2804000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nAMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1. inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 20 ngay\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nGoogle Wear OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 10 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nMặt kính nhựa\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n47.7 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Đồng hồ thông minh Huami Amazfit T-Rex Đen</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219682/Slider/xiaomi-amazfit-t-rex-260520-0934540.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế hầm hố, năng động và trẻ trung</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Huami Amazfit T-Rex sở hữu màn hình 1.3 Inch cùng kích thước khá là hầm hố nhưng chỉ nặng 36g. Mặt kính cường lực cùng với khả năng chống chịu tốt ở mọi môi trường khắc nghiệt. Dây đeo silicone đem lại cho người dùng cảm giác êm ái, thoải mái khi đeo trong thời gian dài.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219682/xiaomi-amazfit-t-rex-1-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kết nối nhanh chóng qua bluetooth v5.0</h2>\r\n                    <p class = \"paragraph__p\">Amazfit T-Rex tương thích với các thiết bị hệ điều hành Android (5.0 trở lên) và iOS (10 trở lên). Khi kết nối với điện thoại, bạn có thể dễ dàng kiểm tra tin nhắn, thông báo ứng dụng (Face, Viber, Line,...), rung thông báo khi bạn bỏ lỡ cuộc gọi.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219682/xiaomi-amazfit-t-rex-2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình AMOLED và mặt kính cường lực</h2>\r\n                    <p class = \"paragraph__p\">Công nghệ màn hình AMOLED 1.3 inch hiển thị mọi thông tin, hình ảnh sắc nét cùng khả năng chống chói khi mang ra ngoài trời. Ngoài ra, bao phủ trên mặt đồng hồ là lớp mặt kính Corning Gorilla Glass 3+ và lớp phủ AF chống trầy xước hiệu quả, hạn chế bám vân tay, bảo vệ mặt đồng hồ bởi các va chạm thường ngày.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219682/xiaomi-amazfit-t-rex-3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Giao diện mặt đồng hồ phong cách và màn hình luôn hiển thị</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Amazfit T-Rex có bộ sưu tập 30 mặt đồng hồ cá tính để bạn cài đặt thông qua ứng dụng Amazfit Watch Face. Mẫu đồng hồ này được trang bị tính năng Always on Display (màn hình luôn hiển thị) giúp người dùng dễ dàng xem giờ và không bị bỏ lỡ những thông báo quan trọng, tuy nhiên tính năng này sẽ giới hạn ở một số mặt đồng hồ.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219682/xiaomi-amazfit-t-rex-4.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thời lượng pin kéo dài đến 20 ngày sử dụng</h2>\r\n                    <p class = \"paragraph__p\">So với những mẫu đồng hồ thông minh khác, Amazfit T-Rex là một thiết bị có thời lượng pin cực kỳ ấn tượng. Sở hữu viên pin Lithium Polymer với dung lượng 390mAh, thiết bị có thể sử dụng đến 66 ngày ở chế độ cơ bản (không kết nối, chỉ dùng để xem giờ), còn khi dùng như 1 chiếc đồng hồ thông minh (đo nhịp tim, thông báo ứng dụng,...) thì dùng được 20 ngày.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219682/xiaomi-amazfit-t-rex-5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chịu được những môi trường khắc nghiệt</h2>\r\n                    <p class = \"paragraph__p\">Một trong những lí do khiến người dùng ngại mua smartwatch chính là dễ bị hỏng hóc khi phải chống chịu với môi trường và thời tiết khắc nghiệt. Đối với Amazfit T-Rex, bạn có thể an tâm sử dụng vì mẫu đồng hồ này đạt tiêu chuẩn quân đội MIL-STD-810G cùng khả năng chống nước độ sâu 50M, hoạt động tốt ở các điều kiện môi trường khắc nghiệt với nhiệt độ từ -400C đến 700C. Cực kỳ phù hợp với các đối tượng người dùng phải hoạt động trong những điều kiện thời tiết không lý tưởng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219682/xiaomi-amazfit-t-rex-6.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Tính năng theo dõi nhịp tim </h2>\r\n                    <p class = \"paragraph__p\">Tương tự nhưng các sản phẩm trong dòng Amazfit, T-rex được trang bị 4 cảm biến: Cảm biến quang theo dõi sinh học BioTrackerTM PPG, cảm biến gia tốc 3 trục, cảm biến địa từ, cảm biến ánh sáng xung quanh, giúp bạn theo dõi nhịp tim 24h với độ chính xác cao. Ngoài ra còn có tính năng theo dõi giấc ngủ, phân tích giấc ngủ qua đó cho người dùng biết về chất lượng giấc ngủ mỗi ngày.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219682/xiaomi-amazfit-t-rex-7.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">14 chế độ rèn luyện thể thao, nâng cao thể lực</h2>\r\n                    <p class = \"paragraph__p\">Amazfit T-Rex cung cấp cho người dùng 14 chế độ tập luyện thể thao như chạy, đi bộ, bơi lội, xe đạp,…. Dễ dàng theo dõi quá trình luyện tập của bản thân, từ đó hình thành thói quen tập luyện hằng ngày. Định vị kép GPS + GLONASS giúp theo dõi lộ trình và khoảng cách của bạn mà không cần kết nối với điện thoại.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219682/xiaomi-amazfit-t-rex-8.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kết nối nghe nhạc mọi lúc mọi nơi</h2>\r\n                    <p class = \"paragraph__p\">Kết nối bluetooth giúp điều khiển phát nhạc trên điện thoại được tiện lợi hơn, có thể điều chỉnh tăng giảm âm lượng, chuyển bài hát ngay trên chiếc đồng hồ đang đeo.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219682/huami-amazfit-t-rex-061420-011412.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nhiều tiện ích hỗ trợ người dùng</h2>\r\n                    <p class = \"paragraph__p\">Gồm các tính năng: Báo thức, la bàn, thông báo nhắc nhở, dự báo thời tiết,... Và còn nhiều tính năng khác.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219682/xiaomi-amazfit-t-rex-10.jpg\" alt=\"\">  \r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Huami Amazfit T-Rex với thiết kế năng động, thể thao cùng màn hình AMOLED và thời lượng pin khủng lên đến 20 ngày. Nhiều tính năng chăm sóc sức khoẻ như đo nhịp tim, theo dõi giấc ngủ, rèn luyện thể thao. Mặt kính cường lực chống trầy và độ bền đạt chuẩn quân đội với lớp bảo vệ chắc chắn. Đồng hồ thông minh Xiaomi Amazfit T-Rex sẽ luôn là sự lựa chọn tốt nhất cho ai cần chiếc đồng hồ thông minh chống chọi lại được nhiều yếu tố khắc nghiệt của môi trường.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 14, 1),
(58, 4, 'Oppo Watch 46mm dây silicone đen', '../assets/image/products/watch/Oppo-Watch-46mm-dây-silicone-đen/Nền.jpg', 7990000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nAMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.91 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 21 ngay\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nGoogle Wear OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nKính cường lực\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n46 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Oppo Watch 46mm dây silicone đen</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225648/Slider/oppo-watch-46mm-day-silicone-301020-0959020.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế sang trọng, tinh tế</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Oppo Watch màu đen phiên bản 46mm sử dụng mặt đồng hồ vuông, bo cong nhẹ ở 4 cạnh, cùng với mặt kính bo cong 2D sang hai bên có chiều sâu tạo cảm giác như mặt kính cong 3D, màn hình AMOLED 1.91 inch độ phân giải 402 x 476 pixels, mật độ điểm ảnh 326 ppi và dải màu rộng chuẩn DCI-P3 cho chất lượng hiển thị sắc nét, sống động. Dây đeo silicone cho cảm giác mang được dễ chịu và thoải mái.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225648/oppo-watch-46mm-day-silicone-13.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Đồng bộ hóa với điện thoại thông qua kết nối bluetooth</h2>\r\n                    <p class = \"paragraph__p\">Oppo Watch hỗ trợ kết nối với smartphone thông qua chuẩn Bluetooth v4.2 tương thích với Android, iOS cho phép bạn dễ dàng đồng bộ điện thoại và smartwatch để nhận các thông báo tin nhắn SMS, Zalo, Messenger,… nghe nhận cuộc gọi (chỉ nghe gọi được trên điện thoại Android).</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225648/oppo-watch-46mm-day-silicone-1-2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thoải mái lựa chọn mặt đồng hồ</h2>\r\n                    <p class = \"paragraph__p\">Oppo Watch còn tích hợp nhiều giao diện mặt đồng hồ theo từng phong cách khác nhau, người dùng có thể dễ dàng tuỳ chỉnh giao diện ngay trên đồng hồ.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225648/oppo-watch-46mm-day-silicone-writee3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thời gian sử dụng lâu dài, hỗ trợ VOOC</h2>\r\n                    <p class = \"paragraph__p\">Oppo Watch cho thời gian sử dụng ấn tượng lên đến 36 giờ với viên pin dung lượng 430 mAh.Ngoài ra Oppo Watch còn hỗ trợ công nghệ sạc nhanh VOOC, cho phép sạc 46% pin trong vòng 15 phút và mất khoảng 60 phút để sạc đầy.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225648/oppo-watch-46mm-day-silicone-174620-104658.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Luyện tập thể thao toàn diện</h2>\r\n                    <p class = \"paragraph__p\">Oppo Watch hỗ trợ nhiều chế độ tập luyện thể thao, mỗi bài tập đều có hướng dẫn chi tiết để người dùng có thể tập theo, đảm bảo đúng động tác và hiệu quả.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225648/oppo-watch-46mm-day-silicone-writee8.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi nhịp tim liên tục</h2>\r\n                    <p class = \"paragraph__p\">Oppo Watch được trang bị chức năng theo dõi nhịp tim của người dùng một cách liên tục với hệ thống đèn hồng ngoại ở mặt sau, chiếc đồng hồ này sẽ gửi cảnh bảo cho người dùng nếu phát hiện nhịp tim tăng cao hoặc giảm bất thường.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225648/oppo-watch-46mm-day-silicone-writee6.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi giấc ngủ chuyên sâu</h2>\r\n                    <p class = \"paragraph__p\">Oppo Watch sẽ theo dõi giấc ngủ của bạn một cách liên tục để đưa ra đánh giá về chất lượng giấc ngủ, từ đó đưa ra những giải pháp để người dùng có giấc ngủ chất lượng hơn. Bên cạnh đó, nhịp tim của bạn sẽ luôn được theo dõi dù bạn tập luyện hay nghỉ ngơi.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225648/oppo-watch-46mm-day-silicone-175720-105719.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chuẩn chống nước độ sâu 50m (5ATM)</h2>\r\n                    <p class = \"paragraph__p\">Người dùng có thể đeo để tắm gội, bơi lội, không phải lo sợ nước vào đồng hồ khi trời mưa lớn. Tuy nhiên để giữ đồng hồ hoạt động tốt, người dùng cần tránh sử dụng quá 30 phút và mở bật chế độ Lock khi bơi.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225648/oppo-watch-46mm-day-silicone-writee9.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nghe nhạc mọi lúc, giải trí mọi nơi</h2>\r\n                    <p class = \"paragraph__p\">Oppo Watch được trang bị hệ thống loa ngoài, cho phép phát nhạc trực tiếp trên đồng hồ.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225648/oppo-watch-46mm-day-silicone-writee10.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nhiều tính năng hữu ích khác</h2>\r\n                    <p class = \"paragraph__p\">Vì sở hữu màn hình AMOLED nên Oppo Watch sẽ sở hữu tính năng Always on Display, ngoài ra chiếc smartwatch này còn có các tính năng khác như la bàn, đồng hồ bấm giờ, dự báo thời tiết, tìm điện thoại,…</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/225648/oppo-watch-46mm-day-silicone-writee11.jpg\" alt=\"\">  \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 6, 1),
(59, 4, 'Samsung Galaxy Watch Active 2 40mm viền nhôm dây silicone đen', '../assets/image/products/watch/Samsung-Galaxy-Watch-Active-2-40mm-viền-nhôm-dây-silicone-đen/Nền.jpg', 4990000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nAMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.6 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 14 ngay\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nGoogle Wear OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nKính cường lực Gorilla Glass Dx+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n44 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy Watch Active 2 40mm viền nhôm dây silicone đen</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219147/Slider/samsung-galaxy-watch-active-2-40-mm-den-273320-083347.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế năng động và thời trang</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Samsung Galaxy Watch Active 2 nổi bật với màn hình rộng 1.2 Inch với độ phân giải 360 x 360 Pixels và mặt kính cường lực giúp người đeo an tâm khi mang ra ngoài. Giao diện mặt đồng hồ có thể thay đổi tuỳ ý theo phòng cách người dùng. Dây đeo Silicone hạn chế bị trầy xước và dễ lau khô khi tiếp xúc với nước hay mồ hôi.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219147/samsung-galaxy-watch-active-2-40-mm-den.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình cường lực chống trầy và công nghệ Super AMOLED</h2>\r\n                    <p class = \"paragraph__p\">Được trang bị mặt kính cường lực Gorilla Glass DX+ giúp chống trầy hiệu quả và công nghệ màn hình Super AMOLED hiển thị mọi thông tin, hình ảnh sắc nét, chi tiết hơn, ngoài ra công nghệ này còn chống chói và tiết kiệm pin cho thiết bị.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219147/samsung-galaxy-watch-active-2-40-mm-den-cont-7-fixxa4.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kết nối nhanh chóng qua Bluetooth v5.0</h2>\r\n                    <p class = \"paragraph__p\">Samsung Galaxy Watch Active 2 kết nối được với hệ điều hành Android (5.0) và iOS 10 trở lên. Cho phép người đeo có thể dùng để gọi, nghe điện thoại, xem thông báo, tin nhắn và còn nhiều tính năng tiện ích khác.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219147/samsung-galaxy-watch-active-2-40-mm-den-cont-2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi nhịp tim người đeo đều đặn</h2>\r\n                    <p class = \"paragraph__p\">Sở hữu 8 đèn điốt quang ở mặt sau đồng hồ cùng với thiết kế dây đeo ôm trọn cổ tay, Samsung Galaxy Watch Active 2 rất chú trọng theo dõi tình trạng sức khoẻ của người sử dụng. Tính năng theo dõi nhịp tim sẽ đo và gửi bạn cảnh báo khi nhịp tim hạ thấp hoặc tăng cao hơn các ngưỡng bình thường</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219147/samsung-galaxy-watch-active-2-40-mm-den-22.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Dung lượng Pin lớn và sử dụng lâu dài</h2>\r\n                    <p class = \"paragraph__p\">Với dung lượng 247 mAh đủ để bạn sử dụng trọn vẹn cả ngày dài. Khi về nhà, bạn chỉ cần đặt nó lên bộ sạc không dây nam châm vừa hiện đại, vừa nhỏ gọn để nhanh chóng sạc đầy Pin mà không cần phải cắm dây sạc rườm rà.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219147/samsung-galaxy-watch-active-2-40-mm-den-cont-4.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Tính năng theo dõi giấc ngủ và cảnh báo Stress</h2>\r\n                    <p class = \"paragraph__p\">Khi đeo vào tay khi đi ngủ, đồng hồ Galaxy Watch Active 2 có cơ chế theo dõi giấc ngủ của người đeo bằng cách đo nhịp tim ECG và huyết áp. Từ đó, có thể nhận diện được chủ nhân có thay đổi gì trong khi ngủ hay không, giấc ngủ có sâu không và đưa ra các lời khuyên để có được giấc ngủ ngon.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219147/samsung-galaxy-watch-active-2-40-mm-den-cont-7-fix1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Điều khiển Camera để chụp ảnh và quay phim</h2>\r\n                    <p class = \"paragraph__p\">Tính năng điều khiển camera cho phép người dùng có thể chụp ở khoảng cách xa, thích hợp khi chụp hình tập thể cùng gia đình và bạn bè.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219147/samsung-galaxy-watch-active-2-40-mm-den-cont-7-fixxa3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Phát nhạc qua Bluetooth</h2>\r\n                    <p class = \"paragraph__p\">Người dùng có thể kết nối với tai nghe Bluetooth để nghe nhạc khi luyện tập thể thao mà không cần phải dùng thao tác trên điện thoại.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219147/samsung-galaxy-watch-active-2-40-mm-den-cont-7-fixxa2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thúc đẩy vận động, nâng cao thể lực</h2>\r\n                    <p class = \"paragraph__p\">Galaxy Watch Active 2 tích hợp nhiều bài tập với nhiều chế độ từ cơ bản đến nhanh dần. Ngoài ra còn tích hợp khả năng tự động tối ưu chạy bộ bằng chương trình theo dõi tốc độ và thời gian chạy theo thời gian thực tế, từ đó đưa ra những gợi ý hữu ích về các chương trình chạy, giúp người dùng đáp ứng mục tiêu bản thân. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219147/samsung-galaxy-watch-active-2-40-mm-den-cont-7-fix3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Những tính năng tiện ích khác</h2>\r\n                    <p class = \"paragraph__p\">Gồm báo thức, rung thông báo, tìm điện thoại, xem lịch,... Đồng hồ thông minh Samsung Galaxy Watch Active 2 còn có cả một kho ứng dụng độc đáo cho người dùng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/219147/samsung-galaxy-watch-active-2-40-mm-den-cont-7-fixxa5.jpg\" alt=\"\">  \r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Samsung Galaxy Watch Active 2 sở hữu vẻ ngoài đơn giản, sang trọng và tinh tế, thiết kế đơn giản nhưng mang \"chất\" riêng này cũng chính là phong cách mà các nhà sản xuất đang theo đuổi. Samsung Galaxy Watch Active 2 Gồm các tính nổi bật như theo dõi nhịp tim, theo dõi giấc ngủ, cảnh báo Stress, rèn luyện thể thao chắc chắn sẽ mang đến những trải nghiệm thú vị và tiện lợi cho người dùng. </p>   \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 2, 1),
(60, 4, 'Samsung Galaxy Watch Active 2 44mm viền thép dây da', '../assets/image/products/watch/Samsung-Galaxy-Watch-Active-2-44mm-viền-thép-dây-da/Nền.jpg', 7990000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nAMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.6 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 1.5 ngày\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nGoogle Wear OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">Kính cường lực Gorilla Glass Dx+\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n41 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Samsung Galaxy Watch Active 2 44mm viền thép dây da</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/Slider/samsung-galaxy-watch-active-2-44-mm-day-da-134320-084342.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">7 chế độ luyện tập nâng cao sức khỏe</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Samsung Galaxy Watch Active 2 thực sự là huấn luyện viên thể thao chuyên nghiệp của bạn với việc tích hợp những chế độ luyện tập khác nhau cho phù hợp với cơ địa và thể trạng của từng người.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da4-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi giấc ngủ - đo nhịp tim - cảnh báo stress</h2>\r\n                    <p class = \"paragraph__p\">Chiếc đồng hồ thông minh này sẽ giúp bạn theo dõi nhịp tim chính xác 24/24 ngay cả khi bạn đang ngủ và cảnh báo nếu nhịp tim thay đổi bất thường.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da5-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế với những đường nét tinh tế</h2>\r\n                    <p class = \"paragraph__p\">Màn hình SUPER AMOLED 1.4 inch sắc nét với chất liệu mặt là kính cường lực Gorilla Glass chống trầy hiệu quả.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da0.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Samsung Galaxy Watch Active 2 mang đến bạn thiết kế trẻ trung năng động nhưng không kém phần tinh tế.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da2-2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Gọi điện trực tiếp trên đồng hồ</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Samsung Galaxy Watch Active 2  còn tích hợp tính năng gọi điện trực tiếp, hiển thị thông báo về nội dung tin nhắn, zalo, messenger, line,...</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da1-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Các tính năng tiện ích khác</h2>\r\n                    <p class = \"paragraph__p\">Bên cạnh những tính năng quan trọng thì chiếc đồng hồ này cũng hỗ trợ nhiều tính năng cơ bản khác như: Tìm điện thoại, báo thức, nghe nhạc..</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/212732/samsung-galaxy-watch-active-2-44-mm-day-da0101.jpg\" alt=\"\">\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 2, 1);
INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(61, 4, 'Huawei Watch Fit dây silicone', '../assets/image/products/watch/Huawei-Watch-Fit-dây-silicone/Nền.jpg', 2390000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nAMOLED\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1.64 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 10 ngay\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nLiteOS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nKính cường lực\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n41 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Huawei Watch Fit dây silicone</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/227985/Slider/vi-vn-huawei-watch-fit-day-silicone.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế mỏng nhẹ, màn hình sắc nét</h2>\r\n                    <p class = \"paragraph__p\">Màn hình AMOLED 1.64 inch có độ phân giải 280x456, mật độ điểm ảnh 326 ppi cho chất lượng hiển thị rõ ràng, màu sắc rực rỡ và hiển thị ngoài trời tốt.\r\n                    Đồng hồ chỉ nặng 34 gam, có thiết kế bo cong mềm mại ở 4 góc kết hợp với dây đeo silicone tạo cảm giác thoải mái, thời thượng. Viền màn hình mỏng, chỉ 0.95 mm càng làm tăng vẻ đẹp cho mặt đồng hồ.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/227985/huawei-watch-fit-day-silicone-man-hinh.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Dễ dàng thay đổi mặt đồng hồ theo phong cách trang phục</h2>\r\n                    <p class = \"paragraph__p\">Bên cạnh những mặt đồng hồ vô cùng độc đáo do Huawei cung cấp sẵn, bạn còn có thể thoải mái lựa chọn từ Watch Face Store hoặc tự tạo mặt đồng hồ từ thư viện ảnh cá nhân. Ngoài ra, Huawei Watch Fit còn hỗ trợ thay đổi chủ đề mặt đồng hồ để phù hợp với trang phục mỗi ngày của bạn.\r\n                     Bạn có thể ưu tiên hiển thị những thông tin mình quan tâm nhất như nhịp tim, quãng đường, thời lượng pin, thời tiết… trên mặt đồng hồ chính.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/227985/huawei-watch-fit-day-silicone-mat-dong-ho.gif\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Sạc pin 90 phút, sử dụng 10 ngày, có hỗ trợ sạc nhanh</h2>\r\n                    <p class = \"paragraph__p\">Thời gian sạc đầy pin chỉ 90 phút. Nếu bạn đang vội không có nhiều thời gian, công nghệ sạc nhanh của Huawei sẽ giúp bạn sạc đến 70% pin chỉ trong vòng 30 phút. Một số điều kiện để sử dụng pin lên đến 10 ngày:\r\n                    - Đeo liên tục 24 giờ mỗi ngày và bật các tính năng theo dõi nhịp tim, theo dõi giấc ngủ\r\n                    - 30 phút tập luyện mỗi tuần (có bật GPS)\r\n                    - Bật thông báo tin nhắn (50 tin nhắn SMS, 6 cuộc gọi và 3 báo thức mỗi ngày)\r\n                    - Bật màn hình 200 lần một ngày</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/227985/huawei-watch-fit-day-silicone-pin-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">96 chế độ tập luyện, 12 hình thức tập luyện</h2>\r\n                    <p class = \"paragraph__p\">Huawei Watch thích hợp cho cả đối tượng tập thể thao và tập tại nhà. 96 chế độ luyện tập gồm 11 chế độ thể thao chuyên nghiệp theo thời gian thực và 85 chế độ tùy chỉnh. Đồng hồ thông minh Huawei có thể tự nhận biết khi bạn bắt đầu tập luyện và sẽ theo dõi nhịp tim, lượng calori tiêu thụ theo thời gian thực.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/227985/huawei-watch-fit-day-silicone13.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi nhịp tim, độ bão hòa oxy</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ có thể đo nhịp tim bằng công nghệ HUAWEI TrueSeen, độ bão hòa oxy trong máu (SpO2) theo thời gian thực để thể hiện tình trạng sức khỏe của bạn, đưa ra các lời khuyên để cải thiện sức khỏe.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/227985/huawei-watch-fit-day-silicone-nhip-tim-spo2.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chăm sóc giấc ngủ, đo chỉ số căng thẳng, chu kỳ kinh nguyệt</h2>\r\n                    <p class = \"paragraph__p\">Thiết bị có khả năng phân tích chính xác tình trạng và chất lượng giấc ngủ của bạn và đưa ra những lời khuyên khoa học để cải thiện giấc ngủ.  Thuật toán theo dõi căng thẳng có thể phát hiện những lúc chỉ số stress của bạn tăng cao để đưa ra các bài tập hít thở giúp bạn giải tỏa căng thẳng và bình tĩnh hơn.Đồng hồ còn là người bạn hữu ích của phái nữ, giúp tính toán chu kỳ và đưa ra dự báo giúp phái nữ chuẩn bị cho những ngày đèn đỏ.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/227985/huawei-watch-fit-day-silicone-suc-khoe.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Nhận cuộc gọi, tin nhắn, thông báo ngay trên đồng hồ</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ Huawei Smart Fit có thể đảm nhiệm vai trò trợ lý cá nhân giúp bạn nhận các cuộc gọi, tin nhắn, thông báo, sự kiện quan trọng, tiết kiệm quỹ thời gian cho những người dùng bận rộn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/227985/huawei-watch-fit-day-silicone14.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chiếc đồng hồ đa tiện ích</h2>\r\n                    <p class = \"paragraph__p\">Bên cạnh những tính năng hữu ích trên, đồng hồ còn được trang bị các tiện ích như Thời tiết, báo thức, hẹn giờ, đồng hồ bấm giờ, đèn pin…</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/227985/huawei-watch-fit-day-silicone-khac.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Mang trong mình rất nhiều công nghệ và tính năng hữu ích như tuỳ chỉnh mặt đồng hồ, đo các chỉ số sức khoẻ, theo dõi chu kỳ kinh nguyệt, các chế độ luyện tập thông minh nhưng đồng hồ thông minh Huawei Watch Fit vẫn có thời lượng pin ấn tượng lên đến 10 ngày. Chiếc đồng hồ thông minh này thực sự là một sản phẩm nổi bật so với các sản phẩm cùng phân khúc giá và là một lựa chọn không thể bỏ qua đối với những người dùng thích luyện tập thể thao và quan tâm đến các vấn đề sức khỏe của bản thân cũng như những người thân trong gia đình nhưng khả năng tài chính không cao.</p>\r\n<button class =\"paragraph__more\">Xem thêm !</button>', 3, 1),
(62, 4, 'Garmin Lily dây silicone vàng', '../assets/image/products/watch/Garmin-Lily-dây-silicone-vàng/Nền.jpg', 4990000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">Công nghệ màn hình</span>\r\n<span class=\"content__row-content\">\r\nLCD\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước màn hình:</span>\r\n<span class=\"content__row-content\">\r\n1 inch\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời gian sử dụng pin:</span>\r\n<span class=\"content__row-content\">\r\nKhoảng 5 ngày\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nGoogle Wear OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối với hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nAndroid  trở lên, iOS 9 trở lên\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Chất liệu mặt:</span>\r\n<span class=\"content__row-content\">\r\nKính cường lực Gorilla Glass 3\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Đường kính mặt:</span>\r\n<span class=\"content__row-content\">\r\n35 mm\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kết nối:</span>\r\n<span class=\"content__row-content\">\r\nBluetooth v4.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ngôn ngữ:</span>\r\n<span class=\"content__row-content\">\r\nTiếng Việt, Tiếng Anh\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Theo dõi sức khỏe:</span>\r\n<span class=\"content__row-content\">\r\nĐo nồng độ oxy trong máu, Đo nhịp tim, Theo dõi giấc ngủ, Tính lượng calories tiêu thụ, Tính quãng đường chạy, Đếm số bước chân, Chế độ luyện tập\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Đồng hồ thông minh Garmin Lily dây silicone vàng</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235501/Slider/vi-vn-garmin-lily-day-silicone-vang-(1).jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế thời trang nữ tính, sang trọng</h2>\r\n                    <p class = \"paragraph__p\">Với thiết kế nhỏ gọn, đồng hồ thông minh Garmin Lily sở hữu đường kính mặt 35 mm cùng trọng lượng nhẹ khoảng 24g. Ngoài ra, chiếc đồng hồ được thiết kế có khung viền làm từ chất liệu polyme bền chắc, có khả năng chịu sự va đập tốt.\r\n                    Dây đeo làm từ silicone mềm dẻo, êm nhẹ với độ dài 10 cm và độ rộng của dây thanh mảnh nữ tính khoảng 1.4 cm. Đây sẽ mẫu đồng hồ thích hợp cho các bạn nữ sử dụng trong quá trình tập luyện thể thao cũng như là có thể dùng làm đồng hồ thời trang.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235501/garmin-lily-day-silicone-vang1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Quan sát thông tin dễ dàng với màn hình LCD 1 inch sắc nét, được trang bị kính cường lực chống trầy xước</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Garmin Lily được trang bị màn hình LCD có độ phân giải 240 x 201 pixels mang lại khả năng hiển thị sắc nét giúp dễ dàng quan sát thông tin hiển thị trên mặt đồng hồ. Mẫu đồng hồ này còn được trang bị mặt kính cường lực Gorilla Glass 3 trong suốt, chống trầy xước. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235501/garmin-lily-day-silicone-vang7.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kết nối bluetooth với hệ điều hành iOS và Android</h2>\r\n                    <p class = \"paragraph__p\">Bạn có thể dễ dàng thao tác trực tiếp trên đồng hồ như: từ chối cuộc gọi, nhận tin nhắn, xem thông báo ứng dụng (Facebook, Messenger, Zalo, Viber,...) bằng khả năng kết nối không dây bluetooth, chỉ cần bạn đang sử dụng điện thoại có hệ điều hành iOS hoặc Android.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235501/garmin-lily-day-silicone-vang3.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thời lượng Pin lên đến 5 ngày sử dụng</h2>\r\n                    <p class = \"paragraph__p\">Thoải sức trải nghiệm khi đi chơi, du lịch, tập luyện, làm việc hay học tập với thời lượng pin 5 ngày sử dụng và chỉ mất khoảng thời gian ngắn 1.5 giờ để sạc đầy pin.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235501/garmin-lily-day-silicone-vang8.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Tính năng theo dõi năng lượng cơ thể - Body Battery</h2>\r\n                    <p class = \"paragraph__p\">Trên các thiết bị đồng hồ thông minh Garmin, đều được tích hợp tính năng độc quyền Body Battery đo năng lượng cơ thể, đồng hồ sẽ phân tích nhịp tim, mức độ căng thẳng,...\r\n                    Từ đó tính toán được mức nạp năng lượng vào cơ thể cũng như là mức tiêu hao năng lượng trong ngày của bạn, giúp bạn hiểu sâu hơn về cơ thể của mình để đảm bảo có sức khỏe tốt nhất. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235501/garmin-lily-day-silicone-vang4.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi chu kỳ kinh nguyệt cho nữ giới</h2>\r\n                    <p class = \"paragraph__p\">Kết nối đồng hồ với ứng dụng Garmin Connect để theo dõi chu kì kinh nguyệt của bạn. Theo dõi đơn giản bằng cách bạn nhập thông tin chi tiết về chu kỳ kinh nguyệt của mình vào ứng dụng Garmin Connect, đồng hồ sẽ ghi lại các hoạt động như bước đi, chế độ luyện tập, chế độ dinh dưỡng,...\r\n                    Từ đó sẽ cho ra các dự đoán về chu kì kinh nguyệt và khả năng sinh sản hoặc những cảnh báo cho về chu kỳ cho bản thân</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235501/garmin-lily-day-silicone-vang5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Huấn luận viên Garmin hỗ trợ luyện tập chuyên nghiệp - Garmin Coach</h2>\r\n                    <p class = \"paragraph__p\">Trợ giúp bạn tập luyện tốt hơn với chế độ Huấn luyện viên Garmin - Garmin Coach cùng nhiều bài tập thể thao như: Yoga, chạy bộ, fitness, đi bộ,... Tại chế độ này, bạn có thể thiết lập lịch tập luyện hoặc tùy chỉnh các bài tập theo sở thích của mình. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235501/garmin-lily-day-silicone-vang6.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Theo dõi và cải thiện sức khỏe cho người sử dụng</h2>\r\n                    <p class = \"paragraph__p\">Chức năng đo nhịp tim thông qua cảm biến được đặt ở mặt sau đồng hồ, cảm biến sẽ tiếp xúc với da tay và đo nhịp tim của cơ thể để cho ra những chỉ số sức khỏe và cảnh báo nếu gặp dấu hiệu bất thường.\r\n                    Đặc biệt, mẫu đồng hồ thông minh này có đo chỉ số nồng độ oxy trong máu (SpO2) và lượng oxy tiêu thụ tối đa (VO2 max) khi bạn luyện tập thể thao, chỉ số này giúp bạn biết ngưỡng luyện tập của bản thân.\r\n                    Tính năng theo dõi giấc ngủ: đồng hồ sẽ phân tích các giai đoạn giấc ngủ để đưa ra những lời khuyên về giấc ngủ nhằm cải thiên thêm sức khỏe cho bạn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235501/garmin-lily-day-silicone-vang10.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chia sẻ vị trí cho người thân ngay khi gặp sự cố</h2>\r\n                    <p class = \"paragraph__p\">Khi đã được ghép đôi giữa đồng hồ và điện thoại, trong trường hợp bạn gặp sự cố làm nhịp tim bỗng tăng cao hoặc giảm dần tối thiểu, đồng hồ sẽ tự động gửi vị trí của bạn đến số điện thoại người thân mà bạn đã cài đặt trước đó.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235501/garmin-lily-day-silicone-vang9.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Tận hưởng thêm nhiều tiện ích thông minh khác</h2>\r\n                    <p class = \"paragraph__p\">Đồng hồ thông minh Garmin Lily còn được tích hợp thêm nhiều tính năng thú vị khác: Tìm điện thoại, la bàn, dự báo thời tiết, báo thức, đo lượng calo tiêu thụ, đếm bước chân, đồng hồ bấm giờ, điểu khiển nhạc ngay trên đồng hồ,... giúp bạn có trải nghiệm tuyệt vời hơn khi sử dụng mẫu đồng hồ thông minh này.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/7077/235501/garmin-lily-day-silicone-vang2.jpg\" alt=\"\">  \r\n                    <p class = \"paragraph__p\">Đây là sản phẩm có thiết kế tinh tế, nhỏ gọn, sang trọng phù hợp dành cho phái nữ. Chăm sóc sức khỏe của bạn bằng cách đo lường các chỉ số cơ thể: theo dõi giấc ngủ, đo nhịp tim, theo dõi chu kì kinh nguyệt, đo lượng pin cơ thể,... và có nhiều bài luyện thể thao cùng huấn luyện viên Garmin. Ngoài ra, mẫu đồng hồ này cũng sẽ là món phụ kiện thời trang độc đáo, giúp bạn thể hiện phong cách của bản thân. </p>      \r\n<button class =\"paragraph__more\">Xem thêm !</button>   ', 13, 1),
(63, 2, 'Lenovo ThinkBook 15IIL i3 1005G1/4GB/512GB/Win10 (20SM00D9VN)', '../assets/image/products/laptop/Lenovo-ThinkBook-15IIL-20SM00D9VN/Nền.jpg', 11690000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i3 Ice Lake, 1005G1, 1.20 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n4 GB, DDR4 (1 khe), 2666 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe, Hỗ trợ khe cắm HDD SATA\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n15.6 inch, Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel UHD Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 3.1, HDMI, LAN (RJ45), USB 2.0, 2 x USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ kim loại, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 18.9 mm, 1.8 Kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo ThinkBook 15IIL i3 1005G1/4GB/512GB/Win10 (20SM00D9VN)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230161/Slider/lenovo-thinkbook-15iil-i3-20sm00d9vn-301020-1029150.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Laptop Lenovo ThinkBook 15IIL i3 (20SM00D9VN) sở hữu thiết kế từ kim loại toát lên vẻ sang trọng, sắc sảo, cấu hình lí tưởng cho học tập, trình duyệt web khi trang bị bộ vi xử lý Intel thế hệ thứ 10 mới và ổ cứng SSD cực nhanh.</h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế mỏng nhẹ, bền bỉ</h2>\r\n                    <p class = \"paragraph__p\">Thiết kế của Laptop Lenovo ThinkBook 15 IML hướng đến sự tối giản và hiện đại. Vỏ máy được làm từ kim loại vừa mang lại vẻ sang trọng, vừa bền bỉ. Máy có độ dày chỉ 18.9 mm, trọng lượng chỉ 1.8 kg, khá mỏng nhẹ đối với laptop cùng phân khúc, dễ dàng cho vào balo hay túi xách, sẵn sàng cùng bạn “lên đường”.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230161/lenovo-thinkbook-15iil-i3-20sm00d9vn-303920-103944.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Laptop Lenovo ThinkBook 15 IIL được tích hợp rất nhiều cổng kết nối, bao gồm cổng USB 3.1 tiện lợi để sạc thiết bị ngoại vi như điện thoại, cổng USB Type-C, cổng USB 2.0, HDMI, đầu đọc thẻ SD, jack tai nghe, cổng mạng LAN để kết nối mạng có dây. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230161/lenovo-thinkbook-15iil-i3-20sm00d9vn-304020-104013.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bạn hoàn toàn có thể an tâm về độ an toàn, tin cậy của dữ liệu trên máy nhờ có tính năng bảo mật vân tay, chạm nhẹ để mở khóa tương tự như trên smartphone</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230161/lenovo-thinkbook-15iil-i3-20sm00d9vn-304020-104052.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Làm việc, học tập năng suất </h2>\r\n                    <p class = \"paragraph__p\">Lenovo ThinkBook 15-IML 20RW008WVN được trang bị bộ vi xử lý Intel Core i3 Ice Lake thế hệ 10, với 2 nhân 4 luồng, xung nhịp tối đa đạt 3.4 GHz cho hiệu năng mạnh mẽ, xử lý trơn tru các tác vụ văn phòng, trình duyệt web, các ứng dụng học tập phổ biến.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/230161/lenovo-thinkbook-15iil-i3-20sm00d9vn-304120-104143.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Dung lượng RAM 4 GB hỗ trợ đa nhiệm ở mức trung bình, có thể chạy cùng lúc một số ứng dụng như vừa lướt web, vừa làm việc trên Excel,... Bạn có thể nâng cấp RAM lên tối đa đến 32 GB để đa nhiệm, xử lý nhiều tác vụ nặng khác mượt mà hơn. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/230161/lenovo-thinkbook-15iil-i3-20sm00d9vn-304220-104249.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Chiếc laptop 15,6 inch siêu mỏng nhẹ</h2>\r\n                    <p class = \"paragraph__p\">Lenovo có màn hình 15,6 inch trong thân máy gọn gàng như laptop 14 inch nhờ thiết viền màn hình mỏng cho tỉ lệ màn hình trên thân máy lên tới 83%.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/230161/lenovo-thinkbook-15iil-i3-20sm00d9vn-304320-104328.jpg\" alt=\"\"> \r\n                    <p class = \"paragraph__p\">Công nghệ âm thanh Dolby Audio cung cấp âm thanh vòm 3D sống động như trong rạp hát, nghe nhạc, học ngoại ngữ hay call video sẽ rất tiện lợi với chất lượng âm thanh tốt</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/230161/lenovo-thinkbook-15iil-i3-20sm00d9vn-304420-104419.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Laptop Lenovo ThinkBook 15IIL i3 (20SM00D9VN) là chiếc laptop học tập - văn phòng hiện đại, phù hợp với những người yêu thích một chiếc laptop sang trọng, mạnh mẽ và bảo mật tốt.</p>\r\n                                       \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 4, 1),
(64, 2, 'Lenovo IdeaPad Slim 3 15IIL05 i3 1005G1/4GB/512GB/Win10 (81WE0032VN)', '../assets/image/products/laptop/Lenovo-IdeaPad-Slim-3-15IIL05-81WE0032VN/Nền.jpg', 11990000, 0, 5, 15, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i3 Ice Lake, 1005G1, 1.20 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n4 GB, DDR4 (On board +1 khe), 2666 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe, Hỗ trợ khe cắm HDD SATA\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n15.6 inch, Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel UHD Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 3.0, HDMI, USB 2.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ nhua, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 19.9 mm, 1.85 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/223534/Slider/lenovo-ideapad-3-15iil05-i3-81we003rvn-080720-0426490.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Laptop Lenovo IdeaPad 3 15IIL05 là sản phẩm hướng đến người dùng văn phòng, học sinh sinh viên, xử lý ổn định các tác vụ cơ bản hằng ngày.</h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế sang trọng, cầm nhẹ tay</h2>\r\n                    <p class = \"paragraph__p\">Lenovo IdeaPad 3 có mặt lưng xước dọc, logo khắc kim loại ở mặt lưng tạo nên vẻ cao cấp hơn so với các sản phẩm trước. Trọng lượng máy 1.85 kg cầm nhẹ tay cũng như thoải mái cho vào balo mang đi học hằng ngày.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230161/lenovo-thinkbook-15iil-i3-20sm00d9vn-303920-103944.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bản lề gập mở 180 độ giúp bạn tương tác với màn hình nhiều góc độ hơn cũng như tăng tuổi thọ cho máy khi bạn lỡ tay mở màn hình mạnh thì cũng không bị gãy bản lề. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/223534/lenovo-ideapad-3-15iil05-i3-81we003rvn-180-do%CC%A3%CC%82-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bàn phím có cụm phím số dễ dàng sử dụng cho việc nhập liệu, hành trình phím tốt, có độ nảy và nút bấm to nên việc bấm cũng dễ dàng hơn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/223534/lenovo-ideapad-3-15iil05-i3-81we003rvn-bp-so%CC%82%CC%81-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hiệu năng vừa đủ cho công việc văn phòng</h2>\r\n                    <p class = \"paragraph__p\">Máy trang bị chip Intel Core i3 thế hệ 10, Ram 4 GB cho phép bạn mở 2 3 ứng dụng cùng lúc mà máy vẫn hoạt động tốt như soạn thảo trên Word, nghe nhạc, chỉnh sửa hình ảnh,...</p>\r\n                    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/223534/lenovo-ideapad-3-15iil05-i3-81we003rvn-i5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình rộng, viền máy mỏng</h2>\r\n		    <p class = \"paragraph__p\">Laptop Lenovo có màn hình 15.6 inch, độ phân giải Full HD hiển thị sắc nét mọi hình ảnh. Tấm nền TN và công nghệ chống chói cho phép bạn sử dụng máy ở nơi có ánh sáng mạnh mà không bị chói.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/223534/lenovo-ideapad-3-15iil05-i3-81we003rvn-inch-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Phía trên viền màn hình có công tắc khóa webcam khi không sử dụng, đây là công tắc độc quyền của nhà Lenovo giúp bạn tránh bị đánh cắp thông tin cá nhân mà không phải dán sticker. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/223534/lenovo-ideapad-3-15iil05-i3-81we003rvn-ma%CC%80n-tra%CC%A3%CC%82p.jpg\" alt=\"\"> \r\n                    <p class = \"paragraph__p\">Với các thao tác lướt web, xem phim, soạn văn bản,... thì máy hoạt động liên tục khoảng  đến 4 giờ, đủ để bạn tham gia 1 buổi học hay buổi làm việc hiệu quả.</p>          \r\n                    <p class = \"paragraph__p\">Laptop Lenovo IdeaPad 3 15IIL05 có cấu hình vừa đủ dùng văn phòng, thiết kế đẹp mắt lại cầm nhẹ tay phù hợp với các bạn học sinh sinh viên hay dân văn phòng. </p>\r\n                                       \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 4, 1),
(65, 2, 'Lenovo IdeaPad Slim 3 15IIL05 i3 1005G1/4GB/512GB/Win10 (81WE0132VN)', '../assets/image/products/laptop/Lenovo-IdeaPad-Slim-3-15IIL05-81WE0132VN/Nền.jpg', 12490000, 0, 5, 12, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i3 Ice Lake, 1005G1, 1.20 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n4 GB, DDR4 (On board +1 khe), 2666 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe, Hỗ trợ khe cắm HDD SATA\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n15.6 inch, Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel UHD Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 3.0, HDMI, USB 2.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ nhua, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 19.9 mm, 1.85 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo IdeaPad Slim 3 15IIL05 i3 1005G1/4GB/512GB/Win10 (81WE0132VN)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236315/Slider/vi-vn-lenovo-ideapad-slim-3-15iil05-i3-81we0132vn.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Laptop Lenovo IdeaPad Slim 3 15IIL05 i3 (81WE0132VN) là một chiếc laptop học tập văn phòng có thiết kế đẹp mắt, phong cách năng động. Không chỉ vượt trội về thiết kế mà hiệu năng cực kỳ ổn định với chip Intel Core i3 giúp bạn hoàn thành công việc nhanh chóng.</h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế gọn gàng, phong cách trẻ trung</h2>\r\n                    <p class = \"paragraph__p\">Laptop Lenovo có lớp vỏ bằng nhựa được phủ gam màu xám bạc hiện đại, máy có trọng lượng 1.85 kg và có độ dày 19.9 m, bạn có thể dễ dàng bỏ vào balo mang theo xử lý công việc mọi lúc mọi nơi.</p>\r\n                    <p class = \"paragraph__p\">Cụm bàn phím full size có đủ bàn phím số thuận tiện cho các công việc liên quan đến số, nhập số liệu nhanh hơn và chính xác hơn. Bàn di chuột nằm lệch về bên trái tạo không gian đặt tay rộng rãi, tránh vướng víu và ấn nhầm khi đánh máy.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/236315/2.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Máy còn có bản lề có thể mở 180 độ, giúp bạn dễ dàng điều chỉnh góc máy cũng như chia sẻ hình ảnh trong những cuộc họp.</p>\r\n                    <p class = \"paragraph__p\">Dọc hai bên thân máy được trang bị các cổng kết nối thông dụng như: USB 3.2, HDMI, USB 2.0 và khe đọc thẻ nhớ SD phục vụ tốt cho việc kết nối các thiết bị ngoại vi như máy chiếc, tivi, chuột, bàn phím,...</p>\r\n		    <p class = \"paragraph__p\">Ngoài ra, laptop còn hỗ trợ kết nối không dây Wi-Fi chuẩn 802.11 a/b/g/n/ac cho tốc độ truyền tải lên đến 1730 Mbps và Bluetooth v5.0 với tốc độ cao nhất đạt được là 50 Mbps, đảm bảo đường truyền ổn định trong quá trình làm việc.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/236315/7.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Xử lý công việc văn phòng mượt mà, tốc độ phản hồi nhanh chóng</h2>\r\n                    <p class = \"paragraph__p\">Chiếc laptop Lenovo IdeaPad này được trang bị con chip Intel Core i3 Ice Lake 1005G1 bạn có thể thực hiện tốt các công việc văn phòng trên các ứng dụng của Microsoft như: Word, Excel, Powerpoint,... với xung nhịp từ 1.20 GHz và đạt xung nhịp tối đa 3.4 GHz nhờ Turbo Boost</p>\r\n                    <p class = \"paragraph__p\">Đi cùng với CPU là card đồ họa Intel UHD Graphics giúp nâng cao khả năng xử lý hình ảnh của máy, đáp ứng tốt cho công việc đồ họa căn bản phục vụ tốt hơn cho công việc và giải trí.</p>\r\n		    <p class = \"paragraph__p\">RAM 4 GB GB DDR4 (On board + 1 khe) cho khả năng đa nhiệm mượt mà, bạn có thể mở cùng lúc 3 - 4 ứng dụng và chuyển đổi qua lại nhanh chóng không bị giật, lag hay bị đơ với tốc độ Bus RAM là 2666 MHz. Ngoài ra, máy cũng hỗ trợ nâng cấp RAM tối đa lên đến 12 GB đáp ứng nhu cầu cao hơn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236315/lenovo-ideapad-slim-3-15iil05-i3-81we0132vn-1-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">SSD 512 GB M.2 PCIe không chỉ đem đến không gian lưu trữ rộng mà còn mang đến cho bạn tốc độ khởi động máy và mở ứng dụng nhanh chóng, bạn sẽ chỉ mất khoảng 3 - 5 giây để hoàn thành các thao tác này. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236315/5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hình ảnh hiển thị sắc nét, màu sắc chân thực</h2>\r\n                    <p class = \"paragraph__p\">Màn hình Full HD kích thước 15.6 inch cho hình ảnh hiển thị rõ nét, màu sắc gần với tự nhiên nhờ mật độ điểm ảnh cao tái tạo hoàn hảo mọi chi tiết. Tấm nền TN cho tốc độ phản hồi có thể đạt tới 1 ms được xem là nhanh hơn rất nhiều so với những màn hình khác, tiết kiệm điện năng hiệu quả.</p>\r\n                    <p class = \"paragraph__p\">Chất lượng trải nghiệm hình ảnh của bạn sẽ được nâng cao hơn với công nghệ chống chói sáng Anti Glare màn hình sẽ không bị đổ bóng, lóe sáng làm hình ảnh bị mờ đi gây mỏi mắt khi dùng và thể hiện màu sắc chính xác</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236315/6.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Công nghệ âm thanh Stereo speakers đem đến cho bạn âm thanh to rõ, phát ra trong trẻo sống động, chất âm mạnh mẽ, nghe nhạc, học online thoải mái. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236315/8.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Laptop Lenovo IdeaPad Slim 3 15IIL05 i3 (81WE0132VN) là một sự lựa chọn tuyệt vời trong tầm giá cho những ai tìm kiếm một chiếc laptop vẻ ngoài thời trang, cấu hình ổn định phù hợp cho học tập và làm việc văn phòng.</p>\r\n                                       \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 4, 1),
(66, 2, 'Acer Aspire 5 A514 54 3204 i3 1115G4/4GB/512GB/Win10 (NX.A23SV.009)', '../assets/image/products/laptop/Acer-Aspire-5-A514-54-NX.A23SV.009/Nền.jpg', 14290000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i3 Tiger Lake, 1115G4, 3.00 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n4 GB, DDR4 (On board +1 khe), 2666 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe, Hỗ trợ khe cắm HDD SATA\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n14\", Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel UHD Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n3 x USB 3.2, HDMI, LAN (RJ45),USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ nhựa - nắp lưng bằng kim loại, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 17.95 mm, 1.46 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Acer Aspire 5 A514 54 3204 i3 1115G4/4GB/512GB/Win10 (NX.A23SV.009)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232715/Slider/acer-aspire-5-a514-54-3204-i3-nxa23sv009-311220-0227542.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Acer Aspire 5 A514 (NX.A23SV.009) thuộc dòng laptop học tập - văn phòng với thiết kế gọn gàng bạn dễ dàng mang theo bên mình. Cấu hình ổn định với CPU Intel thế hệ thứ 11 sẽ giúp bạn xử lý trơn tru các công việc.</h1>\r\n                    <h2 class = \"paragraph__h2\">Hiệu năng ổn định mượt mà </h2>\r\n                    <p class = \"paragraph__p\">Laptop được trang bị con chip Intel Core i3 gen 11 được sản xuất trên tiến trình sản xuất 10 nm ++ mới. Con chip này sẽ có xung nhịp cơ bản với các tác vụ nhẹ nhàng là 3 GHz gấp 1.5 lần so với thế hệ trước và xung nhịp tối đa lên tới 4.1 GHz nhờ công nghệ Turbo Boost. Máy được trang bị thêm card đồ họa tích hợp Intel UHD Graphics giúp nâng cao khả năng đồ họa của máy bạn có thể thiết kế đồ họa cơ bản trên một số ứng dụng như Adobe Photoshop, Adobe AI,..</p>\r\n		    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/232715/acer-aspire-5-a514-54-3204-i3-nxa23sv009-313220-023207.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bên cạnh đó máy còn sở hữu RAM 4 GB có tốc độ Bus RAM là 2666 MHz bạn có thể mở cùng lúc nhiều cửa sổ làm việc như: Word, Excel,... một cách mượt mà, không lo máy bị đơ hay giật lag giúp bạn hoàn thành công việc nhanh chóng và hiệu quả. Laptop còn hỗ trợ RAM tối đa lên đến 20 GB bạn có thể dễ dàng nâng cấp theo nhu cầu sử dụng.</p>\r\n                    <p class = \"paragraph__p\">Ổ cứng SSD 512 GB cho tốc độ khởi động máy hay vào ứng dụng cực nhanh chỉ trong vài giây giúp bạn tiết kiệm thời gian hơn. Với dung lượng 512 GB, bạn vô tư lưu trữ nào hình ảnh, tài liệu hay những bản nhạc yêu thích nếu bạn cảm thấy chưa đủ thì chiếc laptop Acer này còn hỗ trợ khe cắm HDD SATA giúp bạn mở rộng không gian lưu trữ.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/232715/acer-aspire-5-a514-54-3204-i3-nxa23sv009-313220-023237.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hình ảnh hiển thị sắc nét, góc nhìn rộng hơn</h2>\r\n                    <p class = \"paragraph__p\">Acer Aspire sở hữu màn hình Full HD (1920 x 1080) kích thước 14 inch cho hình ảnh hiển thị sắc nét, văn bản rõ nét đem đến cho bạn trải nghiệm học tập, làm việc tuyệt vời. Tấm nền IPS cho góc nhìn rộng hơn lên đến 178 độ, bạn có thể quan sát màn hình ở nhiều góc độ mà hình ảnh vẫn không bị biết đổi về kích thước và màu sắc. </p>\r\n                    <p class = \"paragraph__p\">Công nghệ màn hình Acer ComfyView giúp màn hình không bị bóng, chói sáng cho khả năng hiển thị ở nơi ánh sáng cao tốt hơn. Bên cạnh khả năng chống chói thì công nghệ này còn giúp giảm ánh sáng xanh, chống mỏi mắt khi bạn làm việc với máy tính trong thời gian dài.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232715/acer-aspire-5-a514-54-3204-i3-nxa23sv009-313320-023308.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bên cạnh hình ảnh thì âm thanh cũng là một yếu tố không thể thiếu trong những lúc giải trí, công nghệ âm thanh Acer TrueHarmony trên chiếc laptop này sẽ mang đến những âm tiết rõ ràng, cùng khả năng tái tạo âm thanh vòm cho bạn trải nghiệm xem phim, nghe nhạc sẽ tuyệt vời hơn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232715/acer-aspire-5-a514-54-3204-i3-nxa23sv009-313620-023611.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế gọn gàng, sang trọng</h2>\r\n                    <p class = \"paragraph__p\">Laptop Acer 5 A514 có lớp vỏ nhựa và mặt lưng kim loại vừa mang đến sự gọn nhẹ vừa sang trọng. Trọng lượng khá nhẹ chỉ 1.46 kg và mỏng 17.95 mm cực gọn gàng bạn có thể cho vào balo và mang theo bên mình đi bất kỳ đâu.</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232715/acer-aspire-5-a514-54-3204-i3-nxa23sv009-313420-023433.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Trang bị những cổng kết nối cần thiết cho việc trình chiếu hay in giấy tờ như: USB 3.2, HDMI, LAN (RJ45), USB Type-C,... và còn trang bị thêm 2 kết nối không dây tiện lợi là Wi-Fi 6 - 802.11ax có tốc độ truyền tải dữ liệu lên đến hơn 10.000 Mbps và Bluetooth 5.1 tương thích với nhiều thiết bị.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232715/acer-aspire-5-a514-54-3204-i3-nxa23sv009-313420-023444.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Acer Aspire 5 A514 (NX.A23SV.009) sẽ là chiếc máy tính phù hợp dành cho bạn, nếu bạn đang tìm một người bạn đồng hành với thiết kế mỏng nhẹ và cấu hình ổn định.</p>            \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 15, 1);
INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(67, 2, 'Acer Aspire 7 A715 42G R4ST R5 5500U/8GB/256GB/4GB GTX1650/Win10 (NH.QAYSV.004)', '../assets/image/products/laptop/Acer-Aspire-7-A715-42G-R4ST-R5-NH.QAYSV.004/Nền.jpg', 19990000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nAMD Ryzen 5, 5500U, 2.10 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB, DDR4 (2 khe), 3200 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD 256GB NVMe PCIe\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n15.6 inch, Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa rời, NVIDIA GeForce GTX1650 4GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 3.2, HDMI, LAN (RJ45), USB 2.0, USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ nhựa, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 22.9 mm, 2.1kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Acer Aspire 7 A715 42G R4ST R5 5500U/8GB/256GB/4GB GTX1650/Win10 (NH.QAYSV.004)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/235405/Slider/acer-aspire-7-a715-42g-r4st-r5-nhqaysv004-250321-0822190.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Laptop Acer Aspire 7 A715 42G R4ST R5 (NH.QAYSV.004) có thiết kế đơn giản, trang nhã nhưng trang bị cấu hình mạnh mẽ đáp ứng nhu cầu đồ hoạ, kỹ thuật chuyên nghiệp và chiến game mượt mà.</h1>\r\n                    <h2 class = \"paragraph__h2\">Xử lí mọi tác vụ với hiệu năng mạnh mẽ</h2>\r\n                    <p class = \"paragraph__p\">Laptop Acer Aspire 7 sở hữu bộ xử lí AMD Ryzen 5 5500U giúp máy hoạt động mượt mà, ổn định khi thực hiện mọi tác vụ và chơi game  với 6 lõi 12 luồng. Xử lí nhanh chóng các ứng dụng nặng như AutoCad, After Effect, Premiere,... nhờ xung nhịp trung bình của CPU AMD này là 2.1 GHz và được đẩy lên tối đa 4.0 GHz nhờ công nghệ Turbo Boost</p>\r\n                    <p class = \"paragraph__p\">Mẫu laptop này được Acer trang bị card đồ họa rời NVIDIA GeForce GTX 1650 4GB được thiết kế theo kiến trúc Turning hiện đại, đáp ứng tốt nhu cầu làm việc, giải trí. Bạn sẽ không cần phải lo lắng và luôn tràn đầy cảm hứng khi chiến các tựa game phổ biến nhất hiện nay như PUBG, Fornite, GTA V,... ở mức yêu cầu về độ hoạ trung bình và cao (Medium hoặc High).</p>\r\n		    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/235405/a3.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bộ nhớ RAM 8 GB giúp máy có thể mở cùng lúc nhiều ứng dụng mà không ngại giật, lag. Ngoài ra, Acer còn trang bị một khe RAM trống có thể hỗ trợ nâng cấp lên đến 32 GB, thoải mái đáp ứng nhu cầu của từng đối tượng người sử dụng.</p>\r\n                    <p class = \"paragraph__p\">Aspire 7 A715 trang bị sẵn ổ cứng 256 GB MVMe PCle cho laptop có tốc độ khởi động máy, mở ứng dụng, truy xuất dữ liệu nhanh chóng. Dung lượng lưu trữ dữ liệu từ ổ cứng này là đủ dùng để cài đặt các phần mềm, tựa game ưa thích và lưu dữ những tài liệu quan trọng.</p>\r\n		    <p class = \"paragraph__p\">Ngoài ra, tình trạng quá nhiệt ở laptop này sẽ hiếm khi xảy ra nhờ hệ thống hai quạt tản nhiệt mạnh mẽ, tối ưu hiệu năng trong thời gian dài sử dụng.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/235405/acer-aspire-7-a715-42g-r4st-r5-nhqaysv004-ssd.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế đơn giản, trang nhã</h2>\r\n                    <p class = \"paragraph__p\">Laptop Acer Aspire này vẫn giữ thiết kế phổ biến của một chiếc laptop với vỏ ngoài bằng nhựa, kết cấu cứng cáp, cho cảm giác chắc tay khi cầm. Chiếc laptop này có độ dày 22.9 mm và trọng lượng 2.1 kg bạn có thể để vào ba lô để mang theo khi đi làm việc.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/235405/a4.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bàn phím được trang bị đèn nền màu trắng giúp bạn gõ chính xác trong môi trường ánh sáng yếu.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/235405/a6.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Mẫu laptop Acer này sở hữu đầy đủ cổng kết nối phổ biến hiện nay: USB 3.2, HDMI, LAN (RJ45), USB 2.0 và USB Type-C giúp việc truyền tải dữ liệu giữa các thiết bị hiệu quả, tiện lợi.</p>\r\n		    <p class = \"paragraph__p\">Đặc biệt, khả năng kết nối không dây của mẫu này được nâng cấp thành chuẩn Wi-Fi 6 802.11ax với tốc độ truyền tải lên đến hơn 10.000 Mbps. Bên cạnh đó, laptop này đạt chuẩn Bluetooth v5.0 với tốc độ kết nối 50 Mbps.</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://www.thegioididong.com/images/44/235405/acer-aspire-7-a715-42g-r4st-r5-nhqaysv004-ckn.jpg\" alt=\"\">           \r\n                    <h2 class = \"paragraph__h2\">Trải nghiệm màn hình Full HD trên tấm nền IPS sinh động</h2>\r\n		    <p class = \"paragraph__p\">Laptop có kích thước 15.6 inch rộng rãi, độ phân giải Full HD (1920 x 1080) bắt mắt mang đến cho người dùng một không gian làm việc, giải trí thuận tiện. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/235405/a2.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Laptop Acer Aspire 7 A715 xứng đáng là một laptop đồ hoạ đáp ứng được đa dạng nhu cầu người sử dụng với hiệu năng và thiết kế mạnh mẽ.</p>            \r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n', 15, 1),
(68, 2, 'Asus VivoBook X515MA N5030/4GB/512GB/Win10 (EJ120T)', '../assets/image/products/laptop/Asus-VivoBook-X515MA-EJ120T/Nền.jpg', 8900000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\n	Intel Pentium, N5030, 1.10 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n	4 GB, DDR4 (1 khe), 2666 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\n	SSD: 512 GB, M.2 PCIe\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n15.6 inch, Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel UHD Graphics 605\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n1 x USB 3.2, 2 x USB 2.0, HDMI, USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ nhựa, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 19.9 mm, 1.8 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Asus VivoBook X515MA N5030/4GB/512GB/Win10 (EJ120T)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232444/Slider/asus-vivobook-x515ma-n5030-ej120t-221220-0744100.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Laptop Asus VivoBook X515MA (EJ120T) có cấu hình ổn định, thực hiện tốt những nhu cầu lướt web, xem phim,... cơ bản hằng ngày thích hợp với những sinh viên hay dân văn phòng cần một chiếc máy có giá phải chăng để học online hay làm việc với Word, Excel.</h1>\r\n                    <h2 class = \"paragraph__h2\">Hiệu năng ổn định đủ dùng </h2>\r\n                    <p class = \"paragraph__p\">Đây đích thị là một chiếc laptop đủ dùng cho những nhu cầu thường ngày bởi nó được trang bị CPU Intel Pentium N5030 xử lí tốt các ứng dụng văn phòng phổ biến trong công việc văn phòng như Word, Excel, PowerPoint,... hoặc cân được mọi ứng dụng học tập, làm việc trực tuyến như Microsoft Teams, Zoom, Skype, Google Hangouts và rồi lướt web xem phim thư giãn sau một ngày dài mệt mỏi.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232444/asus-vivobook-x515ma-n5030-ej120t-220120-080115.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Laptop có RAM 4 GB mang lại khả năng đa nhiệm ở mức ổn, vừa tính toán lương trên bảng tính Excel, vừa làm báo cáo gửi sếp, vừa nghe một bản nhạc không lời máy vẫn chạy tốt. Máy còn hỗ trợ nâng cấp RAM lên 16 GB nếu nhu cầu làm việc của bạn đòi hỏi mở nhiều tab cùng lúc.</p>\r\n                    <p class = \"paragraph__p\">Bất ngờ hơn là phần ổ cứng của máy. Thường với cấu hình này thì những chiếc laptop khác chỉ được trang bị ổ cứng SSD 256 GB nhưng với laptop Asus VivoBook X515MA (EJ120T) thì nhà Asus đã ưu ái hơn khi cài sẵn cho máy một ổ cứng SSD 512 GB, mang đến không gian lưu trữ lớn và tốc độ phản hồi các tác vụ siêu nhanh.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232444/asus-vivobook-x515ma-n5030-ej120t-220120-080154.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Kiểu dáng sang trọng, tinh tế</h2>\r\n		    <p class = \"paragraph__p\">Asus trang bị cho chiếc laptop này tính năng bảo mật vân tay hiện đại với mục đích tối giản thời gian gõ mật khẩu, nhanh chóng đăng nhập chỉ với một chạm.</p>\r\n                    <p class = \"paragraph__p\">Asus VivoBook X515MA có sắc vóc quen thuộc của dòng máy VivoBook được làm bằng vỏ nhựa phủ một lớp sơn màu bạc mang đến cảm giác sang, xịn như được chế tác từ kim loại. Trọng lượng 1.8 kg đối với việc di chuyển hay mang laptop theo bên mình hằng ngày sẽ không quá khó khăn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232444/asus-vivobook-x515ma-n5030-ej120t-220220-080254.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Asus trang bị cho chiếc laptop này tính năng bảo mật vân tay hiện đại với mục đích tối giản thời gian gõ mật khẩu, nhanh chóng đăng nhập chỉ với một chạm.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232444/asus-vivobook-x515ma-n5030-ej120t-250020-100040.jpg\" alt=\"\">\r\n		    <p class = \"paragraph__p\">Và khi nhắc đến nhu cầu học tập - văn phòng thì không thể quên được những buổi thuyết trình trên lớp hay in tài liệu, những bản kế hoạch nộp lên cấp trên. Và đó là lí do mà Asus VivoBook X515MA trang bị đầy đủ các cổng kết nối thông dụng như cổng USB 3.2, USB 2.0, HDMI và cổng USB Type-C.</p>\r\n		    <h2 class = \"paragraph__h2\">Màn hình viền mỏng giải trí cực đã</h2>\r\n		    <p class = \"paragraph__p\">Với thiết kế màn hình NanoEdge cho những chiếc laptop Asus đời mới thì VivoBook X515MA cũng không ngoại lệ. Kích thước màn hình rộng 15.6 inch đi kèm là những viền mỏng giúp tăng diện tích màn hình. Những hình ảnh được thể hiện trên chiếc laptop này đều sẽ cực kỳ sống động với độ phân giải Full HD, thỏa mãn mắt nhìn dù cho làm việc hay giải trí.</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232444/asus-vivobook-x515ma-n5030-ej120t-220420-080404.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Màn hình còn được trang bị công nghệ chống chói Anti Glare, điều đó có nghĩa là hiện tượng màn hình bị lóa sẽ không xảy ra khi bạn sử dụng chiếc laptop này ngoài trời nắng hay ngồi ở một quán cà phê vỉa hè có ánh sáng mạnh vẫn làm việc tốt.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232444/asus-vivobook-x515ma-n5030-ej120t-220820-080807.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Laptop Asus VivoBook X515MA (EJ120T) sở hữu một cấu hình ổn so với các tác vụ hằng ngày, phù hợp với học sinh sinh viên hoặc nhân viên văn phòng vì mức giá của máy khá phải chăng so với những gì nó mang lại.</p>            \r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n\r\n', 16, 1),
(69, 2, 'Asus VivoBook X515EP i5 1135G7/8GB/512GB/2GB MX330/Win10 (BQ011T)', '../assets/image/products/laptop/Asus-VivoBook-X515EP-BQ011T/Nền.jpg', 17590000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i5 Tiger Lake, 1135G7, 2.40 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB, DDR4 (On board +1 khe), 3200 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\n	SSD: 512 GB, M.2 PCIe\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n15.6 inch, Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\n	Card đồ họa rời, NVIDIA GeForce MX330, 2GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n1 x USB 3.2, 2 x USB 2.0, HDMI, USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ nhựa, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 19.9 mm, 1.8 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Asus VivoBook X515EP i5 1135G7/8GB/512GB/2GB MX330/Win10 (BQ011T)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236728/Slider/asus-vivobook-x515ep-i5-bq011t-090421-053056-120.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Laptop Asus VivoBook X515EP i5 (BQ011T) thuộc dòng laptop học tập - văn phòng sở hữu vi xử lý Intel thế hệ 11 mạnh mẽ và card đồ họa rời nâng cao hiệu năng khi thiết kế đồ họa hay chơi game, tất cả được gói gọn trong lớp vỏ ngoài sang trọng và thanh lịch, phù hợp cho nhu cầu học tập, làm việc hằng ngày.</h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế tối giản, bảo mật vân tay hiện đại</h2>\r\n                    <p class = \"paragraph__p\">Laptop Asus VivoBook luôn được đánh giá cao về ngoại hình hiện đại và thời trang, tổng thể màu bạc hướng đến phong cách thanh lịch và các góc được bo nhẹ mềm mại nhưng không lỗi thời. Viền màn hình NanoEdge cho không gian hiểu thị rộng hơn với tỷ lệ màn hình trên kích thước là 83%.</p>\r\n                    <p class = \"paragraph__p\">Khung máy bằng nhựa cao cấp tản nhiệt tốt hơn trọng lượng 1.8 kg, không quá nặng để mang theo khi đi làm hay đi học. Thân máy dày 19.9 mm được thiết kế cắt vát mặt đáy tạo cảm giác mỏng hơn. Kích thước dài 360.2 mm và rộng 234.9 mm, bạn sẽ cần một chiếc balo khá rộng để cất gọn vào.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236728/asus-vivobook-x515ep-i5-bq011t-2-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bàn phím full-size được thiết kế hướng tới trải nghiệm người dùng với cấu trúc một mảnh chắc chắn và hành trình phím 1.4 mm mang lại cảm giác thoải mái khi đánh máy. Bàn di chuột lệch về bên trái cho không gian đặt tay rộng rãi và hạn chế gõ nhầm khi sử dụng cụm phím số bên phải</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236728/asus-vivobook-x515ep-i5-bq011t-7-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Ngoài ra, bảo mật vân tay được tích hợp ở góc phải bàn di chuột cho phép bạn mở khóa thiết bị một cách rất nhanh chóng chỉ với một chạm, vô cùng tiện lợi so với nhập mật khẩu truyền thống.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236728/asus-vivobook-x515ep-i5-bq011t-6-1.jpg\" alt=\"\">		    \r\n		    <h2 class = \"paragraph__h2\">Hiệu năng mạnh mẽ, chất lượng đồ họa nâng cao</h2>\r\n                    <p class = \"paragraph__p\">Với sức mạnh từ con chip Intel Core i5 Tiger Lake 1135G7 thế hệ 11 gồm 4 nhân và 8 luồng, bạn có thể thực hiện các công việc từ thiết kế cơ bản trên các phần mềm như Photoshop, Illustrator,... đến chơi các game online và offline. Xung nhịp cơ bản đạt 2.40 GHz và đạt xung nhịp tối đa là 4.2 GHz khi thực hiện các tác vụ nặng nhờ công nghệ Turbo Boost.</p>\r\n		    <p class = \"paragraph__p\">Bộ nhớ RAM 8 GB chuẩn DDR4 cho khả năng chạy các ứng dụng nặng và đa nhiệm mượt mà, mở được cùng lúc nhiều ứng dụng như vừa nghe nhạc vừa làm việc trên Word với tốc bộ truyền tải cực nhanh lên đến 3200 MHz. Bên cạnh đó, máy cũng hỗ trợ nâng cấp RAM tối đa lên đến 20 GB, bạn có thể nâng cấp mức RAM phù hợp để phục vụ nhu cầu của mình.</p>\r\n                    <p class = \"paragraph__p\">Card đồ họa rời NVIDIA GeForce MX330 2 GB sở hữu 384 nhân đồ họa (Cuda core), bạn có thể thiết kế đồ họa và chơi các game online hiện nay như Liên Minh Huyền Thoại, CS:Go,... ở mức trung bình. Công nghệ Nvidia GameWorks giả lập các hiệu ứng trong game như hiệu ứng khói, cháy nổ,... làm cho không gian ảo trở nên chân thật và gần với thực tế hơn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236728/asus-vivobook-x515ep-i5-bq011t-4-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Máy được trang bị ổ cứng SSD M.2 PCIe 512 GB tăng tốc khởi động máy chỉ trong 10 - 15 giây, mở các ứng dụng trong vài giây và phản hồi các thao tác phức tạp nhanh đến không ngờ nhờ tốc độ đọc ghi gấp 6 lần so với SATA III, đạt tới 3500 MB/s. Bộ nhớ lớn cho bạn thoải mái lưu trữ tài liệu hay tải về các bộ phim và các tựa game ưa thích</p>                    \r\n		    <h2 class = \"paragraph__h2\">Đắm chìm trong không gian âm thanh hình ảnh rộng và sống động</h2>\r\n		    <p class = \"paragraph__p\">Laptop 15.6 inch cho không gian hiển thị rộng lớn và độ phân giải Full HD (1920 x 1080) cho chất lượng hình ảnh siêu sắc nét nhờ mật độ điểm ảnh dày, mang lại cảm giác đắm chìm và thỏa mãn thị giác.</p>          \r\n                    <p class = \"paragraph__p\">Chiếc laptop Asus này còn được trang bị thêm lớp phủ chống chói Anti Glare giúp màn hình không bị bóng, lóe sáng, nhờ vậy bạn có thể thực sự tập trung vào những gì trước mắt. Công nghệ LED Backlit đóng vai trò hỗ trợ cho màu sắc tươi sáng hơn, đẹp và độ tương phản hình ảnh cao hơn.</p>                    \r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236728/asus-vivobook-x515ep-i5-bq011t-8-1.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Bên cạnh phần hình ảnh, chất lượng âm thanh cũng được chú trọng khi laptop sở hữu công nghệ âm thanh độc quyền của Asus là SonicMaster, giúp âm thanh trở nên to, rõ ràng, lọc bỏ tiếng ồn, âm bass sâu và hơn nữa là âm thanh vòm sinh động, tạo cho người nghe cảm giác hình ảnh đang được tái hiện lại trước mặt mình.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236728/asus-vivobook-x515ep-i5-bq011t-9-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Nếu bạn đang tìm kiếm một chiếc laptop đồng hành trong học tập hay công việc, Asus VivoBook X515EP i5 với sức mạnh từ CPU hiện đại và chất lượng hình ảnh, đồ họa vượt trội sẽ là sự lựa chọn thông minh cho bạn.</p>            \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 16, 1),
(70, 2, 'Asus VivoBook X415EA i3 1115G4/4GB/512GB/Win10 (EK044T)', '../assets/image/products/laptop/Asus-VivoBook-X415EA-EK044T/Nền.jpg', 11990000, 0, 5, 7, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i5 Tiger Lake, 1135G7, 2.40 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n4 GB, DDR4 (On board +1 khe), 3200 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\n	SSD: 512 GB, M.2 PCIe, Hỗ trợ khe cắm HDD SATA\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n14\", Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel UHD Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n1 x USB 3.2, 2 x USB 2.0, HDMI, USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ nhựa, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 19.9 mm, 1.8 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Asus VivoBook X415EA i3 1115G4/4GB/512GB/Win10 (EK044T)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/235815/Slider/vi-vn-asus-vivobook-x415ea-i3-ek044t-4.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Laptop Asus VivoBook X415EA i3 (EK044T) là một trong những chiếc máy tính xách tay nhỏ gọn nhất thế giới, đi kèm hiệu năng mạnh mẽ và chất lượng hình ảnh vô cùng sắc nét, chắc chắn là sự lựa chọn tốt nhất cho những người năng động và di chuyển nhiều.</h1>\r\n                    <h2 class = \"paragraph__h2\">Tối ưu kích thước, bắt kịp nhịp sống nhanh của bạn</h2>\r\n                    <p class = \"paragraph__p\">Laptop Asus VivoBook có bộ khung nhựa phủ màu bạc ánh kim giúp thân máy nhẹ hơn nhưng vẫn không mất đi vẻ tinh tế và sang trọng. Khung viền siêu mỏng đem đến phong cách thời thượng và cho tỷ lệ màn hình trên thân máy lên đến 82% mà vẫn giữ được sự tối ưu kích thước.</p>\r\n                    <p class = \"paragraph__p\">Với trọng lượng tổng thể chỉ 1.55 kg, thân máy dày 19.9 mm và kích thước xấp xỉ 1 tờ giấy A4, chiếc laptop này trông gọn nhẹ như một cuốn sách và bạn có thể dễ dàng cất vào balo để mang theo hằng ngày. </p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/235815/asus-vivobook-x415ea-i3-ek044t-02.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bàn phím được thiết kế hướng tới trải nghiệm người dùng với cấu trúc một mảnh chắc chắn và hành trình phím 1.4 mm mang lại cảm giác thoải mái khi đánh máy. Với cảm biến vân tay tích hợp trong bàn di chuột, bạn có thể mở khóa thiết bị một cách rất nhanh chóng chỉ với một chạm, vô cùng tiện lợi so với nhập mật khẩu truyền thống.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/235815/asus-vivobook-x415ea-i3-ek044t-6-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Khả năng kết nối mạnh mẽ thông qua chuẩn kết nối hiện đại USB 3.2 sử dụng cổng giao tiếp Type-C cho tốc độ tuyền tải 20 Gbps tương đương với 2500 MB/s. Các chuẩn kết nối quen thuộc khác gồm 1 cổng USB Type-C, 2 cổng USB 2.0, 1 đầu ra HDMI giúp bạn dễ dàng kết nối tất cả các thiết bị ngoại vi, màn hình và máy chiếu của mình.</p>                    \r\n		    <h2 class = \"paragraph__h2\">Xử lý nhanh hơn, lưu trữ nhiều hơn</h2>\r\n                    <p class = \"paragraph__p\">Chiếc laptop Asus này được trang bị con chip Intel Core i3 Tiger Lake 1115G4 thế hệ thứ 11 gồm 2 nhân và 4 luồng, bạn có thể thực hiện các công việc từ thiết kế trên các phần mềm như Photoshop, Illustrator,... đến chơi các game có cấu hình nhẹ. Xung nhịp trung bình đạt 3.00 GHz ở các tác vụ nhẹ nhàng như mở trình duyệt, Word, Excel,... và xung nhịp tối đa là 4.1 GHz ở các tác vụ nâng cao hơn nhờ Turbo Boost.</p>\r\n		    <p class = \"paragraph__p\">Card đồ họa tích hợp Intel UHD Graphics cho hiệu năng ổn định, đáp ứng tốt nhu cầu làm việc và giải trí.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/235815/asus-vivobook-x415ea-i3-ek044t-4-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bộ nhớ RAM 4 GB chuẩn DDR4 phục vụ tốt các tác vụ văn phòng hằng ngày như lướt web, soạn thảo, xử lý hình cơ bản bằng Photoshop và mở được cùng lúc nhiều ứng dụng như vừa nghe nhạc vừa làm việc trên Word với tốc bộ truyền tải cực nhanh lên đến 3200 MHz. Bên cạnh đó, máy cũng hỗ trợ nâng cấp RAM tối đa lên đến 20 GB, bạn có thể nâng cấp mức RAM phù hợp để phục vụ nhu cầu của mình.</p>\r\n                    <p class = \"paragraph__p\">Máy sử dụng ổ cứng SSD M.2 PCIe 512 GB tăng tốc khởi động máy, đặc biệt từ trạng thái ngủ đông và phản hồi các thao tác phức tạp nhanh đến không ngờ nhờ tốc độ đọc ghi gấp 6 lần so với SATA III, đạt tới 3500 MB/s. </p>                    \r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/235815/asus-vivobook-x415ea-i3-ek044t-5-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hình ảnh và âm thanh sống động đưa bạn đến gần thế giới thực </h2>\r\n		    <p class = \"paragraph__p\">Màn hình 14 inch độ phân giải Full HD (1920 x 1080) cho chất lượng hình ảnh siêu sắc nét nhờ mật độ điểm ảnh dày, trang bị thêm lớp phủ chống chói Anti Glare giúp màn hình không bị bóng, lóe sáng, nhờ vậy bạn có thể thực sự tập trung vào những gì trước mắt. Công nghệ LED Backlit đóng vai trò hỗ trợ cho màu sắc tươi sáng hơn, đẹp và độ tương phản hình ảnh cao hơn.</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/235815/asus-vivobook-x415ea-i3-ek044t-7-1.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Chiếc laptop này còn sở hữu công nghệ SonicMaster audio độc quyền của Asus, cho chất lượng âm thanh to, rõ ràng, lọc bỏ tiếng ồn, âm bass sâu và đặc biệt là hiệu ứng âm thanh vòm sinh động, tạo người nghe cảm giác khung cảnh đang được tái hiện lại trước mặt.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/235815/asus-vivobook-x415ea-i3-ek044t-8-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Cho dù để làm việc hay giải trí, Asus VivoBook X415EA i3 thuộc dòng laptop học tập - văn phòng vẫn có thể đáp ứng được hầu hết các nhu cầu nhờ vào hiệu năng mạnh mẽ và chất lượng hình ảnh cũng như âm thanh sống động, đây là sự lựa chọn tuyệt vời trong phân khúc.</p>            \r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n', 16, 1),
(71, 2, 'Apple MacBook Air 2017 i5 1.8GHz/8GB/128GB (MQD32SA/A)', '../assets/image/products/laptop/Apple-MacBook-Air-2017-MQD32SAA/Nền.jpg', 19990000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i5 Broadwell, 1.80 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB, DDR3L(On board), 1600 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 128 GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n13.3 inch, WXGA+(1440 x 900)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel HD Graphics 6000\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\nMagSafe 2, 2 x USB 3.0, Thunderbolt 2\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nMac OS\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ kim loại nguyên khối, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 17 mm, 1.35 Kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2017\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Apple MacBook Air 2017 i5 1.8GHz/8GB/128GB (MQD32SA/A)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/106875/Slider/vi-vn-macbook-air-2017.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">MacBook Air 2017 i5 128GB là mẫu laptop văn phòng, có thiết kế siêu mỏng và nhẹ, vỏ nhôm nguyên khối sang trọng. Máy có hiệu năng ổn định, thời lượng pin cực lâu 12 giờ, phù hợp cho hầu hết các nhu cầu làm việc và giải trí. </h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế siêu mỏng và nhẹ </h2>\r\n                    <p class = \"paragraph__p\">Macbook Air 2017 mang những đặc trưng thiết kế của dòng MacBook Air với trọng lượng và độ dày của laptop lần lượt là 1.7 cm và 1.35 kg rất tiện lợi và dễ dàng giúp người dùng không cảm thấy bất tiện khi mang trên vai thường xuyên khi đi học hay đi làm. </p>\r\n                    <p class = \"paragraph__p\">Đây cũng là chiếc MacBook chính hãng có giá rẻ nhất hiện tại, phù hợp với mọi người tiêu dùng. </p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-4.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hiệu năng mượt mà</h2>                    \r\n		    <p class = \"paragraph__p\">Macbook Air MQD32SA/A i5 5350U có bộ xử lý Intel Core i5 Broadwell hai nhân xung nhịp 1.8 GHz, bộ nhớ RAM 8 GB thoải mái trong việc duyệt web, chơi game, sử dụng mượt mà các thao tác trong phần mềm Office, Photoshop, AI…</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-1-1.jpg\" alt=\"\">                    \r\n		    <p class = \"paragraph__p\">Ổ cứng lưu trữ SSD 128 GB cho tốc độ cực nhanh, thời gian để bạn khởi động máy chỉ khoảng 10-15 giây, dung lượng 128 GB không quá dư giả, nhưng vẫn đủ để lưu trữ các tài liệu, hình ảnh, ứng dụng thường dùng.</p>\r\n                    <h2 class = \"paragraph__h2\">Thời lượng pin cực lâu</h2>\r\n                    <p class = \"paragraph__p\">Macbook Air MQD32SA/A i5 5350U cho thời lượng sử dụng lên đến 12 tiếng sau một lần sạc đầy khi sử dụng soạn thảo và lướt web thông thường. </p>\r\n                    <p class = \"paragraph__p\">Với các tác vụ nặng hơn như Photoshop, lướt web từ 10-20 tab, sử dụng các ứng dụng nhắn tin, gọi video khi làm việc thì chiếc máy vẫn trụ được khoảng 5-7 tiếng liên tục.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình đáp ứng nhu cầu sử dụng hàng ngày</h2>\r\n                    <p class = \"paragraph__p\">Mặc dù chưa được nâng cấp lên màn hình Retina siêu nét như các dòng khác, màn hình MacBook Air 2017 vẫn cho ra màu sắc trung thực, và độ tương phản khá tốt nhờ tấm nền LED Backlit chất lượng cao.</p>        \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-manhinh.jpg\" alt=\"\">           \r\n                    <h2 class = \"paragraph__h2\">Vẫn giữ đầy đủ các cổng kết nối cần thiết</h2>                     \r\n		    <p class = \"paragraph__p\">Macbook Air MQD32SA/A i5 5350U là dòng MacBook duy nhất ở hiện tại còn trang bị 2 cổng USB 3.0, SD card giúp khách hàng thuận tiện hơn trong việc gắn kèm phụ kiện như chuột, bàn phím hay chia sẻ dữ liệu qua USB, thẻ nhớ máy ảnh…</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/106875/apple-macbook-air-mqd32sa-a-i5-5350u-400-cong-ket-noi.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Macbook Air MQD32SA/A i5 5350U với bàn rê có kích thước lớn và bề mặt trơn nhẵn dễ thao tác. Trackpad này còn hỗ trợ nhiều thao tác sử dụng tiện lợi như: Cuộn, thu phóng, chuyển qua lại giữa các ứng dụng, mở khay ứng dụng nhanh chóng...</p>            \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 1, 1),
(72, 2, ' Acer Aspire 3 A315 54K 37B0 i3 8130U/4GB/256GB/Win10 (NX.HEESV.00D)', '../assets/image/products/laptop/Acer-Aspire-3-A315-54K-37B0-NX.HEESV.00D/Nền.jpg', 10990000, 0, 5, 2, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i3 Coffee Lake, 8130U, 2.20 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n4 GB, DDR4 (On board +1 khe), 2400 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD 256GB NVMe PCIe, Hỗ trợ khe cắm HDD SATA\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n15.6 inch, Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel HD Graphics 620\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 2.0, USB 3.1, HDMI, LAN (RJ45)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ nhựa, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 19.9 mm, 1.7 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2019\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Acer Aspire 3 A315 54K 37B0 i3 8130U/4GB/256GB/Win10 (NX.HEESV.00D)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232192/Slider/lenovo-ideapad-slim-5-15itl05-i5-82fg001pvn-281220-0530290.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Acer Aspire 3 A315 54K 37B0 i3 (NX.HEESV.00D) là mẫu laptop học tập - văn phòng được thiết kế gọn nhẹ, vẫn mang đến nhiều điểm nổi bật khi sở hữu cấu hình tốt, ổ cứng SSD cực nhanh và màn hình Full HD sắc nét. Sản phẩm sẽ là lựa chọn tuyệt vời trong tầm giá.</h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế gọn nhẹ, thời trang </h2>\r\n                    <p class = \"paragraph__p\">Laptop có một thiết kế theo phong cách đơn giản, hiện đại đúng xu thế. Viền màn hình được làm mỏng đáng kể, tối ưu không gian làm việc hơn đồng thời giúp sản phẩm trở nên gọn gàng.</p>\r\n		    <p class = \"paragraph__p\">Máy có thiết kế mỏng nhẹ đặc trưng của dòng Acer Aspire A315. Với trọng lượng chỉ 1.7 kg cho một chiếc laptop 15.6 inch, Aspire 3 A315 mới rất dễ để di chuyển, tiện lợi cho công việc của bạn. </p>\r\n                    <p class = \"paragraph__p\">Máy có bản lề gập 180 độ thuận lợi hơn để giao tiếp với đối tác hay giao tiếp với bạn bè khi học nhóm.</p>		    \r\n	   	    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/221015/acer-aspire-3-a315-54k-i3-nxheesv00d-kg.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình Full HD, sắc nét</h2>\r\n                    <p class = \"paragraph__p\">Màn hình 15.6 inch viền mỏng của Acer Aspire 3 A315 mang đến chất lượng hiển thị tuyệt vời nhờ độ phân giải Full HD sắc nét, đủ để bạn giải trí với những bộ phim dài tập hay làm việc cả ngày dài mà không bị mỏi mắt.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/221015/acer-aspire-3-a315-54k-i3-nxheesv00d-inch.jpg\" alt=\"\">               \r\n	 	    <h2 class = \"paragraph__h2\">Cấu hình tốt trong tầm giá</h2>		    	\r\n		    <p class = \"paragraph__p\">Acer Aspire A315 được trang bị bộ vi xử lý Intel Core i3 Coffee Lake, đi cùng là 4 GB RAM, giải quyết tốt các hoạt động làm việc và giải trí thường ngày như xem Youtube, Word, Excel,...</p>                         \r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/221015/acer-aspire-3-a315-54k-i3-nxheesv00d-i34gb.jpg\" alt=\"\">	\r\n		    <p class = \"paragraph__p\">Với sự trợ giúp của ổ cứng SSD 256 GB, laptop khởi động, vào ứng dụng chưa tới 10 giây và sao chép dữ liệu cực nhanh.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232192/lenovo-ideapad-slim-5-15itl05-i5-82fg001pvn-140020-060034.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Được trang bị đầy đủ các cổng kết nối truyền thống lẫn hiện đại như: USB 3.1, HDMI, LAN (RJ45), USB 2.0, USB Type-C, Acer Aspire 3 A315 54K giúp cho người dùng kết nối với các thiết bị ngoại vi dễ dàng hơn cũng như nhập, xuất dữ liệu nhanh chóng hơn.</p>\r\n		    <p class = \"paragraph__p\">Acer Aspire 3 A315 54K 37B0 i3 (NX.HEESV.00D) là mẫu máy tính xách tay dành cho học sinh, sinh viên hoặc nhân viên văn phòng có thu nhập tầm trung, cần một chiếc máy mỏng nhẹ mà cấu hình vẫn đủ dùng. </p>               \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 15, 1),
(73, 2, 'Lenovo ThinkBook 14 G2 ITL i5 1135G7/8GB/512GB/Win10 (20VD003KVN)', '../assets/image/products/laptop/Lenovo-ThinkBook-14-G2-ITL-20VD003KVN/Nền.jpg', 17990000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i5 Tiger Lake, 1135G7, 2.40 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB, DDR4 (On board +1 khe), 3200 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe, Hỗ trợ khe cắm HDD SATA\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n14\", Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel Iris Xe Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 3.2, Thunderbolt4 USB-C, HDMI, LAN (RJ45), USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ kim loại, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 14.9 mm, 1.4 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo ThinkBook 14 G2 ITL i5 1135G7/8GB/512GB/Win10 (20VD003KVN)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236960/Slider/lenovo-thinkbook-14-g2-itl-i5-20vd003kvn-01-800x444.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Lenovo ThinkBook 14 G2 ITL i5 (20VD003KVN) sở hữu con chip Intel Gen 11 mạnh mẽ giúp bạn giải quyết công việc nhanh chóng. Thiết kế gọn nhẹ thuận tiện mang theo bên mình xử lý công việc mọi lúc mọi nơi.</h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế cao cấp, sang trọng </h2>\r\n                    <p class = \"paragraph__p\">Chiếc laptop Lenovo này có thiết kế tối giản từ các đường nét thanh mảnh đến gam màu xám trung tính, mang đến sự hài hòa với mọi phong cách. Vỏ máy được làm từ chất liệu kim loại cứng cáp bảo vệ tốt các linh kiện bên trong khi xảy ra va đập, không chỉ mang đến sự bền bỉ lớp vỏ này còn tô điểm cho vẻ ngoài của máy bởi sự bóng bẩy.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236960/lenovo-thinkbook-14-g2-itl-i5-20vd003kvn-2-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bản lề gập mở 180 độ giúp bạn tương tác với màn hình nhiều góc độ hơn cũng như tăng tuổi thọ cho máy khi bạn lỡ tay mở màn hình mạnh thì cũng không bị gãy bản lề. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236960/lenovo-thinkbook-14-g2-itl-i5-20vd003kvn-02.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Máy đạt chuẩn độ bền chuẩn quân sự Mỹ MIL-STD-810H chứng thực khả năng chịu đựng của máy khi bị va đập, rơi từ trên cao, thay đổi nhiệt độ đột ngột, bền bỉ để có thể đồng hành lâu dài cùng bạn trong công việc.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236960/lenovo-thinkbook-14-g2-itl-i5-20vd003kvn-7-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Bảo mật và kết nối</h2>\r\n                    <p class = \"paragraph__p\">Nút nguồn được tích hợp thêm cảm biến vân tay giúp bạn mở máy nhanh chóng, không cần nhập mật khẩu phiền phức như trước đây. Bạn cũng có thể sử dụng vân tay thay cho mật khẩu khi thanh toán online trên laptop đảm bảo an toàn cho tài khoản ngân hàng của bạn khỏi những kẻ gian.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236960/lenovo-thinkbook-14-g2-itl-i5-20vd003kvn-8-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Độ phân giải Full HD cho hình ảnh hiển thị sắc nét</h2>\r\n		    <p class = \"paragraph__p\">Màn hình 14 inch rộng rãi, cho không gian hiển thị lớn, tấm nền IPS hỗ trợ góc nhìn rộng hơn, thoải mái hơn khi quan sát, hình ảnh không bị biến đổi khi nhìn nghiêng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/223534/lenovo-ideapad-3-15iil05-i3-81we003rvn-inch-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Phía trên viền màn hình có công tắc khóa webcam khi không sử dụng, đây là công tắc độc quyền của nhà Lenovo giúp bạn tránh bị đánh cắp thông tin cá nhân mà không phải dán sticker. </p>\r\n                    <p class = \"paragraph__p\">Tạo hiệu ứng âm thành vòm sống động và loại bỏ các tạp âm ​giúp âm thanh phát ra trong trẻo hơn, chi tiết với công nghệ âm thanh Dolby AudioPremium.</p>          \r\n                    <p class = \"paragraph__p\">Với ngoại hình bắt mắt, màn hình lớn hình ảnh sắc nét, cấu hình ổn định Lenovo ThinkBook 14 G2 ITL i5 (20VD003KVN) sẽ là sự lựa chọn tuyệt vời trong phân khúc laptop học tập - văn phòng.</p>\r\n                                       \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 4, 1);
INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(74, 2, 'Lenovo IdeaPad S145 15IIL i5 1035G1/8GB/512GB/Win10 (81W80021VN)', '../assets/image/products/laptop/Lenovo-IdeaPad-S145-15IIL-81W80021VN/Nền.jpg', 15490000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i3 Ice Lake, 1005G1, 1.20 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB, DDR4 (On board 4GB +1 khe 4GB), 2666 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe, Hỗ trợ khe cắm HDD SATA\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n15.6 inch, Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel UHD Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 3.0, HDMI, USB 2.0\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ nhua, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 19.9 mm, 1.85 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>\r\n', '\r\n<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo IdeaPad S145 15IIL i5 1035G1/8GB/512GB/Win10 (81W80021VN)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/214720/Slider/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-113920-033951.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Lenovo IdeaPad S145 15IIL i5 (81W80021VN) là mẫu laptop có cấu hình đáp ứng tốt công việc văn phòng, học tập. Thiết bị sở hữu ổ cứng SSD cho tốc độ xử lí nhanh chóng, màn hình Full HD sắc nét.</h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế gọn gàng, phong cách trẻ trung</h2>\r\n                    <p class = \"paragraph__p\">Laptop Lenovo có lớp vỏ bằng nhựa được phủ gam màu xám bạc hiện đại, máy có trọng lượng 1.85 kg và có độ dày 19.9 m, bạn có thể dễ dàng bỏ vào balo mang theo xử lý công việc mọi lúc mọi nơi.</p>\r\n                    <p class = \"paragraph__p\">Cụm bàn phím full size có đủ bàn phím số thuận tiện cho các công việc liên quan đến số, nhập số liệu nhanh hơn và chính xác hơn. Bàn di chuột nằm lệch về bên trái tạo không gian đặt tay rộng rãi, tránh vướng víu và ấn nhầm khi đánh máy.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-179kg.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Máy còn có bản lề có thể mở 180 độ, giúp bạn dễ dàng điều chỉnh góc máy cũng như chia sẻ hình ảnh trong những cuộc họp.</p>\r\n                    <p class = \"paragraph__p\">Dọc hai bên thân máy được trang bị các cổng kết nối thông dụng như: USB 3.2, HDMI, USB 2.0 và khe đọc thẻ nhớ SD phục vụ tốt cho việc kết nối các thiết bị ngoại vi như máy chiếc, tivi, chuột, bàn phím,...</p>\r\n		    <p class = \"paragraph__p\">Ngoài ra, laptop còn hỗ trợ kết nối không dây Wi-Fi chuẩn 802.11 a/b/g/n/ac cho tốc độ truyền tải lên đến 1730 Mbps và Bluetooth v5.0 với tốc độ cao nhất đạt được là 50 Mbps, đảm bảo đường truyền ổn định trong quá trình làm việc.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-usb.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Xử lý công việc văn phòng mượt mà, tốc độ phản hồi nhanh chóng</h2>\r\n                    <p class = \"paragraph__p\">Chiếc laptop Lenovo IdeaPad này được trang bị con chip Intel Core i3 Ice Lake 1005G1 bạn có thể thực hiện tốt các công việc văn phòng trên các ứng dụng của Microsoft như: Word, Excel, Powerpoint,... với xung nhịp từ 1.20 GHz và đạt xung nhịp tối đa 3.4 GHz nhờ Turbo Boost</p>\r\n                    <p class = \"paragraph__p\">Đi cùng với CPU là card đồ họa Intel UHD Graphics giúp nâng cao khả năng xử lý hình ảnh của máy, đáp ứng tốt cho công việc đồ họa căn bản phục vụ tốt hơn cho công việc và giải trí.</p>\r\n		    <p class = \"paragraph__p\">RAM 4 GB GB DDR4 (On board + 1 khe) cho khả năng đa nhiệm mượt mà, bạn có thể mở cùng lúc 3 - 4 ứng dụng và chuyển đổi qua lại nhanh chóng không bị giật, lag hay bị đơ với tốc độ Bus RAM là 2666 MHz. Ngoài ra, máy cũng hỗ trợ nâng cấp RAM tối đa lên đến 12 GB đáp ứng nhu cầu cao hơn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-i5-10.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">SSD 512 GB M.2 PCIe không chỉ đem đến không gian lưu trữ rộng mà còn mang đến cho bạn tốc độ khởi động máy và mở ứng dụng nhanh chóng, bạn sẽ chỉ mất khoảng 3 - 5 giây để hoàn thành các thao tác này. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236315/5.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hình ảnh hiển thị sắc nét, màu sắc chân thực</h2>\r\n                    <p class = \"paragraph__p\">Màn hình Full HD kích thước 15.6 inch cho hình ảnh hiển thị rõ nét, màu sắc gần với tự nhiên nhờ mật độ điểm ảnh cao tái tạo hoàn hảo mọi chi tiết. Tấm nền TN cho tốc độ phản hồi có thể đạt tới 1 ms được xem là nhanh hơn rất nhiều so với những màn hình khác, tiết kiệm điện năng hiệu quả.</p>\r\n                    <p class = \"paragraph__p\">Chất lượng trải nghiệm hình ảnh của bạn sẽ được nâng cao hơn với công nghệ chống chói sáng Anti Glare màn hình sẽ không bị đổ bóng, lóe sáng làm hình ảnh bị mờ đi gây mỏi mắt khi dùng và thể hiện màu sắc chính xác</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/214720/lenovo-ideapad-s145-15iil-i5-1035g1-8gb-512gb-win1-156inch.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Công nghệ âm thanh Stereo speakers đem đến cho bạn âm thanh to rõ, phát ra trong trẻo sống động, chất âm mạnh mẽ, nghe nhạc, học online thoải mái. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236315/8.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Laptop Lenovo IdeaPad sử dụng được khoảng 4 tiếng nếu sử dụng liên tục các tác vụ văn phòng. Với thời lượng này, bạn có thể yên tâm sử dụng cả ngày mà không sợ hết pin gây gián đoạn công việc.  </p>\r\n                                       \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 4, 1),
(75, 2, 'Lenovo IdeaPad Gaming 3 15IMH05 i7 10750H/8GB/512GB/4GB GTX1650/Win10 (81Y40068VN)', '../assets/image/products/laptop/Lenovo-IdeaPad-Gaming-3-15IMH05-81Y40068VN/Nền.jpg', 23990000, 0, 5, 1, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i7 Comet Lake, 10750H, 2.60 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB, DDR4 (2 khe), 2933 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n15.6 inch, Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa rời, NVIDIA GeForce GTX1650 4GB\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 3.2, USB Type-C (Power Delivery and DisplayPort), HDMI\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ nhựa - nắp lưng bằng kim loại, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 23 mm, 2.2 Kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo IdeaPad Gaming 3 15IMH05 i7 10750H/8GB/512GB/4GB GTX1650/Win10 (81Y40068VN)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230161/Slider/lenovo-thinkbook-15iil-i3-20sm00d9vn-301020-1029150.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Sở hữu cấu hình khủng bao gồm chip Intel Core i7 gen 10, RAM 8 GB cùng card đồ họa rời mạnh mẽ, Lenovo Gaming 15IMH05 i7 (81Y40068VN) hoàn toàn xứng danh là một trong những chiếc laptop chơi game mạnh hiện nay.</h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế thời thượng, mạnh mẽ</h2>\r\n                    <p class = \"paragraph__p\">Vẫn giữ nét đặc trưng là sự đơn giản, laptop Lenovo Gaming 15IMH05 có thiết kế bề mặt nhẵn mịn với logo nhà hãng ở cạnh bên, tuy nhiên mặt lưng tráng màu sơn tối nên trông sản phẩm chắc chắn và mạnh mẽ hơn mà vẫn không kém phần sang trọng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/225389/lenovo-gaming-15imh05-i7-81y40068vn-215520-085528.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Phần trackpad nằm lệch về bên trái tạo không gian rộng rãi để bạn có thể kê chuột hay kê tay khi chơi game thoải mái. Một điểm khác biệt so với phiên bản cũ là nút nguồn được tách riêng ra với bàn phím.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/225389/lenovo-gaming-15imh05-i7-81y40068vn-215620-085633.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bàn phím êm, có đủ các cụm phím số, và cụm phím điều khiển được đặt lệch xuống dưới một chút để các game thủ có 1 không gian riêng biệt sử dụng mà không sợ nhầm lẫn với các phím khác. Bàn phím này còn có đèn nền màu xanh bắt mắt để bạn dễ gõ phím vào ban đêm.</p>\r\n                    <h2 class = \"paragraph__h2\">Cấu hình khủng, chiến game mượt </h2>\r\n                    <p class = \"paragraph__p\">Card đồ họa rời NVIDIA GeForce GTX 1650 4 GB hỗ trợ loại bỏ các hiện tượng bóng ma hay giật lag hình khi chơi các game hot hiện nay như PUBG, CSGO, GTA 5,... từ mức setting trung bình đến cao. Sử dụng Photoshop, Premiere mượt mà để chỉnh sửa video, hình ảnh.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/225389/lenovo-gaming-15imh05-i7-81y40068vn-211620-091633.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Thiết bị này được trang bị chip Intel Core i7 hệ thứ 10, xung nhịp tối đa là 5.0 GHz cùng RAM 8 GB nên gần như mọi thao tác trên Chrome hay xử lý hình ảnh chuyên nghiệp không còn là vấn đề. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/225389/lenovo-gaming-15imh05-i7-81y40068vn-215620-085626.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình rộng rãi </h2>\r\n                    <p class = \"paragraph__p\">Kích thước màn hình là 15.6 inch cùng với viền mỏng 5 mm ở hai cạnh bên, nơi dày nhất cũng chỉ 14 mm nên không gian hiển thị mở rộng như tràn ra khỏi màn hình cho bạn cảm giác nhập vai hơn khi chiến game cũng như tránh sao nhãng khi làm việc.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/225389/lenovo-gaming-15imh05-i7-81y40068vn-215620-085639.jpg\" alt=\"\"> \r\n                    <p class = \"paragraph__p\">Độ phân giải Full HD cùng tấm nền IPS thể hiện hình ảnh tốt, màu sắc tươi mới bắt mắt để bạn trải nghiệm nhập vai hay đơn giản là xem video thoải mái.</p>                  \r\n                    <p class = \"paragraph__p\">Cấu hình khủng, hiệu năng cao, thiết kế lại thuộc hàng mỏng nhẹ cho laptop gaming, Lenovo Gaming 15IMH05 phù hợp với những ai là game thủ, streamer hay người có nhu cầu đồ họa chuyên nghiệp.</p>\r\n                                       \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 4, 1),
(76, 2, 'Lenovo ThinkBook 14IIL i7 1065G7/8GB/512GB/Win10 (20SL00MEVN)', '../assets/image/products/laptop/Lenovo-ThinkBook-14IIL-20SL00MEVN/Nền.jpg', 22790000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i7 Ice Lake, 1065G7, 1.30 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB, DDR4 (1 khe), 3200 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe, Hỗ trợ khe cắm HDD SATA\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n14\", Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel Iris Plus Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 3.2, Thunderbolt4 USB-C, HDMI, LAN (RJ45), USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ kim loại, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 17.9 mm, 1.5 Kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo ThinkBook 14IIL i7 1065G7/8GB/512GB/Win10 (20SL00MEVN)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230160/Slider/lenovo-thinkbook-14iil-i7-20sl00mevn-031120-1144000.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Laptop Lenovo ThinkBook 14IIL i7 (20SL00MEVN) là dòng laptop học tập - văn phòng với thiết kế sang trọng, hiệu năng mạnh mẽ, độ bền cao, khả năng bảo mật tối ưu sẽ giúp bạn đạt được hiệu năng làm việc tốt nhất.</h1>\r\n                    <h2 class = \"paragraph__h2\">Hiệu năng mạnh mẽ, xử lý trơn tru các tác vụ</h2>\r\n                    <p class = \"paragraph__p\">Laptop được trang bị CPU Intel Core i7 thế hệ thứ 10 có xung nhịp trung bình là 1.30 GHz ở các tác vụ văn phòng và xung nhịp tối đa lên đến 3.9 GHz với các tác vụ nặng như đồ họa nhờ có Turbo Boost, đáp ứng tốt đa dạng nhu cầu công việc của bạn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230160/lenovo-thinkbook-14iil-i7-20sl00mevn-274020-064012.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">RAM 8 GB và hệ điều hành Windows 10 Home SL cho khả năng đa nhiệm mượt mà, đủ sức đáp ứng tốt công việc thiết kế cơ bản bằng Photoshop, AI,... Và cũng đáp ứng chơi một số tựa game nhẹ nhàng như: Diablo II, Portal, GTA Vice City,... Nếu bạn muốn thực hiện các tác vụ nâng cao hơn nữa như: Chỉnh sửa video, đồ họa 3D, chơi game hạng nặng,... thì máy có thể hỗ trợ bạn nâng cấp RAM lên tối đa là 32 GB.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230160/lenovo-thinkbook-14iil-i7-20sl00mevn-274020-064006.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bên cạnh đó chiếc laptop Lenovo ThinkBook này còn được trang ổ cứng SSD 512 GB cho tốc độ khởi động và mở máy cực nhanh chỉ trong vài giây. Dung lượng 512 GB cho bạn không gian lưu trữ lớn bạn có thể thoải mái lưu trữ các bộ phim điện ảnh yêu thích hay các tập tin lớn mà không lo lắng bị đầy dung lượng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230160/lenovo-thinkbook-14iil-i7-20sl00mevn-274020-064006.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế mỏng nhẹ cực sang trọng</h2>\r\n                    <p class = \"paragraph__p\">Chiếc laptop Lenovo có thtết kế mỏng nhẹ, chỉ dày 17.9 mm và nhẹ 1.5 kg. Bạn có thể dễ để laptop vào balo mang theo bên mình thuận tiện xử lý công việc.</p>\r\n                    <p class = \"paragraph__p\">Vỏ máy được làm từ kim loại cứng cáp, bảo vệ tốt các linh kiện bên trong khi có va đập. Ngoài ra, vỏ kim loại sáng bóng giúp tăng thêm thanh lịch, sang trọng cho bạn.</p>                    \r\n	 	    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230160/lenovo-thinkbook-14iil-i7-20sl00mevn-273920-063953.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hình ảnh sắc nét, âm thanh sống động</h2>\r\n		    <p class = \"paragraph__p\">Màn hình 14 inch rộng rãi, cho không gian hiển thị lớn, tấm nền IPS hỗ trợ góc nhìn rộng hơn, thoải mái hơn khi quan sát, hình ảnh không bị biến đổi khi nhìn nghiêng.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230160/lenovo-thinkbook-14iil-i7-20sl00mevn-034720-114706.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Laptop có kích thước màn hình 14 inch cùng xu thế viền màn hình mỏng cho không gian hiển thị rộng rãi, màn hình có độ phân giải Full HD cho hình ảnh hiển thị một cách sắc nét màu sắc chính xác đầy chân thực, cùng tấm nền IPS cho góc nhìn rộng hơn bạn có thể nhìn rõ hình ảnh trên màn hình ở nhiều góc độ khác nhau.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/230160/lenovo-thinkbook-14iil-i7-20sl00mevn-273920-063957.jpg\" alt=\"\"> \r\n                    <p class = \"paragraph__p\">Tạo hiệu ứng âm thanh vòm sống động đầy lôi cuốn, âm sắc trong trẻo nhờ công nghệ âm thanh của máy là Dolby Audio Premium, kết hợp cùng màn hình sắc nét mang đến cho bạn những phút giây giải trí tuyệt vời.</p>          \r\n                    <p class = \"paragraph__p\">Laptop Lenovo ThinkBook 14IIL i7 (20SL00MEVN) với hiệu năng mạnh mẽ đáp ứng tốt công việc của bạn, vẻ ngoài sang trọng sẽ là sự lựa chọn tuyệt vời dành cho bạn.</p>\r\n                                       \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 4, 1),
(77, 2, 'Lenovo IdeaPad S340 14IIL i5 1035G1/8GB/512GB/Win10 (81VV003SVN)', '../assets/image/products/laptop/Lenovo-IdeaPad-S340-14IIL-i5-81VV003SVN/Nền.jpg', 15990000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i5 Ice Lake, 1035G1, 1.00 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB, DDR4 (On board 4GB +1 khe 4GB), 2666 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe, Hỗ trợ khe cắm HDD SATA\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n14\", Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel UHD Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 3.1, HDMI, USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ nhựa - nắp lưng bằng kim loại, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 17.9 mm, 1.6 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2019\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo IdeaPad S340 14IIL i5 1035G1/8GB/512GB/Win10 (81VV003SVN)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/214708/Slider/vi-vn-lenovo-ideapad-s340-14iil-i5-1035g1-8gb-512gb-win1-(3).jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Laptop Lenovo IdeaPad S340 14IIL i5 (81VV003SVN) là một lựa chọn phù hợp dành cho nhân viên văn phòng, học sinh sinh viên. Máy có cấu hình khá với vi xử lí mới nhất đến từ Intel, ổ cứng SSD cực nhanh, thiết kế sang trọng, mỏng nhẹ sẵn sàng đồng hành cùng bạn mọi lúc mọi nơi.</h1>\r\n                    <h2 class = \"paragraph__h2\">Sử dụng văn phòng, đồ họa</h2>\r\n                    <p class = \"paragraph__p\">Với chip Intel Core i5 thế hệ 10 (ra mắt cuối năm 2019) mạnh mẽ, RAM 8 GB, ngoài việc sử dụng tốt các ứng dụng văn phòng, laptop còn có thể xử lý hình ảnh trên Photoshop, hay chỉnh sửa video đơn giản bằng Premiere. </p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/214708/lenovo-ideapad-s340-14iil-i5-1035g1-8gb-512gb-win15.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế gọn nhẹ, cơ động </h2>\r\n                    <p class = \"paragraph__p\">Laptop có thiết kế đơn giản, tinh tế với chất liệu từ nhựa, nắp lưng kim loại bền bỉ và sang trọng. Máy có độ dày 17.9 mm và trọng lượng 1.6 kg, mỏng nhẹ tối ưu cho những ai phải thường xuyên di chuyển.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/214708/lenovo-ideapad-s340-14iil-i5-1035g1-8gb-512gb-win13.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Ổ cứng SSD khởi động cực nhanh</h2>\r\n                    <p class = \"paragraph__p\">Laptop có trang bị ổ cứng SSD 512 GB M.2 PCIe vận hành mượt mà, nhanh chóng. Thời gian mở máy chỉ khoảng 10-15 giây. Với 512 GB người dùng cũng có thể lưu được một lượng lớn dữ liệu.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/214708/lenovo-ideapad-s340-14iil-i5-1035g1-8gb-512gb-win12.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Màn hình viền mỏng, chống chói</h2>\r\n                    <p class = \"paragraph__p\">Màn hình 14 inch độ phân giải Full HD cho bạn không gian hình ảnh đẹp, sắc nét, những gam màu chân thật. Công nghệ Anti Glare chống chói hiệu quả dù sử dụng ở dưới nắng hay ngược sáng. </p>       \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/214708/lenovo-ideapad-s340-14iil-i5-1035g1-8gb-512gb-win16.jpg\" alt=\"\">           \r\n                    <h2 class = \"paragraph__h2\">Cổng kết nối đa dạng</h2>\r\n                    <p class = \"paragraph__p\">Laptop Lenovo IdeaPad S340 được trang bị đầy đủ các cổng kết nối phổ biến như USB Type C, USB 3.1, HDMI, giúp bạn dễ dàng thực hiện các thao tác truy xuất tài liệu, hình ảnh qua các thiết bị khác để phục vụ cho công việc của mình.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/214708/lenovo-ideapad-s340-14iil-i5-1035g1-8gb-512gb-win11.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Với cấu hình mạnh và thiết kế thuận tiện xê dịch, Lenovo IdeaPad S340 14IIL là mẫu laptop học tập văn phòng dành cho giới trẻ hiện đại. </p>            \r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n', 4, 1),
(78, 2, 'Lenovo Yoga Slim 7 14IIL05 i5 1035G4/8GB/512GB/Win10 (82A1007UVN)', '../assets/image/products/laptop/Lenovo-Yoga-Slim-7-14IIL05 i5-82A1007UVN/Nền.jpg', 22490000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i5 Ice Lake, 1035G1, 1.10 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB, DDR4 (On board), 3200 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n14\", Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel Iris Plus Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 3.2, HDMI, USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ kim loại, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 15.1 mm, 1.55 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo Yoga Slim 7 14IIL05 i5 1035G4/8GB/512GB/Win10 (82A1007UVN)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/228356/Slider/lenovo-yoga-slim-7-14iil05-i5-82a1007uvn-150920-0153240.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Laptop Lenovo Yoga Slim 7 14IIL05 i5 (82A1007UVN) sở hữu bộ xử lý Intel Core thế hệ thứ 10 với hiệu năng đáp ứng tốt mọi nhu cầu thường ngày của bạn trong một thiết kế nhỏ gọn chắc chắn sẽ là người bạn đồng hành tuyệt vời cho các bạn tân sinh viên bước chân vào cánh cổng đại học.</h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế phong cách và năng động</h2>\r\n                    <p class = \"paragraph__p\">Được chế tạo từ nhôm cao cấp, Yoga Slim 7 có trọng lượng chỉ 1.55 kg, mỏng 15.1 mm nhẹ nhàng trong ba lô để các bạn mang theo bên mình đến mọi nơi.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/228356/lenovo-yoga-slim-7-14iil05-i5-82a1007uvn-150920-020921.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Thiết kế bản lề mở 180 độ linh hoạt cho việc họp nhóm hay trao đổi thông tin với mọi người.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/228356/lenovo-yoga-slim-7-14iil05-i5-82a1007uvn-152020-022053.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Siêu nhạy bén, siêu năng suất</h2>\r\n                    <p class = \"paragraph__p\">Thông minh để đáp ứng nhu cầu của bạn với hiệu suất cao cấp của bộ xử lý Intel Core thế hệ thứ 10, RAM 8 GB và ổ cứng SSD 512 GB. Yoga Slim 7 được tối ưu hóa để cung cấp hiệu năng siêu nhanh và ổn định trong một chiếc máy tính xách tay cực mỏng nhẹ.</p>\r\n                    <p class = \"paragraph__p\">Yoga Slim 7 sở hữu bộ xử lý Intel Core i5 thế hệ thứ 10 với khả năng tính toán thông minh và trực quan phù hợp với nhu cầu lướt web, soạn thảo văn bản,... hay thậm chí là thiết kế đồ hoạ 2D cơ bản trên Photoshop, Ai.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/228356/lenovo-yoga-slim-7-14iil05-i5-82a1007uvn-151020-021026.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">RAM 8 GB với card đồ họa tích hợp Intel Iris Plus mang đến trải nghiệm sống động, sắc nét hơn khi bạn truyền dữ liệu cho thiết bị thứ 3 như màn hình trình chiếu hay tivi, chỉnh sửa video và đa tác vụ khi vừa phát nhạc vừa trình chiếu, làm việc cũng khá mượt mà.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/228356/lenovo-yoga-slim-7-14iil05-i5-82a1007uvn-151020-021059.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Âm thanh và hình ảnh tuyệt vời</h2>\r\n                    <p class = \"paragraph__p\">Với viền bezel mỏng, màn hình 14 inch độ phân giải Full HD cùng tấm nền IPS sở hữu độ sắc nét và màu sắc tuyệt đẹp, cho góc nhìn rộng có thể quan sát được hình ảnh hiển thị ở mọi hướng.</p>\r\n		    <p class = \"paragraph__p\">Hệ thống loa trước Dolby Atmos tạo nên âm thanh ba chiều, nhờ vậy mà âm thanh được truyền tải chân thật và chi tiết nhất.</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/228356/lenovo-yoga-slim-7-14iil05-i5-82a1007uvn-151720-021713.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Các cổng kết nối USB 3.2, HDMI và USB Type-C mang đến cho Lenovo Yoga Slim 7 14IIL05 i5 (82A1007UVN) tốc độ truyền file, kết nối thiết bị ngoại vi nhanh, ổn định.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/228356/lenovo-yoga-slim-7-14iil05-i5-82a1007uvn-151820-021834.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Laptop Lenovo Yoga Slim 7 14IIL05 i5 (82A1007UVN) mang đến những tính năng ổn định với mức giá phải chăng, chắc chắn sẽ làm bạn hài lòng trong công việc, học tập và cả giải trí.</p>            \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 4, 1),
(79, 2, 'Lenovo ThinkBook 14 G2 ITL i7 1165G7/8GB/512GB/Win10 (20VD003LVN)', '../assets/image/products/laptop/Lenovo-ThinkBook-14-G2-ITL-20VD003LVN/Nền.jpg\r\n', 21990000, 0, 5, 2, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i7 Tiger Lake, 1165G7, 2.80 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB, DDR4 (1 khe), 3200 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe, Hỗ trợ khe cắm HDD SATA\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n14\", Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel Iris Plus Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 3.2, Thunderbolt4 USB-C, HDMI, LAN (RJ45), USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ kim loại, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 14.9 mm, 1.4 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo ThinkBook 14 G2 ITL i7 1165G7/8GB/512GB/Win10 (20VD003LVN)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236772/Slider/vi-vn-lenovo-thinkbook-14-g2-itl-i7-20vd003lvn-1.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Laptop Lenovo ThinkBook 14 G2 ITL i7 (20VD003LVN) với thiết kế cao cấp và hiệu năng mạnh mẽ, độ bền đạt chuẩn quân đội, là sự lựa chọn lí tưởng cho giới doanh nhân hay người làm công việc sáng tạo nội dung. </h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế cao cấp, sang trọng </h2>\r\n                    <p class = \"paragraph__p\">Lenovo ThinkBook được thiết kế theo hướng tối giản, vỏ máy được làm từ kim loại, các chi tiết có độ hoàn thiện cao tạo nên một chiếc máy đơn giản, tinh tế nhưng vẫn cực kì nổi bật. Chiếc laptop 14 inch này có trọng lượng chỉ vào khoảng 1.4 kg và dày 17.9 mm cho bạn cảm giác nhẹ nhàng, thoải mái khi mang theo laptop bên mình.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236772/lenovo-thinkbook-14-g2-itl-i7-20vd003lvn-2-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bản lề của laptop Lenovo có khả năng gập mở 180 độ, vừa giúp tiện lợi hơn khi sử dụng, vừa hạn chế tình trạng gãy bản lề do va đập trong quá trình sử dụng.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236772/lenovo-thinkbook-14-g2-itl-i7-20vd003lvn-7-1.jpg\" alt=\"\">\r\n		    <p class = \"paragraph__p\">Lenovo rất chú trọng đến vấn đề bảo mật của người dùng khi trang bị cho chiếc máy này một cảm biến vân tay để mở khóa hoặc đăng nhập vào các tài khoản để thanh toán tiện lợi và an toàn. Laptop Lenovo còn có công tắc khóa camera vật lí có thể đóng mở bằng tay để người dùng có thể tắt webcam khi không sử dụng, tránh bị hacker đánh cắp hình ảnh hay thông tin cá nhân.</p>                    \r\n		    <h2 class = \"paragraph__h2\">Cấu hình mạnh, làm việc năng suất </h2>\r\n                    <p class = \"paragraph__p\">Laptop trang bị cho mình bộ xử lí Core i7 thế hệ 11 của nhà Intel có khả năng vận hành tốt các ứng dụng văn phòng, doanh nghiệp lẫn đồ họa từ cơ bản đến bán chuyên. Con chip này có xung nhịp tối đa khoảng 4.7 GHz với 4 nhân 8 luồng được tích hợp thêm 4 lõi xử lý Willow Cove với công nghệ 10 nm SuperFin giúp xử lí ổn định và nhanh chóng.</p>\r\n                    <p class = \"paragraph__p\">Card đồ họa Intel Iris Xe Graphics mang đến trải nghiệm hoàn toàn mới khi xem phim với chất lượng hình ảnh sắc nét, tăng hiệu năng đồ họa giúp công việc xử lí hình ảnh trên Photoshop, thiết kế bằng AI được mượt mà. </p>\r\n		    <p class = \"paragraph__p\">RAM 8 GB DDR4 giúp bạn chuyển đổi liền mạch giữa các ứng dụng với nhau mà không xảy ra tình trạng giật lag với tốc độ Bus RAM 3200 MHz. Bạn cũng có thể nâng cấp RAM lên đến 40 GB để đa nhiệm tốt hơn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236772/lenovo-thinkbook-14-g2-itl-i7-20vd003lvn-4-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Phản hồi mọi thao tác chỉ trong vài giây, mở máy cực nhanh với Lenovo ThinkBook nhờ trang bị ổ cứng SSD 512 GB. Máy cũng có sẵn khe cắm HDD SATA để nâng cấp nếu bạn cần nhiều không gian lưu trữ hơn. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236772/lenovo-thinkbook-14-g2-itl-i7-20vd003lvn-5-1.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Mãn nhãn với màn hình 14 inch chất lượng sắc nét</h2>\r\n                    <p class = \"paragraph__p\">Laptop sử dụng màn hình độ phân giải full HD (1920 x 1080) cho chất lượng hình ảnh sắc nét, mật độ điểm ảnh dày, tấm nền IPS hỗ trợ góc nhìn rộng hơn, thoải mái hơn khi quan sát, hình ảnh không bị biến đổi khi nhìn nghiêng.</p>\r\n		    <p class = \"paragraph__p\">Độ sáng màn hình 250 nits cho khả năng hiển thị rực rỡ, đảm bảo hình ảnh vẫn hiển thị tốt kể cả khi sử dụng nơi có nhiều ánh sáng.</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236772/lenovo-thinkbook-14-g2-itl-i7-20vd003lvn-9-1.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Công nghệ âm thanh Dolby Audio Premium có khả năng tạo nên hiệu ứng âm thành vòm và lọc bỏ các tạp âm ​giúp âm thanh trong trẻo hơn, cải thiện chi tiết âm thanh.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/236772/lenovo-thinkbook-14-g2-itl-i7-20vd003lvn-10-1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Laptop Lenovo ThinkBook 14 G2 ITL i7 (20VD003LVN) phù hợp với dân văn phòng và doanh nhân khi vừa sở hữu ngoại hình sang trọng, vừa có cấu hình mạnh mẽ để phục vụ cho công việc mà vẫn mỏng nhẹ, cơ động.</p>            \r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n', 4, 1),
(80, 2, 'Lenovo IdeaPad Slim 5 15ITL05 i5 1135G7/8GB/512GB/Win10 (82FG001PVN)', '../assets/image/products/laptop/Lenovo-IdeaPad-Slim-5-15ITL05-82FG001PVN/Nền.jpg\r\n', 17900000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i5 Tiger Lake, 1135G7, 2.40 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n8 GB, DDR4 (On board), 3200 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n15.6 inch, Full HD (1920 x 1080)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel Iris Xe Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x USB 3.2, USB Type-C (Power Delivery and DisplayPort), HDMI\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nVỏ nhựa - nắp lưng bằng kim loại, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 17.9 mm, 1.66 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo IdeaPad Slim 5 15ITL05 i5 1135G7/8GB/512GB/Win10 (82FG001PVN)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232192/Slider/lenovo-ideapad-slim-5-15itl05-i5-82fg001pvn-281220-0530290.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Sở hữu thiết kế mỏng nhẹ và thanh lịch, Lenovo IdeaPad Slim 5 15ITL05 i5 1135G7 (82FG001PVN) không chỉ là một mẫu laptop thời trang, mà còn mang trên mình sức mạnh vô cùng mạnh mẽ đến từ bộ vi xử lý Intel Core i5 thế hệ thứ 11 giúp bạn hoàn thành xuất sắc mọi công việc.</h1>\r\n                    <h2 class = \"paragraph__h2\">Hiệu năng cực đỉnh, tốc độ hoàn hảo</h2>\r\n                    <p class = \"paragraph__p\">Lenovo IdeaPad Slim 5 15ITL05 sở hữu một hiệu năng vượt trội nhờ việc trang bị con chip Intel Core i5 Tiger Lake 1135G7 sản xuất trên tiến trình 10 nm tiên tiến. Con chip thế hệ thứ 11 này mang đến một xung nhịp cơ sở là 2.4 GHz và được ép xung lên tối đa tới 4.2 GHz. Nhờ vậy tốc độ xử lý của máy nhanh vượt trội, đồng thời tiết kiệm được năng lượng, kéo dài tuổi thọ pin, giúp bạn đạt hiệu suất cao trong công việc.</p>\r\n                    <p class = \"paragraph__p\">Với RAM 8 GB chuẩn DDR4, laptop sẽ giúp bạn làm được nhiều việc hơn nhờ nâng cao khả năng đa nhiệm với tốc độ Bus RAM lên đến 3200 MHz. RAM 8 GB cho phép bạn sử dụng cùng lúc nhiều ứng dụng, bạn có thể sử dụng máy để lướt web, soạn thảo hay thiết kế với Photoshop ổn định và mượt mà.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232192/lenovo-ideapad-slim-5-15itl05-i5-82fg001pvn-092121-112124.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Đi kèm với chip i5 thế hệ 11, Intel Iris Xe Graphics giúp người dùng có được một tốc độ và hiệu suất cao hơn ngay cả khi khối lượng công việc tăng lên, khả năng đồ họa của máy cũng được nâng lên một mức đáng kể, giúp bạn tha hồ chỉnh sửa, thiết kế ảnh hay video ở chất lượng trung bình đến cao ổn định hơn.</p>\r\n                    <p class = \"paragraph__p\">Không chỉ vậy, SSD 512 GB vừa đảm bảo tăng tốc máy tính toàn diện, từ khởi động máy cho đến tốc độ mở các ứng dụng, lại vừa cho dung lượng lưu trữ lớn, mang lại cho bạn một không gian rộng để dễ dàng học tập, lưu trữ những tài liệu, những hình ảnh hay video.</p>		    \r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232192/lenovo-ideapad-slim-5-15itl05-i5-82fg001pvn-140020-060056.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Thiết kế hiện đại, cao cấp hàng đầu</h2>\r\n                    <p class = \"paragraph__p\">Sở hữu bề ngoài đẹp, sang trọng, laptop Lenovo IdeaPad được bao bọc bởi lớp vỏ nhựa và nắp kim loại vô cùng cao cấp với trọng lượng chỉ 1.66 kg và dày 17.9 mm. Nhờ kiểu dáng vuông vắn, khi gấp lại hoàn toàn cân đối tạo nên dáng vẻ hiện đại, chiếc laptop 15.6 inch này vẫn hết sức gọn gàng để bạn có thể mang máy đến bất cứ đâu.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232192/lenovo-ideapad-slim-5-15itl05-i5-82fg001pvn-140120-060100.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Các cổng kết nối của laptop cũng rất đa dạng và không kém phần hiện đại, đáp ứng đủ các yêu cầu kết nối với các thiết bị ngoại vi khác với 5 cổng như sau: 2 cổng USB 3.2, cổng USB Type-C, cổng HDMI và khe đọc thẻ nhớ SD. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232192/lenovo-ideapad-slim-5-15itl05-i5-82fg001pvn-140020-060048.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Trải nghiệm hình ảnh cực chất với màn hình viền mỏng</h2>\r\n                    <p class = \"paragraph__p\">Là một trong những chiếc laptop có viền màn hình mỏng trên thị trường hiện nay, laptop Lenovo sẽ giúp bạn đắm chìm trong những bộ phim, chương trình yêu thích với màn hình 15.6 inch Full HD (1920 x1080) sắc nét, viền mỏng đến tối đa. Tỷ lệ khung hình chiếm tới 90% so với thân máy, bạn sẽ được đắm mình trong thế giới hình ảnh chân thật, mang lại những trải nghiệm đầy bất ngờ cho bạn.</p>\r\n		    <p class = \"paragraph__p\">Được trang bị thêm tấm nền IPS, góc nhìn của bạn cũng được mở rộng đến 178 độ, mang đến những cảm giác mới lạ khi xem phim hay giải trí. Ngoài ra nhờ công nghệ chống chói Anti Glare, dù bạn làm việc ở bất kỳ đâu, kể cả ngoài trời sáng, bạn cùng sẽ hoàn thành thật tốt công việc của mình.</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232192/lenovo-ideapad-slim-5-15itl05-i5-82fg001pvn-140020-060052.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Công nghệ âm thanh cũng không kém phần quan trọng với một chiếc laptop hoàn hảo. Dolby Audio Premium sẽ giúp bạn đắm chìm vào những giai điệu âm thanh nhẹ nhàng, sống động đến từ những bản nhạc hay gay cấn đến từ các bộ phim bom tấn, hành động.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/232192/lenovo-ideapad-slim-5-15itl05-i5-82fg001pvn-140020-060034.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Là một con laptop cao cấp, sang trọng, Lenovo IdeaPad Slim 5 15ITL05 i5 (82FG001PVN) sở hữu cấu hình mạnh mẽ, thiết kế hiện đại cùng với bảo mật tối ưu, đây là con laptop thật sự phù hợp cho các bạn sinh viên hay nhân viên công sở trong phân khúc laptop học tập - văn phòng.</p>            \r\n<button class =\"paragraph__more\">Xem thêm !</button>\r\n', 4, 1);
INSERT INTO `product` (`id`, `type`, `name`, `image`, `price`, `stock`, `rate`, `view`, `buy`, `voucher`, `configuration`, `description`, `manufacturer`, `isVisible`) VALUES
(81, 2, 'Lenovo YOGA Slim 7 Carbon 13ITL5 i5 1135G7/16GB/512GB/Win10 (82EV0016VN)', '../assets/image/products/laptop/Lenovo-YOGA-Slim-7-Carbon-13ITL5-i5-82EV0016VN/Nền.jpg', 26990000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i5 Tiger Lake, 1135G7, 2.40 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n16 GB, LPDDR4X (On board), 4266 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\nSSD: 512 GB, M.2 PCIe\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n13.3 inch, QHD (2560x1600)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel Iris Xe Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x Thunderbolt4 USB-C, USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nMặt lưng Carbon - Chiếu nghỉ tay bằng Nhôm Magie, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 15 mm, 0.966 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo YOGA Slim 7 Carbon 13ITL5 i5 1135G7/16GB/512GB/Win10 (82EV0016VN)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234365/Slider/lenovo-yoga-slim-7-carbon-13itl5-i5-82ev0016vn-234365-800x444-2.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Lenovo YOGA Slim 7 Carbon 13ITL5 i5 (82EV0016VN) mang trong mình bộ xử lý cực mạnh với kiểu dáng vô cùng sang trọng, laptop Lenovo hứa hẹn sẽ là một con sản phẩm đáng mong đợi, lý tưởng cho công việc văn phòng của bạn.</h1>\r\n                    <h2 class = \"paragraph__h2\">Thiết kế mỏng gọn, siêu nhẹ</h2>\r\n                    <p class = \"paragraph__p\">Với trọng lượng chỉ 0.966 kg, Lenovo YOGA đem lại cho người dùng một cảm giác cực kỳ mới lạ và thú vị bởi cân nặng siêu nhẹ đáng kinh ngạc của chiếc laptop này. Cộng thêm vào đó là độ mỏng chỉ 15 mm mang cho bạn cảm giác thoải mái và nhẹ nhàng, dễ dàng xách đi bất cứ đâu. Với chất liệu bằng Carbon ở mặt lưng đem đến cho laptop một khả năng chống sốc, cực kỳ bền bỉ và đạt tiêu chuẩn độ bền quân đội MIL-STD-810G sau khi vượt qua đến 9 cấp độ thử nghiệm.</p>\r\n                    <p class = \"paragraph__p\"></p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234365/lenovo-yoga-slim-7-carbon-13itl5-i5-82ev0016vn-kg.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bàn phím gõ êm ái, cảm giác gõ chính xác và thoải mái. Ngoài việc trang bị đèn nền thì Lenovo còn tích hợp 2 chế độ sáng, cho bạn có thể tùy chỉnh theo thực trạng ánh sáng của môi trường, một mặt khác còn có thể tiết kiệm điện năng hơn.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234365/lenovo-yoga-slim-7-carbon-13itl5-i5-82ev0016vn-den-bp.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Không chỉ đem lại thiết kế gọn nhẹ, Lenovo YOGA Slim 7 Carbon 13ITL5 i5 còn đáp ứng tốt các cổng kết nối hiện đại hiện nay, phục vụ hoàn hảo cho công việc của bạn bao gồm 2 cổng Thunderbolt 4 USB-C, USB Type-C có khả năng kết nối với thiết bị khác, truyền dữ liệu, xuất hình Display Port 1.4 và sạc nhanh, hết sức đa năng. Bên cạnh đó máy còn được 2 chuẩn kết nối không dây Wi-Fi 6</p>\r\n		    <p class = \"paragraph__p\">AX201 và Bluetooth v5.0 hiện đại mang đến tốc độ kết nối Internet ổn định ngay cả ở nơi công cộng.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234365/lenovo-yoga-slim-7-carbon-13itl5-i5-82ev0016vn-usb.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Cấu hình cực kỳ mạnh mẽ từ con chip thế hệ 11 mới</h2>\r\n                    <p class = \"paragraph__p\">Được trang bị cấu hình chip Intel Core i5 Tiger Lake 1135G7 sản xuất trên tiến trình 10 nm tiên tiến, con chip thế hệ thứ 11 này mang đến một xung nhịp cơ sở là 2.4 GHz và được ép xung lên tối đa tới 4.2 GHz. Nhờ đó laptop đem lại khả năng làm mượt mọi tác vụ văn phòng dù phức tạp nhất, các ứng dụng văn phòng, quản lý đều được xử lý cực mượt mà, đồng thời tiết kiệm năng lượng, kéo dài tuổi thọ pin, giúp bạn đạt hiệu suất cao trong công việc. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234365/lenovo-yoga-slim-7-carbon-13itl5-i5-82ev0016vn-i5-gen-11.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bộ nhớ RAM 16GB đa nhiệm mang đến khả năng xử lý đồng thời nhiều công việc cực nhanh, mở hơn 20 tabs trình duyệt không giật, lag. Ngoài ra nó còn mang đến khả năng đồ họa đáng kinh ngạc, dù chỉnh những bức ảnh độ phân giải hàng tỷ pixels hay thiết kế đồ họa photoshop, AI và xử lý video cùng lúc, laptop vẫn đáp ứng chạy cực kỳ mượt mà.</p>\r\n		    <p class = \"paragraph__p\">Không chỉ vậy, SSD 512 GB không chỉ đem đến tốc độ vận hành nhanh, tốc độ truy xuất dữ liệu siêu nhanh, khởi động máy, lưu mở file chỉ trong giây lát mà còn cho bạn không gian lưu trữ lớn, giúp bạn tha hồ lưu những nội dung, tài liệu cần thiết cho công việc cũng như học tập.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234365/lenovo-yoga-slim-7-carbon-13itl5-i5-82ev0016vn-ssd.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Đi kèm với chip i5 thế hệ 11, Intel Iris Xe Graphics giúp người dùng có được một tốc độ và hiệu suất cao hơn ngay cả khi khối lượng công việc tăng lên, cho khả năng làm tốt cả những công việc thiết kế đồ họa 2D trên PS, AI giúp bạn tha hồ chỉnh sửa, thiết kế ảnh hay thậm chí chơi những tựa game phổ biến hiện nay ở cài đặt max setting cực mượt.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234365/lenovo-yoga-slim-7-carbon-13itl5-i5-82ev0016vn-nvidia.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Trải nghiệm hình ảnh sống động, âm thanh chân thực</h2>\r\n                    <p class = \"paragraph__p\">Với màn hình 13.3 inch cho độ phân giải QHD (2560x1600) đem lại một màn hình vô cùng sắc nét. Thêm vào đó, laptop được trang bị công nghệ màn hình IPS, góc nhìn của bạn cũng được mở rộng đến 178 độ, mang đến những cảm giác mới lạ khi xem phim hay giải trí.</p>\r\n		    <p class = \"paragraph__p\">Ngoài ra, màn hình còn được trang bị độ sáng 300 nits cho mọi hình ảnh hiển thị sống động như ngoài đời thực. Hơn nữa, công nghệ hình ảnh Dolby Vision sẽ giúp bảo vệ mắt bạn một cách tối ưu. Công nghệ chống chói Anti Glare giúp dù bạn làm việc ở bất kỳ đâu, kể cả ngoài trời sáng cũng sẽ hoàn thành thật tốt công việc của mình.</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234365/lenovo-yoga-slim-7-carbon-13itl5-i5-82ev0016vn-inch.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Công nghệ âm thanh Audio by Harman/Kardon sẽ giúp bạn đắm chìm vào những những âm thanh không chỉ lớn mà còn sắc nét tới từng chất âm, để mọi hoạt động giải trí như nghe nhạc, xem phim hay chơi game của bạn đều trở nên thú vị hơn. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234365/lenovo-yoga-slim-7-carbon-13itl5-i5-82ev0016vn-am-thanh.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Với thiết kế vô cùng nổi bật và hiệu năng cao, Lenovo YOGA Slim 7 Carbon 13ITL5 thật sự là chiếc laptop rất đáng để sở hữu trong phân khúc laptop học tập - văn phòng.</p>            \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 4, 1),
(82, 2, 'Lenovo YOGA Slim 7 Carbon 13ITL5 i7 1165G7/16GB/1TB SSD/Win10 (82EV0017VN)', '../assets/image/products/laptop/Lenovo-YOGA-Slim-7-Carbon-13ITL5-i7-82EV0017VN/Nền.jpg', 31990000, 0, 5, 0, 0, NULL, '<div class=\"content__row\">\r\n<span class=\"content__row-title\">CPU:</span>\r\n<span class=\"content__row-content\">\r\nIntel Core i7 Tiger Lake, 1165G7, 2.80 GHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">RAM:</span>\r\n<span class=\"content__row-content\">\r\n16 GB, LPDDR4X (On board), 4266 MHz\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Ổ cứng:</span>\r\n<span class=\"content__row-content\">\r\n	1TB SSD M.2 PCIe\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Màn hình:</span>\r\n<span class=\"content__row-content\">\r\n13.3 inch, QHD (2560x1600)\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Card màn hình:</span>\r\n<span class=\"content__row-content\">\r\nCard đồ họa tích hợp, Intel Iris Xe Graphics\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Cổng kết nối:</span>\r\n<span class=\"content__row-content\">\r\n2 x Thunderbolt4 USB-C, USB Type-C\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Hệ điều hành:</span>\r\n<span class=\"content__row-content\">\r\nWindows 10 Home SL\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thiết kế:</span>\r\n<span class=\"content__row-content\">\r\nMặt lưng Carbon - Chiếu nghỉ tay bằng Nhôm Magie, PIN liền\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Kích thước:</span>\r\n<span class=\"content__row-content\">\r\nDày 15 mm, 0.966 kg\r\n</span>\r\n</div>\r\n<div class=\"content__row\">\r\n<span class=\"content__row-title\">Thời điểm ra mắt:</span>\r\n<span class=\"content__row-content\">\r\n2020\r\n</span>\r\n</div>', '<b class =\"paragraph__title\">Đặc điểm nổi bật của Lenovo YOGA Slim 7 Carbon 13ITL5 i7 1165G7/16GB/1TB SSD/Win10 (82EV0017VN)</b>\r\n<img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234364/Slider/lenovo-yoga-slim-7-carbon-13itl5-i7-82ev0017vn-250321-0139501.jpg\" alt=\"\">\r\n                    <h1 class = \"paragraph__h1\">Siêu phẩm laptop Lenovo YOGA Slim 7 Carbon 13ITL5 i7 (82EV0017VN) cực mỏng nhẹ và bền bỉ với trọng lượng chưa đến 1 kg. Hiệu năng mạnh mẽ cùng màn hình QHD siêu nét và hệ thống bảo mật tân tiến là những ưu điểm khiến chiếc laptop này trở nên thu hút không thể bỏ qua.</h1>	\r\n                    <h2 class = \"paragraph__h2\">Laptop siêu mỏng nhẹ, di động tối ưu</h2>\r\n                    <p class = \"paragraph__p\">Lenovo Yoga mang đến thiết kế cực kì nổi bật với chất liệu kim loại sáng bóng, phần mặt lưng được làm từ Carbon Fibre cứng cáp nhưng độ dày máy chỉ 15 mm và trọng lượng siêu mỏng nhẹ 0.966 kg, chỉ xấp xỉ 1 quyển sách để bạn thoải mái mang theo bên mình và giải quyết công việc hàng ngày.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234364/1.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Bằng sự tỉ mỉ hoàn thiện trong từng chi tiết, vỏ máy gia công nhiệt 3 lớp, máy tính cũng đạt được tiêu chuẩn độ bền quân sự MIL-STD-810G với 9 thử nghiệm bao gồm thả rơi, độ rung, nhiệt độ, áp suất và khả năng chống cát, bụi. Tất cả đã tạo nên một chiếc laptop có vẻ ngoài hoàn mỹ nhất, siêu nhẹ và bền.</p>\r\n                    <p class = \"paragraph__p\">Laptop Lenovo này còn ghi điểm với hệ thống bảo mật hiện đại của mình, máy trang bị 1 camera IR có tính năng mở khóa khuôn mặt vừa giúp mở máy nhanh hơn mở khóa bằng cách thông thường, vừa an toàn khi bạn chính là mật khẩu.</p>\r\n		    <p class = \"paragraph__p\">Điểm sáng tiếp theo là bàn phím layout vừa đủ mang lại hành trình gõ phím thoải mái và chính xác. Phần Trackpad rộng rãi cho cảm giác vuốt mượt mà hơn, không bị rít tay. Nhất là đèn nền được tích hợp dưới bàn phím có 2 chế độ tùy chỉnh độ sáng thích hợp sử dụng trong nhiều điều kiện môi trường ánh sáng khác nhau.</p>\r\n		    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234364/4.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hình ảnh hiển thị siêu nét, thỏa mãn giải trí trong một chiếc laptop</h2>\r\n                    <p class = \"paragraph__p\">Màn hình 13.3 inch độ phân giải QHD (2560x1600) cho hình ảnh, văn bản hiển thị sắc nét hơn 35% so với Full HD. Độ sáng màn hình đạt 300 nits kết hợp với công nghệ chống chói cho phép bạn có thể sử dụng máy tính ở ngoài trời có ánh sáng mạnh mà vẫn đảm bảo hiển thị rõ nét và màu sắc tốt hơn. Tấm nền IPS cộng thêm thiết kế viền mỏng sẽ đem đến không gian nhìn thật thoải mái và thư giãn với góc nhìn rộng không giới hạn.</p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234364/6.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Đặc biệt, Lenovo còn trang bị cho siêu phẩm của mình bộ đôi công nghệ Dolby Vision và Dolby Atmos. Bằng sự kết hợp giữa hình ảnh sắc nét, có độ tương phản vượt trội cùng âm thanh vòm giả lập bao trùm không gian sẽ giúp cho những giây phút xem phim, nghe nhạc giải trí ngay trên chiếc laptop này trở nên tuyệt vời hơn.</p>\r\n		    <p class = \"paragraph__p\">Công nghệ âm thanh Audio by Harman/Kardon cho chất âm tuyệt hảo, hài hòa giữa âm cao và âm trầm, đem đến độ chi tiết chính xác đồng thời tạo nên âm thanh sống động. </p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234364/7.jpg\" alt=\"\">\r\n                    <h2 class = \"paragraph__h2\">Hiệu năng mạnh mẽ tăng năng suất công việc tối ưu</h2>\r\n                    <p class = \"paragraph__p\">Lenovo Yoga Slim 7 sử dụng bộ vi xử Intel Core i7 Tiger Lake thế hệ 11 vừa ra mắt cuối năm 2020 cho mức xung nhịp cao nhất lên đến 4.7 GHz xử lí nhanh, ổn định mọi ứng dụng văn phòng thông dụng. Kết hợp với bộ nhớ RAM 16 GB giúp đa nhiệm các tác vụ nặng mà vẫn mượt mà và ổn định. Bạn có thể mở cùng lúc đến 20 tab Chrome hay hàng chục layer trên Photoshop, Ai mà không lo đứng máy. </p>\r\n		    <p class = \"paragraph__p\">Tích hợp trong CPU là card đồ họa Intel Iris Xe Graphics tăng khả năng đồ họa, cho khả năng xử lí hình ảnh chuyên nghiệp, chỉnh sửa video, dựng hình 3D không quá phức tạp. Tuy là một chiếc laptop doanh nhân nhưng chiếc máy này cũng \"cân\" được nhiều tựa game hot với cấu hình cao hiện nay như PUBG, Far Cry 5,...</p>          \r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234364/9.jpg\" alt=\"\">           \r\n                    <p class = \"paragraph__p\">Bên cạnh đó laptop còn đạt tiêu chuẩn Evo để đạt tiêu chuẩn này chiếc laptop phải có kích thước mỏng nhẹ, thời lượng pin trên 9 tiếng, phải có sạc nhanh và phải vượt qua một số bài kiểm tra về khả năng xử lý tác vụ khắc khe./p>\r\n                    <img class = \"paragraph__img\"src=\"https://cdn.tgdd.vn/Products/Images/44/234364/11.jpg\" alt=\"\">\r\n                    <p class = \"paragraph__p\">Lenovo Yoga Slim 7 là chiếc laptop cao cấp với thiết kế thời trang, cấu hình khủng cùng nhiều tính năng hiện đại có thể đồng hành cùng bạn trong công việc cũng như giải trí hằng ngày.</p>            \r\n<button class =\"paragraph__more\">Xem thêm !</button>', 4, 1);

